DROP TABLE IF EXISTS `tbl_gnc_advices_raw`;

CREATE TABLE `tbl_gnc_advices_raw` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `branch` varchar(255) DEFAULT NULL,
  `raw` text,
  `tab` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

LOCK TABLES `tbl_gnc_advices_raw` WRITE;
/*!40000 ALTER TABLE `tbl_gnc_advices_raw` DISABLE KEYS */;

INSERT INTO `tbl_gnc_advices_raw` (`id`, `branch`, `raw`, `tab`)
VALUES
	(1,'容颜美好','[{\"section\":\"容颜问题盘点\",\"title\":\"问题皮肤更要防护皮肤老化\",\"content\":\"缺乏弹性 表皮细胞减少，真皮层纤维硬化、脆弱、新陈代谢不活跃，这些都会使肌肤失去饱满、圆润的感觉。当然这和足够蛋白质，长期接触污染环境，熬夜、烟酒有很大的关系，我们可以通过生活方式和细心料理令皮肤弹性改善。\"},{\"section\":\"容颜问题盘点\",\"title\":\"问题皮肤更要防护皮肤老化\",\"content\":\"皮肤不细腻 皮肤不够细腻，水油不平衡导致皮肤纹路紊乱。角质在我们的皮肤中不可或缺地存在着，可以保护皮肤不受外界刺激，同时维持皮肤内的水分和电解质。但当各种不良生活习惯导致角质层代谢缓慢而逐渐增厚时则会使得皮肤看起来粗糙、不细腻、毛孔大、T部位油腻、妆容容易脱落等。\"},{\"section\":\"容颜问题盘点\",\"title\":\"问题皮肤更要防护皮肤老化\",\"content\":\"皮肤松弛 虽然皮肤会随着年龄增加而不可避免地松弛，但若加强保护可以延缓这个过程。在日光、辐射、烟雾环境中要对皮肤做适当保护，日常饮食营养要充足。\"},{\"section\":\"容颜问题盘点\",\"title\":\"问题皮肤更要防护皮肤老化\",\"content\":\"细纹、皱纹 细纹通常是缺乏水分和油脂的保护造成的，还有熬夜、烟酒、在日晒和辐射环境中缺乏保护也都是易生细纹的原因。\"},{\"section\":\"容颜问题盘点\",\"title\":\"这些问题更多是身体内因的反映\",\"content\":\"肤色暗沉 内因对皮肤暗沉的影响更大，皮肤颜色晦暗无光泽，一方面与遗传有关，但因不良的生活方式导致的肝功能减弱，血液循环不畅，不但会引起肤色的暗沉，与色斑、过敏、痤疮也都脱不开干系。不良日常生活方式包括：盐分摄入过量、吸烟、睡眠不足、久坐、压力过大等。\"},{\"section\":\"容颜问题盘点\",\"title\":\"这些问题更多是身体内因的反映\",\"content\":\"出油 仅仅清洗不能解决皮肤出油的问题，因为熬夜、吃辣多、吃得油腻、使用的护肤品太滋腻，甚至情绪容易激动都会加重皮肤出油。\"},{\"section\":\"容颜问题盘点\",\"title\":\"这些问题更多是身体内因的反映\",\"content\":\"阳光敏感 并未长时间被阳光照射也会敏感，药物治疗也并未有良好效果。可以从睡眠质量、压力、饮食、环境等方面改善整体健康，提升机体抗氧化能力。\"},{\"section\":\"容颜问题盘点\",\"title\":\"这些问题更多是身体内因的反映\",\"content\":\"皮肤干燥 年龄、气候变化、长期处在中央空调环境中、空气污染、电磁辐射、都会使体内水分流失。减肥或者偏食造成肌肤营养失衡，使肌肤失去弹性及水分；睡眠不足导致身体受到某种程度的损害，血液循环能力变差，肌肤失去活力，这些都是肌肤干燥的原因。\"},{\"section\":\"容颜问题盘点\",\"title\":\"这些问题更多是身体内因的反映\",\"content\":\"毛孔粗大 过多的皮脂分泌是导致毛孔变大的主要原因，次要原因还有毛细孔污物阻塞，过度挤面疱和粉刺等。影响皮脂分泌的因素则有很多，包含衰老、激素、季节、紫外线、压力等，这些原因使皮脂分泌旺盛、皮肤弹性变差，毛孔粗大问题则会更严重。\"},{\"section\":\"容颜问题盘点\",\"title\":\"这些问题更多是身体内因的反映\",\"content\":\"嘴唇干 嘴唇周围一圈发红的区域叫“唇红缘”，它的湿润全靠局部丰富的毛细血管和少量发育不全的皮脂腺来维持。如果人体皮肤黏膜血液循环差，新鲜水果蔬菜吃得少，人体维生素B2、维生素A摄入量不足，嘴唇就会干燥开裂。 \"},{\"section\":\"容颜问题盘点\",\"title\":\"这些问题更多是身体内因的反映\",\"content\":\"季节敏感 季节转换时，尤其是秋冬季会感到皮肤干痒，多数和皮肤干燥缺水有关。\\n而睡眠质量、压力、饮食、环境、年龄、清洁过度都会令皮肤变得干燥敏感。\"},{\"section\":\"容颜问题盘点\",\"title\":\"色斑，身体在皮肤上的印记\",\"content\":\"晒斑 黑色素生成的过程中，最直接的影响就来自于阳光中的紫外线。它能激活色素母细胞中的酪氨酸酶，与血液中的酪氨酸酶产生一系列反应，生成一种叫“多巴”的物质，进而转化成黑色素。\"},{\"section\":\"容颜问题盘点\",\"title\":\"色斑，身体在皮肤上的印记\",\"content\":\"黄褐斑 黄褐斑的发生一般以中年女性居多，多发于面颊两侧、鼻子、额头和眼睛周围，一般见于慢性妇科病、肝斑等。黄褐斑的出现多数与内分泌有关，尤其是和女性的雌性激素水平有关。因此月经不调、服食避孕药、肝功能或慢性肾病都会引起黄褐斑的出现，而日晒和精神压力又会加重黄褐斑的颜色。\"},{\"section\":\"容颜问题盘点\",\"title\":\"色斑，身体在皮肤上的印记\",\"content\":\"雀斑 雀斑的颜色多为棕色、淡褐色，严重的呈浅黑色，呈芝麻大小的圆形或者卵圆形颗粒状分布，表面光滑，不高于皮肤表面，常由家族遗传而来，5—7岁开始生长，隔代遗传呈显性遗传。\"},{\"section\":\"容颜问题盘点\",\"title\":\"痘痘，内因和外因的共同作用\",\"content\":\"情绪不佳长痘 在感到厌烦、紧张、不开心时，体内的压力荷尔蒙就会分泌，肌肤的循环机制也会被打乱，导致内分泌紊乱，皮脂腺分泌异常堵塞毛囊。调节情绪，放松心情，保证张弛有度的规律生活才能解决根本问题。\"},{\"section\":\"容颜问题盘点\",\"title\":\"痘痘，内因和外因的共同作用\",\"content\":\"饮食刺激长痘 刺激性饮食，如辛辣、高脂肪、咖啡、高糖等食物都有可能成为痘痘发生的诱因，不过本质问题还是影响到内分泌失调导致皮脂腺分泌异常,毛囊堵塞所造成的。需要注意一下自己的生活方式来改善如此容易失调的内分泌。\"},{\"section\":\"容颜问题盘点\",\"title\":\"痘痘，内因和外因的共同作用\",\"content\":\"睡眠不佳长痘 睡眠质量不高的，经常失眠、多梦、熬夜、睡眠不规律、睡眠时间不充足等这些都可能造成痘痘爆发。这主要是因为肝脏等器官在帮身体进行排毒期间未能得到妥善休息，体内毒素淤积而引起长痘痘。需要在生活方式上加以注意，帮助肝脏代谢毒素。\"},{\"section\":\"容颜问题盘点\",\"title\":\"痘痘，内因和外因的共同作用\",\"content\":\"长期长痘 皮脂的大量分泌会造成毛孔堵塞，继而成为产生痘痘的原因。黑头或者白头粉刺是痘痘形成的初始状态，发红肿胀的痘痘则是受细菌感染进一步恶化的状况。饮食、睡眠、压力等生活方式失衡、内分泌失调都可能成为皮脂大量分泌的原因。\"},{\"section\":\"容颜问题盘点\",\"title\":\"皮肤敏感引发的问题\",\"content\":\"对酒精和果酸成分过敏  通常对酒精刺激敏感的人，不会仅仅只在面部皮肤发生过敏现象。除了避免接触酒精外，也要避免饮酒。\\n 果酸存在于许多水果、糖类和乳制品中，当浓度过高或皮肤对营养吸收能力差的时候就会发生对果酸的过敏。少数甚至有全身反应，如哮喘、喷嚏、水肿等。\"},{\"section\":\"容颜问题盘点\",\"title\":\"皮肤敏感引发的问题\",\"content\":\"食物引发过敏 食物引起的过敏是免疫系统参与的特异性反应。通常和胃肠道健康程度或遗传因素有关。饮酒、长期服药、缺少必需脂肪酸等都会加重过敏。\"},{\"section\":\"容颜问题盘点\",\"title\":\"皮肤敏感引发的问题\",\"content\":\"红血丝 红血丝皮肤是敏感性皮肤的一种，诱因是冷热温度、季节变化、日光等环境因素、压力、情绪变化的影响。这些都会导致一方面是皮肤的角质层较薄，另一方面是异常扩张的毛细血管。另外一些不良的生活习惯，如烟酒、嗜辣、偏食、疲劳、长时间睡眠不足，都会使皮肤处于亚健康状态，血管壁韧性变差，对红血丝起到推波助澜的作用。\"},{\"section\":\"容颜问题盘点\",\"title\":\"头发的状态也和整体健康有关\",\"content\":\"头发出油 头发出油仅仅靠清洗并不能从根本上解决，因为熬夜、吃辣多、吃得油腻，甚至情绪容易激动都会加重头皮出油。\"},{\"section\":\"容颜问题盘点\",\"title\":\"头发的状态也和整体健康有关\",\"content\":\"头皮屑 头皮屑是头皮的一种不健康状态，包括头皮瘙痒和呈屑状脱落。引起的原因有：皮肤干燥、油性皮肤刺激、头发不清洁、牛皮癣、湿疹及类酵母菌感染，还有压力、季节变化、荷尔蒙失调也会影响头皮屑的发生。\"},{\"section\":\"容颜问题盘点\",\"title\":\"头发的状态也和整体健康有关\",\"content\":\"发质不佳 头发干燥、无光泽、脆弱易折或脱发和过度烫染、营养缺乏有关，比如锌、生物素、蛋白质、必需脂肪酸等。而饮食不均衡、压力过大、生活不规律都可能导致这些营养的缺乏。\"},{\"section\":\"容颜问题盘点\",\"title\":\"头发的状态也和整体健康有关\",\"content\":\"白头发 白头发不一定是衰老的征兆，正常人一般是35岁开始生白头发，如果过早就有了白发，有可能和营养缺乏或长期精神紧张有关。\"},{\"section\":\"容颜问题盘点\",\"title\":\"眼部的美丽健康和生活方式有关\",\"content\":\"黑眼圈 黑眼圈和很多因素相关，眼部下方的皮肤过薄、过于脆弱，当疲劳、盐分摄入过多、生理期、睡眠不足、压力过大等状况发生时，容易导致血液循环不畅，于皮肤最薄的地方显示出来，因此黑眼圈并不仅仅是一个美容问题，也是健康问题。\"},{\"section\":\"容颜问题盘点\",\"title\":\"眼部的美丽健康和生活方式有关\",\"content\":\"眼神不够明亮 眼睛的结膜若长期受到紫外线、电磁辐射、粉尘、身体中的自由基的侵害，就会逐渐失去青春时期的明亮润泽感，增强抗氧化保护可以延缓这个过程。\"},{\"section\":\"容颜问题盘点\",\"title\":\"指甲也能反映身体问题\",\"content\":\"指甲无光泽 通常无光泽的指甲提醒你注意贫血、营养不良，甚至肝脏问题。(信息来自WebMD)  \"},{\"section\":\"容颜问题盘点\",\"title\":\"指甲也能反映身体问题\",\"content\":\"指甲容易断裂 除了经常打磨指甲外，有可能是营养不良所致，尤其是蛋白质和维生素B族中的生物素。\"},{\"section\":\"容颜问题盘点\",\"title\":\"指甲也能反映身体问题\",\"content\":\"指甲有波纹或坑点 提醒你需要注意预防关节炎。注意关节的保暖防湿，避免关节劳累。(信息来自WebMD)  \"},{\"section\":\"生活习惯提醒\",\"title\":\"不良的就餐习惯有损美好容颜\",\"content\":\"不吃早餐 不吃早餐破坏结肠反射，形成便秘，令毒素滞留体内，易生痘痘，色斑等。同时身体又通过分解储存的蛋白质来提供能量，最终导致营养不良，气血不足，从而令面色晦暗无光泽。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不良的就餐习惯有损美好容颜\",\"content\":\"经常吃得过饱 常常吃撑会让皮肤变差。吃撑会引起血糖升高，此时人体脂肪合成加速，同时过剩的食物残渣，特别是高脂肪的食物在肠内利于大量的厌氧菌生长及产生其它有害的毒物，长时间刺激粘膜，超过了肝脏的解毒能力，就会表现到脸上，出现油光，痘痘，色斑等。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不良的就餐习惯有损美好容颜\",\"content\":\"常在外就餐 在繁忙的生活和污染的环境中，人体需要更多的抗氧化剂来清除体内促进衰老的自由基，餐馆的外卖，很少提供新鲜的果蔬和口味清淡的菜肴。这会令维生素和矿物质摄入不足，影响皮肤细胞的正常新陈代谢。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，影响皮肤状态\",\"content\":\"爱吃油炸 常吃油炸食物对皮肤健康不利。油炸食物会产生自由基，破坏食物中的必需脂肪酸，损伤细胞，除了表现出油、长痘、长斑等问题，还会增加心脏病，癌症和早衰的风险。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，影响皮肤状态\",\"content\":\"常吃包装或加工食品 包装零食和加工食品多数含有各种添加剂，这会增加肝脏解毒负担，令面色不佳，皮肤容易长痘，长斑。又因为多吃零食可能影响正餐食欲，导致营养不均衡，缺乏充足的蛋白质和能量，使得精力不足，皮肤缺乏弹性、光泽。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，影响皮肤状态\",\"content\":\"常喝甜饮料 常喝甜饮料对皮肤健康无益。含糖饮料很难提供额外的水分，甚至令身体缺水。同时糖造成酵母菌在肠道的过度繁殖，这可能引起便秘，引发痘痘、色斑等皮肤问题。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，影响皮肤状态\",\"content\":\"爱吃重口味食物 喜欢吃重口味食物说明你需要适当放松和休息。长期吃浓油赤酱的菜，除了会影响皮肤健康外，也容易导致血脂、血压、血糖等健康指标出问题。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，影响皮肤状态\",\"content\":\"肉食过多 过度摄入肉食，则意味着饱和脂肪酸或脂溶性的毒素摄入会较多，这使皮肤容易发炎，增加痘痘、过敏等问题。\"},{\"section\":\"生活习惯提醒\",\"title\":\"健康食物吃得过少，不利于保持皮肤的最佳状态\",\"content\":\"蔬果吃得少 水果蔬菜吃得少，会加重皮肤问题。水果蔬菜中富含能清除毒素和肠道垃圾的维生素以及矿物质，对改善痘痘、出油、色斑有帮助。\"},{\"section\":\"生活习惯提醒\",\"title\":\"健康食物吃得过少，不利于保持皮肤的最佳状态\",\"content\":\"粗粮吃得少 很少吃粗粮会令纤维素和维生素B族以及矿物质缺乏。纤维素能减少胆固醇和脂肪的吸收，还能维持血糖的平稳，也能帮助粪便成型，排出毒素，有助于保持皮肤健康。维生素B族是皮肤组织修复与痊愈所需要的。各种矿物质也是维护身体健康所必需的。\"},{\"section\":\"生活习惯提醒\",\"title\":\"健康食物吃得过少，不利于保持皮肤的最佳状态\",\"content\":\"豆、奶制品吃得少 很少吃豆、奶制品可能导致你缺乏钙质，奶制品和豆制品被认为是钙的主要来源，钙是维持骨骼、牙齿、神经健康的主要矿物质，缺乏则容易造成失眠、紧张、疼痛、骨质疏松、龋齿等问题。豆、奶制品也是优质蛋白质的主要来源，若优质蛋白质缺乏，则增加皮肤老化、干燥的风险 。  \"},{\"section\":\"生活习惯提醒\",\"title\":\"不规律的睡眠扰乱循环系统生物钟，身体隐形“生病”\",\"content\":\"睡眠不规律 其实除了睡眠，我们心率、呼吸、血压、体温、蛋白质合成等体内一切生命活动，往往都呈现昼夜节律的倾向，反映在血液循环、内分泌和各种器官功能中。不规律的睡眠就好比在不停地倒时差，各器官的生物钟调整所需的时间不同，睡眠两三天就可以调整过来，但是体内的器官及循环系统的生物钟却需要两三周的时间调整。被扰乱的内分泌功能会令皮肤的毛细血管健康受到影响，引起痘痘、色斑、肤色暗沉等问题。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳，影响皮肤和健康\",\"content\":\"多梦 造成多梦的因素，同时也会扰乱人体内分泌，导致皮肤问题频发。压力大、精神紧张、焦虑都有可能成为多梦易醒原因。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳，影响皮肤和健康\",\"content\":\"起床困难或睡不够 疲劳或睡眠不规律都可能导致睡不够，而赖床的习惯通常和三餐不规律伴随出现，若不注意营养的均衡搭配则容易导致皮肤状态不佳。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳，影响皮肤和健康\",\"content\":\"睡眠不足 每个人需要的睡眠时间长短并不一样， 优质的充足睡眠对皮肤状态非常重要，如果醒来后面色红润、精力充沛，说明你的睡眠效率很高。睡眠时流经肝脏的血液是平时的七倍，有利于增强肝细胞功能，提高解毒能力。尤其是1点－3点是皮肤代谢的高峰期，此时不睡很容易导致皮肤干燥、暗沉。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳，影响皮肤和健康\",\"content\":\"失眠 入睡困难或者失眠的人往往表现为：很难睡着、晚上总是醒、睡得不踏实，很难再入睡。一两天的失眠就可以导致面色暗沉、痘痘频发。引起失眠的原因很多，压力、咖啡因、酒精、环境变化、时差、慢性疾病、焦虑等等，解决问题首先要找到原因，是不是有些不良的生活习惯，导致这个问题，不过，有时候，这可能是自己无法控制的，但是要明确一点，这并不是不可以解决的问题，所以不用担心，给自己一点儿耐心。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳，影响皮肤和健康\",\"content\":\"经常熬夜 经常熬夜，可以被称为慢性睡眠不足，已经有大量的科学研究表明睡眠不足与心脏疾病、高血压、中风、糖尿病有直接关系。\\n当然在这些问题发生之前，你可能正在经历着熬夜带来的皮肤问题，只需要几个晚上，皮肤就马上会呈现出蜡黄，眼睛浮肿，长期则导致皮肤黯淡，细纹，眼睛下方的黑眼圈。 没有在适当的时候睡觉，生物节律被打乱，身体会释放更多的应激激素皮质醇，这些皮质醇会分解皮肤中的胶原蛋白，令你的皮肤失去光滑和弹性。\\n\"},{\"section\":\"生活习惯提醒\",\"title\":\"运动捍卫您的生命，不可或缺\",\"content\":\"每天或者隔天花30~60分钟，做一些有氧运动，这是保持年轻活力的很好方法，有氧运动会使肌肉消耗大量的氧气和营养物质，从而使得胃肠、心肺、肾脏等功能提高。在一定条件下，能量消耗得越多，这种补偿作用越明显。\"},{\"section\":\"生活习惯提醒\",\"title\":\"辐射产生的自由基加速人体细胞氧化\",\"content\":\"手机和电脑产生的低频电磁辐射是人体外源性自由基产生的原因之一。随着辐射剂量累积增加，可能会引起神经系统、循环系统、免疫系统、生殖系统以及皮肤和眼睛的功能、代谢或形态的改变，这是一个自由基生成加速身体细胞氧化的过程。\"},{\"section\":\"生活习惯提醒\",\"title\":\"吸烟将危及人的全身皮肤\",\"content\":\"也许你见过香烟面容，这是吸烟者的容颜收到永久性损害后呈现的特有的烟熏火燎的面容，口唇发乌，牙齿黑黄，舌苔黄腻，口气很重，身上味道难闻。\\n美国权威专业杂志《皮肤病学文献》中的一项研究表明，吸烟将危及人的全身皮肤。当皮肤(尤其是脸部)被曝晒于强光下时，人的皮肤就变得很粗糙，变得有褶皱，并变成淡黄色。\"},{\"section\":\"生活习惯提醒\",\"title\":\"过量饮酒影响美好容颜\",\"content\":\"过量饮酒的人看上去往往更加憔悴。人们普遍认为过量饮酒可导致提前衰老并遭到与衰老有关的疾病侵袭。\\n意大利米兰大学进行的一项研究指出，过量酒精可使细胞染色体失去完整性和稳定性的保护，从而加速衰老增加癌症风险。\\n世卫组织认为每日安全饮酒量的上限是25g酒精，相当于1两42度白酒，或200ml红酒，一瓶啤酒。\"},{\"section\":\"体检问题预警\",\"title\":\"降低血脂，降低心脑血管患病风险\",\"content\":\"目前无论在美国还是中国，心脑血管疾病仍然是头号杀手。 血脂在动脉血管壁的沉积是毫无声息的，如果体检已经确认血脂超标，就必须在生活方式上加以注意，加强运动，进行低脂肪饮食等，无效的情况下，需要在医生指导下借助药物改善状况。 \"},{\"section\":\"体检问题预警\",\"title\":\"体重增加令代谢功能降低，增加罹患多种慢性疾病风险\",\"content\":\"肥胖的原因很多，但总归导致体内脂质代谢紊乱，造成脂肪在皮下和内脏器官周围堆积，这使得人们患冠心病、高血压、糖尿病等慢性疾病的风险增加，要改变生活方式；饮食和运动水平合理化；下定决心减肥，给自己时间和耐心，逐渐改变饮食和运动习惯；只有长期的改变才算成功。\"},{\"section\":\"体检问题预警\",\"title\":\"冠心病增加猝死风险\",\"content\":\"冠心病若养护不当，猝死率很高（统计表明50-60%猝死是冠心病）。吸烟、高血压、高血糖、高血脂、肥胖、久坐等问题都是冠心病发作的危险因素，通过合理的饮食和有规律的运动，可以大大减少患病的风险，甚至可以免除药物的使用。可以配合每天服用抗氧化维生素，如维生素E、维生素C、叶酸等，阻止低密度脂蛋白在血管的积累，预防发病。\"},{\"section\":\"体检问题预警\",\"title\":\"腰围超标意味越过慢病安全警戒线\",\"content\":\"腰围的重要性仅次于体重指数。腰围的大小可以表明脂肪堆积的部位。脂肪聚集在腰上的人更容易患高血压、糖尿病、冠状动脉疾病，因为腹部的脂肪更容易分解并在动脉里聚集。男性腰围超过102厘米或女性超过88厘米，患相关疾病的危险就会增大，尤其是体重指数为25或以上的人群。\"},{\"section\":\"体检问题预警\",\"title\":\"有高血压问题，生活方式是不容忽视的\",\"content\":\"最好的方法是改变生活方式，包括饮食习惯以及运动等。世界高血压组织研究表明，如果只是1期的高血压，是可以通过生活方式的改善控制血压的。即便是通过用药，生活方式也不容忽视，限制食盐的摄入，减重，运动，戒烟，限制酒精的摄入：每日饮用超过30毫升酒精,相当于600毫升啤酒,200毫升葡萄酒或75毫升标准威士忌,会使血压升高并使冠心病、中风的发病和死亡率上升。\"},{\"section\":\"体检问题预警\",\"title\":\"糖尿病意味着您需要更平衡的生活\",\"content\":\"血糖水平往往受到饮食、运动、差旅、压力等生活习惯的影响。糖尿病意味着需要比其他人更注意你的生活，摄入具有丰富膳食纤维的蔬果，减少高脂肪食物和零食，控制体重，控制吸烟和饮酒。有规律的运动可以帮助保持身体素质，对心脏和血管都有益处，促进血液循环，并定期监测自己的血糖水平，防止糖尿病并发症发生的风险。\"},{\"section\":\"体检问题预警\",\"title\":\"对于骨质疏松日常生活的调理比治疗更有效果\",\"content\":\"先天因素，饮食中钙摄入不足，不常运动，体格瘦弱，吸烟、过量饮酒、女性过早绝经，都是直接导致骨质疏松的危险因素，不过相对于恢复已降低的骨密度，保持骨密度不变要来的更容易些。预防措施包括摄入足够的钙和维生素D和从事负重锻炼。\"},{\"section\":\"体检问题预警\",\"title\":\"即便没有不舒服的症状也要重视脂肪肝\",\"content\":\"脂肪肝是肝细胞内过量的脂肪（甘油三酯）积累，脂肪肝最常见的原因是酗酒、肝炎、肥胖、糖尿病、血清甘油三酯水平过高，因为起初没有任何症状，但可引起肝脏的反复损害，最终可从脂肪肝发展到肝硬化。早期如果在生活方式上加以改善也很快就会看到改善。\"},{\"section\":\"体检问题预警\",\"title\":\"前列腺炎需要积极配合治疗\",\"content\":\"慢性前列腺感染除了积极地配合治疗以外，在日常生活中也需要注意保护和防止急性发作，少吃辛辣，不可憋尿，精神压力不要过大，避免久坐，多喝水、防止受寒，保持清洁并且多参加体育锻炼。\"},{\"section\":\"体检问题预警\",\"title\":\"恰当的饮食起居可以改善胆囊健康\",\"content\":\"除了家族遗传的因素外，饮食油腻、暴饮暴食、压力造成的焦虑或易怒都会诱使或加重胆囊问题。少吃多餐、避免油腻高脂肪的食物、腹部保暖、保持情绪平稳都有助于避免胆囊问题的加重。\"}]','容颜健康评估'),
	(2,'容颜美好','[{\"section\":\"饮食习惯\",\"title\":\"口味清淡，皮肤洁净\",\"content\":\"身体的每一个细胞都是由所吃食物的营养构成的，从这个意义上说，你今天吃的东西，就是明天所“穿”的皮肤。少油、少盐、少糖、少辣的饮食令血液更清洁，也会令皮肤更健康。\"},{\"section\":\"饮食习惯\",\"title\":\"口味清淡，皮肤洁净\",\"content\":\"保护皮肤状态良好，减少痘痘、出油等问题，最好少吃这类食物：如煎炸食物、饱和脂肪（畜类肉、黄油、奶油）、高糖饮食（甜点、饮料、糖果）、高盐饮食等。\"},{\"section\":\"饮食习惯\",\"title\":\"口味清淡，皮肤洁净\",\"content\":\"蒸、煮、炖这些烹饪方式相对于煎炸能保留更多的营养，尤其是皮肤健康所需的抗氧化剂类和维生素。多汤水的饮食也能令皮肤更滋润。\\n\"},{\"section\":\"饮食习惯\",\"title\":\"多吃未经加工的天然食物，提供美颜所需新鲜营养\",\"content\":\"尽量选择新鲜的当地、当季产新鲜食物，它们除了能提供必需的营养素还能帮助代谢掉人体中产生有的有害物质，如促使老化的自由基。\\n加工食品中存在多种人工合成的化学物质，已有许多国家研究表明加工食品中的各种添加剂摄入过多，会存在致癌风险。\"},{\"section\":\"饮食习惯\",\"title\":\"多吃未经加工的天然食物，提供美颜所需新鲜营养\",\"content\":\"爱吃零食也可以改善肤质，重点是如何选择。时令水果、酸奶、原味坚果、天然果干，这些都可以作为零食，既能满足吃零食的习惯，也能给身体提供必要营养。\"},{\"section\":\"饮食习惯\",\"title\":\"多吃未经加工的天然食物，提供美颜所需新鲜营养\",\"content\":\"健康的头发所需的营养素和维护身体健康的全面营养一致，多吃营养全面的天然食物，尤其是富含蛋白质、优质脂肪的坚果、种子类食物，还有富含维生素和矿物质的全谷物和绿叶蔬菜。（信息来自WebMD）\"},{\"section\":\"饮食习惯\",\"title\":\"健康皮肤需要足量的洁净饮水\",\"content\":\"人体的70%是水分，含有矿物质的水或稀释的鲜榨果蔬汁可以协助身体排除细胞和血液中的代谢废物。这也是令你的皮肤看起来健康、紧致的基础条件。\"},{\"section\":\"饮食习惯\",\"title\":\"细嚼慢咽，七分饱\",\"content\":\"实验表明，长期适度减少热量摄入的人更长寿，体重更轻，患心脑血管疾病的风险更低。咀嚼的习惯也可以改善面部血液循环，有益于皮肤健康。\"},{\"section\":\"饮食结构\",\"title\":\"富含必需脂肪酸的食物有助于皮肤健康\",\"content\":\"吃些种子（芝麻、南瓜籽、松籽、葵花籽、花生等），坚果和种子油，可以提供对皮肤有益的必需脂肪酸，这是构成健康细胞膜的原料。健康的细胞膜可以保证皮肤的含水性和柔软性。（源自《营养圣经》）\"},{\"section\":\"饮食结构\",\"title\":\"多彩蔬果，保护容颜健康\",\"content\":\"多吃些黄色、橙色和红色的蔬果，它们都富含β-胡萝卜素，在体内能转换成维生素A，是维持皮肤健康必需的营养素。当维生素A缺乏时皮肤抗感染能力下降，肤质粗糙。\"},{\"section\":\"饮食结构\",\"title\":\"多彩蔬果，保护容颜健康\",\"content\":\"多吃些富含维生素C的蔬果，如：番石榴、草莓、猕猴桃、樱桃、橙子、鲜枣、番茄等。维生素C对容颜美丽必不可少1.维生素C是合成胶原蛋白必需的营养素，帮助保持皮肤弹性。2.维生素C是抗氧化剂，能够使深色的氧化型色素还原为淡色的还原型色素。从而抑制黑色素的形成，并还原已经形成的黑色素。3.维生素C是多种肝脏解毒酶的活化因子和酶促因子，可以提高肝脏排毒能力，促进血液循环，改善肤色暗沉。4.维生素C能增强毛细血管壁健康，增强机体抗氧化能力，从而改善黑眼圈和红血丝。\"},{\"section\":\"饮食结构\",\"title\":\"多彩蔬果，保护容颜健康\",\"content\":\"水果富含果胶和纤维素，可以减少脂肪吸收，协助清洁肠道，减少毒素，从而保护皮肤健康。\"},{\"section\":\"饮食结构\",\"title\":\"多彩蔬果，保护容颜健康\",\"content\":\"蔬菜水果能提供有益于皮肤健康的抗氧化剂，多吃杏肉，所有浆果，哈密瓜，柑橘类，猕猴桃，木瓜，桃子，芒果，瓜类，红葡萄。蔬菜类有，洋蓟，甜菜根，西兰花，紫甘蓝，胡萝卜，白色菜花，黄瓜，甘蓝，南瓜，菠菜，甘薯，番茄，豆瓣菜，豆类和豆芽。\"},{\"section\":\"饮食结构\",\"title\":\"充足的蛋白质是健康皮肤重要基础\",\"content\":\"食物中充足的蛋白质是构成健康皮肤的基础材料，皮肤真皮层75%由胶原蛋白组成，纤维状的胶原蛋白组成网状机关，蛋白质纤维就像撑起皮肤组织的钢筋架构，维持皮肤弹性和饱满度。\"},{\"section\":\"饮食结构\",\"title\":\"充足的蛋白质是健康皮肤重要基础\",\"content\":\"平日可以有意多吃些富含胶原蛋白的猪皮、鱼皮、牛蹄筋等。如果吃素也可以从丰富的杂粮、种子、豆类中获得足够的蛋白质。\"},{\"section\":\"饮食结构\",\"title\":\"充足的蛋白质是健康皮肤重要基础\",\"content\":\"“对付”红血丝的根本方法，在于增强皮肤细胞的免疫功能和自我修复功能。胶原蛋白可增强血管壁细胞的代谢功能，并形成保护屏障，有效地预防血管壁被轻易的破坏或受到病理作用的侵袭，恢复毛细血管弹性，增强毛细血管扩张、收缩能力。\"},{\"section\":\"饮食结构\",\"title\":\"充足的蛋白质是健康皮肤重要基础\",\"content\":\"头发、指甲和皮肤一样，主要是由蛋白质组成的，而缺乏胶原蛋白时，毛发、指甲无光泽、容易断裂。\"},{\"section\":\"饮食结构\",\"title\":\"常吃粗杂粮，清洁机体内环境\",\"content\":\"最新的美国营养学研究建议，主食中的一半应该是全谷物和粗杂粮，包括玉米、全麦制品、糙米、燕麦、薯类等。粗粮富含水溶性纤维素，能协助体内垃圾清除，维护肠道健康，还含有可以调节体内脂肪代谢的B族维生素，这都能改善皮肤健康状态。\"},{\"section\":\"饮食结构\",\"title\":\"增加钙的补充\",\"content\":\"骨矿研究测试表明，人到35岁以后，平均骨矿含量以每年0.5-1%的速率下降。钙在人体中处于不断丢失和补损的动态平衡过程中，人体中钙的存量关系到骨骼、牙齿的韧性，血液凝结，激活酶系统，维持肌肉伸缩性和心跳规律。\\n通过膳食来调节体内钙平衡是人类最佳补钙法，牛奶和豆类食物是钙的优质来源，此外还可以选择发菜、紫菜、荠菜、木耳、海米、软骨、骨汤等，或者适量芝麻、花生、榛子等坚果，都是很好的钙质来源。\"},{\"section\":\"饮食结构\",\"title\":\"富含维生素E的食物帮助延缓皮肤衰老\",\"content\":\"维生素E是抗氧化剂，能够进入皮肤细胞，有助于减少黑色素的沉积，延缓皮肤出现皱纹、干燥等衰老情况。维生素E只能在植物中合成，绿色的部分均含有维生素E。麦胚、玉米、大豆、向日葵中提取的油脂维生素E含量较高。\"},{\"section\":\"饮食结构\",\"title\":\"生物素可以帮助你的指甲更坚韧\",\"content\":\"生物素是维生素B族中的一种，研究表明80%指甲易断裂的人开始多吃富含生物素的食物或膳食补充剂几个月之后，情况大为改善。富含生物素的食物包括：啤酒酵母、玉米、大麦、大豆、核桃、花生等。\"}]','合理营养建议'),
	(3,'容颜美好','[{\"section\":\"\",\"title\":\"良好睡眠，健康容颜\",\"content\":\"可应对所有肌肤问题的对策之一就是保持充足的睡眠。肌肤的新陈代谢一般在夜间10点至凌晨2点之间进行，在正确的时间睡眠并且保证充足的睡眠可以增加肝脏血流量，使肝脏得到更多的血液、氧气及营养的供给，促进肝细胞的康复，这将有助于改善脸色,减少长痘、出油等。有时身体和心理上的疲倦也会引起肤色暗沉，因此要保持充足的睡眠，让身心同时得到放松。\"},{\"section\":\"\",\"title\":\"轻松心情，令皮肤更干净\",\"content\":\"让生活保持平衡，有自己放松的方法和时间，对健康和美好容颜都不可缺少。皮肤在压力的状态下更容易出现各种问题，如油脂分泌过度、长痘、长斑等，这是肾上腺素等激素在体内失衡的表现。\"},{\"section\":\"\",\"title\":\"吸烟，要停下来很容易\",\"content\":\"你可能尝试戒烟，并体会过戒烟带来的各种不适反应。但是应该记得的是，这些不适反应只不过是些可以改善的症状，远比吸烟对内脏、心脑血管的直接损伤来的微小的多。\"},{\"section\":\"\",\"title\":\"长期有规律的适量饮酒帮助提升机体健康\",\"content\":\"人体可自然分解消化酒精，每天适当饮酒可使动脉血管扩张、血压下降，有利于促进血液循环，更可提升对人体有利的高密度脂蛋白及降低有害的低密度脂蛋白，从而防止心脏病发作。而适量葡萄酒（含有抗氧化物质白藜芦醇）可能降低机体老化速度。\\n适量的饮酒相当于每天一杯150ml红葡萄酒或360ml啤酒。\"},{\"section\":\"\",\"title\":\"常常受到辐射侵扰，需要抗氧化剂抵抗辐射侵袭\",\"content\":\"1.需要有意识地通过饮食或膳食补充剂摄入花青素、维生素C、维生素E、维生素A等抗氧化剂，及时清除代谢产生的自由基。 2.避免长时间连续操作电脑，眼睛与屏幕的距离应在40~50厘米。  3.室内光线要适宜，不可过亮或过暗。  4.安装防护装置，削弱电磁辐射的强度。\"},{\"section\":\"\",\"title\":\"不论晚上睡眠如何糟糕，你的生物钟需要一个起点\",\"content\":\"比起控制睡眠时间，控制起床时间更为容易，保证在7：30分左右起床（或者是自己既定的时间，但是不要超过8：00），尽量将其保持在30分钟的浮动范围内，让你的起床时间有规律是改善睡眠不规律的方法，当然这取决于个人的意愿。\"},{\"section\":\"\",\"title\":\"循序渐进延长睡眠时间\",\"content\":\"如果你经常看着电视、看着书进入梦乡，这意味着你缺乏足够的睡眠，补充睡眠并不需要比往常提前1小时上床，这只会令你睡不着觉，每天晚上只要提前10~15分钟，在下午13点~16点之间小睡一下，半个小时为宜，多了则会影响晚间的睡眠，在调整睡眠规律期间清淡饮食。\"},{\"section\":\"\",\"title\":\"给自己一些时间调整你的睡眠\",\"content\":\"上床以后不要继续思考烦心事，如果睡不着，宁可起来做点事，也不要躺在床上发愁，真正折磨你的不是睡眠不足，而是你的忧愁。无论晚上几点入睡，早上都要在同一时间起床。可以尝试这个睡前的意念调节，平躺，全身放松，集中精神调节呼吸。闭上眼睛，浮想一些简单的图形；然后控制呼吸的节奏，缓慢吸气，保持一秒钟静止状态，缓慢呼气，反复几次。这样可以排除杂念，达到睡眠前的最佳状态！\"},{\"section\":\"\",\"title\":\"养成排便好习惯，有利于保持容颜洁净\",\"content\":\"养成良好排便习惯，保持肠道清洁，减少毒素被再次吸收，影响皮肤健康。最佳的排便时间是清晨起床后，即使缺少便意，也可以培养，逐渐形成条件反射。睡前按揉腹部，有助于胃肠蠕动，增加便量。\"},{\"section\":\"\",\"title\":\"自制天然去屑洗发水\",\"content\":\"将头发洗净，再用一个柠檬榨汁或者等量果醋滴在温水里冲洗头发，同时按揉头皮。坚持一段时间，可以改善头屑困扰。\"},{\"section\":\"\",\"title\":\"水温适中保护头部油脂分泌正常\",\"content\":\"过高的水温会刺激头皮油脂分泌，常用过热的水洗发会导致头发油脂分泌过多，长久如此又会令头发干燥。洗头发最好用略低于体温的水。\"},{\"section\":\"\",\"title\":\"梳头有助改善头发健康\",\"content\":\"每天梳头，可以改善头皮供血，改善头发和头皮的健康。\\n1.用木质、玉石、牛角或鬃刷梳头发。\\n2.不要用力牵扯，尤其是头发还湿湿的时候。\\n3.要梳到头皮，给你的头皮做个按摩。\"},{\"section\":\"\",\"title\":\"你的头发需要适当的保护\",\"content\":\"1.减少烫、染的次数，每年不超过2次。\\n2.洗发后先用干发毛巾吸干水分，再用合适的热度吹干发根即可。避免过热的吹风伤害头发。\\n3.避免在阳光下暴晒过久，这也会伤害你的头发。\\n4.游泳前不要洗发，游泳后要用有保护功能的洗发水去除粘附在头发上的泳池中化学物质。\"},{\"section\":\"\",\"title\":\"绿茶冷敷，改善阳光敏感\",\"content\":\"日晒后过敏，可用浓的绿茶水冷敷，新鲜的绿茶含有丰富的茶多酚，能抗紫外线损伤。最好用当年的新茶，开水冲泡后放凉，浸泡面膜纸或者干净毛巾敷面。\"},{\"section\":\"\",\"title\":\"减少日光直射\",\"content\":\"防晒是去斑关键。防晒全年都有紫外线，因此不管什么季节都要坚持防晒。尽量减少紫外线进入肌肤内层的机会，预防肌肤细胞向黑素母细胞传递信息，从而预防晒斑形成。\"}]','生活方式建议'),
	(4,'容颜美好','[{\"section\":\"运动改善皮肤问题\",\"title\":\"肤色差 ?  有氧运动给你好气色\",\"content\":\"定期的有氧运动，会帮助血液和淋巴液的循环流畅，减少体内毒素积累，有助于脸色保持健康红润。\"},{\"section\":\"运动改善皮肤问题\",\"title\":\"毛孔大 ?  运动排毒，改善肤质\",\"content\":\"在运动过程中，身体的血液循环加快，皮肤新陈代谢也自然加速，身体大量排汗，一些残留在毛孔中的脏污和废物也随之排除，毛孔能得以自由呼吸。同时，经常运动可以使肌肤紧实而富有弹性。\"},{\"section\":\"运动改善皮肤问题\",\"title\":\"压力下长痘 ?  运动释放压力，改善皮肤状态\",\"content\":\"让身体微微出汗的有氧运动不仅能帮助身体排出毒素，令皮肤更健康，还能调节情绪，因为运动能够促使大脑和其他部位释放β-内啡肽（天然镇定剂）使人感到心情愉快。\"},{\"section\":\"运动改善皮肤问题\",\"title\":\"皮肤不紧致 ?  运动保持容颜年轻态\",\"content\":\"脸部的皮肤和身体其他各个部位一样，也需要靠运动来保持自己的活力。缺少运动，皮肤就容易衰老、缺乏弹性。皮肤的运动包括主动运动和被动运动两种。主动运动则是日常的有氧或无氧训练，被动运动则是对松弛部位的保养按摩。\"}]','有效运动建议'),
	(5,'精力脑力','[{\"section\":\"压力信号盘点\",\"title\":\"精力不佳，需要适当休息\",\"content\":\"精力透支 精力的水平和身体的整体健康有关。长期精力透支是健康的隐形炸弹。你需要更长时间的休息，更有规律的生活习惯，更充足均衡的营养，以保持长久的最佳状态。\"},{\"section\":\"压力信号盘点\",\"title\":\"精力不佳，需要适当休息\",\"content\":\"容易疲劳 除了过度消耗精力外，疲劳也可以因为饮食习惯不佳，缺乏运动或运动过度，缺乏睡眠，闷热季节，或者服用某些处方药物引起。当你获得充分休息后仍然很长一段时间觉得疲劳，最好咨询专业医师。\"},{\"section\":\"压力信号盘点\",\"title\":\"精力不佳，需要适当休息\",\"content\":\"工作拖延 压力越大，越容易拖延，越拖延心理压力就越大，最后的结局就是缺乏雕琢的草草收场，也很难让自己有成就感。\"},{\"section\":\"压力信号盘点\",\"title\":\"精力不佳，需要适当休息\",\"content\":\"过量咖啡、烟、茶 过量食用咖啡、茶、糖或巧克力与患糖尿病的可能性有直接联系。短时间内这些东西可能让你精力充沛，但从长远来看，大量使用刺激物可能让你未老先衰。尝试停用刺激物，并补充营养素（抗氧化剂和必需脂肪酸），多数人在30天内就感到精力有所提升。（源自《营养圣经》）\"},{\"section\":\"压力信号盘点\",\"title\":\"压力会消耗营养素，导致负面情绪\",\"content\":\"压力增加维生素和微量元素的消耗， 存在压力时身体会加大能量消耗，若能量和营养素提供不充足，则导致血糖快速降低，这也是为何焦虑时特别想吃甜食的原因。 血糖低时人容易发怒、紧张、沮丧、疲乏、注意力分散。（源自《营养圣经》）\"},{\"section\":\"压力信号盘点\",\"title\":\"脑力水平下降，需及时调整\",\"content\":\"记忆力、注意力、反应能力会受到年龄、营养水平、睡眠质量、情绪、刺激物等因素的影响。当出现短期的脑力水平下降时，其实是身体在提醒你需要改善生活方式。（源自《压力管理策略》）\"},{\"section\":\"压力信号盘点\",\"title\":\"压力已导致您出现身体问题\",\"content\":\"食欲不佳或亢奋 当人体感受到压力，需要增加能量消耗，身体会分泌一种叫皮质醇的激素，调动体内营养物质转化为血糖，以给细胞提供能量，这就会使食欲增加，尤其对高能量食物更感兴趣（源自《营养圣经》）。压力下也常见消化不良，此时饮食一定要清淡,容易消化。\"},{\"section\":\"压力信号盘点\",\"title\":\"压力已导致您出现身体问题\",\"content\":\"头痛胸闷 压力会引起头痛和胸闷。长期处于紧张生活环境的人心理压力大，当压力无法缓解时表现尤为明显。保证充足的睡眠、规律的饮食、适度的有氧运动都能有缓解头痛的效果。\"},{\"section\":\"压力信号盘点\",\"title\":\"压力已导致您出现身体问题\",\"content\":\"便秘 精神紧张、过度劳累以及不规律的排便习惯都可能引起便秘。长期便秘会影响脾胃的运行，造成大肠的传导失常，产生大量毒素堆积，会继发肠胃不适、口臭、色斑等其他症状。\"},{\"section\":\"体检问题预警\",\"title\":\"压力可通过增加食欲，加速变胖\",\"content\":\"伦敦大学医学院持续19年的研究结果发现压力和肥胖的可能性是成正比的，压力下对高糖、高脂肪食物的需求可以改善情绪，但增加肥胖可能。肥胖使得人们患冠心病、高血压、糖尿病等慢性疾病的风险增加，要改变生活方式；饮食和运动水平合理化；下定决心减肥，给自己时间和耐心，逐渐改变饮食和运动习惯；只有长期的改变才算成功。\"},{\"section\":\"体检问题预警\",\"title\":\"腰围超标意味越过慢病安全警戒线\",\"content\":\"腰围的重要性仅次于体重指数。腰围的大小可以表明脂肪堆积的部位。脂肪聚集在腰上的人更容易患高血压、糖尿病、冠状动脉疾病，因为腹部的脂肪更容易分解并在动脉里聚集。男性腰围超过102厘米或女性超过88厘米，患相关疾病的危险就会增大，尤其是体重指数为25或以上的人群。\"},{\"section\":\"体检问题预警\",\"title\":\"压力令高血压加重\",\"content\":\"压力下体内的儿茶酚胺分泌增多，它们会引起血管的收缩，心脏负荷加重，引发高血压。除了释放压力外，最好的方法是改变生活方式包括饮食习惯以及运动等。世界高血压组织研究表明，如果只是1期的高血压，是可以通过生活方式的改善控制血压的。即便是通过用药，生活方式也不容忽视，限制食盐的摄入，减重，运动，戒烟，限制酒精的摄入：每日饮用超过30毫升酒精,相当于600毫升啤酒,200毫升葡萄酒或75毫升标准威士忌,会使血压升高并使冠心病、中风的发病和死亡率上升。\"},{\"section\":\"体检问题预警\",\"title\":\"精神压力会加重高血脂，增加慢性病风险\",\"content\":\"研究证明，长时间的精神压力可引起血胆固醇升高。另一方面，有些人往往吃许多高脂肪食物来对付压力，这也是血胆固醇升高的重要因素。目前无论在美国还是中国，心脑血管疾病仍然是头号杀手。血脂在动脉血管壁的沉积是毫无声息的，如果体检已经确认血脂超标，就必须在生活方式上加以注意，加强运动，进行低脂肪饮食等，无效的情况下，需要在医生指导下借助药物改善状况。\"},{\"section\":\"体检问题预警\",\"title\":\"高压力会加重血糖紊乱\",\"content\":\"除了饮食、运动等因素，长期紧张的状态也会引起血糖的波动，一是胰腺为了给机体供应能量，而大量释放胰岛素和胰高血糖素。二是压力下对高糖、高脂肪的食物需求增加，也会导致血糖水平紊乱。\\n糖尿病意味着需要比其他人更注意你的生活，摄入具有丰富膳食纤维的蔬果，减少高脂肪食物和零食，控制体重，控制吸烟和饮酒。有规律的运动可以帮助保持身体素质，对心脏和血管都有益处，促进血液循环，并定期监测自己的血糖水平，防止糖尿病并发症发生的风险。\"},{\"section\":\"体检问题预警\",\"title\":\"冠心病增加猝死风险\",\"content\":\"冠心病若养护不当，猝死率很高（统计表明50-60%猝死是冠心病）。吸烟、高血压、高血糖、高血脂、肥胖、久坐等问题都是冠心病发作的危险因素，通过合理的饮食和有规律的运动，可以大大减少患病的风险，甚至可以免除药物的使用。可以配合每天服用抗氧化维生素，如维生素E、维生素C、叶酸等，阻止低密度脂蛋白在血管的积累，预防发病。\"},{\"section\":\"体检问题预警\",\"title\":\"保持骨密度不再降低更为重要\",\"content\":\"先天因素，饮食中钙摄入不足，不常运动，体格瘦弱，吸烟、过量饮酒、女性过早绝经，都是直接导致骨质疏松的危险因素，不过相对于恢复已降低的骨密度，保持骨密度不变要来的更容易些。预防措施包括摄入足够的钙和维生素D和从事负重锻炼。\"},{\"section\":\"体检问题预警\",\"title\":\"即便没有不舒服的症状也要重视脂肪肝\",\"content\":\"脂肪肝是肝细胞内过量的脂肪（甘油三酯）积累，脂肪肝最常见的原因是酗酒、肝炎、肥胖、糖尿病、血清甘油三酯水平过高，因为起初没有任何症状，但可引起肝脏的反复损害，最终可从脂肪肝发展到肝硬化。早期如果在生活方式上加以调整也很快就会看到改善。\"},{\"section\":\"体检问题预警\",\"title\":\"胆囊健康和压力互相影响\",\"content\":\"慢性胆囊炎若不及时治愈，会给生活增加很多限制，常常因为消化不良，厌油腻食物、上腹部闷胀、嗳气、胃部灼热等问题而影响品味美食的心情，而且压力和情绪的变化也会对胆囊健康产生影响。\"},{\"section\":\"体检问题预警\",\"title\":\"前列腺炎症影响生活质量，加大情绪压力\",\"content\":\"前列腺炎若不及时治疗会加重尿频、尿急、尿不尽等症状，还可能影响性功能，严重者还可能出现忧郁等神经官能症。而压力也会导致免疫力下降，加重病情。除了积极配合治疗外，注意尽量避免久坐、受寒、焦虑、憋尿、辛辣食物等。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不良的就餐习惯影响精力、脑力\",\"content\":\"常在外就餐 常在外吃，容易摄入过多的油脂和盐，同时也很难保证均衡的营养提供。少油清淡的饮食和充足的维生素和矿物质是维持情绪平稳，精力旺盛的保障。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不良的就餐习惯影响精力、脑力\",\"content\":\"不吃早餐 高压力的状态下，不吃早餐会增加你得胆结石的风险，并且令上午精力难以集中，事倍功半，还会过度依赖咖啡等提神，增加慢性病风险。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不良的就餐习惯影响精力、脑力\",\"content\":\"经常吃得过饱 有压力时，吃撑是一种放松，但是吃撑会令血糖快速变化，刺激胰岛素的分泌，久之易患糖尿病。吃撑时，胃肠过度扩张，久之有损肠胃健康。如果年轻时经常吃得过饱，年老后患骨质疏松和老年痴呆的机会就会明显增加。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，影响精力、脑力\",\"content\":\"爱吃油炸 香脆的油炸食物有短暂的缓解压力的效果，但吃得过多，则有害，增加患心脑血管疾病风险，令人精力不足，还要额外消耗体内的维生素和矿物质，同时增加胃肠负担。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，影响精力、脑力\",\"content\":\"常吃包装或加工食品 对精力脑力状态要求较高时，不应吃太多包装零食和加工食物，这会导致必要营养素不足，甚至消耗体内已有的能对抗压力的维生素和矿物质，还会影响对正餐的食欲，令营养不均衡。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，影响精力、脑力\",\"content\":\"常喝甜饮料 含糖饮料会增加体内维生素B族的消耗，维生素B群是维持正常情绪不可缺乏的营养物质。同时甜饮料也会令血糖不稳，影响情绪。碳酸饮料和糖份高的饮料相同，会促进钙质流失，令免疫力降低。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，影响精力、脑力\",\"content\":\"爱吃重口味食物 喜欢重口味食物说明你需要休息，因为身体在疲劳的状态下才更需要刺激味蕾来增加食欲。西北有“吃盐力气大”的说法，而辣可以刺激口水分泌，令胃口大开，压力越大越想吃辣，尤其是感到郁闷的时候。压力大会令血糖不稳，导致对甜味食物的渴望。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，影响精力、脑力\",\"content\":\"肉食过多 如果“无荤不饭”，过度摄入肉食，会额外消耗体内的维生素和矿物质，从而产生四肢无力、全身酸痛、精神不振等亚健康症状，更重要的是这将削弱您的免疫力，让身体成为慢性病的温床。\"},{\"section\":\"生活习惯提醒\",\"title\":\"健康食物吃得过少，无法保持最佳的精力、脑力\",\"content\":\"豆、奶制品吃得少 很少吃豆、奶制品可能导致你缺乏钙质，奶制品和豆制品被认为是钙的主要来源，钙是维持骨骼、牙齿、神经健康的主要矿物质，缺乏则容易造成失眠、紧张、疼痛、骨质疏松、龋齿等问题。\"},{\"section\":\"生活习惯提醒\",\"title\":\"健康食物吃得过少，无法保持最佳的精力、脑力\",\"content\":\"粗粮吃得少 很少吃粗粮会令纤维素和维生素B族以及矿物质缺乏。纤维素能减少胆固醇和脂肪的吸收，还能维持血糖的平稳，也能帮助粪便成型，排出毒素。维生素B族能协助体内能量代谢，维持稳定情绪，保持精力充沛。各种矿物质也是维护身体健康所必需的。\"},{\"section\":\"生活习惯提醒\",\"title\":\"健康食物吃得过少，无法保持最佳的精力、脑力\",\"content\":\"蔬果吃得少 蔬菜水果吃得太少，你可能缺乏足够的抗氧化剂应对高压力的生活，加速身体衰老。日常生活的污染，辐射，煎炸食物，阳光照射甚至呼吸都会产生令身体老化的自由基。缺乏那些抗氧化物会令身体更快衰老，更加易患心脑血管疾病等慢性病。蔬菜水果也提供了大量的有减少脂肪吸收、平稳血糖、协助排便和清除肠道功能的纤维素。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳有损精力、脑力\",\"content\":\"经常熬夜 经常熬夜，可以被称为慢性睡眠不足，已经有大量的科学研究表明睡眠不足与心脏疾病、高血压、中风、糖尿病有直接关系。\\n如果你倾向于“晚睡晚起”的睡眠形态，一旦失眠，将会表现出比一般人更加严重的认知缺损，如健忘、不能集中精力等。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳有损精力、脑力\",\"content\":\"睡眠不足 每个人需要的睡眠时间长短并不一样，如果每天醒来你不需要闹钟，不需要冷水，不需要咖啡就能让自己充满能量地迎接新的一天，这说明你的睡眠效率很高，总之自己的感觉是最直接、最重要的。\\n但如果因为睡不够觉得困倦则说明睡眠效率较低。睡眠不足不但产生健康问题，还会影响注意力、处事能力、记忆力和心情。如果只睡很短时间，就会提高血压和心率的平均水平，由此可能会增大心血管系统的压力。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳有损精力、脑力\",\"content\":\"多梦 梦多是在快速动眼睡眠中出现的，这段时间人的体温上升，心跳增加，血流速快，区别于入睡时的生理状态（体温下降），这类似于人们醒着时候的生理状态，人们也更容易在这段时间内醒过来。这种状况出现的原因很多，压力大、精神紧张、焦虑都有可能成为多梦易醒的原因。而长期的睡眠质量不佳也会导致不良情绪，影响精力脑力。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳有损精力、脑力\",\"content\":\"起床困难或睡不够 喜欢赖床或睡后精力不能补足。可能是繁重的脑力或体力劳动造成极度疲劳，使得需要更长时间的睡眠来恢复。也可能不规律的生活习惯造成了生物钟紊乱，无法控制睡眠和觉醒的正常切换。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳有损精力、脑力\",\"content\":\"失眠 入睡困难或者失眠的人往往表现为：很难睡着、晚上总是醒、睡得不踏实，很难再入睡。原因很多，压力、咖啡因、酒精、环境变化、时差、慢性疾病、焦虑等等。失眠者的压力激素含量比一般人高，因此身体处在高度活跃状态，不容易轻松入睡。失眠导致更大压力，甚至形成严重后果。解决问题首先要找到原因，是不是有些不良的生活习惯，导致这个问题，不过，有时候，这可能是自己无法控制的，但是要明确一点，这并不是不可以解决的问题，所以不用担心，给自己一点儿耐心。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不规律的睡眠扰乱循环系统生物钟，身体隐形“生病”\",\"content\":\"睡眠不规律 其实除了睡眠，我们心率、呼吸、血压、体温、蛋白质合成等体内一切生命活动，往往都呈现昼夜节律的倾向，反映在血液循环、内分泌和各种器官功能中。不规律的睡眠就好比在不停地倒时差，各器官的生物钟调整所需的时间不同，睡眠两三天就可以调整过来，但是体内的器官及循环系统的生物钟却需要两三周的时间调整。\"},{\"section\":\"生活习惯提醒\",\"title\":\"用运动释放您的压力\",\"content\":\"多做运动，这样不仅能消耗过多会带来压力感的肾上腺素，同时还能够有效地培养我们身体和精神上的毅力。深呼吸能够给大脑提供更多的氧气，而氧气是大脑最好的补品。\"},{\"section\":\"生活习惯提醒\",\"title\":\"烟草会强化您的压力和焦虑\",\"content\":\"烟草中的尼古丁很快就可以到达大脑，令人有一种轻松愉快的感觉。可是，由于尼古丁是成瘾性物质，在逐渐的重复使用中，机体对尼古丁产生了耐受性，即必须吸入更多的尼古丁以达到和以前同样的舒适感，形成烟瘾。\\n最初的压力和不适也许来自生活和情绪，吸烟后似乎缓解了。但形成烟瘾之后，不适和焦虑感则来自尼古丁水平的下降，不吸烟会更加难受。这个时候，烟草的作用，变成了负性强化，不得不用了。\"},{\"section\":\"生活习惯提醒\",\"title\":\"过量饮酒影响精力、脑力\",\"content\":\"也许饮酒是释放压力的渠道，但连续5年以上平均每天摄入酒精超过40克者，48％的人会患有不同程度的酒精性肝病。过量的酒精会使肝脏形成瘢痕组织，称为肝硬化。还会对大脑和神经系统造成多种损伤，严重损伤肝脏、脾脏和心血管系统。\"},{\"section\":\"生活习惯提醒\",\"title\":\"辐射产生的自由基加速人体细胞氧化\",\"content\":\"手机和电脑产生的低频电磁辐射是人体外源性自由基产生的原因之一。随着辐射剂量累积增加，可能会引起神经系统、循环系统、免疫系统、生殖系统以及皮肤和眼睛的功能、代谢或形态的改变，这是一个自由基生成加速身体细胞氧化的过程。\"}]','精力脑力评估'),
	(6,'精力脑力','[{\"section\":\"饮食习惯\",\"title\":\"压力状态下，多选择蒸煮炖食物\",\"content\":\"蒸、煮、炖的烹饪方式相对于煎炸能保留更多的营养，当体内营养均衡时，精力和体力能够保持最佳的自然状态，很少对刺激提神的咖啡，烟酒产生依赖。\"},{\"section\":\"饮食习惯\",\"title\":\"压力状态下，多选择蒸煮炖食物\",\"content\":\"这种烹饪方式令食物口感更细软，容易被消化吸收，减少压力下的肠胃消化负担。\"},{\"section\":\"饮食习惯\",\"title\":\"容易消化的早餐令你上午精力充沛，情绪稳定\",\"content\":\"越是繁忙，早餐越重要。选择容易消化的碳水化合物和水果为主的早餐。全麦面包，坚果，豆浆，或一个水果，这种组合会令能量平稳的释放，而且对消化系统压力最小。早起30分钟，给早餐预留时间，匆忙不利于消化。\"},{\"section\":\"饮食习惯\",\"title\":\"餐前吃些水果，有助于情绪稳定，精力持久\",\"content\":\"草莓、樱桃、桑椹、蓝莓、苹果、梨，这类水果可以缓释能量，保持血糖平稳。保持血糖平稳是你保持精力持久、情绪稳定和维持正常食欲最重要的因素。餐前少吃些这类水果可以增加饱腹感，调节血糖，避免正餐过饱。\"},{\"section\":\"饮食习惯\",\"title\":\"天然零食，缓解压力的方便法门\",\"content\":\"压力下吃些零食有助于舒缓情绪，挑选健康零食一举两得。时令水果、酸奶、原味坚果、天然果干，这些都可以作为零食，既能满足吃零食的习惯，也能给身体提供必要营养。\"},{\"section\":\"饮食习惯\",\"title\":\"天然零食，缓解压力的方便法门\",\"content\":\"当食欲大增时，不要和大脑作对，但是要选对食物。对甜品的渴望可以由水果或葡萄干、菠萝干、黑巧克力等代替。\\n食欲不佳时，可以用味道清新的芹菜、柠檬、薄荷、橙子等制成饮料，提神醒脑，补充维生素。\"},{\"section\":\"饮食习惯\",\"title\":\"口味清淡，头脑清爽\",\"content\":\"常喝白水或矿泉水。人体的70%是水分，确保体内有足够洁净的水分，才能将代谢产生的废物及时排出体外。尤其是体型偏瘦的人，更需要有效水份来滋润你的细胞。白水、矿泉水、鲜榨的蔬果汁、口味清淡的菜汤都可以是补充水分的来源。\"},{\"section\":\"饮食习惯\",\"title\":\"口味清淡，头脑清爽\",\"content\":\"逐渐让口味变淡，不仅有益于健康，低油、盐糖的饮食在避免增加血液粘稠的同时也能令头脑更清爽。你可以用水洗涮菜肴的酱汁。少吃红烧，糖醋，水煮，回锅等浓油赤酱的菜式。当然，及时让自己减压，才能令粗茶淡饭也香甜。\"},{\"section\":\"饮食习惯\",\"title\":\"多吃天然食物，尽量少吃包装加工食物\",\"content\":\"尽量选择新鲜的当地、当季产新鲜食物，它们除了能提供必需的营养素还能帮助代谢掉人体中产生有的有害物质，如促使老化的自由基。\\n加工食品中存在多种人工合成的化学物质，已有许多国家研究表明加工食品中的各种添加剂摄入过多，会存在致癌风险。\"},{\"section\":\"饮食习惯\",\"title\":\"细嚼慢咽，七分饱\",\"content\":\"研究表明，吃饭过快会给精神带来较大负担。多咀嚼可加快心脏运动，使脑部荷尔蒙分泌增多，从而思维能力和记忆力也随之提高。当然最重要的是能够避免吃撑。日本学者统计约有30%--40%的老年性痴呆病人，青壮年时期有饱食的习惯。因摄入的总热量远远超过机体的需要，致使体内脂肪过剩、血脂增高，导致脑动脉粥样硬化，有一种被称为“纤维芽细胞生长因子”的物质在大脑中明显增多，这种物质被认为是引起脑动脉硬化的重要因素。\"},{\"section\":\"饮食结构\",\"title\":\"充足钙质，稳定情绪\",\"content\":\"牛奶和豆类食物是钙的优质来源，您还可以选择发菜、紫菜、荠菜、木耳等，或者适量芝麻、花生、榛子等坚果，虾米也是很不错的选择，它们都是很好的钙质来源。充足钙质是健康的神经系统必需的。\"},{\"section\":\"饮食结构\",\"title\":\"粗杂粮稳定你的情绪，提高你的精力脑力\",\"content\":\"最新的美国营养学研究建议，主食中的一半应该是全谷物和粗杂粮，包括玉米、全麦制品、糙米、燕麦等。粗粮富含水溶性纤维素，可以改善肠道健康，维持血糖平稳释放，有利于精力体力的保持。\"},{\"section\":\"饮食结构\",\"title\":\"富含抗氧化剂的食物提供充沛精力脑力\",\"content\":\"颜色丰富，时令新鲜的果蔬，未经深加工的粗粮谷豆，坚果和种子会提供身体必需的全面营养。研究表明血液中营养素水平越低，智力表现就越差。摄入最佳水平的维生素和矿物质，可以思考的更快，精神集中的时间可以更长。\"},{\"section\":\"饮食结构\",\"title\":\"富含抗氧化剂的食物提供充沛精力脑力\",\"content\":\"需要更多的精力？你可以将多种色彩的时令蔬果搭配组合，其中含有的抗氧化剂可以协同作用，清除体内自由基，改善疲劳，精力不佳。比如：草莓、桑椹、樱桃、杏子、葡萄、番茄、玉米、胡萝卜、西兰花、红薯、豌豆等。\"},{\"section\":\"饮食结构\",\"title\":\"白肉多于红肉\",\"content\":\"压力越大对脂肪含量高的食物越渴望，但富含饱和脂肪的红肉往往更难消化，在满足口腹之欲后，就可能面对消化不良。而贝类，鱼类，去皮的鸡鸭肉相对胆固醇含量低，更容易被消化。\"},{\"section\":\"饮食结构\",\"title\":\"压力下，最好避免刺激物\",\"content\":\"咖啡、可乐、糖和精制食物，这些都是消耗身体中营养素的食物，只能一时提神。血液中营养素水平越低，智力表现就越差。保持体内有最佳水平的维生素和矿物质，可以思考的更快，精神集中的时间可以更长。\"},{\"section\":\"饮食结构\",\"title\":\"压力下，最好避免刺激物\",\"content\":\"咖啡因确有提神效果，不过这种提神效果因人而异，而且也只能是暂时性的，大量饮用咖啡会造成代谢速度加快，消耗体内与神经、肌肉协调有关的维生素B群，缺乏维生素B群人本来就容易累，更可能形成恶性循环，使人饮用成瘾，越饮越多。\"},{\"section\":\"饮食结构\",\"title\":\"你需要有益头脑的好脂肪\",\"content\":\"食用优质鱼油，或种子油，如：南瓜籽，芝麻，葵花籽，亚麻籽，红花，玉米，核桃，大豆和麦胚油等。这些可以提供必需的不饱和脂肪酸，满足灵活头脑和旺盛精力的需要。\"},{\"section\":\"饮食结构\",\"title\":\"你需要有益头脑的好脂肪\",\"content\":\"“许多研究表明，摄入最佳的必需脂肪，特别是Omega-3脂肪，可以改善情绪，提高智力”（出自《营养圣经》）。因为Omega-3脂肪能促进有情绪稳定作用的五羟色胺的“分泌”。亚麻籽、南瓜籽、三文鱼、鲭鱼、鲱鱼、金枪鱼或者它们榨取的油都可以提供Omega-3脂肪。\"}]','合理营养建议'),
	(7,'精力脑力','[{\"section\":\"运动缓解压力问题\",\"title\":\"压力时头痛或胸闷 ?  舒缓运动和按摩改善紧张性头痛\",\"content\":\"过量的运动会提高紧张激素皮质醇的水平，所以不要在过度紧张的时候运动太多。心理体操，瑜伽，太极，按摩、散步半个小时或是静坐，都有助于恢复紧张激素的水平。\"},{\"section\":\"运动缓解压力问题\",\"title\":\"情绪低落 ?  有氧运动，让心情HIGH起来\",\"content\":\"经常做些有氧运动可以提高情绪，在心血管耐力型运动中，如：跑步、散步、游泳，能够由大脑和其他部位释放β-内啡肽（天然镇定剂）使人感到心情愉快。\"},{\"section\":\"运动缓解压力问题\",\"title\":\"焦虑、易发火 ?  运动可以降低对压力和紧张的敏感度\",\"content\":\"适度的运动可以对心理产生影响，包括能够降低沮丧和焦虑的感觉。那些让你觉得活力充沛但又不太疲劳的运动最为适宜，瑜伽、慢跑、快走、游泳等。\"},{\"section\":\"运动缓解压力问题\",\"title\":\"懒惰、杂乱 ?  适度运动，提神醒脑\",\"content\":\"“实际上，使自己更有精神的最好的方式是更积极更有活力的生活方式”，一项对于70多个有关运动效应和精力水平的不同研究的回归性研究发现，90%的研究揭示同一个道理：久坐的人开始锻炼后疲劳感减少，而且更有精神。当然要避免运动过度，那会令你更疲劳。\"}]','有效运动建议'),
	(8,'精力脑力','[{\"section\":\"\",\"title\":\"早起30分钟，给生物钟一个起点\",\"content\":\"比起控制睡眠时间，控制起床时间更为容易，早起至少30分钟，是令你充满活力的开始，这可以让你的早晨更从容，顺手清理一下居室，整洁的环境也会令心情更好。让你的起床时间有规律是改善睡眠不规律的方法，当然这取决于个人的意愿。\"},{\"section\":\"\",\"title\":\"正视焦虑\",\"content\":\"正视焦虑将减少恐惧和不安，焦虑往往源自对未知的消极看法，理性分析有助于认清形势，减轻压力。\"},{\"section\":\"\",\"title\":\"深呼吸，扩展安稳的边界\",\"content\":\"胆小和敏感也是身体的情绪。每天早起，练习深呼吸，深呼吸不仅能促进人体与外界的氧气交换，还能使人心跳减缓，血压降低。它能转移人在压抑环境中的注意力，并提高自我意识。可以通过深呼吸保持镇静，缓解焦虑情绪。\"},{\"section\":\"\",\"title\":\"深呼吸，扩展安稳的边界\",\"content\":\"常常练习深呼吸，放松身体的每一块肌肉，就能节省很多营养补充剂。保守估计，处于压力状态下，你需要的维生素是平时的两倍。\"},{\"section\":\"\",\"title\":\"压力下保持健康，从一个微笑开始\",\"content\":\"美国《今日心理学》杂志结合多项科学研究指出，看喜剧、听笑话时最自然、最舒畅的“傻笑”，对健康最有益。而且令人惊讶的是，在绽放笑容之前，人们对欢笑的期待也会收到奇迹般的效果。喜剧开幕前，研究人员抽取了观众的血样，结果发现其内啡肽的含量已经增加了27%，生长激素的含量增加了87%。内啡肽有助于增强免疫系统的功能，生长激素对肌肉、骨骼和内脏有益。\"},{\"section\":\"\",\"title\":\"提高睡眠质量\",\"content\":\"研究表明，睡眠可以降低体内压力激素的分泌。每当感到压力大的时候，即使打个小盹，也能让你迅速释放压力，提高工作效率。\"},{\"section\":\"\",\"title\":\"提高睡眠质量\",\"content\":\"睡得好，能让你更聪明。在睡眠状态下，脑细胞能量得到贮存，大脑耗氧量开始减少。醒后人的大脑思路开阔，思维敏捷，记忆力增强。\"},{\"section\":\"\",\"title\":\"提高睡眠质量\",\"content\":\"固定起床时间可以帮助生物钟正常工作，也就是说，无论什么时候开始睡觉一定要在每天的同一时间起床，这样可以在一定程度上保证生物钟的正常运作，也是拥有高质量睡眠的前提条件，也可以通过午睡或者安全状态下的Flash睡眠(瞬时闪睡)来进行一定的睡眠补充，这是最现实的办法。但是决不能根据前一天的睡觉时间决定起床的时间，否则很难改善生物钟紊乱的症状。\"},{\"section\":\"\",\"title\":\"提高睡眠质量\",\"content\":\"除了正常的睡眠（23点--7点），可以增加30分钟的午睡，经常性的闭目养神，3分钟左右的打盹等。利用好短时间的休息，同样可以补充精力。\"},{\"section\":\"\",\"title\":\"吸烟，要停下来很容易\",\"content\":\"你可能尝试戒烟，并体会过戒烟带来的各种不适反应。但是应该记得的是，这些不适反应只不过是些可以改善的症状，远比吸烟对内脏、心脑血管的直接损伤来的微小的多。\"},{\"section\":\"\",\"title\":\"长期有规律的适量饮酒帮助提升机体健康\",\"content\":\"人体可自然分解消化酒精，每天适当饮酒可使动脉血管扩张、血压下降，有利于促进血液循环，更可提升对人体有利的高密度脂蛋白及降低有害的低密度脂蛋白，从而防止心脏病发作。而适量葡萄酒（含有抗氧化物质白藜芦醇）可能降低机体老化速度。\\n适量的饮酒相当于每天一杯150ml红葡萄酒或360ml啤酒。\"},{\"section\":\"\",\"title\":\"常常受到辐射侵扰，需要抗氧化剂抵抗辐射侵袭\",\"content\":\"1.需要有意识地通过饮食或膳食补充剂摄入花青素、维生素C、维生素E、维生素A等抗氧化剂，及时清除代谢产生的自由基。 2.避免长时间连续操作电脑，眼睛与屏幕的距离应在40~50厘米。  3.室内光线要适宜，不可过亮或过暗。  4.安装防护装置，削弱电磁辐射的强度。\"},{\"section\":\"\",\"title\":\"一定要在23点也就是子时到来前睡觉\",\"content\":\"人的一天有两个自然的睡眠周期，这是由人的生物节律决定的，一个是22点左右，这时候人的体温下降，进入能量低潮期，这时候如果关闭灯光、游戏、电视、互联网，便可以很快进入睡眠状态。另外一个自然的睡眠周期是下午1点~4点，同样的也是进入能量低潮期，这时候也会犯困，需要睡眠。\\n所以，正确的睡眠应该遵循身体的生物节律，22~23点入睡，早上6-7点醒来。可以把你工作的时间放在早上，这样不仅有适量的睡眠，也睡在合适的时间。\"},{\"section\":\"\",\"title\":\"循序渐进延长睡眠时间\",\"content\":\"如果你经常看着电视、看着书进入梦乡，这意味着你缺乏足够的睡眠，补充睡眠并不需要比往常提前1小时上床，这只会令你睡不着觉，每天晚上只要提前10~15分钟，在下午13点~16点之间小睡一下，半个小时为宜，多了则会影响晚间的睡眠，在调整睡眠规律期间清淡饮食。\"},{\"section\":\"\",\"title\":\"梦是你熟睡时心中产生的愿望，你可以学习控制你的梦\",\"content\":\"通过一些训练，学习控制你的梦，享受美好的梦境，缓解压力，帮助你更好的睡眠。\\n根据英国睡眠指导专家研究，有42%的人通过睡前的想象控制自己的梦，多试几次这个比例可以达到更高。其实很简单，在睡前安静的坐上10分钟，放慢呼吸，暗示自己可以在做梦的时候保持清醒，想象一个快乐美好的主题，多试几次。\"},{\"section\":\"\",\"title\":\"给自己一些时间调整你的睡眠\",\"content\":\"上床以后不要继续思考烦心事，如果睡不着，宁可起来做点事，也不要躺在床上发愁，真正折磨你的不是睡眠不足，而是你的忧愁。无论晚上几点入睡，早上都要在同一时间起床。可以尝试这个睡前的意念调节，平躺，全身放松，集中精神调节呼吸。闭上眼睛，浮想一些简单的图形；然后控制呼吸的节奏，缓慢吸气，保持一秒钟静止状态，缓慢呼气，反复几次。这样可以排除杂念，达到睡眠前的最佳状态！\"},{\"section\":\"\",\"title\":\"分解任务，立即行动\",\"content\":\"把事情分解成小任务，先行动起来。心理学研究发现，行动引发动机，动机又激发更多的行动。因此不要等到你有动力了准备好了才开始做事儿，当你去做了，积极性就会上来，愿意做的事情也更多了。\"},{\"section\":\"\",\"title\":\"张弛有度，劳逸结合更能保持激情\",\"content\":\"长期的亢奋，有激情的生活虽然令人振奋，有成就感，但长期如此，对身体的消耗过度，容易导致疲劳过度引起各种虚弱不适：气短、心慌、失眠等植物性神经紊乱问题。\\n保证有充足的优质睡眠、适度的放松运动，常常让自己慢下来，全身心的放松，才能保证更持久的高效率。\"},{\"section\":\"\",\"title\":\"成为真正的自己\",\"content\":\"减轻压力的捷径，首先最重要的是要有一种良好的自我感觉，要去做自己真正想做的事，要为自己的理想而生活。在寻找自己真正想做的事的过程中，反省自己是非常重要的。为此，每天一定要抽出一点时间让自己的心安静下来，这种放松是很必要的。\"},{\"section\":\"\",\"title\":\"冥想或禅定有助于改善免疫力\",\"content\":\"医学研究指出，当人禅坐进入入定状态时所出现的生理效应，如呼吸减缓、交感神经系统功能下降、副交感神经系统功能相对增强、消化系统功能亢进、耗能减缓而储能增大等，有利体内能量的充盈。禅定可以调节免疫系统，同时为神经、内分泌系统带来正面影响。\"}]','生活方式建议'),
	(9,'免疫力方面','[{\"section\":\"生活习惯影响免疫力\",\"title\":\"不健康食物吃得多，削弱免疫力\",\"content\":\"常喝甜饮料 体内糖过多会抑制免疫系统。研究表明在摄入75-100g糖之后的几个小时内，白细胞的抗菌功能会持续地受到抑制（来自WebMD）。每100ml可乐含11g糖，相当于只要喝2罐350ml的可乐就足以降低免疫力了，茶或果汁类饮料含糖量也并不低于可乐。\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"不健康食物吃得多，削弱免疫力\",\"content\":\"爱吃油炸 常吃油炸食物对免疫系统不利，食物在油炸的过程中会产生致癌物（丙烯酰胺 ）和自由基，这增加了免疫系统负担，清除这类致癌物是免疫细胞的基本工作之一，过量的自由基会抑制免疫功能。油炸过程会破坏食物中部分的必需脂肪酸、维生素等免疫系统必需的营养素， 这也会导致免疫力受到影响。\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"不健康食物吃得多，削弱免疫力\",\"content\":\"爱吃重口味食物 偏爱重口味食物会增加消化系统发生炎症的风险，高盐或辣味食物会刺激胃粘膜，日久则容易形成溃疡等问题，影响消化吸收，会因营养不足而降低免疫系统功能。\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"不健康食物吃得多，削弱免疫力\",\"content\":\"常吃包装或加工食品 经过加工的包装食物吃得过多会降低免疫力。这些食物中的添加剂不仅会阻止营养素的吸收和利用，还会加速营养素的流失，并增加肝脏的代谢负担。肝脏是一个重要的免疫器官，负责吞噬和清除血液中和肠道吸收来的毒物、细菌、残留农药的解毒等。\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"不健康食物吃得多，削弱免疫力\",\"content\":\"肉食过多 过度摄入肉食会削弱免疫力。 研究表明，若饮食中饱和脂肪含量高，会阻塞淋巴管而抑制免疫系统（来自WebMD）。\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"不良的就餐习惯削弱免疫力\",\"content\":\"常在外就餐 经常外食可能会影响免疫功能。餐馆食物多油盐调料、少新鲜果蔬，很难保证营养均衡充足，而健康的免疫系统需要维生素A\\\\B\\\\C\\\\E，叶酸、铁、锌、镁、钾和充足的抗氧化剂，若长期在外就餐就需要额外补充复合维生素同时尽量多选择营养损失较小的烹饪方法（凉拌、蒸、少油炒）。\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"不良的就餐习惯削弱免疫力\",\"content\":\"不吃早餐 经常不吃早餐有可能令免疫力下降。因为没有供能早餐，身体会分解蛋白质作为能量来源，久之容易引起蛋白质不足，令免疫力降低。长期不吃早餐也是引发胃肠消化问题的原因，可能导致身体缺乏免疫系统必需的营养而降低免疫力。\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"不良的就餐习惯削弱免疫力\",\"content\":\"经常吃得过饱 吃撑会影响免疫力。因为过剩的食物残渣，特别是高脂肪食物在肠内利于大量的厌氧菌生长及产生其它毒物，这会增加肝脏解毒负担，增加患胃肠炎症风险，也会破坏肠道菌群平衡，胃肠中存在的益生菌群会因此受到破坏，益生菌能帮助改善免疫系统健康，减少过敏、炎症。\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"健康食物吃得过少，无法保持免疫系统的最佳状态\",\"content\":\"蔬果吃得少 蔬菜水果吃的少会影响免疫系统健康水平。为了抵抗感染，身体需要大量的抗氧化物质，特别是维生素C。而天然的抗氧化物，如维生素C的主要来源就是新鲜的多彩蔬果。\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"健康食物吃得过少，无法保持免疫系统的最佳状态\",\"content\":\"豆、奶制品吃得少 很少吃豆、奶制品可能导致你缺乏钙质，奶制品和豆制品被认为是钙的主要来源，钙是维持骨骼、牙齿、神经健康的主要矿物质，缺乏则容易造成失眠、紧张、骨质疏松、龋齿等问题。虽然钙并未被证明和免疫力有直接的联系，但是优质睡眠和轻松的情绪对提高免疫力是有帮助的。\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"质量不佳的睡眠削弱免疫功能\",\"content\":\"起床困难或睡不够 早晨睡不醒或醒后精力不能补足的疲劳状态下，免疫力会降低。可能是繁重的脑力或体力劳动造成极度疲劳，使得需要更长时间的睡眠来恢复。也可能是不规律的生活习惯造成了生物钟紊乱，无法控制睡眠和觉醒的正常切换。\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"质量不佳的睡眠削弱免疫功能\",\"content\":\"经常熬夜 经常熬夜、超过24点不睡，可以被称为慢性睡眠不足，长时间熬夜会引起神经和内分泌调节紊乱，从而导致机体免疫力下降。日久还会引起各类健康问题，已经有大量的科学研究表明睡眠不足与心脏疾病、高血压、中风、糖尿病有直接关系。\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"质量不佳的睡眠削弱免疫功能\",\"content\":\"睡眠不足 美国《内科学文献》提到，每天睡眠不足７小时会降低人体免疫能力。芝加哥大学的研究人员发现注射流感疫苗后，相对于每天睡7-8小时的人，每天只睡4小时的人，血液里的流感抗体只有前者的50%。这反映了睡眠不足时，免疫系统产生抗体的能力大大降低。（来自WebMD）\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"质量不佳的睡眠削弱免疫功能\",\"content\":\"失眠 经常失眠的人更要注重保护免疫力，失眠引起的免疫力下降会导致身体整体健康水平下降。导致失眠的原因可能有：压力、咖啡因、酒精、环境变化、时差、慢性疾病、焦虑等等。调整自身的生活习惯为第一位。这可能是自己无法控制的，但是要明确一点，这并不是不可以解决的问题，所以不用担心，给自己一点儿耐心。\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"质量不佳的睡眠削弱免疫功能\",\"content\":\"多梦 多梦和易醒出现的原因很多，压力大、精神紧张、焦虑都有可能成为多梦易醒的原因。 多梦和易醒会降低您的睡眠质量，这会影响免疫系统的功能，虽然还没有明确的研究指出高质量的睡眠是如何提升免疫系统的，但是保证7-9小时的优质睡眠对健康的免疫力是必要的。\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"不规律的睡眠扰乱生物钟，影响免疫功能\",\"content\":\"睡眠不规律 免疫系统和血液循环、内分泌系统一样受到昼夜节律的影响，不规律的睡眠（上夜班、跨时区旅行）会令身体的各系统功能降低。美国弗吉尼亚大学教授吉恩·布洛克在一项实验中意外发现，生理时钟错乱的老鼠比较早死，研究人员据此认为，工作需要轮班导致日夜颠倒，或者经常搭飞机跨越不同时区，可能会对身体健康带来永久性的负面影响。该研究在《现代生物学》期刊发表。\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"缺乏运动会降低免疫力\",\"content\":\"和有适度运动量的人相比，缺乏运动会令你无精打采，甚至免疫力降低。缺乏运动会直接影响免疫系统健康，你会更容易感冒或感染。缺乏运动还会影响睡眠,令你更容易患糖尿病，这些都增加了疾病风险。（来自WebMD）\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"辐射产生的自由基加速人体细胞氧化\",\"content\":\"长时间的手机和电脑会对人体产生低频电磁辐射影响。辐射对人体的危害，最主要就是产生大量自由基，自由基会攻击和破坏细胞内各种执行正常功能的生物分子，从而造成包括免疫系统在内的人体组织损伤。\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"吸烟破坏肺组织，降低免疫力\",\"content\":\"吸烟会破坏肺组织，影响肺部对病菌的防御；同时烟雾会削弱鼻毛阻挡病菌的能力。（美国疾病预防控制中心）这会增加感冒、哮喘和肺部罹患癌症的风险。 虽然日常补充果蔬或绿茶等抗氧化物质含量高的食物有助于减少吸烟带来的危害，但戒烟才是解决问题的根本之道。\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"过量饮酒影响机体的抗菌能力\",\"content\":\"英国《BMC免疫学》杂志最新的研究表明，一场大醉后，身体的免疫力至少在24小时内难以恢复至正常水平。此外，酒精促使身体脱水，影响鼻子和喉咙排出细菌的能力。\"},{\"section\":\"免疫力相关疾病/问题\",\"title\":\"冠心病增加猝死风险\",\"content\":\"冠心病若养护不当，猝死率很高（统计表明50-60%猝死是冠心病）。吸烟、高血压、高血糖、高血脂、肥胖、久坐等问题都是冠心病发作的危险因素，通过合理的饮食和有规律的运动，可以大大减少患病的风险，甚至可以免除药物的使用。可以配合每天服用抗氧化维生素，如维生素E、维生素C、叶酸等，阻止低密度脂蛋白在血管的积累，预防发病。\"},{\"section\":\"免疫力相关疾病/问题\",\"title\":\"骨质疏松可能和伴随衰老发生的免疫力降低有关\",\"content\":\"骨骼密度在30岁左右到达高峰后开始流失，这个过程同时受到先天因素、钙摄入不足、缺乏运动、吸烟、过量饮酒、使用激素类药物、女性过早绝经等因素的影响。同时最新的科学研究发现，正常衰老引起的骨质疏松是免疫细胞和骨髓共同作用所保持的骨钙平衡被破坏的结果，通常这也意味着免疫系统也随之变弱。不过相对于恢复已降低的骨密度，保持骨密度不变要来的更容易些。预防措施包括摄入足够的钙和维生素D和从事负重锻炼。\"},{\"section\":\"免疫力相关疾病/问题\",\"title\":\"脂肪肝影响免疫器官肝脏的健康\",\"content\":\"脂肪肝是肝细胞内过量的脂肪（甘油三酯）积累，脂肪肝最常见的原因是酗酒、肝炎、肥胖、糖尿病、血清甘油三酯水平过高，因为起初没有任何症状，但可引起肝脏的反复损害，最终可从脂肪肝发展到肝硬化。肝脏是体内强大的免疫器官，若功能受损，则会影响免疫功能。\"},{\"section\":\"免疫力相关疾病/问题\",\"title\":\"前列腺炎症和免疫力降低有关\",\"content\":\"男性尿道以及前列腺内的寄生菌群在正常情况下是安全存在的，但是当某些影响免疫力的因素存在时（肿瘤、外科手术、过度劳累、酗酒、服用免疫抑制剂、患有其他疾病以及频繁手淫或性生活等），菌群失去制约而发展为危害健康的炎症。除了积极配合治疗外，注意尽量避免久坐、受寒、焦虑、憋尿、辛辣食物等。\"},{\"section\":\"免疫力相关疾病/问题\",\"title\":\"免疫系统反应过激和失调导致的疾病\",\"content\":\"鼻炎 鼻炎由特定因素引起的免疫应激，如冷空气、花粉、异味等，改善鼻炎除了避免诱发因素外，重点是改善呼吸系统的健康，增加免疫力。刺激的物质，如汽车废气、香烟、有烟雾的环境、灰尘和花粉等会引起鼻炎的发作，同时过多的酒精、油炸食物、或压力，缺乏足够的睡眠，吃得太多会削弱免疫系统，加重鼻炎症状。\"},{\"section\":\"免疫力相关疾病/问题\",\"title\":\"免疫系统反应过激和失调导致的疾病\",\"content\":\"哮喘 哮喘是呼吸系统的炎症反应，和免疫系统反应过激有关。潜在的过敏原、过大的压力或者环境条件的改变如天气变化都可能引起疾病的发作。通过增加饮食中的抗氧化营养和必需脂肪酸有助于改善哮喘的问题。（具体参见营养建议部分）\"},{\"section\":\"免疫力相关疾病/问题\",\"title\":\"免疫系统反应过激和失调导致的疾病\",\"content\":\"过敏 过敏是免疫系统敌友不分导致的，也是免疫系统不健康的表现。除了遵医嘱和避免接触过敏源外，补充必需脂肪酸，保证消化系统健康，还有增加体内的益生菌都有助于改善过敏。(具体参考饮食建议部分)\"},{\"section\":\"免疫力相关疾病/问题\",\"title\":\"免疫系统反应过激和失调导致的疾病\",\"content\":\"荨麻疹 免疫的防御功能紊乱时，可以导致机体出现“变态反应”，即通常所说的“过敏”，荨麻疹是其中之一。可能受到温度变化，饮食添加剂，特定食物，药物等因素的影响而发生。除了避开过敏源之外，调整生活习惯和饮食结构可以改善症状。\"},{\"section\":\"免疫力相关疾病/问题\",\"title\":\"免疫系统反应过激和失调导致的疾病\",\"content\":\"类风湿关节炎 类风湿关节炎属于难以根治的自身免疫疾病，防治重点在于减少痛苦并减缓关节破坏，同时要降低药物对身体的损害。减少甚至断绝烟酒、饱和脂肪、高糖；适度的运动；保持健康体重；补充改善炎症反应的食物，如富含抗氧化剂的彩色果蔬和深海鱼油；适当的钙质对保持骨关节健康也是必要的。（具体参见营养建议部分）\"},{\"section\":\"免疫力相关疾病/问题\",\"title\":\"超重会降低免疫力，增加慢病风险\",\"content\":\"超重不仅增加了你患糖尿病、癌症、心脏病的几率，还会增加免疫系统的健康风险。因为脂肪细胞会在体内引起慢性的炎症反应，使得健康组织逐渐被损害。 实验表明肥胖的老鼠注射疫苗后的免疫应答能力降低，产生的抗体低于平均数量。（来自WebMD）\"},{\"section\":\"免疫力相关疾病/问题\",\"title\":\"造成胆囊炎的危险因素也会降低免疫力\",\"content\":\"免疫力低下会造成胆道感染，而饮食油腻、暴饮暴食、压力造成的焦虑或易怒都会诱使或加重胆囊问题，同时这些情况也会导致免疫力降低。提高免疫力、少吃多餐、避免油腻高脂肪的食物、腹部保暖、保持情绪平稳都有助于避免胆囊问题的加重。\"},{\"section\":\"免疫力相关疾病/问题\",\"title\":\"腰围超标增加免疫系统负担，更易患慢性疾病\",\"content\":\"腰围的重要性仅次于体重指数。腰围的大小可以表明脂肪堆积的部位。脂肪聚集在腰上的人更容易患高血压、糖尿病、冠状动脉疾病,研究表明腹部脂肪过多可能释放某种蛋白质或激素，导致炎症，增加免疫系统负担。男性腰围超过102厘米或女性超过88厘米，患相关疾病的危险就会增大，尤其是体重指数为25或以上的人群。\"},{\"section\":\"免疫力相关疾病/问题\",\"title\":\"功能较弱的免疫系统导致炎症或感冒\",\"content\":\"感冒 容易感冒而迁延日久提示需要加强免疫力。免疫力较高的人，感冒症状在24小时之内就会烟消云散。在保证营养充足的基础上补充提高免疫力的植物精华有助于减少感冒次数和症状困扰。（具体参见营养建议部分）\"},{\"section\":\"免疫力相关疾病/问题\",\"title\":\"功能较弱的免疫系统导致炎症或感冒\",\"content\":\"炎症 机体的抗炎能力和免疫力有关。当炎症发生时，免疫系统的反应是产生用于攻击特异入侵微生物的各种物质和因子。当免疫力下降时炎症会难以痊愈，同时局部血液供应减少，营养的缺乏（蛋白质、维生素C、锌）对炎症的消除影响也较大。（具体参见营养建议部分）\"},{\"section\":\"免疫力相关疾病/问题\",\"title\":\"高血压和免疫功能息息相关\",\"content\":\"美国埃默里大学科研人员的研究发现，如果增强对免疫系统的调节，那么对血压的平稳也有帮助，因为从调节免疫入手，会改善受损器官和血管修复 。\"},{\"section\":\"免疫力相关疾病/问题\",\"title\":\"血脂高诱发体内的自由基反应， 影响免疫功能\",\"content\":\"血脂高会诱发体内一系列自由基反应，损伤各类细胞，尤其是作为免疫器官和脂肪代谢的肝脏健康会受到影响，从而降低免疫功能。 因此必须在生活方式上加以注意，并在医生指导下借助药物改善状况。\"},{\"section\":\"免疫力相关疾病/问题\",\"title\":\"血糖高会增加体内炎症反应\",\"content\":\"血糖高的人易发炎症，增加免疫系统负担。一旦被病菌感染，因为血中糖含量高，很容易称为细菌的培养基，发展成各类炎症，本身的伤口也因此而易感染而难愈合。所以降低血糖的同时要改善免疫功能，减少患病风险。\"},{\"section\":\"影响免疫力的其他因素\",\"title\":\"长期服药或滥用药物会削弱您的免疫系统\",\"content\":\"长期服药 长期服药可能削弱免疫力。因为患有慢性病而需要长期用药，这不仅会增加肝脏解毒负担，还会消耗对免疫系统有益的维生素和矿物质，从而令机体免疫力下降。\"},{\"section\":\"影响免疫力的其他因素\",\"title\":\"长期服药或滥用药物会削弱您的免疫系统\",\"content\":\"惯用抗生素 不科学的使用抗生素会增加健康风险。因为抗生素会杀死部分菌群而导致人体菌群失调，同时也增加了细菌的耐药性，所以加重了继发感染的危险。世卫组织警告说抗生素的滥用可能导致这一最强效的药物失去作用。\"},{\"section\":\"影响免疫力的其他因素\",\"title\":\"节食导致的营养不均衡影响免疫力\",\"content\":\"你的免疫能力和维生素、矿物质的摄入量是否适宜有关。缺乏维生素A、维生素B族、维生素C、维生素E、铁、锌、镁、钾都会影响免疫系统的功能，适当的蛋白质是免疫系统的物质基础。（《营养圣经》）只有科学的搭配饮食才能保证好形体又不损害免疫力，塑造健康之美。\"},{\"section\":\"影响免疫力的其他因素\",\"title\":\"不佳的心理状态会降低机体免疫力\",\"content\":\"害怕生病 恐惧削弱免疫力。美国医生可瑞斯珶纳-瑙斯汝浦认为，仅仅对疾病的恐惧就足以让人染病。她发现，潜意识压力的出现，能够导致皮质醇和肾上腺激素的释放。如果这些激素在较长时间里保持很高水平的话，免疫力被削弱，病原体就表现活跃。卡内基梅隆大学的心理学家谢尔登-科恩的研究也得出了类似的结果。\"},{\"section\":\"影响免疫力的其他因素\",\"title\":\"不佳的心理状态会降低机体免疫力\",\"content\":\"遇事担忧 消极情绪削弱免疫力。如果一个人经常设想不好的情景，他得病的机率就越高。这是美国威斯康星大学的科学家们最近研究得出的结论，该结论刊登在美国国家科学院公报上。这些科学家们研究证明，大脑的“消极”活动与免疫力的削弱有着直接的关系。\"},{\"section\":\"影响免疫力的其他因素\",\"title\":\"不佳的心理状态会降低机体免疫力\",\"content\":\"孤独感 孤独感会削弱你的免疫系统。研究表明长期处于孤独状态不仅会影响身心健康，甚至身体会在基因层面影响对免疫系统的调控。\"},{\"section\":\"影响免疫力的其他因素\",\"title\":\"不佳的心理状态会降低机体免疫力\",\"content\":\"压力和紧张 短暂、适度压力可以调节免疫系统，因为身体在压力状态下会释放令免疫系统更活跃的皮质醇，但长期的心理状态低迷，如紧张、沮丧、悲痛，会抑制免疫系统。\"},{\"section\":\"影响免疫力的其他因素\",\"title\":\"影响免疫功能的身体状态\",\"content\":\"怕冷 通常免疫力低的人都会有怕冷的表现，而研究也表明当体温每降低1度，人体中抗菌的白血球所提供的免疫力便会减少37%。免疫系统在温暖的环境中工作效果最佳，这也解释了身体用发烧来产热升温的原因。\"},{\"section\":\"影响免疫力的其他因素\",\"title\":\"影响免疫功能的身体状态\",\"content\":\"从小身体弱 无论是遗传因素还是营养因素，从小就较弱的身体提示您更需关注您的免疫系统。免疫系统的功能是可以被改善的，避免有损免疫力的食物，减少自由基的伤害，补充增强免疫力的营养素，适度平缓的运动，保持轻松乐观的精神状态，可以增强免疫力，从而改善体质。\"},{\"section\":\"影响免疫力的其他因素\",\"title\":\"影响免疫功能的身体状态\",\"content\":\"疲劳 疲劳状态下免疫力会受到负面影响。因为处于疲劳状态下免疫系统活力较差，这也是为什么在劳累时更容易感冒的原因。及时休息，疲劳时避免感受风寒都会减少患病的可能。\"},{\"section\":\"影响免疫力的其他因素\",\"title\":\"影响免疫功能的身体状态\",\"content\":\"压力大 压力和疲劳会降低免疫力，在此状态下，身体的营养和能量处于匮乏状态，这令身体用于常规修复和保养的能量不足，包括对免疫系统的维护。通过放松和休息以及补充营养可以改善整体状态，从而加强免疫系统功能。\"},{\"section\":\"影响免疫力的其他因素\",\"title\":\"污染的空气削弱免疫力，增加患病风险\",\"content\":\"空气污染 污染的大气长期反复作用于机体，对人体健康构成现实危害和潜在危害 。生活在重污染区的人体对空气污染物的反应更敏感，患支气管哮喘的潜在危险性增加。俄亥俄州立大学的一项研究表明，呼吸被污染的空气，会引起身体炎症。因为空气中的细颗粒物（PM）会激发肺部炎症，炎症的产物溢出进入身体的循环系统，到达脂肪组织引起炎症、血管功能障碍，从而引起心脏，呼吸道疾病。\"},{\"section\":\"影响免疫力的其他因素\",\"title\":\"污染的空气削弱免疫力，增加患病风险\",\"content\":\"二手烟或油烟 二手烟或油烟都含有对健康有害自由基。而过多的自由基会作用于免疫系统，引起淋巴细胞损害，造成人体免疫功能下降，对疾病的抵抗能力下降，还能导致自身免疫性疾病\"},{\"section\":\"影响免疫力的其他因素\",\"title\":\"性爱可以增强免疫力\",\"content\":\"研究表明每周做爱一次或两次可以拥有较高水平的抗体免疫球蛋白A（IgA），IgA可以加强机体避免感冒和其他感染的能力。（来自WebMD）\"}]','免疫力评估'),
	(10,'免疫力方面','[{\"section\":\"饮食习惯\",\"title\":\"口味清淡，令免疫系统维持最佳状态\",\"content\":\"多选择蒸 、煮、生吃的烹饪方式， 相对于煎炸保留食物中更多的营养，尤其是维生素和抗氧化物质，产生较少的自由基，这对维护健康的免疫系统有益。\"},{\"section\":\"饮食习惯\",\"title\":\"正确喝水，也能提高免疫力\",\"content\":\"口渴要及时喝水。人体的70%是水分，确保体内有足够洁净的水分，才能将代谢产生的废物及时排出体外。多喝水可使口腔和鼻腔黏膜保持湿润，从而发挥捕捉细菌的功能。矿泉水、淡茶、鲜榨的蔬果汁、口味清淡的菜汤都是优质水分的来源。若没有胃寒的问题，可以常喝绿茶，因其富含强抗氧化物质茶多酚，对感冒和癌症都有预防效果。\"},{\"section\":\"饮食习惯\",\"title\":\"细嚼慢咽，七分饱\",\"content\":\"保持细嚼慢咽的习惯，有助于消化吸收，也能自然达到七分饱。这对免疫系统的门户－胃肠的健康有益。 实验表明，长期适度减少热量摄入的动物，体重更轻，患有心脑血管疾病的风险更低，更健康，更长寿。\"},{\"section\":\"饮食习惯\",\"title\":\"多吃天然食物， 给免疫系统加油\",\"content\":\"有效提高免疫力的途径之一就是吃品种丰富的天然食物，和加工包装食物相比，以素食为主的新鲜的食材组合能提供免疫系统需要的全面维生素、矿物质以及抗氧化物质。\"},{\"section\":\"饮食结构\",\"title\":\"增加抗氧化食物的种类，有效提升免疫力\",\"content\":\"抗氧化剂丰富的食物能够协助清除体内的自由基，增强免疫系统的力量，从而抑制炎症反应。对免疫系统而言，食物的协同作用要优于单独补充营养素，协同抗氧化能力最佳的三种维生素是胡萝卜素、维生素C和维生素E 。抗氧化营养素在紫、蓝、红、桔、黄色果蔬中含量较高。如，杏子、芦笋、甜菜、西兰花、哈密瓜、 胡萝卜、玉米、 卷心菜、芒果、芜菁（香大头）、羽衣甘蓝、油桃、粉红西柚、南瓜、倭瓜、红薯、紫薯、蜜橘、西红柿、西瓜、蓝莓、树莓、覆盆子  、球芽甘蓝、菜花、柚子、甜瓜 、奇异果、油桃、橙子、木瓜、彩椒、荷兰豆、 草莓、 樱桃、芥末、菠菜等。也要多吃些富含维生素E的食物，如坚果和葵花籽等。\"},{\"section\":\"饮食结构\",\"title\":\"好油脂帮助改善过敏和炎症\",\"content\":\"油脂占总热量的30％以下，选择浅色肉食，如鱼类、水生贝壳、鸡鸭等，因为它们中的饱和脂肪及胆固醇含量明显低于红肉。过多的饱和脂肪会和高糖分一样影响白细胞的杀菌能力，增加机体炎症发生几率。\"},{\"section\":\"饮食结构\",\"title\":\"好油脂帮助改善过敏和炎症\",\"content\":\"必需脂肪酸在体内可以转化为具有抗炎作用的前列腺素，从而帮助改善过敏和炎症反应。种子（芝麻、南瓜籽、松籽、葵花籽、亚麻籽）和深海鱼（金枪鱼、三文鱼、沙丁鱼、鲭鱼、鲱鱼）、冷榨种子油或必需脂肪酸营养补充剂都是良好来源。\"},{\"section\":\"饮食结构\",\"title\":\"补充更多钙质食物\",\"content\":\"牛奶和豆类食物是钙的优质来源，若对牛奶过敏，您还可以选  择发菜、紫菜、荠菜、木耳等，或者适量芝麻、花生、榛子等坚果，虾米也是很不错的选择，它们都是很好的钙质来源。\"},{\"section\":\"饮食结构\",\"title\":\"多吃些公认的免疫力援兵食物\",\"content\":\"能够提高免疫力的食物还包括， 槲皮素含量较高的红酒、苹果、洋葱等可以帮助改善过敏和炎症。接骨木富含抗氧化剂，抵御感冒和发炎。蘑菇在抗感冒和抗菌以及预防癌症都有着公认的卓越表现。鸡汤可以加速感冒的康复。\"},{\"section\":\"饮食结构\",\"title\":\"补充益生菌能改善炎症和过敏\",\"content\":\"益生菌对减轻过敏时的炎症反应有帮助。益生菌不仅令致病菌生存艰难，它们还会积极地激活你的免疫系统，这意味着益生菌对于治疗癌症和过敏症同样重要。（出自《营养圣经》）常吃酸奶、干酪、泡菜、味增汤、豆腐、葡萄酒、酸面包（小麦或黑麦制品）等发酵食物，可以补充体内的益生菌。\"}]','合理营养建议'),
	(11,'免疫力方面','[{\"section\":\"运动对抗各种免疫力问题\",\"title\":\"类风湿关节炎 ?  传统慢运动可以改善关节疼痛\",\"content\":\"如果你尝试了很多治疗都不能减弱关节疼痛，可以尝试冥想、太极、腹式呼吸，它们被证明有改善疼痛，减少压力荷尔蒙的效果。尤其是太极，可以扩展关节的活动，并增加关节的力量。\"},{\"section\":\"运动对抗各种免疫力问题\",\"title\":\"怕冷 ?  适度的运动让身体温暖\",\"content\":\"除了身体虚弱的原因，适度的运动可以改善怕冷的问题。运动会加速血液循环，通过增加新鲜血液的输送将热量分布到肢体末端和体表，同时也加速了身体废物的代谢，提高到正常的体温有助于免疫系统正常运作。运动到身体发热或微微出汗为好，避免出汗过多。\"}]','有效运动建议'),
	(12,'免疫力方面','[{\"section\":\"\",\"title\":\"大笑能提高免疫力\",\"content\":\"加利福尼亚洛马琳达大学医学院的研究发现，大笑能减少压力荷尔蒙的分泌，增加免疫细胞的数量。午休时间看段搞笑视频、分享朋友转发的笑话或漫画都是获得快乐的方法。\"},{\"section\":\"\",\"title\":\"冥想或禅定有助于改善免疫力\",\"content\":\"学会如何应付紧张、处理心理问题和放松身心，是激发免疫系统的关键。实验表明在注射感冒疫苗时，经过持续8周冥想训练的人会比不冥想的人产生更多的抗体，而且这个免疫系统优势会持续4个月之久。\"},{\"section\":\"\",\"title\":\"冥想或禅定有助于改善免疫力\",\"content\":\"医学研究指出，当人禅坐进入入定状态时所出现的生理效应，如呼吸减缓、交感神经系统功能下降、副交感神经系统功能相对增强、消化系统功能亢进、耗能减缓而储能增大等，有利体内能量的充盈。禅定可以调节免疫系统，同时为神经、内分泌系统带来正面影响。\"},{\"section\":\"\",\"title\":\"为免疫系统的健康而减重\",\"content\":\"超重对免疫系统的健康不利，保持稳定的健康体重需要要改变生活方式：饮食和运动水平合理化；下定决心减肥，给自己时间和耐心，逐渐改变饮食和运动习惯；只有长期的改变才算成功。\"},{\"section\":\"\",\"title\":\"您需要高质量睡眠以维持免疫系统健康\",\"content\":\"虽然研究人员并不知道充足的睡眠如何提高免疫力，但是很明显，成人保持7-9小时的优质睡眠对免疫系统健康是有帮助的（信息来自WebMD）。繁忙时，在下午13点~16点之间小睡15分钟也可以帮助缓解缺乏睡眠带来的疲劳感。\"},{\"section\":\"\",\"title\":\"您需要高质量睡眠以维持免疫系统健康\",\"content\":\"睡眠质量低可能影响细胞激素或组胺，从而影响人体免疫功能。而固定起床时间可以帮助生物钟正常工作，也是拥有高质量睡眠的前提条件，也可以通过午睡或者安全状态下的Flash睡眠(瞬时闪睡)来进行一定的睡眠补充，这是最现实的办法。但是决不能根据前一天的睡觉时间决定起床的时间，否则很难改善生物钟紊乱的症状。\"},{\"section\":\"\",\"title\":\"您需要高质量睡眠以维持免疫系统健康\",\"content\":\"机体免疫力可以通过良好的睡眠得以恢复。美国哈佛大学医院的学者研究者发现，人在睡眠时体内的微生物可制造一种叫“胞壁酸”的物质，这种“胞壁酸”既可促进睡眠，也有增强免疫力的作用。可促使白细胞增多，巨噬细胞活跃，肝脏解毒功能增强。\"},{\"section\":\"\",\"title\":\"戒烟，是最佳的选择\",\"content\":\"你可能尝试戒烟，并体会过戒烟带来的各种不适反应。但是应该记得的是，这些不适反应只不过是些可以改善的症状，远比吸烟对内脏、心脑血管的直接损伤来的微小得多。更有许多实践经验表明，戒烟会令体重增加，增加的体重可以通过深呼吸练习、运动、均衡营养的方式减下来。\"},{\"section\":\"\",\"title\":\"长期有规律的适量饮酒帮助提升机体健康\",\"content\":\"人体可自然分解消化酒精，每天适当饮酒可使动脉血管扩张、血压下降，有利于促进血液循环，更可提升对人体有利的高密度脂蛋白及降低有害的低密度脂蛋白，从而防止心脏病发作。而适量葡萄酒（含有抗氧化物质白藜芦醇）可能降低机体老化速度。\\n适量的饮酒相当于每天一杯150ml红葡萄酒或360ml啤酒。\"},{\"section\":\"\",\"title\":\"常常受到辐射侵扰，需要抗氧化剂抵抗辐射侵袭\",\"content\":\"1.需要有意识地通过饮食或膳食补充剂摄入花青素、维生素C、维生素E、维生素A等抗氧化剂，及时清除代谢产生的自由基。 2.避免长时间连续操作电脑，眼睛与屏幕的距离应在40~50厘米。  3.室内光线要适宜，不可过亮或过暗。  4.安装防护装置，削弱电磁辐射的强度。\"},{\"section\":\"\",\"title\":\"亲密关系有助于提高免疫力\",\"content\":\"研究表明，良好的性爱或者亲密关系可以通过提高IGA水平而增加免疫力。IGA是免疫系统中一种关键的蛋白质，大脑分泌的内啡肽水平可以影响其数量。在愉悦的身体接触时，大脑会分泌更多的内啡肽。（来自WebMD）\"}]','生活方式建议'),
	(13,'抗衰老方面','[{\"section\":\"衰老信号盘点\",\"title\":\"韧带、骨骼和肌肉的退化使人体活力下降加速衰老\",\"content\":\"肌肉松软 通常过了40岁，人的肌肉开始以平均每年0.5%到2%的速度减少。从肌肉力量减少到松软下垂，多是30岁以后缺乏运动加速衰老的表现。30岁之后，肌肉蛋白质降解速度渐渐加快，但其合成速度不变，从而使肌肉渐渐衰老，不过此时肌肉仍然处于生长、衰退的循环之中，可以通过优质蛋白的摄入和日常锻炼得以恢复。\"},{\"section\":\"衰老信号盘点\",\"title\":\"胃肠功能减弱提醒您改善生活习惯，防止衰老\",\"content\":\"不耐受甜食和油腻 吃太多甜食或油腻食物有不适感说明消化能力下降，尤其是协助脂肪和糖类代谢的肝胆功能下降。随着年龄的增加，肝血流量减少，肝的转运、代谢、排泄、解毒功能都有下降。胆囊弹性降低，胆汁淤积浓缩。但正常情况60岁才会有明显的功能减退。而不良的生活习惯，如饮酒、熬夜、吃得过油腻等会令肝胆功能退化加速。\"},{\"section\":\"衰老信号盘点\",\"title\":\"胃肠功能减弱提醒您改善生活习惯，防止衰老\",\"content\":\"消化不良 正常的消化功能会随着年龄的增长而减弱。一般40岁以后消化功能会逐步减弱，减弱的过程分为机械损伤和化学损伤两部分。胃黏膜及腺细胞萎缩、退化，胃液分泌减少，易造成胃黏膜的机械损伤；胃粘膜的退化萎缩，致使胃黏膜易被胃酸和胃蛋白酶破坏，造成化学性损伤。加之不良的饮食习惯、长期不放松的情绪等因素都会加速这种老化进程。尝试七分饱的饮食方式给肠胃减负。\"},{\"section\":\"衰老信号盘点\",\"title\":\"胃肠功能减弱提醒您改善生活习惯，防止衰老\",\"content\":\"便秘 通常人在50岁左右会因肠道平滑肌支配神经系统的退化令肠道蠕动减慢，这是增龄引起便秘的重要因素。在30、40岁出现长期便秘的状况可能是肠道提早老化，也可能是其他因素，但无论何种原因的长期便秘都会影响肠道的消化、吸收和蠕动动力，从而加速肠道的老化，而适当的营养、运动、饮水可以延缓这一退化过程。\"},{\"section\":\"衰老信号盘点\",\"title\":\"胃肠功能减弱提醒您改善生活习惯，防止衰老\",\"content\":\"饭量减少 随着年龄的增长，胃的容量、动力下降（由于弹性下降），导致饭量下降，但这一般是4、50岁发生的衰老转变。若是3、40岁出现这种状况多半是缺乏运动、喜食油腻等引起新陈代谢缓慢或是因饮食不规律、压力等原因导致慢性胃肠炎的先兆，这些都是加速胃衰老的原因。改善不良饮食习惯，保护你的胃。\"},{\"section\":\"衰老信号盘点\",\"title\":\"胃肠功能减弱提醒您改善生活习惯，防止衰老\",\"content\":\"怕吃凉 《默克家庭医学手册》提到，年龄导致胃酸分泌减少的一些疾病，如萎缩性胃炎是很常见的，50岁以上发病率高达53%。怕吃凉的是萎缩性胃炎发生的先兆和典型症状。胃是一个对外界气候和温度很敏感的器官，人体受到冷空气刺激后，胃部容易发生痉挛性收缩，从而引发胃痛、消化不良、呕吐、腹泻等症状，平日需要注意胃部保暖并尽量食用温热的食物。\"},{\"section\":\"衰老信号盘点\",\"title\":\"容颜和五官变化透露衰老信息\",\"content\":\"牙龈萎缩、齿缝变宽 年轻时，牙与牙之间的缝隙很小，随着年龄增长，牙周组织萎缩，牙缝变宽，咀嚼效率也随之下降。出现明显的牙龈萎缩现象一般是在40岁左右，可以通过细嚼慢咽的方法利用食物对牙龈产生按摩促进牙龈、牙槽骨血液循环，防止齿根外露，过敏，牙齿松动等进一步老化现象发生。\"},{\"section\":\"衰老信号盘点\",\"title\":\"容颜和五官变化透露衰老信息\",\"content\":\"视物模糊或飞蚊症 通常40岁以后视觉细胞衰老的征象开始出现，晶状体纤维的弹性减弱，透明度降低，眼睛不再有以前明亮，出现浑浊，视物模糊，这是一种自然老化的现象。但是往往我们因为用眼过度，使得眼睛提前出现退化变性的现象，需要特别注意眼睛的休息和养护。\"},{\"section\":\"衰老信号盘点\",\"title\":\"内分泌水平紊乱引发衰老问题\",\"content\":\"压力引发头疼 压力作用于交感神经，引发肾上腺素、去甲肾上腺素分泌增加，引起脑缺血及血管的“无菌性炎症”，产生头痛感。《营养圣经》提出由压力引发的头痛出现频繁则说明您长期处于过度压力中，而长期压力与老化过程加速密切相关。\"},{\"section\":\"衰老信号盘点\",\"title\":\"内分泌水平紊乱引发衰老问题\",\"content\":\"脾气暴躁 通常无论男女，在更年期年龄附近（40-55岁）容易发生激素水平紊乱的情况。三十几岁经常有难以控制的坏脾气，可能是压力过大导致的，长期如此会加速机体衰老。当压力过大时体内的激素水平会出现较大的波动，从而需要调用本应用来修复或维护机体日常生理功能的营养素，甚至影响了正常的生理功能，增加了心脏病、高血压、糖尿病等慢性疾病的患病风险。\"},{\"section\":\"衰老信号盘点\",\"title\":\"内分泌水平紊乱引发衰老问题\",\"content\":\"性趣降低 《抗衰老学》提出：性激素的分泌，男性在40岁时达高峰，以后就逐渐减弱，女性35~40岁雌激素急剧减少。不但会使得性趣降低，同时女性雌激素的减少还影响骨质成分，骨胶原，钙盐等减少，出现骨质疏松。可以通过补充钙质和大豆异黄酮对雌激素起到调节作用。\"},{\"section\":\"衰老信号盘点\",\"title\":\"内分泌水平紊乱引发衰老问题\",\"content\":\"腰腹发胖 调查显示：腰部肥胖更容易引起胰岛素反应迟钝，因此，摄入同等量糖类食物时会释放更多胰岛素，过多的胰岛素将增加脂肪的合成，使体内有更多的脂肪堆积在腰腹及内脏，让你更容易患上与衰老密切相关的各种慢性疾病。\"},{\"section\":\"衰老信号盘点\",\"title\":\"心肺功能的降低，意味着衰老的到来\",\"content\":\"爬楼气喘 爬5层楼梯是衰老与心肺活力的基础测试， 30 岁左右的人，一步迈两层台阶，能快速登上 5 层楼，仍觉得轻松，说明健康状况良好； 不论哪个年龄阶段的人，如果气喘吁吁，心跳加速，则说明心肺功能降低。心肺功能关系着一呼一吸的大事，体内供氧量的大小及新陈代谢的快慢和正常与否，与人体的衰老紧密相连。需要加强日常运动习惯养成，提高心肺功能，减缓衰老进程。\"},{\"section\":\"衰老信号盘点\",\"title\":\"泌尿系统的衰老变化\",\"content\":\"尿频、尿少或夜尿增多 一般来说人到50岁以后，肾功能逐渐减退，膀胱容量减少，尿道肌肉松弛使得排尿出现问题。如果是3、40岁出现尿频、夜尿增多、小便量少的状况需要引起关注，养成良好的运动习惯（详情见运动建议部分），改变久坐不动、憋尿等生活中的不良习惯延缓其衰老速度，必要时就医调养。\"},{\"section\":\"衰老信号盘点\",\"title\":\"脑力会随着年龄增长而出现退化现象\",\"content\":\"记忆力减退 美国有研究表明，人的记忆力到了37岁开始明显转差，大脑的其他一些能力也会在42岁左右开始走下坡路。过早出现的记忆力减退可能由于压力或睡眠问题引发，一方面注意休息，另一方面大脑的衰退，完全是受到“用进废退”规律的支配，常常阅读、学习，进行常识性知识的积累（人的这方面能力直到60~70岁也不会减退），能够延缓大脑其他功能的衰老速度，培养多用脑的习惯，在整个生命阶段中都不要停顿，这样才能常保脑力青春。\"},{\"section\":\"衰老信号盘点\",\"title\":\"固执或对新鲜事物缺少兴趣更多的是心理上的衰老现象\",\"content\":\"固执、兴趣缺乏 现代心理学研究指出，人到一定年龄（30岁以后），神经系统的反射就形成一定的固定模式，如果不进行积极的调节和控制就趋向于自我内向性认同，导致固执己见或对新鲜事物缺少兴趣，这是一种心理衰老的表现。如果因此而很少接触新鲜事物，使大脑缺少应有的刺激，就会加速生理衰老，导致敏感性和反应力明显减退，而形成思维能力下降、记忆力锐减及老年痴呆等一系列问题。积极培养自己各种爱好和兴趣，养成锻炼身体的习惯，有助于恢复心理活力。\"},{\"section\":\"衰老信号盘点\",\"title\":\"免疫力下降与衰老相互影响，形成恶性循环\",\"content\":\"炎症、感染恢复时间长 当发现炎症及感冒恢复明显变慢时，可能是机体衰老的信号。随着年龄增长，人体内的补体蛋白不断流失，使得机体自我修复功能受到影响，因此会有免疫力下降感觉。《抗衰老学》中提到，下降的免疫力同时会加速衰老的进程，使得人们更易患感染、自身免疫性疾病和恶性肿瘤。注意日常优质蛋白的补充（如乳清蛋白，大豆蛋白）、提升免疫力，树立抗衰老屏障。\"},{\"section\":\"衰老信号盘点\",\"title\":\"免疫力下降与衰老相互影响，形成恶性循环\",\"content\":\"过敏、感冒等频发 《抗衰老学》提到：随着年龄增长，免疫功能减退，对自身免疫细胞的识别能力减弱，除了攻击外来病原体外，还攻击自身免疫组织，引发机体衰老，导致流感、炎症、过敏等免疫疾病更容易发生。可通过调整饮食结构，合理运动提升自身免疫力延缓衰老。\"},{\"section\":\"衰老相关疾病\",\"title\":\"血糖高引起的糖基化作用会加速衰老\",\"content\":\"血糖升高会对人体细胞产生糖基化作用，胶原质是最早受到影响的蛋白质之一。胶原质因为糖基化而失去弹性，从而加速老化的发生，这意味着血管、肺组织和关节、皮肤会失去弹性，眼睛的晶状体变模糊，形成白内障。\"},{\"section\":\"衰老相关疾病\",\"title\":\"延缓衰老的生活方式有助于降低冠心病风险\",\"content\":\"冠心病若养护不当，猝死率很高（统计表明50-60%猝死是冠心病）。吸烟、高血压、高血糖、高血脂、肥胖、久坐等问题都是冠心病发作的危险因素，通过合理的饮食和有规律的运动，可以大大减少患病的风险，甚至可以免除药物的使用。可以配合每天服用抗氧化维生素，如维生素E、维生素C、叶酸等，阻止低密度脂蛋白在血管的积累，预防发病。\"},{\"section\":\"衰老相关疾病\",\"title\":\"过早出现骨质疏松症可能是提前衰老的表现\",\"content\":\"大多数人的骨密度在30岁后开始减低，特别是女性更年期后骨密度降低的速度加快。《默克家庭医学手册》提到，骨质疏松的发生不但与遗传和免疫功能异常有关，尤其与体内激素的分泌失调，营养状况及物理因素有关。不过相对于恢复已降低的骨密度，保持骨密度不变要来的更容易些。预防措施包括摄入足够的钙和维生素D和从事负重锻炼。\"},{\"section\":\"衰老相关疾病\",\"title\":\"脂肪肝是导致多种肝脏损害、衰老的诱因\",\"content\":\"运动少，高热量饮食，过量饮酒都是脂肪肝发生的原因，这本身就是一个不良生活习惯导致的自由基和脂质过氧化物增加导致肝损伤的过程。 因为起初没有明显症状，所以不太引起重视，但如果不改变生活习惯，则容易引起肝脏的反复损害，最终发展到肝硬化。即便在单纯的脂肪肝阶段，身体的新陈代谢也会因为肝功能受损而受到影响，有毒物质的排出和油脂、蛋白质、维生素的利用出现紊乱，最初的表现可能只在油光的皮肤，但最终的结果却是加速全身器官的老化。\"},{\"section\":\"衰老相关疾病\",\"title\":\"前列腺炎症若不改善，会令你体会衰老时常见的困扰\",\"content\":\"前列腺炎若不及时治疗会加重尿频、尿急、尿不尽等症状，还可能影响性功能，严重者还可能出现忧郁等神经官能症。除了积极配合治疗外，注意尽量避免久坐、受寒、焦虑、憋尿、辛辣食物等。\"},{\"section\":\"衰老相关疾病\",\"title\":\"健康的胆囊，助您保持年轻态\",\"content\":\"慢性胆囊炎若不及时治愈，会给生活增加很多限制，常常因为消化不良，厌油腻食物、上腹部闷胀、嗳气、胃部灼热等问题而影响品味美食的心情，而且压力和情绪的变化也会对胆囊健康产生影响。\"},{\"section\":\"衰老相关疾病\",\"title\":\"体重增加令代谢功能降低，增加罹患多种慢性疾病风险\",\"content\":\"肥胖的原因很多，但总归导致体内脂质代谢紊乱，机体虽可能无明确疾病，但令整体活力和适应性不足，成为加速衰老的因素。 此外，脂肪在皮下和内脏器官周围堆积，这使得患冠心病、高血压、糖尿病等慢性疾病的风险增加。\"},{\"section\":\"衰老相关疾病\",\"title\":\"腰围超标意味越过慢病安全警戒线\",\"content\":\"腰围的重要性仅次于体重指数（BMI=体重KG/身高M²）。腰围的大小可以表明脂肪堆积的部位。脂肪聚集在腰上的人更容易患高血压、糖尿病、冠状动脉疾病，因为腹部的脂肪更容易分解并在动脉里聚集。男性腰围超过102厘米或女性超过88厘米，患相关疾病的危险就会增大，尤其是体重指数为25或以上的人群。\"},{\"section\":\"衰老相关疾病\",\"title\":\"高血压会加速机体衰老\",\"content\":\"高血压若不及时防治则会损害血管、心、脑、肾、视网膜等组织器官，从而加速机体衰老。血压高会令动脉粥样硬化发病率增高，影响重要器官组织供血甚至威胁生命；血压升高会导致心脏的结构和功能发生变化，日久会出现心力衰竭与心率失常；高血压引起的脑出血会导致身体致残，甚至死亡；高血压会影响肾中的小动脉，导致肾脏缺血、肾小球萎缩，最终发展为肾功能损害引起尿毒症。因此积极治疗，保持良好的生活习惯，则有助于稳定血压，延缓衰老。\"},{\"section\":\"衰老相关疾病\",\"title\":\"血脂高诱发体内的自由基反应，会加速衰老\",\"content\":\"有大量调查资料表明，血脂中血清总胆固醇和甘油三酯会从青年时期就随年龄而上升，当然这与生活习惯和脂类物质代谢功能退化有很大关系。 大量基础研究表明，高血脂时体内自由基的产生和清除平衡被破坏，许多自由基清除剂（如SOD、CAT）活性降低，产生大量的脂质过氧化物（LPO），继而损伤各类细胞，加速肝、脑等重要器官的衰老；同时造成动脉狭窄或阻塞，导致主要器官供血不足，加速衰老。因此必须及时调整生活方式，并在医生指导下借助药物改善状况。\"},{\"section\":\"衰老危险因素\",\"title\":\"不健康食物吃得多，会令衰老提前发生\",\"content\":\"爱吃油炸 常吃油炸食物增加早衰风险。油炸会破坏食物中的必需脂肪酸、蛋白质和维生素，食物经油炸产生的致癌物（丙烯酰胺 ）会威胁细胞DNA的安全，这会增加心脏病，癌症的风险。\"},{\"section\":\"衰老危险因素\",\"title\":\"不健康食物吃得多，会令衰老提前发生\",\"content\":\"常吃包装或加工食品 包装或加工食物对想保持年轻活力的您无益，这些食物中的添加剂不仅会阻止营养素的吸收和利用，还会加速营养素的流失，并增加肝脏的代谢负担，常吃这类“反营养食物”会加速身体衰老。（出自《营养圣经》）\"},{\"section\":\"衰老危险因素\",\"title\":\"不健康食物吃得多，会令衰老提前发生\",\"content\":\"爱吃重口味食物 爱吃重口味（如过咸）食物可能会加速衰老。盐摄入过度不仅容易导致高血压，而且能令皮肤细胞缺水萎缩，导致皱纹增多（此信息来自法国国家医学院），甚至影响肾代谢而导致蛋白质合成受损，引起脱发。长期吃辣对消化道刺激大，引起慢性炎症。\"},{\"section\":\"衰老危险因素\",\"title\":\"不健康食物吃得多，会令衰老提前发生\",\"content\":\"常喝甜饮料 “甜味垃圾食品吃得太多的人更容易衰老” 发表于以色列科学技术研究所《营养学杂志》。一是糖分会使皮肤和骨质中的胶原蛋白丧失，结果是皮肤、骨骼缺乏弹性。二是加工食物中的糖会令血糖快速升高，增加糖尿病和心脑血管疾病的风险。三是人工合成的甜味剂，如阿斯巴甜也能刺激大脑对甜食产生更多渴望。\"},{\"section\":\"衰老危险因素\",\"title\":\"不健康食物吃得多，会令衰老提前发生\",\"content\":\"肉食过多 “无荤不饭”会引起各种老化性疾病提早发生。肉类中的饱和脂肪和胆固醇会增加动脉硬化的风险，进而影响到听力、视力、心脏、大脑、肝脏等各个器官健康，加速机体衰老。芝加哥大学的一项研究表明持续六周的高蛋白膳食就会给肾脏带来明显的酸代谢负担，增加形成结石的风险，降低钙平衡水平，加速骨质疏松症的发生。\"},{\"section\":\"衰老危险因素\",\"title\":\"不良的就餐习惯会令你提前衰老\",\"content\":\"常在外就餐 常在外就餐可能会加速机体老化。餐馆食物多油盐调料、少新鲜果蔬，很难保证营养均衡充足，尤其是缺少清除自由基的抗氧化物质，这种饮食不足以抵御现代城市生活带来的衰老风险。你需要适当补充富含维生素和高抗氧化物质的食物。（具体膳食建议见营养补充部分）\"},{\"section\":\"衰老危险因素\",\"title\":\"不良的就餐习惯会令你提前衰老\",\"content\":\"不吃早餐 养成每天一顿认真早餐的习惯能够帮你延缓衰老。伦敦大学帝国学院研究发现，不吃早餐，会令大脑自觉对高热量食物产生好感，引起肥胖，胆固醇增高，增加患心脏病的风险；另外，因空腹时间过长，使胆固醇在胆囊中沉积形成结晶形成胆石症；此外长期不吃早餐的人需要动用体内储存的糖原和蛋白质来提供能量，产生过度消耗也会加速身体老化。\"},{\"section\":\"衰老危险因素\",\"title\":\"不良的就餐习惯会令你提前衰老\",\"content\":\"经常吃得过饱 经常吃得过饱，会损害人体的各种机能甚至导致疾病。常常吃撑会令血糖快速变化，刺激胰岛素的分泌，久之易患糖尿病。吃撑时，胃肠过度扩张，易患胃肠黏膜炎症，有实验证明，长期吃撑，年老后体内甲状旁腺素含量会明显增加，加速了骨骼脱钙，从而增加骨质疏松风险。日本有关专家还发现，大约有30%-40%的老年痴呆病人，在青壮年时期都有长期饱食的习惯。\"},{\"section\":\"衰老危险因素\",\"title\":\"健康食物吃得过少，不利机体保持年轻\",\"content\":\"蔬果吃得少 蔬果吃得少对延缓衰老无益，蔬果提供丰富的抗氧化物质，可以延缓细胞衰老并对抗感染，提高免疫力。你吃的蔬果种类越丰富，抗氧化作用就越理想，因为抗氧剂需要“团队作业”。良好的营养，尤其是维生素C和维生素E可以极大地延长寿命。（出自《营养圣经》）\"},{\"section\":\"衰老危险因素\",\"title\":\"健康食物吃得过少，不利机体保持年轻\",\"content\":\"粗粮吃得少 很少吃粗粮会令纤维素、维生素B族和矿物质缺乏，这会增加患慢性病（血脂高、血压高、血糖高）的风险并加速机体老化。纤维素能减少胆固醇和脂肪的吸收，维持血糖的平稳，也能令排便顺畅、清洁肠道。维生素B族能协助体内能量代谢，尤其是保持血糖稳定，也能维持稳定情绪，保持精力充沛。各种矿物质也是维护健康和活力所必需的，如钙和镁对健康的血压是必要的。适当增加粗粮种类，有助于降低慢性病风险。（具体膳食建议见营养补充部分）\"},{\"section\":\"衰老危险因素\",\"title\":\"健康食物吃得过少，不利机体保持年轻\",\"content\":\"豆、奶制品吃得少 很少吃豆、奶制品可能导致因缺乏钙质而产生机体功能衰退，奶制品和豆制品被认为是钙的主要来源，钙是维持骨骼、牙齿、神经健康的主要矿物质，缺乏则容易造成骨质疏松、龋齿、失眠、紧张等问题。\"},{\"section\":\"衰老危险因素\",\"title\":\"睡眠质量不佳影响荷尔蒙功能及新陈代谢，开启衰老加速器\",\"content\":\"起床困难或睡不够 喜欢赖床或睡后精力不能补足。可能是繁重的脑力或体力劳动造成极度疲劳，使得需要更长时间的睡眠来恢复。也可能不规律的生活习惯造成了生物钟紊乱，无法控制睡眠和觉醒的正常切换。\"},{\"section\":\"衰老危险因素\",\"title\":\"睡眠质量不佳影响荷尔蒙功能及新陈代谢，开启衰老加速器\",\"content\":\"失眠 失眠者的衰老速度是正常人的2.5-3倍（出自《抗衰老学》）。失眠会影响人的精力和脑力，尤其是创造性思维能力，很多慢性疾病如心脏病、高血压等也被证明和睡眠质量不佳有关，这些都能加速机体老化。 思考生活习惯的问题：如压力、咖啡因、酒精、环境变化、时差、慢性疾病、焦虑等等，调整自身的生活习惯视为第一位。这可能是自己无法控制的，但是要明确一点，这并不是不可以解决的问题，所以不用担心，给自己一点儿耐心。\"},{\"section\":\"衰老危险因素\",\"title\":\"睡眠质量不佳影响荷尔蒙功能及新陈代谢，开启衰老加速器\",\"content\":\"经常熬夜 超过24点不睡会影响新陈代谢效率，从而加速机体老化。睡眠时流经肝脏的血液是平时的七倍，有利于增强肝细胞功能，提高解毒能力。尤其是1点－3点是皮肤代谢的高峰期，此时不睡很容易导致皮肤干燥、暗沉。\"},{\"section\":\"衰老危险因素\",\"title\":\"睡眠质量不佳影响荷尔蒙功能及新陈代谢，开启衰老加速器\",\"content\":\"睡眠不足 美国芝加哥大学研究发现，长期处于睡眠不足的状态，使得体内紧张激素“皮质醇”水平长期偏高，其负面效应显现为：血糖升高、影响情绪和记忆、抑制肌肉及骨骼中蛋白质合成等，将身体推向衰老状态。\"},{\"section\":\"衰老危险因素\",\"title\":\"睡眠质量不佳影响荷尔蒙功能及新陈代谢，开启衰老加速器\",\"content\":\"多梦 梦多是在快速动眼睡眠中出现的，这段时间人的体温上升，心跳增加，血流速快，这种状况出现的原因很多，也会对睡眠质量造成影响，压力大、精神紧张、焦虑都有可能成为多梦易醒的原因。 但是如果并不能构成对睡眠质量的影响，且没有其他睡眠问题则不妨将其看做一次美妙的夜的旅行。\"},{\"section\":\"衰老危险因素\",\"title\":\"不规律的睡眠扰乱循环系统生物钟，身体隐形“生病”\",\"content\":\"睡眠不规律 其实除了睡眠，我们心率、呼吸、血压、体温、蛋白质合成等体内一切生命活动，往往都呈现昼夜节律的倾向，反映在血液循环、内分泌和各种器官功能中。不规律的睡眠就好比在不停地倒时差，各器官的生物钟调整所需的时间不同，睡眠两三天就可以调整过来，但是体内的器官及循环系统的生物钟却需要两三周的时间调整。经常如此，会加速机体衰老。\"},{\"section\":\"衰老危险因素\",\"title\":\"运动量不够，依然健康长寿的可能性不大\",\"content\":\"运动量不够而依然健康长寿，并非完全不可能，但这种可能性不大。《抗衰老计划——阿特金斯医生的建议》中提到“调查显示，在有可能导致心脏病的各种因素中，缺乏运动是首要因素。锻炼身体能够预防、减缓或逆转随着年老而减弱的葡萄糖耐受和胰岛素敏感性。”因此，重要的是知道自己的身体需要多大的运动量，此外还应该知道什么样的运动对你的健康最有利。（建议详情请见有效运动部分）\"},{\"section\":\"衰老危险因素\",\"title\":\"每吸一根烟会使人折寿11分钟\",\"content\":\"据英国一项研究表明，吸一根烟会使人折寿11分钟，如果一天吸15支烟，则会减寿2小时45分钟，若从16岁开始吸烟，71岁的平均年龄死亡，一生将吸烟31万余支，这样吸烟者平均比不吸烟的人早死亡6.5年。\\n吸烟除了人所共知的尼古丁危害外，在吸烟过程中产生的气相烟雾和焦油中还存在着大量对人体有害的自由基，引发各种过氧化反应，损伤细胞膜成分，进而引发衰老甚至恶性疾病，如癌症或心脏病。\\n日常需要适当补充天然抗氧化剂，如：绿茶、新鲜果蔬等，起到清除活性氧自由基的作用。\"},{\"section\":\"衰老危险因素\",\"title\":\"过量饮酒可导致提前衰老，并遭到与衰老有关的疾病侵袭\",\"content\":\"过量饮酒的人看上去往往更加憔悴。人们普遍认为过量饮酒可导致提前衰老并遭到与衰老有关的疾病侵袭。\\n意大利米兰大学进行的一项研究指出，过量酒精可使细胞染色体失去完整性和稳定性的保护，从而加速衰老，增加癌症风险。\\n世卫组织认为每日安全饮酒量的上限是25g酒精，相当于1两42度白酒，或200ml红酒，一瓶啤酒（640ml）。\"},{\"section\":\"衰老危险因素\",\"title\":\"辐射产生的自由基加速人体细胞氧化\",\"content\":\"手机和电脑产生的低频电磁辐射是人体外源性自由基产生的原因之一。随着辐射剂量累积增加，可能会引起神经系统、循环系统、免疫系统、生殖系统以及皮肤和眼睛的功能、代谢或形态的改变，这是一个自由基加速身体细胞氧化的过程。\"}]','抗衰老评估'),
	(14,'抗衰老方面','[{\"section\":\"饮食习惯\",\"title\":\"口味清淡，衰老慢些\",\"content\":\"尽量选择蒸、炖、煮的烹饪方式，这些烹饪方式相对于煎炸能保留更多的营养，产生更少的自由基，减少体内的自由基对延缓细胞衰老有帮助。\"},{\"section\":\"饮食习惯\",\"title\":\"天然食物有效抗击衰老\",\"content\":\"健康零食都是天然食物，时令水果、酸奶、原味坚果、天然果干，这些都可以作为零食，既能满足吃零食的习惯，也能给身体提供必要营养。\"},{\"section\":\"饮食习惯\",\"title\":\"天然食物有效抗击衰老\",\"content\":\"尽量选择新鲜的当地、当季产新鲜食物，它们除了能提供必需的营养素外，还能帮助清除体内有害物质，如导致衰老的自由基。加工食品中存在多种人工合成的化学物质，已有许多国家研究表明加工食品中的各种添加剂摄入过多，会存在致癌风险。\"},{\"section\":\"饮食习惯\",\"title\":\"聪明饮水，保持机体年轻态\",\"content\":\"常喝白水或矿泉水。人体的70%是水分，确保体内有足够洁净的水分，才能将代谢产生的废物及时排出体外，保持机体年轻态。尤其是体型偏瘦的人，更需要有效水份来滋润你的细胞。白水、矿泉水、鲜榨的蔬果汁、口味清淡的菜汤都可以是补充水分的来源。\"},{\"section\":\"饮食习惯\",\"title\":\"聪明饮水，保持机体年轻态\",\"content\":\"无论红茶、绿茶还是乌龙茶、普洱茶，都可以根据自己的喜好每天喝一杯，茶中含有促进消化酶，能帮助分解脂肪，减轻消化系统负担。喝茶宜在饭后1小时。\"},{\"section\":\"饮食习惯\",\"title\":\"聪明饮水，保持机体年轻态\",\"content\":\"养成每天晨起空腹喝一杯温开水的习惯。可刺激胃肠蠕动，促进排便，且能增加肠中水分，防止粪便干燥。\"},{\"section\":\"饮食习惯\",\"title\":\"细嚼慢咽、七分饱，轻松保持年轻态\",\"content\":\"保持细嚼慢咽的习惯，每口饭至少咀嚼20次，不仅有助于消化吸收，也能自然达到七分饱。日本学者统计约有30%--40%的老年性痴呆病人在青壮年时期有饱食的习惯。因摄入的总热量远远超过机体的需要，致使体内脂肪过剩、血脂增高，导致脑动脉粥样硬化，有一种被称为“纤维芽细胞生长因子”的物质在大脑中明显增多，这种物质被认为是引起脑动脉硬化的重要因素，而脑动脉硬化是引发老年痴呆的重要原因。实验表明，长期适度减少热量摄入的人更长寿，体重更轻，患心脑血管疾病的风险更低。\"},{\"section\":\"饮食结构\",\"title\":\"全谷物食物助您保持年轻活力\",\"content\":\"一顿对自己负责任的早餐最好是全谷物配合优质蛋白；如杂豆粥、薯类、全麦面包等，加一份蛋白质（坚果、鸡蛋、瘦肉、牛奶或豆浆等）。繁忙时，最简单的早餐也可以是一份冲饮的藕粉、芝麻糊、豆奶、五谷粥、麦片等。全谷物搭配优质蛋白可以提供令你保持精力充沛的全面营养。\"},{\"section\":\"饮食结构\",\"title\":\"全谷物食物助您保持年轻活力\",\"content\":\"最新的美国营养学研究建议，主食中的一半应该是全谷物和粗杂粮，包括玉米、全麦制品、糙米、燕麦等。全谷物和粗杂粮富含膳食纤维和维生素B族，其中不可溶性膳食纤维可以促进排便、改善大肠健康，可溶性纤维会影响小肠内的脂质和葡萄糖的吸收，从而降低三高风险。维生素B族则是降低同型半胱氨酸的关键营养素，血中同型半胱氨酸超标直接损伤神经血管，引发动脉硬化，是导致心脑血管病、某些癌症、老年性痴呆等多种疾病的重要危险因素。\"},{\"section\":\"饮食结构\",\"title\":\"优质蛋白抗衰老\",\"content\":\"吃肉的时候尽量选择浅色肉食，如鱼类、水生贝壳、鸡鸭等，因为它们中的饱和脂肪及胆固醇含量明显低于红肉，提供丰富蛋白质和细嫩口感的同时，也降低了令心脑血管衰老的慢性疾病。\"},{\"section\":\"饮食结构\",\"title\":\"优质蛋白抗衰老\",\"content\":\"我们的皮肤、肌肉、骨骼和韧带都是由大量的胶原蛋白构成并维持良好的特性，平日可以有意多吃些富含胶原蛋白的猪皮、猪蹄、牛蹄筋、牛腱。如果吃素也可以从丰富的杂粮、种子、豆类中获得足够的蛋白质。\"},{\"section\":\"饮食结构\",\"title\":\"色彩鲜艳的果蔬是良好的抗氧化、抗衰老食物\",\"content\":\"衰老就像“生锈”一样，体内产生大量自由基，污染和腐蚀人体细胞、组织、器官，比如常见的心脑血管疾病，眼睛功能老化等都和自由基有关 。研究表明，血液中抗氧化剂的含量或日常饮食摄入抗氧化剂量高的人死亡率低 ，会降低老年痴呆、癌症、心血管疾病、白内障、糖尿病、高血压、关节炎、视力功能退化等问题的发生率。\"},{\"section\":\"饮食结构\",\"title\":\"色彩鲜艳的果蔬是良好的抗氧化、抗衰老食物\",\"content\":\"保持皮肤年轻态可以多吃些黄色、橙色和红色的蔬果，它们都富含β-胡萝卜素，在体内能转换成维生素A，是维持皮肤健康必需的营养素，且对眼睛极有帮助。当维生素A缺乏时皮肤抗感染能力下降，肤质粗糙，眼睛失去光泽。\"},{\"section\":\"饮食结构\",\"title\":\"色彩鲜艳的果蔬是良好的抗氧化、抗衰老食物\",\"content\":\"维生素C是抗氧化能力较强的物质。生活中，维生素C广泛存在于草莓、樱桃、蓝莓、葡萄、番茄、西兰花等水果蔬菜中。\"},{\"section\":\"饮食结构\",\"title\":\"色彩鲜艳的果蔬是良好的抗氧化、抗衰老食物\",\"content\":\"维生素E作为主要的天然抗氧化剂之一， 具有抗皮肤老化的作用，帮助保持皮肤弹性，有助延缓肌肤松弛和细纹形成，保持肌肤年轻状态。维生素E则广泛存在于坚果、粗粮中。\"},{\"section\":\"饮食结构\",\"title\":\"必需脂肪酸有助于保持年轻态的精力脑力\",\"content\":\"必需脂肪酸对保持年轻态有重要作用，尤其是OMEGA-3不饱和脂肪酸对大脑的视觉、学习能力、协调能力和情绪等功能的维持是必不可少的。平日要多吃些：南瓜籽、胡桃、三文鱼、青鱼、沙丁鱼、金枪鱼和鸡蛋、葵花籽、芝麻、玉米、大豆和小麦胚芽。\"},{\"section\":\"饮食结构\",\"title\":\"补充益生菌改善肠道健康\",\"content\":\"并非由于缺乏纤维素引起的便秘，可以由补充增加肠道益生菌食物而得以缓解。常吃酸奶、干酪、泡菜、味增汤、豆腐、葡萄酒、酸面包（小麦或黑麦制品）。常吃香蕉、洋葱、大豆等也有帮助。因为益生菌在肠道中能促进矿物质的吸收，并能制造维生素，主要是维生素K、B12、叶酸，这些维生素能够帮助缓解便秘。同时益生菌也能保护肠道的健康。\"},{\"section\":\"饮食结构\",\"title\":\"补充含钙质丰富的食物，预防钙丢失引发衰老\",\"content\":\"中国营养学会建议，30岁以后需要每天补充800mg的钙质，以维持机体健康，并预防衰老性疾病发生。牛奶和豆类食物是钙的优质来源，此外还可以选择发菜、紫菜、荠菜、木耳、海米、软骨、骨汤等，或者适量芝麻、花生、榛子等坚果，都是很好的钙质来源。\"}]','合理营养建议'),
	(15,'抗衰老方面','[{\"section\":\"运动对抗各种衰老问题\",\"title\":\"记忆力下降 ?  有氧运动和冥想能帮助调节情绪\",\"content\":\"有氧运动也叫心血管耐力型运动，能够由大脑和其他部位释放β-内啡肽（天然镇定剂）使人感到心情愉快，而且这种重复的、有规律性的运动，提供了一种冥想形式，将大脑左半球的优势转移到右半球。它可以影响人的情绪、提高记忆力水平。\\n结合心理体操，瑜伽，或是静坐，把你的脑袋调至放空的频率，渐渐让自己安静下来。\"},{\"section\":\"运动对抗各种衰老问题\",\"title\":\"免疫力下降 ?  适度运动提升人体免疫力\",\"content\":\"研究表明，常规适度的运动，如每天步行30分钟，就可以增加白细胞的水平，白细胞是免疫系统中抗感染的主力军（信息来自WebMD）。平缓而非紧张的运动形式，对提高免疫力也有利。\"},{\"section\":\"运动对抗各种衰老问题\",\"title\":\"用眼过度 ?  经常参加室外运动是对眼睛很好的放松\",\"content\":\"运动不仅可以锻炼身体，在运动时眼睛还可以得到休息。人在运动时，往往习惯向远处看，如果近处有活动的物体，又会调节眼肌，追随着活动的物体；此外，在室外散步、跑步、爬山还可以让眼睛进行远近视野的交换，这对眼调节肌的放松以及视疲劳的预防有很好的作用。\"},{\"section\":\"运动对抗各种衰老问题\",\"title\":\"肌肉松软 ?  力量训练保护骨骼、关节和肌肉\",\"content\":\"力量训练是身体的必须活动，目的在于增加和保持骨骼重量（这一点对女性更为重要，女性骨骼重量下降的风险比男性早得多），拥有足够的肌肉量以保护和稳定关节。\\n安排一周两三天进行身体力量训练，每次半小时，有指导的器械、哑铃或普拉提运动。\"}]','有效运动建议'),
	(16,'抗衰老方面','[{\"title\":\"吸烟，要停下来也容易\",\"content\":\"你可能尝试戒烟，并体会过戒烟带来的各种不适反应。但是应该记得的是，这些不适反应只不过是些可以改善的症状，远比吸烟产生的直接损伤微小的多。更有许多实践经验表明，虽然戒烟会令体重增加，但增加的体重完全可以通过深呼吸练习、运动、均衡营养的方式减下来。\"},{\"title\":\"长期有规律的适量饮酒帮助提升机体健康\",\"content\":\"人体可自然分解消化酒精，每天适当饮酒可使动脉血管扩张、血压下降，有利于促进血液循环，更可提升对人体有利的高密度脂蛋白及降低有害的低密度脂蛋白，从而防止心脏病发作。而适量葡萄酒（含有抗氧化物质白藜芦醇）可能降低机体老化速度。\\n适量的饮酒相当于每天一杯150ml红葡萄酒或360ml啤酒。\"},{\"title\":\"常常受到辐射侵扰，需要抗氧化剂抵抗辐射侵袭\",\"content\":\"1.需要有意识地通过饮食或膳食补充剂摄入花青素、维生素C、维生素E、维生素A等抗氧化剂，及时清除代谢产生的自由基。 2.避免长时间连续操作电脑，眼睛与屏幕的距离应在40~50厘米。  3.室内光线要适宜，不可过亮或过暗。  4.安装防护装置，削弱电磁辐射的强度。\"},{\"title\":\"睡眠周期符合自然规律，有助于延缓衰老\",\"content\":\"虽然不能够像长寿老人那样终生保持日出而作、日落而息的作息习惯，至少应该遵循身体的生物节律，尝试22点关闭灯光、游戏、电视、互联网，便可以很快进入睡眠状态。可以把你工作的时间放在早上，这样不仅有适量的睡眠，也睡在合适的时间。\"},{\"title\":\"睡眠周期符合自然规律，有助于延缓衰老\",\"content\":\"不论晚上睡眠如何糟糕，你的生物钟需要一个起点。比起控制睡眠时间，控制起床时间更为容易，保证在7：30分左右起床（或者是自己既定的时间，但是不要超过8：00），尽量将其保持在30分钟的浮动范围内，让你的起床时间有规律是改善睡眠不规律的方法，当然这取决于个人的意愿。\"},{\"title\":\"午间补充短暂睡眠，缓解疲劳事半功倍\",\"content\":\"在11点－13点小憩可以缓解晚间睡眠不足带来的疲劳感。但是午睡时间不要超过30分钟，否则反而会影响夜间睡眠。\"},{\"title\":\"认真的休息，良好的睡眠，不要对身体产生过度消耗\",\"content\":\"通过一些训练，学习控制你的梦，享受美好的梦境，缓解压力，帮助你更好的睡眠。 根据英国睡眠指导专家研究，有42%的人通过睡前的想象控制自己的梦，多试几次这个比例可以达到更高。其实很简单，在睡前安静地坐上10分钟，放慢呼吸，暗示自己可以在做梦的时候保持清醒，想象一个快乐美好的主题，多试几次。\"},{\"title\":\"避免那些可能引发失眠的习惯\",\"content\":\"避免下午食用这些食物：咖啡因（浓茶、苏打饮料、咖啡），油腻或辛辣食物。睡前4小时不要吃太饱。卧室里不要有蓝光光源，不能关闭的设备可以覆盖。睡前做剧烈运动也会令您因为兴奋而失眠。（信息来自WebMD）\"},{\"title\":\"多动脑，让生活丰富起来，保持年轻心态\",\"content\":\"虽然人自出生后，神经细胞便不会再分裂，但使神经细胞批次联系而传递信息的“树突”却依然生长，即使是40~50岁还有生长的能力，勤于思考、学习是必要的事情。\"},{\"title\":\"多动脑，让生活丰富起来，保持年轻心态\",\"content\":\"改变固执心态，扩大生活的兴趣点有助于抗衰老。新加坡国立大学研究指出，进行购物、烹饪、做家务等活动，比打麻将、跑步、看电影、出游等达到的防脑细胞退化效果明显。\\n将“收藏”作为一种爱好可以延缓衰老，因为“收藏”被证实具有辅助治疗高血压、胃病、神经衰弱的疾病的功效，已被20多个国家列入“心理疗法”的正式科目。\"},{\"title\":\"多动脑，让生活丰富起来，保持年轻心态\",\"content\":\"接触儿童，激活童心和脑细胞。孩子总有问不完的为什么，他们的思路会让你从新的角度看待以为熟悉的事物，或者需要重新学习寻找答案。\"},{\"title\":\"经常休息眼睛有助于保护视力\",\"content\":\"眼睛时刻处在光线的刺激中，时间越久，眼睛受到的伤害越大，当眼睛出现发痒、发紧、干涩时可连续眨眼10~20次，这动作有助于泪液分泌，滋养角膜、结膜，使眼球处于湿润环境中。长时间用眼后，应停下来向远处望一会儿。\"},{\"title\":\"养成饭后漱口的习惯\",\"content\":\"养成饭后漱口的习惯，及时清除部分牙齿间隙中食物残渣，这样既减少了口腔中细菌数量，也断绝了细菌产酸的“粮草”。饭后漱口最好选择茶水，茶叶为碱性，可中和食物中的酸性物质，能使口腔短时间内保持弱碱性环境，有利于牙齿的保护。\"},{\"title\":\"养成最佳的排便习惯\",\"content\":\"对于有便秘的人来说，养成定时大便的习惯十分重要，排便动作本身是一种反射性活动，是可以建立条件反射的。早晨是清理肠道排除宿便的最佳时间，一杯250ml的温水，一顿高纤维的营养早餐，食物和水进入胃内能引起“胃和结肠反射”，促进胃肠蠕动，出现大蠕动波，更易于排便反射的产生。因此早饭后是建立排便反射的好时机，开始蹲厕所的时候，可能并没有便意，也没有粪便排出，但却是结肠道重新调整规律的机会。只要坚持定时排便一段时间，形成习惯后就能定时顺利快捷地 排出大便了。\"}]','生活方式建议'),
	(17,'没有关注','[{\"section\":\"体检问题\",\"title\":\"恰当的饮食起居可以改善胆囊健康\",\"content\":\"除了家族遗传的因素外，饮食油腻、暴饮暴食、压力造成的焦虑或易怒都会诱使或加重胆囊问题。少吃多餐、避免油腻高脂肪的食物、腹部保暖、保持情绪平稳都有助于避免胆囊问题的加重。\"},{\"section\":\"体检问题\",\"title\":\"体重增加令代谢功能降低，增加罹患多种慢性疾病风险\",\"content\":\"肥胖的原因很多，但总归导致体内脂质代谢紊乱，造成脂肪在皮下和内脏器官周围堆积，这使得人们患冠心病、高血压、糖尿病等慢性疾病的风险增加，要改变生活方式；饮食和运动水平合理化；下定决心减肥，给自己时间和耐心，逐渐改变饮食和运动习惯；只有长期的改变才算成功。\"},{\"section\":\"体检问题\",\"title\":\"腰围超标意味越过慢病安全警戒线\",\"content\":\"腰围的重要性仅次于体重指数。腰围的大小可以表明脂肪堆积的部位。脂肪聚集在腰上的人更容易患高血压、糖尿病、冠状动脉疾病，因为腹部的脂肪更容易分解并在动脉里聚集。男性腰围超过102厘米或女性超过88厘米，患相关疾病的危险就会增大，尤其是体重指数为25或以上的人群。\"},{\"section\":\"体检问题\",\"title\":\"有高血压问题，生活方式是不容忽视的\",\"content\":\"最好的方法是改变生活方式，包括饮食习惯以及运动等。世界高血压组织研究表明，如果只是1期的高血压，是可以通过生活方式的改善控制血压的。即便是通过用药，生活方式也不容忽视，限制食盐的摄入，减重，运动，戒烟，限制酒精的摄入：每日饮用超过30毫升酒精,相当于600毫升啤酒,200毫升葡萄酒或75毫升标准威士忌,会使血压升高并使冠心病、中风的发病和死亡率上升。\"},{\"section\":\"体检问题\",\"title\":\"降低血脂，降低心脑血管患病风险\",\"content\":\"目前无论在美国还是中国，心脑血管疾病仍然是头号杀手。血脂在动脉血管壁的沉积是毫无声息的，如果体检已经确认血脂超标，就必须在生活方式上加以注意，加强运动，进行低脂肪饮食等，无效的情况下，需要在医生指导下借助药物改善状况。\"},{\"section\":\"体检问题\",\"title\":\"糖尿病意味着您需要更平衡的生活\",\"content\":\"血糖水平往往受到饮食、运动、差旅、压力等生活习惯的影响。糖尿病意味着需要比其他人更注意你的生活，摄入具有丰富膳食纤维的蔬果，减少高脂肪食物和零食，控制体重，控制吸烟和饮酒。有规律的运动可以帮助保持身体素质，对心脏和血管都有益处，促进血液循环，并定期监测自己的血糖水平，防止糖尿病并发症发生的风险。\"},{\"section\":\"体检问题\",\"title\":\"冠心病增加猝死风险\",\"content\":\"冠心病若养护不当，猝死率很高（统计表明50-60%猝死是冠心病）。吸烟、高血压、高血糖、高血脂、肥胖、久坐等问题都是冠心病发作的危险因素，通过合理的饮食和有规律的运动，可以大大减少患病的风险，甚至可以免除药物的使用。可以配合每天服用抗氧化维生素，如维生素E、维生素C、叶酸等，阻止低密度脂蛋白在血管的积累，预防发病。\"},{\"section\":\"体检问题\",\"title\":\"对于骨质疏松日常生活的调理比治疗更有效果\",\"content\":\"先天因素，饮食中钙摄入不足，不常运动，体格瘦弱，吸烟、过量饮酒、女性过早绝经，都是直接导致骨质疏松的危险因素，不过相对于恢复已降低的骨密度，保持骨密度不变要来的更容易些。预防措施包括摄入足够的钙和维生素D和从事负重锻炼。\"},{\"section\":\"体检问题\",\"title\":\"即便没有不舒服的症状也要重视脂肪肝\",\"content\":\"脂肪肝是肝细胞内过量的脂肪（甘油三酯）积累，脂肪肝最常见的原因是酗酒、肝炎、肥胖、糖尿病、血清甘油三酯水平过高，因为起初没有任何症状，但可引起肝脏的反复损害，最终可从脂肪肝发展到肝硬化。早期如果在生活方式上加以改善也很快就会看到改善。\"},{\"section\":\"体检问题\",\"title\":\"前列腺炎需要积极配合治疗\",\"content\":\"慢性前列腺感染除了积极地配合治疗以外，在日常生活中也需要注意保护和防止急性发作，少吃辛辣，不可憋尿，精神压力不要过大，避免久坐，多喝水、防止受寒，保持清洁并且多参加体育锻炼。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不良的就餐习惯增加健康风险\",\"content\":\"常在外就餐 常在外吃，油脂、盐可能摄入过量了。这对血脂、血压、血糖的控制都不利，吃的又油又咸，是造成动脉粥样硬化的原因之一，过多的盐会影响血压健康，造成肾脏负担。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不良的就餐习惯增加健康风险\",\"content\":\"不吃早餐 不吃早餐是胆囊疾患和超量午餐的隐患，不吃早餐很容易午餐吃撑，这会引起身体肥胖，血糖不稳，血压升高，甚至加重中风隐患。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不良的就餐习惯增加健康风险\",\"content\":\"经常吃得过饱 常常吃撑，这会影响血糖平稳，令脂肪合成增加，同时增加肝脏和消化系统负担，最终影响到血压和血脂。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，不利于机体保持最佳状态\",\"content\":\"爱吃油炸 油炸食物就是三高人群的定时炸弹，会加重高血压，糖尿病，甚至中风、致癌的风险。油炸也令食物不容易被消化吸收，加之高温对营养的破坏，常吃油炸食物会导致营养不均衡。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，不利于机体保持最佳状态\",\"content\":\"常吃包装或加工食品 长期食用加工食物会令身体缺乏必要的营养，尤其是水溶性维生素和蛋白质。这些食物中的添加剂不仅会阻止营养素的吸收和利用，还会加速营养素的流失，并增加肝脏的代谢负担，影响气色和精力。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，不利于机体保持最佳状态\",\"content\":\"常喝甜饮料 甜饮料和加重健康风险。甜饮料尤其是碳酸类饮料会增加身体钙质流失，同时升高血糖，促进脂肪合成，久了会令血管壁受损，血糖紊乱，增加心脑血管疾病风险。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，不利于机体保持最佳状态\",\"content\":\"爱吃重口味食物 口味诱人的菜肴多数采用了多油多酱多糖的烹饪方式，油脂和糖分的过量都会增加体内脂肪含量，盐会增加肾脏负担，这会令三高的风险增加。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，不利于机体保持最佳状态\",\"content\":\"肉食过多 看似美味的肉食，却隐藏着高脂肪、高胆固醇，长期如此会加速血栓的形成。大量的人群调查观察到：多食入动物性脂肪（主要含饱和脂肪酸），可使血胆固醇和低密度脂蛋白含量增高，高密度脂蛋白含量则降低，进而增加动脉硬化的危险，对高血压、高血脂、糖尿病、肥胖等慢性病患者不利。\"},{\"section\":\"生活习惯提醒\",\"title\":\"健康食物吃得过少，令机体缺乏必需营养，影响健康状态\",\"content\":\"豆、奶制品吃得少 很少吃豆、奶制品可能导致你缺乏钙质，奶制品和豆制品被认为是钙的主要来源，钙是维持骨骼、牙齿、神经健康的主要矿物质，缺乏则容易造成失眠、紧张、骨质疏松、龋齿等问题。同时钙也被认为和血压健康关系密切，缺乏时会导致血压升高。\"},{\"section\":\"生活习惯提醒\",\"title\":\"健康食物吃得过少，令机体缺乏必需营养，影响健康状态\",\"content\":\"粗粮吃得少 很少吃粗粮会令纤维素和维生素B族以及矿物质缺乏。纤维素能减少胆固醇和脂肪的吸收，还能维持血糖的平稳，也能帮助粪便成型，排出毒素。维生素B族能协助体内能量代谢，维持稳定情绪，保持精力充沛。各种矿物质也是维护身体健康所必需的。\"},{\"section\":\"生活习惯提醒\",\"title\":\"健康食物吃得过少，令机体缺乏必需营养，影响健康状态\",\"content\":\"蔬果吃得少 果蔬吃得过少对你无益。水果蔬菜能提供丰富的水溶性纤维素可以降低身体对脂肪和胆固醇的吸收，还有平稳血糖的效果。果蔬还能提供清除自由基的抗氧化物，日常生活的污染，辐射，煎炸食物，阳光照射甚至呼吸都会产生令身体老化的自由基。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳影响健康状态\",\"content\":\"经常熬夜 经常熬夜，可以被称为慢性睡眠不足，已经有大量的科学研究表明睡眠不足与心脏疾病、高血压、中风、糖尿病有直接关系。\\n当然在这些问题发生之前，你可能正在经历着熬夜带来的皮肤问题，只需要几个晚上，皮肤就马上会呈现出蜡黄，眼睛浮肿，长期则导致皮肤黯淡，细纹，眼睛下方的黑眼圈。 没有在适当的时候睡觉，生物节律被打乱，身体会释放更多的应激激素皮质醇，这些皮质醇会分解皮肤中的胶原蛋白，令你的皮肤失去光滑和弹性。\\n睡眠不足与食欲增加有直接关系，从而导致肥胖，据2008年的一项研究，熬夜人群发胖的可能性比正常人高出30%，由于刺激了对高脂肪，高碳水化合物的食物的渴望。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳影响健康状态\",\"content\":\"睡眠不足 每个人需要的睡眠时间长短并不一样，如果每天醒来你不需要闹钟，不需要冷水，不需要咖啡就能让自己充满能量地迎接新的一天，这说明你的睡眠效率很高，总之自己的感觉是最直接、最重要的。\\n但如果因为睡不够觉得困倦则说明睡眠效率较低。长期睡眠不足的话，会使得记忆力减退、情绪波动厉害。如果只睡很短时间，就会提高血压和心率的平均水平，由此可能会增大心血管系统的压力。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳影响健康状态\",\"content\":\"多梦 梦多是在快速动眼睡眠中出现的，这段时间人的体温上升，心跳增加，血流速快，区别于入睡时的生理状态（体温下降），这类似于人们醒着时候的生理状态，人们也更容易在这段时间内醒过来。这种状况出现的原因很多，压力大、精神紧张、焦虑都有可能成为多梦易醒的原因。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳影响健康状态\",\"content\":\"起床困难或睡不够 喜欢赖床或睡后精力不能补足。可能是繁重的脑力或体力劳动造成极度疲劳，使得需要更长时间的睡眠来恢复。也可能不规律的生活习惯造成了生物钟紊乱，无法控制睡眠和觉醒的正常切换。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳影响健康状态\",\"content\":\"失眠 入睡困难或者失眠的人往往表现为：很难睡着、晚上总是醒、睡得不踏实，很难再入睡。原因很多，包括压力、咖啡因、酒精、环境变化、时差、慢性疾病、焦虑等等，解决问题首先要找到原因，是不是有些不良的生活习惯，导致这个问题，不过，有时候，这可能是自己无法控制的，但是要明确一点，这并不是不可以解决的问题，所以不用担心，给自己一点儿耐心。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不规律的睡眠扰乱循环系统生物钟，身体隐形“生病”\",\"content\":\"睡眠不规律 其实除了睡眠，我们心率、呼吸、血压、体温、蛋白质合成等体内一切生命活动，往往都呈现昼夜节律的倾向，反映在血液循环、内分泌和各种器官功能中。不规律的睡眠就好比在不停地倒时差，各器官的生物钟调整所需的时间不同，睡眠两三天就可以调整过来，但是体内的器官及循环系统的生物钟却需要两三周的时间调整。\"},{\"section\":\"生活习惯提醒\",\"title\":\"目前的运动状况起不到改善健康的作用\",\"content\":\"每天或者隔天花30~60分钟，做一些有氧运动，这是保持年轻活力的很好方法，有氧运动会使肌肉消耗大量的氧气和营养物质，从而使得胃肠、心肺、肾脏等功能提高。在一定条件下，能量消耗得越多，这种补偿作用越明显。\"},{\"section\":\"生活习惯提醒\",\"title\":\"吸烟几乎损害身体的每个器官\",\"content\":\"吸烟几乎损害身体的每个器官，造成许多疾病和减少一般吸烟者的健康。由吸烟引起的疾病的名单已经扩大到包括腹主动脉瘤，急性髓系白血病，白内障，宫颈癌，肾癌，胰腺癌，肺炎，牙周炎，和胃癌。这些都是除了先前已知的由吸烟引起的的疾病，包括膀胱癌，食道癌，喉癌，肺癌，口腔和咽喉癌症，慢性肺部疾病，冠心病，心血管疾病，以及影响生殖系统和婴儿猝死综合症。\"},{\"section\":\"生活习惯提醒\",\"title\":\"过量饮酒损伤健康\",\"content\":\"过量饮酒的人看上去往往更加憔悴。人们普遍认为过量饮酒可导致提前衰老并遭到与衰老有关的疾病侵袭。\\n意大利米兰大学进行的一项研究指出，过量酒精可使细胞染色体失去完整性和稳定性的保护，从而加速衰老增加癌症风险。\\n世卫组织认为每日安全饮酒量的上限是25g酒精，相当于1两42度白酒，或200ml红酒，一瓶啤酒。\"},{\"section\":\"生活习惯提醒\",\"title\":\"辐射产生的自由基加速人体细胞氧化\",\"content\":\"手机和电脑产生的低频电磁辐射是人体外源性自由基产生的原因之一。随着辐射剂量累积增加，可能会引起神经系统、循环系统、免疫系统、生殖系统以及皮肤和眼睛的功能、代谢或形态的改变，这是一个自由基生成加速身体细胞氧化的过程。\"}]','基本健康评估'),
	(18,'没有关注','[{\"section\":\"饮食习惯\",\"title\":\"口味清淡些\",\"content\":\"过量摄入脂肪会降低身体内胰岛素的活性，使血糖升高，且血中脂肪量过高易沉积在血管内壁形成斑块，造成动脉硬化；吃盐多能引起血压、血糖升高，三高人群应采取低脂肪、低盐、低糖、高纤维、高维生素饮食。\"},{\"section\":\"饮食习惯\",\"title\":\"口味清淡些\",\"content\":\"少用油、酱、糖、辣，你才能体会食物的天然味道，而且也减少对胃肠的刺激。清淡的食物减少代谢负担，对提高免疫力、精力、改善疲劳都有帮助。\"},{\"section\":\"饮食习惯\",\"title\":\"你适合高碳水化合物早餐\",\"content\":\"《美国临床营养学杂志》报道，经常不吃早餐，能增加患心脏病的风险。而且经常不吃早餐的人，更容易肥胖、胆固醇增高、腹部脂肪堆积。\\n你更适合谷物和水果组成的高碳水化合物早餐，而不是油腻的高蛋白早餐。\"},{\"section\":\"饮食习惯\",\"title\":\"最好不吃煎炸食物\",\"content\":\"煎炸食物热量高，高温油炸过程中产生对血管有害的自由基，以及致癌物。研究表明常吃油炸食物人群的冠心病，心绞痛，心梗，癌症发病率远远高于正常人群。\"},{\"section\":\"饮食习惯\",\"title\":\"坚果作为零食\",\"content\":\"原味的开心果、松籽、花生、杏仁、富含对心脑血管有益的不饱和脂肪酸和蛋白质，可以作为零食搭配适量的水果，令血糖平稳释放。\"},{\"section\":\"饮食习惯\",\"title\":\"选择有效饮水\",\"content\":\"常喝白水或矿泉水。人体的70%是水分，确保体内有足够洁净的水分，才能将代谢产生的废物及时排出体外。白水、矿泉水、鲜榨的蔬果汁、口味清淡的菜汤都可以是补充水分的来源。\"},{\"section\":\"饮食习惯\",\"title\":\"天然食物有益健康\",\"content\":\"尽量选择新鲜的当地、当季产新鲜食物，它们除了能提供必需的营养素还能帮助代谢掉人体中产生有的有害物质，如促使老化的自由基。\\n加工食品中存在多种人工合成的化学物质，已有许多国家研究表明加工食品中的各种添加剂摄入过多，会存在致癌风险。\"},{\"section\":\"饮食习惯\",\"title\":\"天然食物有益健康\",\"content\":\"慢慢习惯清淡饮食，让这些食物常常出现在你的餐桌上，这可以帮助你降低胆固醇：豆类及豆制品，含有丰富的优质蛋白质，可以提高身体对蛋白利用； 香菇、黑木耳、洋葱、大蒜能使血中有害胆固醇转化成有益心脏的胆固醇；高纤维饮食可以平衡血糖：燕麦、粗杂粮、干豆类、海带、新鲜的蔬菜。\"},{\"section\":\"饮食习惯\",\"title\":\"细嚼慢咽，七分饱\",\"content\":\"日本学者统计约有30%--40%的老年性痴呆病人，青壮年时期有饱食的习惯。因摄入的总热量远远超过机体的需要 ,致使体内脂肪过剩 ,血脂增高 ,导致脑动脉粥样硬化 ,有一种被称为“纤维芽细胞生长因子”的物质在大脑中明显增多 ,这种物质被认为是引起脑动脉硬化的重要因素。实验表明，长期适度减少热量摄入的人更长寿，体重更轻，患心脑血管疾病的风险更低。\"},{\"section\":\"饮食结构\",\"title\":\"增加矿物质来源\",\"content\":\"骨矿研究测试表明，人到35岁以后，平均骨矿含量以每年0.5-1%的速率下降。钙在人体中处于不断丢失和补损的动态平衡过程中，人体中钙的存量关系到骨骼、牙齿的韧性，血液凝结，激活酶系统，维持肌肉伸缩性和心跳规律。\\n通过膳食来调节体内钙平衡是人类最佳补钙法，牛奶和豆类食物是钙的优质来源，此外还可以选择发菜、紫菜、荠菜、木耳、海米、软骨、骨汤等，或者适量芝麻、花生、榛子等坚果，都是很好的钙质来源。\"},{\"section\":\"饮食结构\",\"title\":\"粗杂粮降低你的健康风险\",\"content\":\"最新的美国营养学研究建议，主食中的一半应该是全谷物和粗杂粮，包括玉米、全麦制品、糙米、燕麦等。粗粮富含水溶性纤维素，可以减少脂肪吸收，维持血糖平稳释放、促进健康血脂，而且粗粮提供健康必需的矿物质如钾、铬、钙等和维生素对健康血压也有益处。\"},{\"section\":\"饮食结构\",\"title\":\"增加果蔬食物，为健康添保障\",\"content\":\"研究表明，血液中抗氧化剂的含量或日常饮食摄入抗氧化剂量高的人死亡率低。同时也会降低老年痴呆、癌症、心血管疾病、白内障、糖尿病、高血压、关节炎、视力功能退化等问题的发生率。富含抗氧化剂的蔬果，主要有草莓、樱桃、蓝莓、葡萄、番茄、西兰花、胡萝卜、豌豆、玉米等。\"},{\"section\":\"饮食结构\",\"title\":\"选择健康肉类\",\"content\":\"吃肉的时候尽量选择浅色肉食，如鱼类、水生贝壳、去皮鸡鸭肉等，因为它们中的饱和脂肪及胆固醇含量明显低于红肉。但要控制好量，平均每天不要超过半幅扑克牌大小的量。\"}]','合理营养建议'),
	(19,'没有关注','[]','有效运动建议'),
	(20,'没有关注','[{\"title\":\"你需要戒烟\",\"content\":\"这里并不是在劝你少吸一点儿，事实上，少吸一支半支对你的现状不会有任何改变，不吸烟的人一定比吸烟的人更加轻松享受，并且不会失去良好的健康，充沛的精力，心灵的安宁，勇气，自信，自尊和自由。这是最关键的，因为你没有必要向任何人证明吸烟给你带来了多大的好处。别人能看到的，只是你被这种东西奴役了，这么容易就被征服了，一支接着一支，失去自由，并且没有勇气克服。\"},{\"title\":\"长期有规律的适量饮酒帮助提升机体健康\",\"content\":\"人体可自然分解消化酒精，每天适当饮酒可使动脉血管扩张、血压下降，有利于促进血液循环，更可提升对人体有利的高密度脂蛋白及降低有害的低密度脂蛋白，从而防止心脏病发作。而适量葡萄酒（含有抗氧化物质白藜芦醇）可能降低机体老化速度。\\n适量的饮酒相当于每天一杯150ml红葡萄酒或360ml啤酒。\"},{\"title\":\"常常受到辐射侵扰，需要抗氧化剂抵抗辐射侵袭\",\"content\":\"1.需要有意识地通过饮食或膳食补充剂摄入花青素、维生素C、维生素E、维生素A等抗氧化剂，及时清除代谢产生的自由基。 2.避免长时间连续操作电脑，眼睛与屏幕的距离应在40~50厘米。  3.室内光线要适宜，不可过亮或过暗。  4.安装防护装置，削弱电磁辐射的强度。\"},{\"title\":\"一定要在23点也就是子时到来前睡觉\",\"content\":\"人的一天有两个自然的睡眠周期，这是由人的生物节律决定的，一个是22点左右，这时候人的体温下降，进入能量低潮期，这时候如果关闭灯光、游戏、电视、互联网，便可以很快进入睡眠状态。另外一个自然的睡眠周期是下午1点~4点，同样的也是进入能量低潮期，这时候也会犯困，需要睡眠。\\n所以，正确的睡眠应该遵循身体的生物节律，22~23点入睡，早上6-7点醒来。可以把你工作的时间放在早上，这样不仅有适量的睡眠，也睡在合适的时间。\"},{\"title\":\"不论晚上睡眠如何糟糕，你的生物钟需要一个起点\",\"content\":\"比起控制睡眠时间，控制起床时间更为容易，保证在7：30分左右起床（或者是自己既定的时间，但是不要超过8：00），尽量将其保持在30分钟的浮动范围内，让你的起床时间有规律是改善睡眠不规律的方法，当然这取决于个人的意愿。\"},{\"title\":\"循序渐进延长睡眠时间\",\"content\":\"如果你经常看着电视、看着书进入梦乡，这意味着你缺乏足够的睡眠，补充睡眠并不需要比往常提前1小时上床，这只会令你睡不着觉，每天晚上只要提前10~15分钟，在下午13点~16点之间小睡一下，半个小时为宜，多了则会影响晚间的睡眠，在调整睡眠规律期间清淡饮食。\"},{\"title\":\"梦是你熟睡时心中产生的愿望，你可以学习控制你的梦\",\"content\":\"通过一些训练，学习控制你的梦，享受美好的梦境，缓解压力，帮助你更好的睡眠。\\n根据英国睡眠指导专家研究，有42%的人通过睡前的想象控制自己的梦，多试几次这个比例可以达到更高。其实很简单，在睡前安静的坐上10分钟，放慢呼吸，暗示自己可以在做梦的时候保持清醒，想象一个快乐美好的主题，多试几次。\"},{\"title\":\"提高睡眠质量\",\"content\":\"固定起床时间可以帮助生物钟正常工作，也就是说，无论什么时候开始睡觉一定要在每天的同一时间起床，这样可以在一定程度上保证生物钟的正常运作，也是拥有高质量睡眠的前提条件，也可以通过午睡或者安全状态下的Flash睡眠(瞬时闪睡)来进行一定的睡眠补充，这是最现实的办法。但是决不能根据前一天的睡觉时间决定起床的时间，否则很难改善生物钟紊乱的症状。\"},{\"title\":\"给自己一些时间调整你的睡眠\",\"content\":\"上床以后不要继续思考烦心事，如果睡不着，宁可起来做点事，也不要躺在床上发愁，真正折磨你的不是睡眠不足，而是你的忧愁。无论晚上几点入睡，早上都要在同一时间起床。可以尝试这个睡前的意念调节，平躺，全身放松，集中精神调节呼吸。闭上眼睛，浮想一些简单的图形；然后控制呼吸的节奏，缓慢吸气，保持一秒钟静止状态，缓慢呼气，反复几次。这样可以排除杂念，达到睡眠前的最佳状态！\"}]','生活方式建议'),
	(21,'优美体形','[{\"section\":\"生活习惯提醒\",\"title\":\"不良的就餐习惯影响体型，有损健康\",\"content\":\"经常吃得过饱 吃撑会令血糖快速变化，刺激胰岛素的分泌，久之易患糖尿病。血糖高时，人体也会加速脂肪合成。吃撑时，胃肠过度扩张，久之有损肠胃健康。如果年轻时经常吃得过饱，年老后体内甲状旁腺素含量会明显增加，骨质脱钙现象就会日趋严重，患骨质疏松的机会就会明显增加。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不良的就餐习惯影响体型，有损健康\",\"content\":\"常在外就餐 常在外吃，油脂、盐可能摄入过量了，这是造成动脉粥样硬化的原因之一，过多的盐会影响血压健康，造成肾脏负担。这增加了心脑血管疾病和Ⅱ型糖尿病的风险。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不良的就餐习惯影响体型，有损健康\",\"content\":\"不吃早餐 不吃早餐，人体首先消耗的是碳水化合物和蛋白质，最后消耗的才是脂肪，所以不要以为不吃早饭会有助于脂肪的消耗。相反，不吃早饭，还会使午饭和晚饭吃得更多，瘦身不成反而更胖，肌肉总量也会减少。 经常不吃早餐，会使胆汁中胆酸含量减少，胆汁浓缩，造成胆囊中胆结石的形成。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，不利于保持健康体型\",\"content\":\"爱吃油炸 油炸食物的热量密度颇高，不知不觉间改变了你的体型，常吃油炸食物加重患“富贵病”的风险，甚至有致癌的潜在威胁。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，不利于保持健康体型\",\"content\":\"常吃包装或加工食品 将食物有效地转化成能量而不是脂肪，是由成千上万的酶来决定的，而这些酶又依赖于维生素和矿物质。常吃包装零食等加工食物反倒会消耗这些对身体有益的元素，同时也会摄入过度的油、盐、糖。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，不利于保持健康体型\",\"content\":\"常喝甜饮料 常喝甜味饮料会增加热量摄入、脂肪合成，以及对体内维生素和矿物质的消耗，降低免疫力，令血糖不稳，让身体丢失钙质。即便是阿斯巴甜也能刺激大脑对甜食和碳水化合物产生更多渴望。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，不利于保持健康体型\",\"content\":\"爱吃重口味食物 你的腰围已经超标，不适合吃过多的重口味食物了，这会增加患心脑血管疾病和糖尿病的风险。重口味会令食欲大增，不知不觉间就吃的过多。糖盐油摄入过度也是慢性疾病的潜在危险因素。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，不利于保持健康体型\",\"content\":\"肉食过多 吃肉太多，严重威胁了你的健康。大量流行病研究提示，随着膳食中脂肪占总能量的产热百分比增加，超重和肥胖的发生率明显升高。而大多数肉类食品含的脂肪多，热量高，是需要减重的人严格限制的。\"},{\"section\":\"生活习惯提醒\",\"title\":\"健康食物吃得过少，令机体缺乏必需营养，影响健康状态和体型\",\"content\":\"豆、奶制品吃得少 很少吃豆、奶制品可能导致你缺乏钙质，奶制品和豆制品被认为是钙的主要来源，钙是维持骨骼、牙齿、神经健康的主要矿物质，缺乏则容易造成失眠、紧张、骨质疏松、龋齿等问题。钙也能够帮助活化脂酶（加速脂肪分解和利用的酶）。\"},{\"section\":\"生活习惯提醒\",\"title\":\"健康食物吃得过少，令机体缺乏必需营养，影响健康状态和体型\",\"content\":\"粗粮吃得少 很少吃粗粮会令纤维素和维生素B族以及矿物质缺乏。纤维素能减少胆固醇和脂肪的吸收，还能维持血糖的平稳，增加饱腹感，有助于控制食欲。维生素B族能协助体内能量代谢，加速脂肪分解 。各种矿物质也是维护身体健康所必需的。\"},{\"section\":\"生活习惯提醒\",\"title\":\"健康食物吃得过少，令机体缺乏必需营养，影响健康状态和体型\",\"content\":\"蔬果吃得少 蔬果提供抗氧化剂和水溶性维生素和矿物质，若吃的不够，则令机体缺乏帮助脂肪燃烧的营养素。大多数水果和蔬菜富含维生素B和维生素C。水果可以作为餐间饥饿时的补充，可以帮助维持血糖平稳，这能保证食欲不会在饥饿时暴增。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳影响健康状态\",\"content\":\"经常熬夜 经常熬夜，可以被称为慢性睡眠不足，已经有大量的科学研究表明睡眠不足与心脏疾病、高血压、中风、糖尿病有直接关系。\\n睡眠不足与食欲增加有直接关系，从而导致肥胖，据2008年的一项研究，熬夜人群发胖的可能性比正常人高出30%，由于刺激了对高脂肪，高碳水化合物类食物的渴望。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳影响健康状态\",\"content\":\"睡眠不足 每个人需要的睡眠时间长短并不一样，如果每天醒来你不需要闹钟，不需要冷水，不需要咖啡就能让自己充满能量地迎接新的一天，这说明你的睡眠效率很高，总之自己的感觉是最直接、最重要的。 \\n但如果因为睡不够觉得困倦则说明睡眠效率较低。长期睡眠不足的话，会使得记忆力减退、情绪波动厉害。如果只睡很短时间，就会提高血压和心率的平均水平，由此可能会增大心血管系统的压力。 \\n睡眠不足会降低让人有饱足感的瘦素水平，同时会升高让人产生饥饿感的多肽激素水平。睡眠不足时人更想吃高碳水化合物、高糖份的食物，这也是导致发胖的原因。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳影响健康状态\",\"content\":\"多梦 梦多是在快速动眼睡眠中出现的，这段时间人的体温上升，心跳增加，血流速快，区别于入睡时的生理状态（体温下降），这类似于人们醒着时候的生理状态，人们也更容易在这段时间内醒过来。这种状况出现的原因很多，压力大、精神紧张、焦虑都有可能成为多梦易醒的原因。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳影响健康状态\",\"content\":\"起床困难或睡不够 喜欢赖床或睡后精力不能补足。可能是繁重的脑力或体力劳动造成极度疲劳，使得需要更长时间的睡眠来恢复。也可能不规律的生活习惯造成了生物钟紊乱，无法控制睡眠和觉醒的正常切换。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳影响健康状态\",\"content\":\"虽然失眠使人体消耗的能量增加，短期内令体重降低，但长期失眠会降低免疫力，扰乱内分泌，影响整体健康。入睡困难或者失眠的人往往表现为：很难睡着、晚上总是醒、睡得不踏实，很难再入睡。原因很多，压力、咖啡因、酒精、环境变化、时差、慢性疾病、焦虑等等，解决问题首先要找到原因，是不是有些不良的生活习惯，导致这个问题，不过，有时候，这可能是自己无法控制的，但是要明确一点，这并不是不可以解决的问题，所以不用担心，给自己一点儿耐心。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不规律的睡眠扰乱循环系统生物钟，身体隐形“生病”\",\"content\":\"睡眠不规律 其实除了睡眠，我们心率、呼吸、血压、体温、蛋白质合成等体内一切生命活动，往往都呈现昼夜节律的倾向，反映在血液循环、内分泌和各种器官功能中。不规律的睡眠就好比在不停地倒时差，各器官的生物钟调整所需的时间不同，睡眠两三天就可以调整过来，但是体内的器官及循环系统的生物钟却需要两三周的时间调整。\"},{\"section\":\"生活习惯提醒\",\"title\":\"运动是减脂的最好途径\",\"content\":\"运动调节代谢功能，促进脂肪分解，有规律的有氧运动，促使你身体产生并大量储存有氧酵素，更多的有氧酵素会有助于你消耗掉更多的脂肪，当然这需要一定的耐心，因为有氧酵素数量的增加需要几周或几个月时间去体现。\"},{\"section\":\"生活习惯提醒\",\"title\":\"辐射产生的自由基加速人体细胞氧化\",\"content\":\"手机和电脑产生的低频电磁辐射是人体外源性自由基产生的原因之一。随着辐射剂量累积增加，可能会引起神经系统、循环系统、免疫系统、生殖系统以及皮肤和眼睛的功能、代谢或形态的改变，这是一个自由基生成加速身体细胞氧化的过程。\"},{\"section\":\"生活习惯提醒\",\"title\":\"吸烟会阻碍您的塑形行动\",\"content\":\"糖和精致碳水化合物不是扰乱血糖平衡的全部物质，刺激物如尼古丁也同样影响着能量和体重控制，这就意味着要控制吸烟或戒烟。\"},{\"section\":\"生活习惯提醒\",\"title\":\"过量饮酒损伤健康\",\"content\":\"过量饮酒的人看上去往往更加憔悴。人们普遍认为过量饮酒可导致提前衰老并遭到与衰老有关的疾病侵袭。\\n意大利米兰大学进行的一项研究指出，过量酒精可使细胞染色体失去完整性和稳定性的保护，从而加速衰老增加癌症风险。\\n世卫组织认为每日安全饮酒量的上限是25g酒精，相当于1两42度白酒，或200ml红酒，一瓶啤酒。\"},{\"section\":\"对塑形的特别提示\",\"title\":\"宿便促生胖腹\",\"content\":\"一般3~5日不解大便而停留于肠管内的粪块叫宿便。宿便积累多了会令小腹突出，一次畅快的排便之后，肚腩会变软、缩小。\"},{\"section\":\"体检问题的预示\",\"title\":\"前列腺炎需要积极配合治疗\",\"content\":\"慢性前列腺感染除了积极地配合治疗以外，在日常生活中也需要注意保护和防止急性发作，少吃辛辣，不可憋尿，精神压力不要过大，避免久坐，多喝水、防止受寒，保持清洁并且多参加体育锻炼。\"},{\"section\":\"体检问题的预示\",\"title\":\"恰当的饮食起居可以改善胆囊健康\",\"content\":\"除了家族遗传的因素外，饮食油腻、暴饮暴食、压力造成的焦虑或易怒都会诱使或加重胆囊问题。少吃多餐、避免油腻高脂肪的食物、腹部保暖、保持情绪平稳都有助于避免胆囊问题的加重。\"},{\"section\":\"体检问题的预示\",\"title\":\"减肥是一种生活态度\",\"content\":\"肥胖的原因很多，但总归导致体内脂质代谢紊乱，造成脂肪在皮下和内脏器官周围堆积，这使得人们患冠心病、高血压、糖尿病等慢性疾病的风险增加，要改变生活方式；饮食和运动水平合理化；下定决心减肥，给自己时间和耐心，逐渐改变饮食和运动习惯；只有长期的改变才算成功。\"},{\"section\":\"体检问题的预示\",\"title\":\"腰围超标意味越过慢病安全警戒线\",\"content\":\"腰围的重要性仅次于体重指数。腰围的大小可以表明脂肪堆积的部位。脂肪聚集在腰上的人更容易患高血压、糖尿病、冠状动脉疾病，因为腹部的脂肪更容易分解并在动脉里聚集。男性腰围超过102厘米或女性超过88厘米，患相关疾病的危险就会增大，尤其是体重指数为25或以上的人群。\"},{\"section\":\"体检问题的预示\",\"title\":\"保持健康体型有助于改善高血压\",\"content\":\"最好的方法是改变生活方式，包括饮食习惯以及运动等。世界高血压组织研究表明，如果只是1期的高血压，是可以通过生活方式的改善控制血压的。即便是通过用药，生活方式也不容忽视，限制食盐的摄入，减重，运动，戒烟，限制酒精的摄入：每日饮用超过30毫升酒精,相当于600毫升啤酒,200毫升葡萄酒或75毫升标准威士忌,会使血压升高并使冠心病、中风的发病和死亡率上升。\"},{\"section\":\"体检问题的预示\",\"title\":\"降低血脂，降低心脑血管患病风险\",\"content\":\"目前无论在美国还是中国，心脑血管疾病仍然是头号杀手。血脂在动脉血管壁的沉积是毫无声息的，如果体检已经确认血脂超标，就必须在生活方式上加以注意，加强运动，进行低脂肪饮食等，无效的情况下，需要在医生指导下借助药物改善状况。\"},{\"section\":\"体检问题的预示\",\"title\":\"糖尿病意味着您需要更平衡的生活\",\"content\":\"血糖水平往往受到饮食、运动、差旅、压力等生活习惯的影响。糖尿病意味着需要比其他人更注意你的生活，摄入具有丰富膳食纤维的蔬果，减少高脂肪食物和零食，控制体重，控制吸烟和饮酒。有规律的运动可以帮助保持身体素质，对心脏和血管都有益处，促进血液循环，并定期监测自己的血糖水平，防止糖尿病并发症发生的风险。\"},{\"section\":\"体检问题的预示\",\"title\":\"冠心病增加猝死风险\",\"content\":\"冠心病若养护不当，猝死率很高（统计表明50-60%猝死是冠心病）。吸烟、高血压、高血糖、高血脂、肥胖、久坐等问题都是冠心病发作的危险因素，通过合理的饮食和有规律的运动，可以大大减少患病的风险，甚至可以免除药物的使用。可以配合每天服用抗氧化维生素，如维生素E、维生素C、叶酸等，阻止低密度脂蛋白在血管的积累，预防发病。\"},{\"section\":\"体检问题的预示\",\"title\":\"对于骨质疏松日常生活的调理比治疗更有效果\",\"content\":\"先天因素，饮食中钙摄入不足，不常运动，体格瘦弱，吸烟、过量饮酒、女性过早绝经，都是直接导致骨质疏松的危险因素，不过相对于恢复已降低的骨密度，保持骨密度不变要来的更容易些。预防措施包括摄入足够的钙和维生素D和从事负重锻炼。\"},{\"section\":\"体检问题的预示\",\"title\":\"塑造健康体型有助于改善脂肪肝\",\"content\":\"脂肪肝是肝细胞内过量的脂肪（甘油三酯）积累，脂肪肝最常见的原因是酗酒、肝炎、肥胖、糖尿病、血清甘油三酯水平过高，因为起初没有任何症状，但可引起肝脏的反复损害，最终可从脂肪肝发展到肝硬化。早期如果在生活方式上加以改善也很快就会看到改善。\"}]','形体健康评估'),
	(22,'优美体形','[{\"section\":\"饮食习惯\",\"title\":\"口味清淡不仅有助于塑身，更有益于健康\",\"content\":\"塑身时，口味要清淡，油、盐、糖、辣尽量少用，可以多用醋、花椒、葱、姜、蒜等天然调料。一是不刺激食欲，也可以减少热量积累。二是清淡的饮食对健康也有益，降低慢性病风险。三是吃的清淡减轻消化负担，节约了能量和维生素，从而保证精力充沛。\"},{\"section\":\"饮食习惯\",\"title\":\"营养合理的早餐有助塑身\",\"content\":\"塑身时期一定要吃早餐，让全天食物总热量均衡分配。合理的早餐能保证血糖平稳，这是控制食欲的关键因素。当血糖过低时，食欲就会大增，而且对高热量的食物更加渴望。理想的早餐应该有适当热量、维生素和优质脂肪酸、优质蛋白质，比如富含膳食纤维的全麦面包、玉米、燕麦和一小把坚果加上能提供蛋白质的豆类食物。\"},{\"section\":\"饮食习惯\",\"title\":\"天然食物有助于塑身\",\"content\":\"塑身时也可以吃零食，重点在于如何选择，时令水果、脱脂酸奶、原味坚果、粗粮饼干，这些都可以作为零食，既能满足吃零食的习惯，也能给身体提供必要营养，有助于平均分配一天摄入的热量，避免过度饥饿带来的情绪波动和食欲失控。\"},{\"section\":\"饮食习惯\",\"title\":\"有效的水份能帮助塑身\",\"content\":\"常喝矿泉水和白开水，而不是甜饮料。而稀释的鲜榨果蔬汁不仅能提供水份还有对身体有益的维生素和矿物质。因为消耗脂肪需要水的协助，如果体内水分不足，脂肪的代谢率会降低。喝水要小口温饮，保持不渴的状态。肉软软的人要避免摄入过多的水份引起虚胖。\"},{\"section\":\"饮食习惯\",\"title\":\"避免过度节食\",\"content\":\"当神经递质（色氨酸）失衡或缺乏的时候，人会食欲亢奋。过度节食令体内的色氨酸缺乏，此时您就很想大吃一顿。色氨酸主要来自于牛奶、奶酪、火鸡、鸡肉、鸡蛋、红肉、大豆、豆腐、杏仁等，所以即便节食，也要尽量保证吃些上述食物。\"},{\"section\":\"饮食习惯\",\"title\":\"避免过度节食\",\"content\":\"当饮食量不足够，粪便体积小也无法给予肠道足够压力而导致便秘。\"},{\"section\":\"饮食习惯\",\"title\":\"避免过度节食\",\"content\":\"你需要来自天然食物的足够能量来提升体能和精神状态。这种状态下过度节食只会令身体缺乏必要的营养，反倒降低新陈代谢效率，令体重停滞不变甚至反弹。\"},{\"section\":\"饮食习惯\",\"title\":\"细嚼慢咽，七分饱\",\"content\":\"细嚼慢咽、七分饱，这是最简单易行的减重法。每口食物尽量咀嚼20次。细嚼慢咽会令食物更容易消化，减少胃肠负担。这也会增加饱腹感，自然减少食量，有助于塑身。\"},{\"section\":\"饮食习惯\",\"title\":\"多选蒸、煮、炖等烹饪方式\",\"content\":\"这种烹饪方式令食物柔软易吸收，保护胃肠健康，而且炖煮的肉类相对煎炸的肉类更健康，纤维中的胆固醇含量降低，且炖出来的肉质更加鲜嫩、容易消化。\"},{\"section\":\"饮食习惯\",\"title\":\"简朴的晚餐\",\"content\":\"晚餐吃的简单些，避免蛋白质和油脂食物，清淡的粥、青菜等更好。如果能够不吃晚餐则更有帮助。可以用一把坚果或一个水果或低脂酸奶代替晚餐。晚上吃的少也有助于保持头脑敏锐。\"},{\"section\":\"饮食结构\",\"title\":\"均衡减重勿忘增加矿物质\",\"content\":\"骨矿研究测试表明，人到35岁以后，平均骨矿含量以每年0.5-1%的速率下降。钙在人体中处于不断丢失和补损的动态平衡过程中，人体中钙的存量关系到骨骼、牙齿的韧性，血液凝结，激活酶系统，维持肌肉伸缩性和心跳规律。\\n通过膳食来调节体内钙平衡是人类最佳补钙法，牛奶和豆类食物是钙的优质来源，此外还可以选择发菜、紫菜、荠菜、木耳、海米、软骨、骨汤等，或者适量芝麻、花生、榛子等坚果，都是很好的钙质来源。\"},{\"section\":\"饮食结构\",\"title\":\"粗杂粮有助于健康塑身\",\"content\":\"最新的美国营养学研究建议，主食中的一半应该是全谷物和粗杂粮，包括玉米、全麦制品、糙米、燕麦、红薯、马铃薯等。粗粮富含水溶性纤维素，可以增加饱腹感，从而减少每餐食物摄入量。纤维素还能减少对油脂的吸收，利于排便，这都对塑身有益。粗粮属于碱性食物，富含维生素，能降低慢性病风险。\"},{\"section\":\"饮食结构\",\"title\":\"塑身需要果蔬的帮助\",\"content\":\"研究表明，血液中抗氧化剂的含量或日常饮食摄入抗氧化剂量高的人死亡率低。同时也会降低老年痴呆、癌症、心血管疾病、白内障、糖尿病、高血压、关节炎、视力功能退化等问题的发生率。富含抗氧化剂的蔬果，主要有草莓、樱桃、蓝莓、葡萄、番茄、西兰花、胡萝卜、豌豆、玉米等。\"},{\"section\":\"饮食结构\",\"title\":\"选择对塑身无害的肉类\",\"content\":\"如果不能停止对肉食的渴望，可以逐渐减少吃肉的次数和总量，并选择浅色肉食，如鱼类、水生贝壳、去皮鸡鸭肉等，因为它们中的饱和脂肪及胆固醇含量明显低于红肉，这会减少增重的风险。\"}]','合理营养建议'),
	(23,'优美体形','[{\"section\":\"运动让塑身事半功倍\",\"title\":\"改善局部形体 ?  每隔1小时起身活动\",\"content\":\"除了燃烧热量的全身运动外，平日最好每1小时左右起身走动，避免久坐引起脂肪在局部的堆积，尤其是饭后不要立即坐卧。\"},{\"section\":\"运动让塑身事半功倍\",\"title\":\"没有时间运动 ?  善用零碎时间，积累运动量\",\"content\":\"运动不必有专门的时间和场地，提早一段下车，走几站路去上班；骑行；放弃电梯爬楼梯；每小时站起来走动5分钟。这些都能积累能量消耗。\"},{\"section\":\"运动让塑身事半功倍\",\"title\":\"改善局部形体 ?  经常上楼梯\",\"content\":\"爬楼梯是锻炼腰臀的最佳方便运动，若膝关节无病痛，可以常常进行上楼梯。尤其是一步跨越两蹬楼梯，更加锻炼大腿和臀部。下楼梯对关节有较大压力，不可跑跳下楼梯，最好是爬上去，坐电梯下来。\"}]','有效运动建议'),
	(24,'优美体形','[{\"section\":\"\",\"title\":\"吸烟，要停下来很容易\",\"content\":\"这里并不是在劝你少吸一点儿，事实上，少吸一支半支对你的现状不会有任何改变，不吸烟的人一定比吸烟的人更加轻松享受，并且不会失去良好的健康，充沛的精力，心灵的安宁，勇气，自信，自尊和自由。这是最关键的，因为你没有必要向任何人证明吸烟给你带来了多大的好处。别人能看到的，只是你被这种东西奴役了，这么容易就被征服了，一支接着一支，失去自由，并且没有勇气克服。\"},{\"section\":\"\",\"title\":\"长期有规律的适量饮酒帮助提升机体健康\",\"content\":\"人体可自然分解消化酒精，每天适当饮酒可使动脉血管扩张、血压下降，有利于促进血液循环，更可提升对人体有利的高密度脂蛋白及降低有害的低密度脂蛋白，从而防止心脏病发作。而适量葡萄酒（含有抗氧化物质白藜芦醇）可能降低机体老化速度。\\n适量的饮酒相当于每天一杯150ml红葡萄酒或360ml啤酒。\"},{\"section\":\"\",\"title\":\"常常受到辐射侵扰，需要抗氧化剂抵抗辐射侵袭\",\"content\":\"1.需要有意识地通过饮食或膳食补充剂摄入花青素、维生素C、维生素E、维生素A等抗氧化剂，及时清除代谢产生的自由基。 2.避免长时间连续操作电脑，眼睛与屏幕的距离应在40~50厘米。  3.室内光线要适宜，不可过亮或过暗。  4.安装防护装置，削弱电磁辐射的强度。\"},{\"section\":\"\",\"title\":\"一定要在23点也就是子时到来前睡觉\",\"content\":\"人的一天有两个自然的睡眠周期，这是由人的生物节律决定的，一个是22点左右，这时候人的体温下降，进入能量低潮期，这时候如果关闭灯光、游戏、电视、互联网，便可以很快进入睡眠状态。另外一个自然的睡眠周期是下午1点~4点，同样的也是进入能量低潮期，这时候也会犯困，需要睡眠。\\n所以，正确的睡眠应该遵循身体的生物节律，22~23点入睡，早上6-7点醒来。可以把你工作的时间放在早上，这样不仅有适量的睡眠，也睡在合适的时间。\"},{\"section\":\"\",\"title\":\"不论晚上睡眠如何糟糕，你的生物钟需要一个起点\",\"content\":\"比起控制睡眠时间，控制起床时间更为容易，保证在7：30分左右起床（或者是自己既定的时间，但是不要超过8：00），尽量将其保持在30分钟的浮动范围内，让你的起床时间有规律是改善睡眠不规律的方法，当然这取决于个人的意愿。\"},{\"section\":\"\",\"title\":\"循序渐进延长睡眠时间\",\"content\":\"如果你经常看着电视、看着书进入梦乡，这意味着你缺乏足够的睡眠，补充睡眠并不需要比往常提前1小时上床，这只会令你睡不着觉，每天晚上只要提前10~15分钟，在下午13点~16点之间小睡一下，半个小时为宜，多了则会影响晚间的睡眠，在调整睡眠规律期间清淡饮食。\"},{\"section\":\"\",\"title\":\"梦是你熟睡时心中产生的愿望，你可以学习控制你的梦\",\"content\":\"通过一些训练，学习控制你的梦，享受美好的梦境，缓解压力，帮助你更好的睡眠。\\n根据英国睡眠指导专家研究，有42%的人通过睡前的想象控制自己的梦，多试几次这个比例可以达到更高。其实很简单，在睡前安静的坐上10分钟，放慢呼吸，暗示自己可以在做梦的时候保持清醒，想象一个快乐美好的主题，多试几次。\"},{\"section\":\"\",\"title\":\"提高睡眠质量\",\"content\":\"固定起床时间可以帮助生物钟正常工作，也就是说，无论什么时候开始睡觉一定要在每天的同一时间起床，这样可以在一定程度上保证生物钟的正常运作，也是拥有高质量睡眠的前提条件，也可以通过午睡或者安全状态下的Flash睡眠(瞬时闪睡)来进行一定的睡眠补充，这是最现实的办法。但是决不能根据前一天的睡觉时间决定起床的时间，否则很难改善生物钟紊乱的症状。\"},{\"section\":\"\",\"title\":\"给自己一些时间调整你的睡眠\",\"content\":\"上床以后不要继续思考烦心事，如果睡不着，宁可起来做点事，也不要躺在床上发愁，真正折磨你的不是睡眠不足，而是你的忧愁。无论晚上几点入睡，早上都要在同一时间起床。可以尝试这个睡前的意念调节，平躺，全身放松，集中精神调节呼吸。闭上眼睛，浮想一些简单的图形；然后控制呼吸的节奏，缓慢吸气，保持一秒钟静止状态，缓慢呼气，反复几次。这样可以排除杂念，达到睡眠前的最佳状态！\"}]','生活方式建议'),
	(25,'容颜美好','[{\"section\":\"容颜问题盘点\",\"title\":\"问题皮肤更要防护皮肤老化\",\"content\":\"缺乏弹性 表皮细胞减少，真皮层纤维硬化、脆弱、新陈代谢不活跃，这些都会使肌肤失去饱满、圆润的感觉。当然这和足够蛋白质，长期接触污染环境，熬夜、烟酒有很大的关系，我们可以通过生活方式和细心料理令皮肤弹性改善。\"},{\"section\":\"容颜问题盘点\",\"title\":\"问题皮肤更要防护皮肤老化\",\"content\":\"皮肤不细腻 皮肤不够细腻，水油不平衡导致皮肤纹路紊乱。角质在我们的皮肤中不可或缺地存在着，可以保护皮肤不受外界刺激，同时维持皮肤内的水分和电解质。但当各种不良生活习惯导致角质层代谢缓慢而逐渐增厚时则会使得皮肤看起来粗糙、不细腻、毛孔大、T部位油腻、妆容容易脱落等。\"},{\"section\":\"容颜问题盘点\",\"title\":\"问题皮肤更要防护皮肤老化\",\"content\":\"皮肤松弛 虽然皮肤会随着年龄增加而不可避免地松弛，但若加强保护可以延缓这个过程。在日光、辐射、烟雾环境中要对皮肤做适当保护，日常饮食营养要充足。\"},{\"section\":\"容颜问题盘点\",\"title\":\"问题皮肤更要防护皮肤老化\",\"content\":\"细纹、皱纹 细纹通常是缺乏水分和油脂的保护造成的，还有熬夜、烟酒、在日晒和辐射环境中缺乏保护也都是易生细纹的原因。\"},{\"section\":\"容颜问题盘点\",\"title\":\"这些问题更多是身体内因的反映\",\"content\":\"肤色暗沉 内因对皮肤暗沉的影响更大，皮肤颜色晦暗无光泽，一方面与遗传有关，但因不良的生活方式导致的肝功能减弱，血液循环不畅，不但会引起肤色的暗沉，与色斑、过敏、痤疮也都脱不开干系。不良日常生活方式包括：盐分摄入过量、吸烟、睡眠不足、久坐、压力过大等。\"},{\"section\":\"容颜问题盘点\",\"title\":\"这些问题更多是身体内因的反映\",\"content\":\"出油 仅仅清洗不能解决皮肤出油的问题，因为熬夜、吃辣多、吃得油腻、使用的护肤品太滋腻，甚至情绪容易激动都会加重皮肤出油。\"},{\"section\":\"容颜问题盘点\",\"title\":\"这些问题更多是身体内因的反映\",\"content\":\"阳光敏感 并未长时间被阳光照射也会敏感，药物治疗也并未有良好效果。可以从睡眠质量、压力、饮食、环境等方面改善整体健康，提升机体抗氧化能力。\"},{\"section\":\"容颜问题盘点\",\"title\":\"这些问题更多是身体内因的反映\",\"content\":\"皮肤干燥 年龄、气候变化、长期处在中央空调环境中、空气污染、电磁辐射、都会使体内水分流失。减肥或者偏食造成肌肤营养失衡，使肌肤失去弹性及水分；睡眠不足导致身体受到某种程度的损害，血液循环能力变差，肌肤失去活力，这些都是肌肤干燥的原因。\"},{\"section\":\"容颜问题盘点\",\"title\":\"这些问题更多是身体内因的反映\",\"content\":\"毛孔粗大 过多的皮脂分泌是导致毛孔变大的主要原因，次要原因还有毛细孔污物阻塞，过度挤面疱和粉刺等。影响皮脂分泌的因素则有很多，包含衰老、激素、季节、紫外线、压力等，这些原因使皮脂分泌旺盛、皮肤弹性变差，毛孔粗大问题则会更严重。\"},{\"section\":\"容颜问题盘点\",\"title\":\"这些问题更多是身体内因的反映\",\"content\":\"嘴唇干 嘴唇周围一圈发红的区域叫“唇红缘”，它的湿润全靠局部丰富的毛细血管和少量发育不全的皮脂腺来维持。如果人体皮肤黏膜血液循环差，新鲜水果蔬菜吃得少，人体维生素B2、维生素A摄入量不足，嘴唇就会干燥开裂。\"},{\"section\":\"容颜问题盘点\",\"title\":\"这些问题更多是身体内因的反映\",\"content\":\"季节敏感 季节转换时，尤其是秋冬季会感到皮肤干痒，多数和皮肤干燥缺水有关。\\n而睡眠质量、压力、饮食、环境、年龄、清洁过度都会令皮肤变得干燥敏感。\"},{\"section\":\"容颜问题盘点\",\"title\":\"色斑，身体在皮肤上的印记\",\"content\":\"晒斑 黑色素生成的过程中，最直接的影响就来自于阳光中的紫外线。它能激活色素母细胞中的酪氨酸酶，与血液中的酪氨酸酶产生一系列反应，生成一种叫“多巴”的物质，进而转化成黑色素。\"},{\"section\":\"容颜问题盘点\",\"title\":\"色斑，身体在皮肤上的印记\",\"content\":\"黄褐斑 黄褐斑的发生一般以中年女性居多，多发于面颊两侧、鼻子、额头和眼睛周围，一般见于慢性妇科病、肝斑等。黄褐斑的出现多数与内分泌有关，尤其是和女性的雌性激素水平有关。因此月经不调、服食避孕药、肝功能或慢性肾病都会引起黄褐斑的出现，而日晒和精神压力又会加重黄褐斑的颜色。\"},{\"section\":\"容颜问题盘点\",\"title\":\"色斑，身体在皮肤上的印记\",\"content\":\"雀斑 雀斑的颜色多为棕色、淡褐色，严重的呈浅黑色，呈芝麻大小的圆形或者卵圆形颗粒状分布，表面光滑，不高于皮肤表面，常由家族遗传而来，5—7岁开始生长，隔代遗传呈显性遗传。\"},{\"section\":\"容颜问题盘点\",\"title\":\"痘痘，内因和外因的共同作用\",\"content\":\"经期长痘 月经前期因皮脂腺活性增强，毛囊皮脂腺导管收缩，导致皮脂分泌易受阻，从而致使月经前痘痘加重。另外，经前紧张、焦虑不安及烦躁失眠等表现，可能也是诱发或加重痘痘的原因之一。\"},{\"section\":\"容颜问题盘点\",\"title\":\"痘痘，内因和外因的共同作用\",\"content\":\"情绪不佳长痘 在感到厌烦、紧张、不开心时，体内的压力荷尔蒙就会分泌，肌肤的循环机制也会被打乱，导致内分泌紊乱，皮脂腺分泌异常堵塞毛囊。调节情绪，放松心情，保证张弛有度的规律生活才能解决根本问题。\"},{\"section\":\"容颜问题盘点\",\"title\":\"痘痘，内因和外因的共同作用\",\"content\":\"饮食刺激长痘 刺激性饮食，如辛辣、高脂肪、咖啡、高糖等食物都有可能成为痘痘发生的诱因，不过本质问题还是影响到内分泌失调导致皮脂腺分泌异常,毛囊堵塞所造成的。需要注意一下自己的生活方式来改善如此容易失调的内分泌。\"},{\"section\":\"容颜问题盘点\",\"title\":\"痘痘，内因和外因的共同作用\",\"content\":\"睡眠不佳长痘 睡眠质量不高的，经常失眠、多梦、熬夜、睡眠不规律、睡眠时间不充足等这些都可能造成痘痘爆发。这主要是因为肝脏等器官在帮身体进行排毒期间未能得到妥善休息，体内毒素淤积而引起长痘痘。需要在生活方式上加以注意，帮助肝脏代谢毒素。\"},{\"section\":\"容颜问题盘点\",\"title\":\"痘痘，内因和外因的共同作用\",\"content\":\"长期长痘 皮脂的大量分泌会造成毛孔堵塞，继而成为产生痘痘的原因。黑头或者白头粉刺是痘痘形成的初始状态，发红肿胀的痘痘则是受细菌感染进一步恶化的状况。饮食、睡眠、压力等生活方式失衡、内分泌失调都可能成为皮脂大量分泌的原因。\"},{\"section\":\"容颜问题盘点\",\"title\":\"皮肤敏感引发的问题\",\"content\":\"对酒精和果酸成分过敏  通常对酒精刺激敏感的人，不会仅仅只在面部皮肤发生过敏现象。除了避免接触酒精外，也要避免饮酒。\\n 果酸存在于许多水果、糖类和乳制品中，当浓度过高或皮肤对营养吸收能力差的时候就会发生对果酸的过敏。少数甚至有全身反应，如哮喘、喷嚏、水肿等。\"},{\"section\":\"容颜问题盘点\",\"title\":\"皮肤敏感引发的问题\",\"content\":\"食物引发过敏 食物引起的过敏是免疫系统参与的特异性反应。通常和胃肠道健康程度或遗传因素有关。饮酒、长期服药、缺少必需脂肪酸等都会加重过敏。\"},{\"section\":\"容颜问题盘点\",\"title\":\"皮肤敏感引发的问题\",\"content\":\"对寒冷敏感 遇到冷空气和风，皮肤会有过敏反应，而遇热会缓解或自然消失，这类似荨麻疹的过敏，并无特效治疗手段，需要从调整机体免疫功能入手，药物只能起到控制病情的作用。增强营养和身体保暖能够帮助缓解该症状。\"},{\"section\":\"容颜问题盘点\",\"title\":\"皮肤敏感引发的问题\",\"content\":\"红血丝 红血丝皮肤是敏感性皮肤的一种，诱因是冷热温度、季节变化、日光等环境因素、压力、情绪变化的影响。这些都会导致一方面是皮肤的角质层较薄，另一方面是异常扩张的毛细血管。另外一些不良的生活习惯，如烟酒、嗜辣、偏食、疲劳、长时间睡眠不足，都会使皮肤处于亚健康状态，血管壁韧性变差，对红血丝起到推波助澜的作用。\"},{\"section\":\"容颜问题盘点\",\"title\":\"头发的状态也和整体健康有关\",\"content\":\"头发出油 头发出油仅仅靠清洗并不能从根本上解决，因为熬夜、吃辣多、吃得油腻，甚至情绪容易激动都会加重头皮出油。\"},{\"section\":\"容颜问题盘点\",\"title\":\"头发的状态也和整体健康有关\",\"content\":\"头皮屑 头皮屑是头皮的一种不健康状态，包括头皮瘙痒和呈屑状脱落。引起的原因有：皮肤干燥、油性皮肤刺激、头发不清洁、牛皮癣、湿疹及类酵母菌感染，还有压力、季节变化、荷尔蒙失调也会影响头皮屑的发生。\"},{\"section\":\"容颜问题盘点\",\"title\":\"头发的状态也和整体健康有关\",\"content\":\"发质不佳 头发干燥、无光泽、脆弱易折或脱发和过度烫染、营养缺乏有关，比如锌、生物素、蛋白质、必需脂肪酸等。而饮食不均衡、压力过大、生活不规律都可能导致这些营养的缺乏。\"},{\"section\":\"容颜问题盘点\",\"title\":\"头发的状态也和整体健康有关\",\"content\":\"白头发 白头发不一定是衰老的征兆，正常人一般是35岁开始生白头发，如果过早就有了白发，有可能和营养缺乏或长期精神紧张有关。\"},{\"section\":\"容颜问题盘点\",\"title\":\"眼部的美丽健康和生活方式有关\",\"content\":\"黑眼圈 黑眼圈和很多因素相关，眼部下方的皮肤过薄、过于脆弱，当疲劳、盐分摄入过多、生理期、睡眠不足、压力过大等状况发生时，容易导致血液循环不畅，于皮肤最薄的地方显示出来，因此黑眼圈并不仅仅是一个美容问题，也是健康问题。\"},{\"section\":\"容颜问题盘点\",\"title\":\"眼部的美丽健康和生活方式有关\",\"content\":\"眼神不够明亮 眼睛的结膜若长期受到紫外线、电磁辐射、粉尘、身体中的自由基的侵害，就会逐渐失去青春时期的明亮润泽感，增强抗氧化保护可以延缓这个过程。\"},{\"section\":\"容颜问题盘点\",\"title\":\"指甲也能反映身体问题\",\"content\":\"指甲无光泽 通常无光泽的指甲提醒你注意贫血、营养不良，甚至肝脏问题。(信息来自WebMD)\"},{\"section\":\"容颜问题盘点\",\"title\":\"指甲也能反映身体问题\",\"content\":\"指甲容易断裂 除了经常打磨指甲外，有可能是营养不良所致，尤其是蛋白质和维生素B族中的生物素。\"},{\"section\":\"容颜问题盘点\",\"title\":\"指甲也能反映身体问题\",\"content\":\"指甲有波纹或坑点 提醒你需要注意预防关节炎。注意关节的保暖防湿，避免关节劳累。(信息来自WebMD)\"},{\"section\":\"生活习惯提醒\",\"title\":\"不良的就餐习惯有损美好容颜\",\"content\":\"不吃早餐 不吃早餐破坏结肠反射，形成便秘，令毒素滞留体内，易生痘痘，色斑等。同时身体又通过分解储存的蛋白质来提供能量，最终导致营养不良，气血不足，从而令面色晦暗无光泽。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不良的就餐习惯有损美好容颜\",\"content\":\"经常吃得过饱 常常吃撑会让皮肤变差。吃撑会引起血糖升高，此时人体脂肪合成加速，同时过剩的食物残渣，特别是高脂肪的食物在肠内利于大量的厌氧菌生长及产生其它有害的毒物，长时间刺激粘膜，超过了肝脏的解毒能力，就会表现到脸上，出现油光，痘痘，色斑等。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不良的就餐习惯有损美好容颜\",\"content\":\"常在外就餐 在繁忙的生活和污染的环境中，人体需要更多的抗氧化剂来清除体内促进衰老的自由基，餐馆的外卖，很少提供新鲜的果蔬和口味清淡的菜肴。这会令维生素和矿物质摄入不足，影响皮肤细胞的正常新陈代谢。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，影响皮肤状态\",\"content\":\"爱吃油炸 常吃油炸食物对皮肤健康不利。油炸食物会产生自由基，破坏食物中的必需脂肪酸，损伤细胞，除了表现出油、长痘、长斑等问题，还会增加心脏病，癌症和早衰的风险。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，影响皮肤状态\",\"content\":\"常吃包装或加工食品 包装零食和加工食品多数含有各种添加剂，这会增加肝脏解毒负担，令面色不佳，皮肤容易长痘，长斑。又因为多吃零食可能影响正餐食欲，导致营养不均衡，缺乏充足的蛋白质和能量，使得精力不足，皮肤缺乏弹性、光泽。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，影响皮肤状态\",\"content\":\"常喝甜饮料 常喝甜饮料对皮肤健康无益。含糖饮料很难提供额外的水分，甚至令身体缺水。同时糖造成酵母菌在肠道的过度繁殖，这可能引起便秘，引发痘痘、色斑等皮肤问题。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，影响皮肤状态\",\"content\":\"爱吃重口味食物 喜欢吃重口味食物说明你需要适当放松和休息。长期吃浓油赤酱的菜，除了会影响皮肤健康外，也容易导致血脂、血压、血糖等健康指标出问题。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，影响皮肤状态\",\"content\":\"肉食过多 过度摄入肉食，则意味着饱和脂肪酸或脂溶性的毒素摄入会较多，这使皮肤容易发炎，增加痘痘、过敏等问题。\"},{\"section\":\"生活习惯提醒\",\"title\":\"健康食物吃得过少，不利于保持皮肤的最佳状态\",\"content\":\"蔬果吃得少 水果蔬菜吃得少，会加重皮肤问题。水果蔬菜中富含能清除毒素和肠道垃圾的维生素以及矿物质，对改善痘痘、出油、色斑有帮助。\"},{\"section\":\"生活习惯提醒\",\"title\":\"健康食物吃得过少，不利于保持皮肤的最佳状态\",\"content\":\"豆、奶制品吃得少 很少吃豆、奶制品可能导致你缺乏钙质，奶制品和豆制品被认为是钙的主要来源，钙是维持骨骼、牙齿、神经健康的主要矿物质，缺乏则容易造成失眠、紧张、疼痛、骨质疏松、龋齿等问题。豆、奶制品也是优质蛋白质的主要来源，若优质蛋白质缺乏，则增加皮肤老化、干燥的风险 。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不规律的睡眠扰乱循环系统生物钟，身体隐形“生病”\",\"content\":\"睡眠不规律 其实除了睡眠，我们心率、呼吸、血压、体温、蛋白质合成等体内一切生命活动，往往都呈现昼夜节律的倾向，反映在血液循环、内分泌和各种器官功能中。不规律的睡眠就好比在不停地倒时差，各器官的生物钟调整所需的时间不同，睡眠两三天就可以调整过来，但是体内的器官及循环系统的生物钟却需要两三周的时间调整。被扰乱的内分泌功能会令皮肤的毛细血管健康受到影响，引起痘痘、色斑、肤色暗沉等问题。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳，影响皮肤和健康\",\"content\":\"多梦 造成多梦的因素，同时也会扰乱人体内分泌，导致皮肤问题频发。压力大、精神紧张、焦虑都有可能成为多梦易醒原因。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳，影响皮肤和健康\",\"content\":\"起床困难或睡不够 疲劳或睡眠不规律都可能导致睡不够，而赖床的习惯通常和三餐不规律伴随出现，若不注意营养的均衡搭配则容易导致皮肤状态不佳。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳，影响皮肤和健康\",\"content\":\"睡眠不足 每个人需要的睡眠时间长短并不一样， 优质的充足睡眠对皮肤状态非常重要，如果醒来后面色红润、精力充沛，说明你的睡眠效率很高。睡眠时流经肝脏的血液是平时的七倍，有利于增强肝细胞功能，提高解毒能力。尤其是1点－3点是皮肤代谢的高峰期，此时不睡很容易导致皮肤干燥、暗沉。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳，影响皮肤和健康\",\"content\":\"失眠 入睡困难或者失眠的人往往表现为：很难睡着、晚上总是醒、睡得不踏实，很难再入睡。一两天的失眠就可以导致面色暗沉、痘痘频发。引起失眠的原因很多，压力、咖啡因、酒精、环境变化、时差、慢性疾病、焦虑等等，解决问题首先要找到原因，是不是有些不良的生活习惯，导致这个问题，不过，有时候，这可能是自己无法控制的，但是要明确一点，这并不是不可以解决的问题，所以不用担心，给自己一点儿耐心。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳，影响皮肤和健康\",\"content\":\"经常熬夜 经常熬夜，可以被称为慢性睡眠不足，已经有大量的科学研究表明睡眠不足与心脏疾病、高血压、中风、糖尿病有直接关系。\\n当然在这些问题发生之前，你可能正在经历着熬夜带来的皮肤问题，只需要几个晚上，皮肤就马上会呈现出蜡黄，眼睛浮肿，长期则导致皮肤黯淡，细纹，眼睛下方的黑眼圈。 没有在适当的时候睡觉，生物节律被打乱，身体会释放更多的应激激素皮质醇，这些皮质醇会分解皮肤中的胶原蛋白，令你的皮肤失去光滑和弹性。\"},{\"section\":\"生活习惯提醒\",\"title\":\"运动捍卫您的生命，不可或缺\",\"content\":\"每天或者隔天花30~60分钟，做一些有氧运动，这是保持年轻活力的很好方法，有氧运动会使肌肉消耗大量的氧气和营养物质，从而使得胃肠、心肺、肾脏等功能提高。在一定条件下，能量消耗得越多，这种补偿作用越明显。\"},{\"section\":\"生活习惯提醒\",\"title\":\"辐射产生的自由基加速人体细胞氧化\",\"content\":\"手机和电脑产生的低频电磁辐射是人体外源性自由基产生的原因之一。随着辐射剂量累积增加，可能会引起神经系统、循环系统、免疫系统、生殖系统以及皮肤和眼睛的功能、代谢或形态的改变，这是一个自由基生成加速身体细胞氧化的过程。\"},{\"section\":\"生活习惯提醒\",\"title\":\"吸烟将危及人的全身皮肤\",\"content\":\"也许你见过香烟面容，这是吸烟者的容颜收到永久性损害后呈现的特有的烟熏火燎的面容，口唇发乌，牙齿黑黄，舌苔黄腻，口气很重，身上味道难闻。\\n美国权威专业杂志《皮肤病学文献》中的一项研究表明，吸烟将危及人的全身皮肤。当皮肤(尤其是脸部)被曝晒于强光下时，人的皮肤就变得很粗糙，变得有褶皱，并变成淡黄色。\"},{\"section\":\"生活习惯提醒\",\"title\":\"过量饮酒影响美好容颜\",\"content\":\"过量饮酒的人看上去往往更加憔悴。人们普遍认为过量饮酒可导致提前衰老并遭到与衰老有关的疾病侵袭。\\n意大利米兰大学进行的一项研究指出，过量酒精可使细胞染色体失去完整性和稳定性的保护，从而加速衰老增加癌症风险。\\n世卫组织认为每日安全饮酒量的上限是25g酒精，相当于1两42度白酒，或200ml红酒，一瓶啤酒。\"},{\"section\":\"体检问题预警\",\"title\":\"降低血脂，降低心脑血管患病风险\",\"content\":\"目前无论在美国还是中国，心脑血管疾病仍然是头号杀手。 血脂在动脉血管壁的沉积是毫无声息的，如果体检已经确认血脂超标，就必须在生活方式上加以注意，加强运动，进行低脂肪饮食等，无效的情况下，需要在医生指导下借助药物改善状况。\"},{\"section\":\"体检问题预警\",\"title\":\"体重增加令代谢功能降低，增加罹患多种慢性疾病风险\",\"content\":\"肥胖的原因很多，但总归导致体内脂质代谢紊乱，造成脂肪在皮下和内脏器官周围堆积，这使得人们患冠心病、高血压、糖尿病等慢性疾病的风险增加，要改变生活方式；饮食和运动水平合理化；下定决心减肥，给自己时间和耐心，逐渐改变饮食和运动习惯；只有长期的改变才算成功。\"},{\"section\":\"体检问题预警\",\"title\":\"冠心病增加猝死风险\",\"content\":\"冠心病若养护不当，猝死率很高（统计表明50-60%猝死是冠心病）。吸烟、高血压、高血糖、高血脂、肥胖、久坐等问题都是冠心病发作的危险因素，通过合理的饮食和有规律的运动，可以大大减少患病的风险，甚至可以免除药物的使用。可以配合每天服用抗氧化维生素，如维生素E、维生素C、叶酸等，阻止低密度脂蛋白在血管的积累，预防发病。\"},{\"section\":\"体检问题预警\",\"title\":\"有高血压问题，生活方式是不容忽视的\",\"content\":\"最好的方法是改变生活方式，包括饮食习惯以及运动等。世界高血压组织研究表明，如果只是1期的高血压，是可以通过生活方式的改善控制血压的。即便是通过用药，生活方式也不容忽视，限制食盐的摄入，减重，运动，戒烟，限制酒精的摄入：每日饮用超过30毫升酒精,相当于600毫升啤酒,200毫升葡萄酒或75毫升标准威士忌,会使血压升高并使冠心病、中风的发病和死亡率上升。\"},{\"section\":\"体检问题预警\",\"title\":\"糖尿病意味着您需要更平衡的生活\",\"content\":\"血糖水平往往受到饮食、运动、差旅、压力等生活习惯的影响。糖尿病意味着需要比其他人更注意你的生活，摄入具有丰富膳食纤维的蔬果，减少高脂肪食物和零食，控制体重，控制吸烟和饮酒。有规律的运动可以帮助保持身体素质，对心脏和血管都有益处，促进血液循环，并定期监测自己的血糖水平，防止糖尿病并发症发生的风险。\"},{\"section\":\"体检问题预警\",\"title\":\"对于骨质疏松日常生活的调理比治疗更有效果\",\"content\":\"先天因素，饮食中钙摄入不足，不常运动，体格瘦弱，吸烟、过量饮酒、女性过早绝经，都是直接导致骨质疏松的危险因素，不过相对于恢复已降低的骨密度，保持骨密度不变要来的更容易些。预防措施包括摄入足够的钙和维生素D和从事负重锻炼。\"},{\"section\":\"体检问题预警\",\"title\":\"即便没有不舒服的症状也要重视脂肪肝\",\"content\":\"脂肪肝是肝细胞内过量的脂肪（甘油三酯）积累，脂肪肝最常见的原因是酗酒、肝炎、肥胖、糖尿病、血清甘油三酯水平过高，因为起初没有任何症状，但可引起肝脏的反复损害，最终可从脂肪肝发展到肝硬化。早期如果在生活方式上加以改善也很快就会看到改善。\"},{\"section\":\"体检问题预警\",\"title\":\"恰当的饮食起居可以改善胆囊健康\",\"content\":\"除了家族遗传的因素外，饮食油腻、暴饮暴食、压力造成的焦虑或易怒都会诱使或加重胆囊问题。少吃多餐、避免油腻高脂肪的食物、腹部保暖、保持情绪平稳都有助于避免胆囊问题的加重。\"},{\"section\":\"体检问题预警\",\"title\":\"改变习惯，加速炎症康复\",\"content\":\"对于女性来说，任何引起阴道酸度下降（PH上升）的因素都会增加患阴道感染的可能性。月经前、月经期及妊娠期的阴道酸度都会随着体内雌激素水平的变化而下降。\\n所以除了遵医嘱用药外，保证阴部环境干爽、减少久坐、穿宽松、透气的裤子，清洁性交、常常运动是预防感染发生的方法，不主张频繁冲洗阴道，这会增加患盆腔炎的风险。\"},{\"section\":\"体检问题预警\",\"title\":\"乳腺增生是可以消失的\",\"content\":\"乳腺增生多发生在25-35岁之间，发生率高达70%~85%，通过触按可以发现，如果没有疼痛的感觉或仅仅是经前期胀痛，往往不会引起重视，导致进一步恶化。婚育、膳食、压力和遗传因素是乳腺发病的主要原因。\\n\\n将体重控制在健康的水平，特别注意心情和压力的调整，因为精神因素引发的内分泌失调、植物神经紊乱，睡不好觉、脾气暴躁，这些都会对乳腺产生不良影响。放松，静坐，舒压按摩都是很好的方法，保证足量有氧运动，每周至少3次。\"},{\"section\":\"体检问题预警\",\"title\":\"腰围超标意味越过慢病安全警戒线\",\"content\":\"腰围的重要性仅次于体重指数。腰围的大小可以表明脂肪堆积的部位。脂肪聚集在腰上的人更容易患高血压、糖尿病、冠状动脉疾病，因为腹部的脂肪更容易分解并在动脉里聚集。男性腰围超过102厘米或女性超过88厘米，患相关疾病的危险就会增大，尤其是体重指数为25或以上的人群。\"}]','容颜健康评估'),
	(26,'容颜美好','[{\"section\":\"饮食习惯\",\"title\":\"口味清淡，皮肤洁净\",\"content\":\"身体的每一个细胞都是由所吃食物的营养构成的，从这个意义上说，你今天吃的东西，就是明天所“穿”的皮肤。少油、少盐、少糖、少辣的饮食令血液更清洁，也会令皮肤更健康。\"},{\"section\":\"饮食习惯\",\"title\":\"口味清淡，皮肤洁净\",\"content\":\"保护皮肤状态良好，减少痘痘、出油等问题，最好少吃这类食物：如煎炸食物、饱和脂肪（畜类肉、黄油、奶油）、高糖饮食（甜点、饮料、糖果）、高盐饮食等。\"},{\"section\":\"饮食习惯\",\"title\":\"口味清淡，皮肤洁净\",\"content\":\"蒸、煮、炖这些烹饪方式相对于煎炸能保留更多的营养，尤其是皮肤健康所需的抗氧化剂类和维生素。多汤水的饮食也能令皮肤更滋润。\"},{\"section\":\"饮食习惯\",\"title\":\"多吃未经加工的天然食物，提供美颜所需新鲜营养\",\"content\":\"尽量选择新鲜的当地、当季产新鲜食物，它们除了能提供必需的营养素还能帮助代谢掉人体中产生有的有害物质，如促使老化的自由基。\\n加工食品中存在多种人工合成的化学物质，已有许多国家研究表明加工食品中的各种添加剂摄入过多，会存在致癌风险。\"},{\"section\":\"饮食习惯\",\"title\":\"多吃未经加工的天然食物，提供美颜所需新鲜营养\",\"content\":\"爱吃零食也可以改善肤质，重点是如何选择。时令水果、酸奶、原味坚果、天然果干，这些都可以作为零食，既能满足吃零食的习惯，也能给身体提供必要营养。\"},{\"section\":\"饮食习惯\",\"title\":\"多吃未经加工的天然食物，提供美颜所需新鲜营养\",\"content\":\"健康的头发所需的营养素和维护身体健康的全面营养一致，多吃营养全面的天然食物，尤其是富含蛋白质、优质脂肪的坚果、种子类食物，还有富含维生素和矿物质的全谷物和绿叶蔬菜。（信息来自WebMD）\"},{\"section\":\"饮食习惯\",\"title\":\"健康皮肤需要足量的洁净饮水\",\"content\":\"人体的70%是水分，含有矿物质的水或稀释的鲜榨果蔬汁可以协助身体排除细胞和血液中的代谢废物。这也是令你的皮肤看起来健康、紧致的基础条件。\"},{\"section\":\"饮食习惯\",\"title\":\"细嚼慢咽，七分饱\",\"content\":\"实验表明，长期适度减少热量摄入的人更长寿，体重更轻，患心脑血管疾病的风险更低。咀嚼的习惯也可以改善面部血液循环，有益于皮肤健康。\"},{\"section\":\"饮食结构\",\"title\":\"富含必需脂肪酸的食物有助于皮肤健康\",\"content\":\"吃些种子（芝麻、南瓜籽、松籽、葵花籽、花生等），坚果和种子油，可以提供对皮肤有益的必需脂肪酸，这是构成健康细胞膜的原料。健康的细胞膜可以保证皮肤的含水性和柔软性。（源自《营养圣经》）\"},{\"section\":\"饮食结构\",\"title\":\"多彩蔬果，保护容颜健康\",\"content\":\"多吃些黄色、橙色和红色的蔬果，它们都富含β-胡萝卜素，在体内能转换成维生素A，是维持皮肤健康必需的营养素。当维生素A缺乏时皮肤抗感染能力下降，肤质粗糙。\"},{\"section\":\"饮食结构\",\"title\":\"多彩蔬果，保护容颜健康\",\"content\":\"多吃些富含维生素C的蔬果，如：番石榴、草莓、猕猴桃、樱桃、橙子、鲜枣、番茄等。维生素C对容颜美丽必不可少1.维生素C是合成胶原蛋白必需的营养素，帮助保持皮肤弹性。2.维生素C是抗氧化剂，能够使深色的氧化型色素还原为淡色的还原型色素。从而抑制黑色素的形成，并还原已经形成的黑色素。3.维生素C是多种肝脏解毒酶的活化因子和酶促因子，可以提高肝脏排毒能力，促进血液循环，改善肤色暗沉。4.维生素C能增强毛细血管壁健康，增强机体抗氧化能力，从而改善黑眼圈和红血丝。\"},{\"section\":\"饮食结构\",\"title\":\"多彩蔬果，保护容颜健康\",\"content\":\"水果富含果胶和纤维素，可以减少脂肪吸收，协助清洁肠道，减少毒素，从而保护皮肤健康。\"},{\"section\":\"饮食结构\",\"title\":\"多彩蔬果，保护容颜健康\",\"content\":\"蔬菜水果能提供有益于皮肤健康的抗氧化剂，多吃杏肉，所有浆果，哈密瓜，柑橘类，猕猴桃，木瓜，桃子，芒果，瓜类，红葡萄。蔬菜类有，洋蓟，甜菜根，西兰花，紫甘蓝，胡萝卜，白色菜花，黄瓜，甘蓝，南瓜，菠菜，甘薯，番茄，豆瓣菜，豆类和豆芽。\"},{\"section\":\"饮食结构\",\"title\":\"充足的蛋白质是健康皮肤重要基础\",\"content\":\"食物中充足的蛋白质是构成健康皮肤的基础材料，皮肤真皮层75%由胶原蛋白组成，纤维状的胶原蛋白组成网状机关，蛋白质纤维就像撑起皮肤组织的钢筋架构，维持皮肤弹性和饱满度。\"},{\"section\":\"饮食结构\",\"title\":\"充足的蛋白质是健康皮肤重要基础\",\"content\":\"平日可以有意多吃些富含胶原蛋白的猪皮、鱼皮、牛蹄筋等。如果吃素也可以从丰富的杂粮、种子、豆类中获得足够的蛋白质。\"},{\"section\":\"饮食结构\",\"title\":\"充足的蛋白质是健康皮肤重要基础\",\"content\":\"“对付”红血丝的根本方法，在于增强皮肤细胞的免疫功能和自我修复功能。胶原蛋白可增强血管壁细胞的代谢功能，并形成保护屏障，有效地预防血管壁被轻易的破坏或受到病理作用的侵袭，恢复毛细血管弹性，增强毛细血管扩张、收缩能力。\"},{\"section\":\"饮食结构\",\"title\":\"充足的蛋白质是健康皮肤重要基础\",\"content\":\"头发、指甲和皮肤一样，主要是由蛋白质组成的，而缺乏胶原蛋白时，毛发、指甲无光泽、容易断裂。\"},{\"section\":\"饮食结构\",\"title\":\"增加钙的补充\",\"content\":\"骨矿研究测试表明，人到35岁以后，平均骨矿含量以每年0.5-1%的速率下降。钙在人体中处于不断丢失和补损的动态平衡过程中，人体中钙的存量关系到骨骼、牙齿的韧性，血液凝结，激活酶系统，维持肌肉伸缩性和心跳规律。\\n通过膳食来调节体内钙平衡是人类最佳补钙法，牛奶和豆类食物是钙的优质来源，此外还可以选择发菜、紫菜、荠菜、木耳、海米、软骨、骨汤等，或者适量芝麻、花生、榛子等坚果，都是很好的钙质来源。\"},{\"section\":\"饮食结构\",\"title\":\"常吃粗杂粮，清洁机体内环境\",\"content\":\"最新的美国营养学研究建议，主食中的一半应该是全谷物和粗杂粮，包括玉米、全麦制品、糙米、燕麦、薯类等。粗粮富含水溶性纤维素，能协助体内垃圾清除，维护肠道健康，还含有可以调节体内脂肪代谢的B族维生素，这都能改善皮肤健康状态。\"},{\"section\":\"饮食结构\",\"title\":\"富含维生素E的食物帮助延缓皮肤衰老\",\"content\":\"维生素E是抗氧化剂，能够进入皮肤细胞，有助于减少黑色素的沉积，延缓皮肤出现皱纹、干燥等衰老情况。维生素E只能在植物中合成，绿色的部分均含有维生素E。麦胚、玉米、大豆、向日葵中提取的油脂维生素E含量较高。\"},{\"section\":\"饮食结构\",\"title\":\"生物素可以帮助你的指甲更坚韧\",\"content\":\"生物素是维生素B族中的一种，研究表明80%指甲易断裂的人开始多吃富含生物素的食物或膳食补充剂几个月之后，情况大为改善。富含生物素的食物包括：啤酒酵母、玉米、大麦、大豆、核桃、花生等。\"}]','合理营养建议'),
	(27,'容颜美好','[{\"section\":\"运动改善皮肤问题\",\"title\":\"肤色差 ?  有氧运动给你好气色\",\"content\":\"定期的有氧运动，会帮助血液和淋巴液的循环流畅，减少体内毒素积累，有助于脸色保持健康红润。\"},{\"section\":\"运动改善皮肤问题\",\"title\":\"毛孔大 ?  运动排毒，改善肤质\",\"content\":\"在运动过程中，身体的血液循环加快，皮肤新陈代谢也自然加速，身体大量排汗，一些残留在毛孔中的脏污和废物也随之排除，毛孔能得以自由呼吸。同时，经常运动可以使肌肤紧实而富有弹性。\"},{\"section\":\"运动改善皮肤问题\",\"title\":\"压力下长痘 ?  运动释放压力，改善皮肤状态\",\"content\":\"让身体微微出汗的有氧运动不仅能帮助身体排出毒素，令皮肤更健康，还能调节情绪，因为运动能够促使大脑和其他部位释放β-内啡肽（天然镇定剂）使人感到心情愉快。\"},{\"section\":\"运动改善皮肤问题\",\"title\":\"皮肤不紧致 ?  运动保持容颜年轻态\",\"content\":\"脸部的皮肤和身体其他各个部位一样，也需要靠运动来保持自己的活力。缺少运动，皮肤就容易衰老、缺乏弹性。皮肤的运动包括主动运动和被动运动两种。主动运动则是日常的有氧或无氧训练，被动运动则是对松弛部位的保养按摩。\"}]','有效运动建议'),
	(28,'容颜美好','[{\"section\":\"\",\"title\":\"良好睡眠，健康容颜\",\"content\":\"可应对所有肌肤问题的对策之一就是保持充足的睡眠。肌肤的新陈代谢一般在夜间10点至凌晨2点之间进行，在正确的时间睡眠并且保证充足的睡眠可以增加肝脏血流量，使肝脏得到更多的血液、氧气及营养的供给，促进肝细胞的康复，这将有助于改善脸色,减少长痘、出油等。有时身体和心理上的疲倦也会引起肤色暗沉，因此要保持充足的睡眠，让身心同时得到放松。\"},{\"section\":\"\",\"title\":\"轻松心情，令皮肤更干净\",\"content\":\"让生活保持平衡，有自己放松的方法和时间，对健康和美好容颜都不可缺少。皮肤在压力的状态下更容易出现各种问题，如油脂分泌过度、长痘、长斑等，这是肾上腺素等激素在体内失衡的表现。\"},{\"section\":\"\",\"title\":\"吸烟，要停下来很容易\",\"content\":\"你可能尝试戒烟，并体会过戒烟带来的各种不适反应。但是应该记得的是，这些不适反应只不过是些可以改善的症状，远比吸烟对内脏、心脑血管的直接损伤来的微小的多。\"},{\"section\":\"\",\"title\":\"长期有规律的适量饮酒帮助提升机体健康\",\"content\":\"人体可自然分解消化酒精，每天适当饮酒可使动脉血管扩张、血压下降，有利于促进血液循环，更可提升对人体有利的高密度脂蛋白及降低有害的低密度脂蛋白，从而防止心脏病发作。而适量葡萄酒（含有抗氧化物质白藜芦醇）可能降低机体老化速度。\\n适量的饮酒相当于每天一杯150ml红葡萄酒或360ml啤酒。\"},{\"section\":\"\",\"title\":\"常常受到辐射侵扰，需要抗氧化剂抵抗辐射侵袭\",\"content\":\"1.需要有意识地通过饮食或膳食补充剂摄入花青素、维生素C、维生素E、维生素A等抗氧化剂，及时清除代谢产生的自由基。 2.避免长时间连续操作电脑，眼睛与屏幕的距离应在40~50厘米。  3.室内光线要适宜，不可过亮或过暗。  4.安装防护装置，削弱电磁辐射的强度。\"},{\"section\":\"\",\"title\":\"不论晚上睡眠如何糟糕，你的生物钟需要一个起点\",\"content\":\"比起控制睡眠时间，控制起床时间更为容易，保证在7：30分左右起床（或者是自己既定的时间，但是不要超过8：00），尽量将其保持在30分钟的浮动范围内，让你的起床时间有规律是改善睡眠不规律的方法，当然这取决于个人的意愿。\"},{\"section\":\"\",\"title\":\"循序渐进延长睡眠时间\",\"content\":\"如果你经常看着电视、看着书进入梦乡，这意味着你缺乏足够的睡眠，补充睡眠并不需要比往常提前1小时上床，这只会令你睡不着觉，每天晚上只要提前10~15分钟，在下午13点~16点之间小睡一下，半个小时为宜，多了则会影响晚间的睡眠，在调整睡眠规律期间清淡饮食。\"},{\"section\":\"\",\"title\":\"给自己一些时间调整你的睡眠\",\"content\":\"上床以后不要继续思考烦心事，如果睡不着，宁可起来做点事，也不要躺在床上发愁，真正折磨你的不是睡眠不足，而是你的忧愁。无论晚上几点入睡，早上都要在同一时间起床。可以尝试这个睡前的意念调节，平躺，全身放松，集中精神调节呼吸。闭上眼睛，浮想一些简单的图形；然后控制呼吸的节奏，缓慢吸气，保持一秒钟静止状态，缓慢呼气，反复几次。这样可以排除杂念，达到睡眠前的最佳状态！\"},{\"section\":\"\",\"title\":\"养成排便好习惯，有利于保持容颜洁净\",\"content\":\"养成良好排便习惯，保持肠道清洁，减少毒素被再次吸收，影响皮肤健康。最佳的排便时间是清晨起床后，即使缺少便意，也可以培养，逐渐形成条件反射。睡前按揉腹部，有助于胃肠蠕动，增加便量。\"},{\"section\":\"\",\"title\":\"自制天然去屑洗发水\",\"content\":\"将头发洗净，再用一个柠檬榨汁或者等量果醋滴在温水里冲洗头发，同时按揉头皮。坚持一段时间，可以改善头屑困扰。\"},{\"section\":\"\",\"title\":\"水温适中保护头部油脂分泌正常\",\"content\":\"过高的水温会刺激头皮油脂分泌，常用过热的水洗发会导致头发油脂分泌过多，长久如此又会令头发干燥。洗头发最好用略低于体温的水。\"},{\"section\":\"\",\"title\":\"梳头有助改善头发健康\",\"content\":\"每天梳头，可以改善头皮供血，改善头发和头皮的健康。\\n1.用木质、玉石、牛角或鬃刷梳头发。\\n2.不要用力牵扯，尤其是头发还湿湿的时候。\\n3.要梳到头皮，给你的头皮做个按摩。\"},{\"section\":\"\",\"title\":\"你的头发需要适当的保护\",\"content\":\"1.减少烫、染的次数，每年不超过2次。\\n2.洗发后先用干发毛巾吸干水分，再用合适的热度吹干发根即可。避免过热的吹风伤害头发。\\n3.避免在阳光下暴晒过久，这也会伤害你的头发。\\n4.游泳前不要洗发，游泳后要用有保护功能的洗发水去除粘附在头发上的泳池中化学物质。\"},{\"section\":\"\",\"title\":\"注意保暖可以缓解遇冷过敏\",\"content\":\"包括风团在内的过敏都可以通过保暖得到改善。皮肤遇冷过敏后可用热水袋敷一会儿。平日出汗后避免被冷风吹到。\"},{\"section\":\"\",\"title\":\"绿茶冷敷，改善阳光敏感\",\"content\":\"日晒后过敏，可用浓的绿茶水冷敷，新鲜的绿茶含有丰富的茶多酚，能抗紫外线损伤。最好用当年的新茶，开水冲泡后放凉，浸泡面膜纸或者干净毛巾敷面。\"},{\"section\":\"\",\"title\":\"减少日光直射\",\"content\":\"防晒是去斑关键。防晒全年都有紫外线，因此不管什么季节都要坚持防晒。尽量减少紫外线进入肌肤内层的机会，预防肌肤细胞向黑素母细胞传递信息，从而预防晒斑形成。\"}]','生活方式建议'),
	(29,'优美体形','[{\"section\":\"生活习惯提醒\",\"title\":\"不良的就餐习惯影响体型，有损健康\",\"content\":\"经常吃得过饱 吃撑会令血糖快速变化，刺激胰岛素的分泌，久之易患糖尿病。血糖高时，人体也会加速脂肪合成。吃撑时，胃肠过度扩张，久之有损肠胃健康。如果年轻时经常吃得过饱，年老后体内甲状旁腺素含量会明显增加，骨质脱钙现象就会日趋严重，患骨质疏松的机会就会明显增加。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不良的就餐习惯影响体型，有损健康\",\"content\":\"常在外就餐 常在外吃，难免油脂、盐吃的过量。多余的油脂会以脂肪形式堆积在身体里和血管中，多余的盐增加肾脏负担，令水潴留体内，影响健康和体型。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不良的就餐习惯影响体型，有损健康\",\"content\":\"不吃早餐 不吃早餐，人体首先消耗的是碳水化合物和蛋白质，最后消耗的才是脂肪，所以不要以为不吃早饭会有助于脂肪的消耗。相反，不吃早饭，还会使午饭和晚饭吃得更多，瘦身不成反而更胖，肌肉总量也会减少。 经常不吃早餐，会使胆汁中胆酸含量减少，胆汁浓缩，造成胆囊中胆结石的形成。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，不利于保持健康体型\",\"content\":\"爱吃油炸 油炸食物的热量密度颇高，不知不觉间改变了你的体型，常吃油炸食物加重患“富贵病”的风险，甚至有致癌的潜在威胁。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，不利于保持健康体型\",\"content\":\"常吃包装或加工食品 将食物有效地转化成能量而不是脂肪，是由成千上万的酶来决定的，而这些酶又依赖于维生素和矿物质。常吃包装零食等加工食物反倒会消耗这些对身体有益的元素，同时也会摄入过度的油、盐、糖。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，不利于保持健康体型\",\"content\":\"常喝甜饮料 常喝甜味饮料会增加热量摄入、脂肪合成，以及对体内维生素和矿物质的消耗，降低免疫力，令血糖不稳，让身体丢失钙质。即便是阿斯巴甜也能刺激大脑对甜食和碳水化合物产生更多渴望。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，不利于保持健康体型\",\"content\":\"爱吃重口味食物 你的腰围已经超标，不适合吃过多的重口味食物了，这会增加患心脑血管疾病和糖尿病的风险。重口味会令食欲大增，不知不觉间就吃的过多。糖盐油摄入过度也是慢性疾病的潜在危险因素。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，不利于保持健康体型\",\"content\":\"肉食过多 大量流行病研究提示，随着膳食中脂肪占总能量的产热百分比的增加，体重和肥胖的发生率明显升高。而大多数肉类食品含的脂肪多，热量高，是需要减重的人应该限制的。\"},{\"section\":\"生活习惯提醒\",\"title\":\"健康食物吃得过少，令机体缺乏必需营养，影响健康状态和体型\",\"content\":\"豆、奶制品吃得少 很少吃豆、奶制品可能导致你缺乏钙质，奶制品和豆制品被认为是钙的主要来源，钙是维持骨骼、牙齿、神经健康的主要矿物质，缺乏则容易造成失眠、紧张、骨质疏松、龋齿等问题。钙也能够帮助活化脂酶（加速脂肪分解和利用的酶）。\"},{\"section\":\"生活习惯提醒\",\"title\":\"健康食物吃得过少，令机体缺乏必需营养，影响健康状态和体型\",\"content\":\"粗粮吃得少 很少吃粗粮会令纤维素和维生素B族以及矿物质缺乏。纤维素能减少胆固醇和脂肪的吸收，还能维持血糖的平稳，增加饱腹感，有助于控制食欲。维生素B族能协助体内能量代谢，加速脂肪分解 。各种矿物质也是维护身体健康所必需的。\"},{\"section\":\"生活习惯提醒\",\"title\":\"健康食物吃得过少，令机体缺乏必需营养，影响健康状态和体型\",\"content\":\"蔬果吃得少 蔬果提供抗氧化剂和水溶性维生素和矿物质，若吃的不够，则令机体缺乏帮助脂肪燃烧的营养素。大多数水果和蔬菜富含维生素B和维生素C。水果可以作为餐间饥饿时的补充，可以帮助维持血糖平稳，这能保证食欲不会在饥饿时暴增。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳影响健康状态\",\"content\":\"经常熬夜 经常熬夜，可以被称为慢性睡眠不足，已经有大量的科学研究表明睡眠不足与心脏疾病、高血压、中风、糖尿病有直接关系。\\n睡眠不足与食欲增加有直接关系，从而导致肥胖，据2008年的一项研究，熬夜人群发胖的可能性比正常人高出30%，由于刺激了对高脂肪，高碳水化合物类食物的渴望。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳影响健康状态\",\"content\":\"睡眠不足 每个人需要的睡眠时间长短并不一样，如果每天醒来你不需要闹钟，不需要冷水，不需要咖啡就能让自己充满能量地迎接新的一天，这说明你的睡眠效率很高，总之自己的感觉是最直接、最重要的。 \\n但如果因为睡不够觉得困倦则说明睡眠效率较低。长期睡眠不足的话，会使得记忆力减退、情绪波动厉害。如果只睡很短时间，就会提高血压和心率的平均水平，由此可能会增大心血管系统的压力。 \\n睡眠不足会降低让人有饱足感的瘦素水平，同时会升高让人产生饥饿感的多肽激素水平。睡眠不足时人更想吃高碳水化合物、高糖份的食物，这也是导致发胖的原因。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳影响健康状态\",\"content\":\"多梦 梦多是在快速动眼睡眠中出现的，这段时间人的体温上升，心跳增加，血流速快，区别于入睡时的生理状态（体温下降），这类似于人们醒着时候的生理状态，人们也更容易在这段时间内醒过来。这种状况出现的原因很多，压力大、精神紧张、焦虑都有可能成为多梦易醒的原因。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳影响健康状态\",\"content\":\"起床困难或睡不够 喜欢赖床或睡后精力不能补足。可能是繁重的脑力或体力劳动造成极度疲劳，使得需要更长时间的睡眠来恢复。也可能不规律的生活习惯造成了生物钟紊乱，无法控制睡眠和觉醒的正常切换。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳影响健康状态\",\"content\":\"虽然失眠使人体消耗的能量增加，短期内令体重降低，但长期失眠会降低免疫力，扰乱内分泌，影响整体健康。入睡困难或者失眠的人往往表现为：很难睡着、晚上总是醒、睡得不踏实，很难再入睡。原因很多，压力、咖啡因、酒精、环境变化、时差、慢性疾病、焦虑等等，解决问题首先要找到原因，是不是有些不良的生活习惯，导致这个问题，不过，有时候，这可能是自己无法控制的，但是要明确一点，这并不是不可以解决的问题，所以不用担心，给自己一点儿耐心。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不规律的睡眠扰乱循环系统生物钟，身体隐形“生病”\",\"content\":\"睡眠不规律 其实除了睡眠，我们心率、呼吸、血压、体温、蛋白质合成等体内一切生命活动，往往都呈现昼夜节律的倾向，反映在血液循环、内分泌和各种器官功能中。不规律的睡眠就好比在不停地倒时差，各器官的生物钟调整所需的时间不同，睡眠两三天就可以调整过来，但是体内的器官及循环系统的生物钟却需要两三周的时间调整。\"},{\"section\":\"生活习惯提醒\",\"title\":\"运动是减脂的最好途径\",\"content\":\"久坐、摄入热量过高是腹部肥胖，年龄增大都是腹部肥胖的原因。腹部肥胖往往是冠心病、糖尿病、高血压等慢性疾病的危险因素，然而单靠局部的运动或者节食往往很难达到良好的效果，需要配合全身性的有氧运动达到预期效果。\"},{\"section\":\"生活习惯提醒\",\"title\":\"吸烟会阻碍您的塑形行动\",\"content\":\"糖和精致碳水化合物不是扰乱血糖平衡的全部物质，刺激物如尼古丁也同样影响着能量和体重控制，这就意味着要控制吸烟或戒烟。\"},{\"section\":\"生活习惯提醒\",\"title\":\"过量饮酒损伤健康\",\"content\":\"过量饮酒的人看上去往往更加憔悴。人们普遍认为过量饮酒可导致提前衰老并遭到与衰老有关的疾病侵袭。\\n意大利米兰大学进行的一项研究指出，过量酒精可使细胞染色体失去完整性和稳定性的保护，从而加速衰老增加癌症风险。\\n世卫组织认为每日安全饮酒量的上限是25g酒精，相当于1两42度白酒，或200ml红酒，一瓶啤酒。\"},{\"section\":\"生活习惯提醒\",\"title\":\"辐射产生的自由基加速人体细胞氧化\",\"content\":\"手机和电脑产生的低频电磁辐射是人体外源性自由基产生的原因之一。随着辐射剂量累积增加，可能会引起神经系统、循环系统、免疫系统、生殖系统以及皮肤和眼睛的功能、代谢或形态的改变，这是一个自由基生成加速身体细胞氧化的过程。\"},{\"section\":\"对塑形的特别提示\",\"title\":\"宿便促生胖腹\",\"content\":\"一般3~5日不解大便而停留于肠管内的粪块叫宿便。宿便积累多了会令小腹突出，一次畅快的排便之后，肚腩会变软、缩小。\"},{\"section\":\"体检问题的预示\",\"title\":\"恰当的饮食起居可以改善胆囊健康\",\"content\":\"除了家族遗传的因素外，饮食油腻、暴饮暴食、压力造成的焦虑或易怒都会诱使或加重胆囊问题。少吃多餐、避免油腻高脂肪的食物、腹部保暖、保持情绪平稳都有助于避免胆囊问题的加重。\"},{\"section\":\"体检问题的预示\",\"title\":\"减肥是一种生活态度\",\"content\":\"肥胖的原因很多，但总归导致体内脂质代谢紊乱，造成脂肪在皮下和内脏器官周围堆积，这使得人们患冠心病、高血压、糖尿病等慢性疾病的风险增加，要改变生活方式；饮食和运动水平合理化；下定决心减肥，给自己时间和耐心，逐渐改变饮食和运动习惯；只有长期的改变才算成功。\"},{\"section\":\"体检问题的预示\",\"title\":\"腰围超标意味越过慢病安全警戒线\",\"content\":\"腰围的重要性仅次于体重指数。腰围的大小可以表明脂肪堆积的部位。脂肪聚集在腰上的人更容易患高血压、糖尿病、冠状动脉疾病，因为腹部的脂肪更容易分解并在动脉里聚集。男性腰围超过102厘米或女性超过88厘米，患相关疾病的危险就会增大，尤其是体重指数为25或以上的人群。\"},{\"section\":\"体检问题的预示\",\"title\":\"保持健康体型有助于改善高血压\",\"content\":\"最好的方法是改变生活方式，包括饮食习惯以及运动等。世界高血压组织研究表明，如果只是1期的高血压，是可以通过生活方式的改善控制血压的。即便是通过用药，生活方式也不容忽视，限制食盐的摄入，减重，运动，戒烟，限制酒精的摄入：每日饮用超过30毫升酒精,相当于600毫升啤酒,200毫升葡萄酒或75毫升标准威士忌,会使血压升高并使冠心病、中风的发病和死亡率上升。\"},{\"section\":\"体检问题的预示\",\"title\":\"降低血脂，降低心脑血管患病风险\",\"content\":\"目前无论在美国还是中国，心脑血管疾病仍然是头号杀手。血脂在动脉血管壁的沉积是毫无声息的，如果体检已经确认血脂超标，就必须在生活方式上加以注意，加强运动，进行低脂肪饮食等，无效的情况下，需要在医生指导下借助药物改善状况。\"},{\"section\":\"体检问题的预示\",\"title\":\"糖尿病意味着您需要更平衡的生活\",\"content\":\"血糖水平往往受到饮食、运动、差旅、压力等生活习惯的影响。糖尿病意味着需要比其他人更注意你的生活，摄入具有丰富膳食纤维的蔬果，减少高脂肪食物和零食，控制体重，控制吸烟和饮酒。有规律的运动可以帮助保持身体素质，对心脏和血管都有益处，促进血液循环，并定期监测自己的血糖水平，防止糖尿病并发症发生的风险。\"},{\"section\":\"体检问题的预示\",\"title\":\"冠心病增加猝死风险\",\"content\":\"冠心病若养护不当，猝死率很高（统计表明50-60%猝死是冠心病）。吸烟、高血压、高血糖、高血脂、肥胖、久坐等问题都是冠心病发作的危险因素，通过合理的饮食和有规律的运动，可以大大减少患病的风险，甚至可以免除药物的使用。可以配合每天服用抗氧化维生素，如维生素E、维生素C、叶酸等，阻止低密度脂蛋白在血管的积累，预防发病。\"},{\"section\":\"体检问题的预示\",\"title\":\"对于骨质疏松日常生活的调理比治疗更有效果\",\"content\":\"先天因素，饮食中钙摄入不足，不常运动，体格瘦弱，吸烟、过量饮酒、女性过早绝经，都是直接导致骨质疏松的危险因素，不过相对于恢复已降低的骨密度，保持骨密度不变要来的更容易些。预防措施包括摄入足够的钙和维生素D和从事负重锻炼。\"},{\"section\":\"体检问题的预示\",\"title\":\"塑造健康体型有助于改善脂肪肝\",\"content\":\"脂肪肝是肝细胞内过量的脂肪（甘油三酯）积累，脂肪肝最常见的原因是酗酒、肝炎、肥胖、糖尿病、血清甘油三酯水平过高，因为起初没有任何症状，但可引起肝脏的反复损害，最终可从脂肪肝发展到肝硬化。早期如果在生活方式上加以改善也很快就会看到改善。\"},{\"section\":\"体检问题的预示\",\"title\":\"乳腺增生是可以消失的\",\"content\":\"乳腺增生多发生在25-35岁之间，发生率高达70%~85%，通过触按可以发现，如果没有疼痛的感觉或仅仅是经前期胀痛，往往不会引起重视，导致进一步恶化。婚育、膳食、压力和遗传因素是乳腺发病的主要原因。\\n将体重控制在健康的水平，特别注意心情和压力的调整，放松，静坐，舒压按摩都是很好的方法，保证足量有氧运动，每周至少3次。\"},{\"section\":\"体检问题的预示\",\"title\":\"改变习惯，加速炎症康复\",\"content\":\"对于女性来说，任何引起阴道酸度下降（PH上升）的因素都会增加患阴道感染的可能性。月经前、月经期及妊娠期的阴道酸度都会随着体内雌激素水平的变化而下降。\\n所以除了遵医嘱用药外，保证阴部环境干爽、减少久坐、穿宽松、透气的裤子，清洁性交、常常运动是预防感染发生的方法，不主张频繁冲洗阴道，这会增加患盆腔炎的风险。\"}]','形体健康评估'),
	(30,'优美体形','[{\"section\":\"饮食习惯\",\"title\":\"口味清淡不仅有助于塑身，更有益于健康\",\"content\":\"塑身时，口味要清淡，油、盐、糖、辣尽量少用，可以多用醋、花椒、葱、姜、蒜等天然调料。一是不刺激食欲，也可以减少热量积累。二是清淡的饮食对健康也有益，降低慢性病风险。三是吃的清淡减轻消化负担，节约了能量和维生素，从而保证精力充沛。\"},{\"section\":\"饮食习惯\",\"title\":\"营养合理的早餐有助塑身\",\"content\":\"塑身时期一定要吃早餐，让全天食物总热量均衡分配。合理的早餐能保证血糖平稳，这是控制食欲的关键因素。当血糖过低时，食欲就会大增，而且对高热量的食物更加渴望。理想的早餐应该有适当热量、维生素和优质脂肪酸、优质蛋白质，比如富含膳食纤维的全麦面包、玉米、燕麦和一小把坚果加上能提供蛋白质的豆类食物。\"},{\"section\":\"饮食习惯\",\"title\":\"有效的水份能帮助塑身\",\"content\":\"常喝矿泉水和白开水，而不是甜饮料。而稀释的鲜榨果蔬汁不仅能提供水份还有对身体有益的维生素和矿物质。因为消耗脂肪需要水的协助，如果体内水分不足，脂肪的代谢率会降低。喝水要小口温饮，保持不渴的状态。肉软软的人要避免摄入过多的水份引起虚胖。\"},{\"section\":\"饮食习惯\",\"title\":\"避免过度节食\",\"content\":\"当神经递质（色氨酸）失衡或缺乏的时候，人会食欲亢奋。过度节食令体内的色氨酸缺乏，此时您就很想大吃一顿。色氨酸主要来自于牛奶、奶酪、火鸡、鸡肉、鸡蛋、红肉、大豆、豆腐、杏仁等，所以即便节食，也要尽量保证吃些上述食物。\"},{\"section\":\"饮食习惯\",\"title\":\"避免过度节食\",\"content\":\"当饮食量不足够，粪便体积小也无法给予肠道足够压力而导致便秘。\"},{\"section\":\"饮食习惯\",\"title\":\"避免过度节食\",\"content\":\"你需要来自天然食物的足够能量来提升体能和精神状态。这种状态下过度节食只会令身体缺乏必要的营养，反倒降低新陈代谢效率，令体重停滞不变甚至反弹。\"},{\"section\":\"饮食习惯\",\"title\":\"细嚼慢咽，七分饱\",\"content\":\"细嚼慢咽、七分饱，这是最简单易行的减重法。每口食物尽量咀嚼20次。细嚼慢咽会令食物更容易消化，减少胃肠负担。这也会增加饱腹感，自然减少食量，有助于塑身。\"},{\"section\":\"饮食习惯\",\"title\":\"多选蒸、煮、炖等烹饪方式\",\"content\":\"这种烹饪方式令食物柔软易吸收，保护胃肠健康，而且炖煮的肉类相对煎炸的肉类更健康，纤维中的胆固醇含量降低，且炖出来的肉质更加鲜嫩、容易消化。\"},{\"section\":\"饮食习惯\",\"title\":\"天然食物有助于塑身\",\"content\":\"吃些健康零食。时令水果、酸奶、原味坚果、天然果干，这些都可以作为零食，既能满足吃零食的习惯，也能给身体提供必要营养。\"},{\"section\":\"饮食习惯\",\"title\":\"天然食物有助于塑身\",\"content\":\"塑身时也可以吃零食，重点在于如何选择，时令水果、脱脂酸奶、原味坚果、粗粮饼干，这些都可以作为零食，既能满足吃零食的习惯，也能给身体提供必要营养，有助于平均分配一天摄入的热量，避免过度饥饿带来的情绪波动和食欲失控。\"},{\"section\":\"饮食结构\",\"title\":\"均衡减重勿忘增加矿物质\",\"content\":\"骨矿研究测试表明，人到35岁以后，平均骨矿含量以每年0.5-1%的速率下降。钙在人体中处于不断丢失和补损的动态平衡过程中，人体中钙的存量关系到骨骼、牙齿的韧性，血液凝结，激活酶系统，维持肌肉伸缩性和心跳规律。\\n通过膳食来调节体内钙平衡是人类最佳补钙法，牛奶和豆类食物是钙的优质来源，此外还可以选择发菜、紫菜、荠菜、木耳、海米、软骨、骨汤等，或者适量芝麻、花生、榛子等坚果，都是很好的钙质来源。\"},{\"section\":\"饮食结构\",\"title\":\"粗杂粮有助于健康塑身\",\"content\":\"最新的美国营养学研究建议，主食中的一半应该是全谷物和粗杂粮，包括玉米、全麦制品、糙米、燕麦、红薯、马铃薯等。粗粮富含水溶性纤维素，可以增加饱腹感，从而减少每餐食物摄入量。纤维素还能减少对油脂的吸收，利于排便，这都对塑身有益。粗粮属于碱性食物，富含维生素，能降低慢性病风险。\"},{\"section\":\"饮食结构\",\"title\":\"塑身需要果蔬的帮助\",\"content\":\"研究表明，血液中抗氧化剂的含量或日常饮食摄入抗氧化剂量高的人死亡率低。同时也会降低老年痴呆、癌症、心血管疾病、白内障、糖尿病、高血压、关节炎、视力功能退化等问题的发生率。富含抗氧化剂的蔬果，主要有草莓、樱桃、蓝莓、葡萄、番茄、西兰花、胡萝卜、豌豆、玉米等。\"},{\"section\":\"饮食结构\",\"title\":\"选择对塑身无害的肉类\",\"content\":\"如果不能停止对肉食的渴望，可以逐渐减少吃肉的次数和总量，并选择浅色肉食，如鱼类、水生贝壳、去皮鸡鸭肉等，因为它们中的饱和脂肪及胆固醇含量明显低于红肉，这会减少增重的风险。\"}]','合理营养建议'),
	(31,'优美体形','[{\"section\":\"运动让塑身事半功倍\",\"title\":\"希望自己富有活力 ?  适度的运动不仅能塑身，还能令你精神饱满\",\"content\":\"“实际上，使自己更有精神的最好的方式是更积极更有活力的生活方式”，一项对于70多个有关运动效应和精力水平的不同研究的回归性研究发现，90%的研究揭示同一个道理：久坐的人开始锻炼后疲劳感减少，而且更有精神。当然要避免运动过度，那会令你更疲劳。\"},{\"section\":\"运动让塑身事半功倍\",\"title\":\"没时间运动 ?  忙碌的你更需要保持良好状态\",\"content\":\"运动不必有专门的时间和场地，提早一段下车，走几站路去上班；骑行；放弃电梯爬楼梯；每小时站起来走动5分钟。这些都能积累能量消耗。\"}]','有效运动建议'),
	(32,'优美体形','[{\"section\":\"\",\"title\":\"吸烟，要停下来很容易\",\"content\":\"这里并不是在劝你少吸一点儿，事实上，少吸一支半支对你的现状不会有任何改变，不吸烟的人一定比吸烟的人更加轻松享受，并且不会失去良好的健康，充沛的精力，心灵的安宁，勇气，自信，自尊和自由。这是最关键的，因为你没有必要向任何人证明吸烟给你带来了多大的好处。别人能看到的，只是你被这种东西奴役了，这么容易就被征服了，一支接着一支，失去自由，并且没有勇气克服。\"},{\"section\":\"\",\"title\":\"长期有规律的适量饮酒帮助提升机体健康\",\"content\":\"人体可自然分解消化酒精，每天适当饮酒可使动脉血管扩张、血压下降，有利于促进血液循环，更可提升对人体有利的高密度脂蛋白及降低有害的低密度脂蛋白，从而防止心脏病发作。而适量葡萄酒（含有抗氧化物质白藜芦醇）可能降低机体老化速度。\\n适量的饮酒相当于每天一杯150ml红葡萄酒或360ml啤酒。\"},{\"section\":\"\",\"title\":\"常常受到辐射侵扰，需要抗氧化剂抵抗辐射侵袭\",\"content\":\"1.需要有意识地通过饮食或膳食补充剂摄入花青素、维生素C、维生素E、维生素A等抗氧化剂，及时清除代谢产生的自由基。 2.避免长时间连续操作电脑，眼睛与屏幕的距离应在40~50厘米。  3.室内光线要适宜，不可过亮或过暗。  4.安装防护装置，削弱电磁辐射的强度。\"},{\"section\":\"\",\"title\":\"一定要在23点也就是子时到来前睡觉\",\"content\":\"人的一天有两个自然的睡眠周期，这是由人的生物节律决定的，一个是22点左右，这时候人的体温下降，进入能量低潮期，这时候如果关闭灯光、游戏、电视、互联网，便可以很快进入睡眠状态。另外一个自然的睡眠周期是下午1点~4点，同样的也是进入能量低潮期，这时候也会犯困，需要睡眠。\\n所以，正确的睡眠应该遵循身体的生物节律，22~23点入睡，早上6-7点醒来。可以把你工作的时间放在早上，这样不仅有适量的睡眠，也睡在合适的时间。\"},{\"section\":\"\",\"title\":\"不论晚上睡眠如何糟糕，你的生物钟需要一个起点\",\"content\":\"比起控制睡眠时间，控制起床时间更为容易，保证在7：30分左右起床（或者是自己既定的时间，但是不要超过8：00），尽量将其保持在30分钟的浮动范围内，让你的起床时间有规律是改善睡眠不规律的方法，当然这取决于个人的意愿。\"},{\"section\":\"\",\"title\":\"循序渐进延长睡眠时间\",\"content\":\"如果你经常看着电视、看着书进入梦乡，这意味着你缺乏足够的睡眠，补充睡眠并不需要比往常提前1小时上床，这只会令你睡不着觉，每天晚上只要提前10~15分钟，在下午13点~16点之间小睡一下，半个小时为宜，多了则会影响晚间的睡眠，在调整睡眠规律期间清淡饮食。\"},{\"section\":\"\",\"title\":\"梦是你熟睡时心中产生的愿望，你可以学习控制你的梦\",\"content\":\"通过一些训练，学习控制你的梦，享受美好的梦境，缓解压力，帮助你更好的睡眠。\\n根据英国睡眠指导专家研究，有42%的人通过睡前的想象控制自己的梦，多试几次这个比例可以达到更高。其实很简单，在睡前安静的坐上10分钟，放慢呼吸，暗示自己可以在做梦的时候保持清醒，想象一个快乐美好的主题，多试几次。\"},{\"section\":\"\",\"title\":\"提高睡眠质量\",\"content\":\"固定起床时间可以帮助生物钟正常工作，也就是说，无论什么时候开始睡觉一定要在每天的同一时间起床，这样可以在一定程度上保证生物钟的正常运作，也是拥有高质量睡眠的前提条件，也可以通过午睡或者安全状态下的Flash睡眠(瞬时闪睡)来进行一定的睡眠补充，这是最现实的办法。但是决不能根据前一天的睡觉时间决定起床的时间，否则很难改善生物钟紊乱的症状。\"},{\"section\":\"\",\"title\":\"给自己一些时间调整你的睡眠\",\"content\":\"上床以后不要继续思考烦心事，如果睡不着，宁可起来做点事，也不要躺在床上发愁，真正折磨你的不是睡眠不足，而是你的忧愁。无论晚上几点入睡，早上都要在同一时间起床。可以尝试这个睡前的意念调节，平躺，全身放松，集中精神调节呼吸。闭上眼睛，浮想一些简单的图形；然后控制呼吸的节奏，缓慢吸气，保持一秒钟静止状态，缓慢呼气，反复几次。这样可以排除杂念，达到睡眠前的最佳状态！\"}]','生活方式建议'),
	(33,'精力脑力','[{\"section\":\"压力信号盘点\",\"title\":\"精力不佳，需要适当休息\",\"content\":\"精力透支 精力的水平和身体的整体健康有关。长期精力透支是健康的隐形炸弹。你需要更长时间的休息，更有规律的生活习惯，更充足均衡的营养，以保持长久的最佳状态。\"},{\"section\":\"压力信号盘点\",\"title\":\"精力不佳，需要适当休息\",\"content\":\"容易疲劳 除了过度消耗精力外，疲劳也可以因为饮食习惯不佳，缺乏运动或运动过度，缺乏睡眠，闷热季节，或者服用某些处方药物引起。当你获得充分休息后仍然很长一段时间觉得疲劳，最好咨询专业医师。\"},{\"section\":\"压力信号盘点\",\"title\":\"精力不佳，需要适当休息\",\"content\":\"工作拖延 压力越大，越容易拖延，越拖延心理压力就越大，最后的结局就是缺乏雕琢的草草收场，也很难让自己有成就感。\"},{\"section\":\"压力信号盘点\",\"title\":\"精力不佳，需要适当休息\",\"content\":\"过量咖啡、烟、茶 过量食用咖啡、茶、糖或巧克力与患糖尿病的可能性有直接联系。短时间内这些东西可能让你精力充沛，但从长远来看，大量使用刺激物可能让你未老先衰。尝试停用刺激物，并补充营养素（抗氧化剂和必需脂肪酸），多数人在30天内就感到精力有所提升。（源自《营养圣经》）\"},{\"section\":\"压力信号盘点\",\"title\":\"压力会消耗营养素，导致负面情绪\",\"content\":\"压力增加维生素和微量元素的消耗， 存在压力时身体会加大能量消耗，若能量和营养素提供不充足，则导致血糖快速降低，这也是为何焦虑时特别想吃甜食的原因。 血糖低时人容易发怒、紧张、沮丧、疲乏、注意力分散。（源自《营养圣经》）\"},{\"section\":\"压力信号盘点\",\"title\":\"脑力水平下降，需及时调整\",\"content\":\"记忆力、注意力、反应能力会受到年龄、营养水平、睡眠质量、情绪、刺激物等因素的影响。当出现短期的脑力水平下降时，其实是身体在提醒你需要改善生活方式。（源自《压力管理策略》）\"},{\"section\":\"压力信号盘点\",\"title\":\"压力已导致您出现身体问题\",\"content\":\"食欲不佳或亢奋 当人体感受到压力，需要增加能量消耗，身体会分泌一种叫皮质醇的激素，调动体内营养物质转化为血糖，以给细胞提供能量，这就会使食欲增加，尤其对高能量食物更感兴趣（源自《营养圣经》）。压力下也常见消化不良，此时饮食一定要清淡,容易消化。\"},{\"section\":\"压力信号盘点\",\"title\":\"压力已导致您出现身体问题\",\"content\":\"头痛胸闷 压力会引起头痛和胸闷。长期处于紧张生活环境的人心理压力大，当压力无法缓解时表现尤为明显。保证充足的睡眠、规律的饮食、适度的有氧运动都能有缓解头痛的效果。\"},{\"section\":\"压力信号盘点\",\"title\":\"压力已导致您出现身体问题\",\"content\":\"便秘 精神紧张、过度劳累以及不规律的排便习惯都可能引起便秘。长期便秘会影响脾胃的运行，造成大肠的传导失常，产生大量毒素堆积，会继发肠胃不适、口臭、色斑等其他症状。\"},{\"section\":\"体检问题预警\",\"title\":\"压力可通过增加食欲，加速变胖\",\"content\":\"伦敦大学医学院持续19年的研究结果发现压力和肥胖的可能性是成正比的，压力下对高糖、高脂肪食物的需求可以改善情绪，但增加肥胖可能。肥胖使得人们患冠心病、高血压、糖尿病等慢性疾病的风险增加，要改变生活方式；饮食和运动水平合理化；下定决心减肥，给自己时间和耐心，逐渐改变饮食和运动习惯；只有长期的改变才算成功。\"},{\"section\":\"体检问题预警\",\"title\":\"腰围超标意味越过慢病安全警戒线\",\"content\":\"腰围的重要性仅次于体重指数。腰围的大小可以表明脂肪堆积的部位。脂肪聚集在腰上的人更容易患高血压、糖尿病、冠状动脉疾病，因为腹部的脂肪更容易分解并在动脉里聚集。男性腰围超过102厘米或女性超过88厘米，患相关疾病的危险就会增大，尤其是体重指数为25或以上的人群。\"},{\"section\":\"体检问题预警\",\"title\":\"压力令高血压加重\",\"content\":\"压力下体内的儿茶酚胺分泌增多，它们会引起血管的收缩，心脏负荷加重，引发高血压。除了释放压力外，最好的方法是改变生活方式包括饮食习惯以及运动等。世界高血压组织研究表明，如果只是1期的高血压，是可以通过生活方式的改善控制血压的。即便是通过用药，生活方式也不容忽视，限制食盐的摄入，减重，运动，戒烟，限制酒精的摄入：每日饮用超过30毫升酒精,相当于600毫升啤酒,200毫升葡萄酒或75毫升标准威士忌,会使血压升高并使冠心病、中风的发病和死亡率上升。\"},{\"section\":\"体检问题预警\",\"title\":\"精神压力会加重高血脂，增加慢性病风险\",\"content\":\"研究证明，长时间的精神压力可引起血胆固醇升高。另一方面，有些人往往吃许多高脂肪食物来对付压力，这也是血胆固醇升高的重要因素。目前无论在美国还是中国，心脑血管疾病仍然是头号杀手。血脂在动脉血管壁的沉积是毫无声息的，如果体检已经确认血脂超标，就必须在生活方式上加以注意，加强运动，进行低脂肪饮食等，无效的情况下，需要在医生指导下借助药物改善状况。\"},{\"section\":\"体检问题预警\",\"title\":\"高压力会加重血糖紊乱\",\"content\":\"除了饮食、运动等因素，长期紧张的状态也会引起血糖的波动，一是胰腺为了给机体供应能量，而大量释放胰岛素和胰高血糖素。二是压力下对高糖、高脂肪的食物需求增加，也会导致血糖水平紊乱。\\n糖尿病意味着需要比其他人更注意你的生活，摄入具有丰富膳食纤维的蔬果，减少高脂肪食物和零食，控制体重，控制吸烟和饮酒。有规律的运动可以帮助保持身体素质，对心脏和血管都有益处，促进血液循环，并定期监测自己的血糖水平，防止糖尿病并发症发生的风险。\"},{\"section\":\"体检问题预警\",\"title\":\"冠心病增加猝死风险\",\"content\":\"冠心病若养护不当，猝死率很高（统计表明50-60%猝死是冠心病）。吸烟、高血压、高血糖、高血脂、肥胖、久坐等问题都是冠心病发作的危险因素，通过合理的饮食和有规律的运动，可以大大减少患病的风险，甚至可以免除药物的使用。可以配合每天服用抗氧化维生素，如维生素E、维生素C、叶酸等，阻止低密度脂蛋白在血管的积累，预防发病。\"},{\"section\":\"体检问题预警\",\"title\":\"保持骨密度不再降低更为重要\",\"content\":\"先天因素，饮食中钙摄入不足，不常运动，体格瘦弱，吸烟、过量饮酒、女性过早绝经，都是直接导致骨质疏松的危险因素，不过相对于恢复已降低的骨密度，保持骨密度不变要来的更容易些。预防措施包括摄入足够的钙和维生素D和从事负重锻炼。\"},{\"section\":\"体检问题预警\",\"title\":\"即便没有不舒服的症状也要重视脂肪肝\",\"content\":\"脂肪肝是肝细胞内过量的脂肪（甘油三酯）积累，脂肪肝最常见的原因是酗酒、肝炎、肥胖、糖尿病、血清甘油三酯水平过高，因为起初没有任何症状，但可引起肝脏的反复损害，最终可从脂肪肝发展到肝硬化。早期如果在生活方式上加以调整也很快就会看到改善。\"},{\"section\":\"体检问题预警\",\"title\":\"压力引起的内分泌失调是导致乳腺增生的原因之一\",\"content\":\"乳腺增生多发生在25-35岁之间，发生率高达70%~85%，通过触按可以发现，如果没有疼痛的感觉或仅仅是经前期胀痛，往往不会引起重视，导致进一步恶化。婚育、膳食、压力和遗传因素是乳腺发病的主要原因。\\n将体重控制在健康的水平，特别注意心情和压力的调整，因为精神因素引发的内分泌失调、植物神经紊乱，睡不好觉、脾气暴躁，这些都会对乳腺产生不良影响。放松，静坐，舒压按摩都是很好的方法，保证足量有氧运动，每周至少3次。\"},{\"section\":\"体检问题预警\",\"title\":\"胆囊健康和压力互相影响\",\"content\":\"慢性胆囊炎若不及时治愈，会给生活增加很多限制，常常因为消化不良，厌油腻食物、上腹部闷胀、嗳气、胃部灼热等问题而影响品味美食的心情，而且压力和情绪的变化也会对胆囊健康产生影响。\"},{\"section\":\"体检问题预警\",\"title\":\"身心压力大，加重妇科炎症\",\"content\":\"《美国科学院学报》发表的一项研究表明，长期身心压力大，会影响人体的免疫系统；耶鲁大学的一项试验证实，压力增加了对甜食的嗜好；加班久坐会导致盆腔血液循环不畅，这些都会令妇科炎症加重。\\n所以除了遵医嘱用药外，保证阴部环境干爽、减少久坐、穿宽松、透气的裤子，清洁性交、常常运动、放轻松都是预防感染发生的方法，不主张频繁冲洗阴道，这会增加患盆腔炎的风险。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不良的就餐习惯影响精力、脑力\",\"content\":\"常在外就餐 常在外吃，容易摄入过多的油脂和盐，同时也很难保证均衡的营养提供。少油清淡的饮食和充足的维生素和矿物质是维持情绪平稳，精力旺盛的保障。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不良的就餐习惯影响精力、脑力\",\"content\":\"不吃早餐 高压力的状态下，不吃早餐会增加你得胆结石的风险，并且令上午精力难以集中，事倍功半，还会过度依赖咖啡等提神，增加慢性病风险。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不良的就餐习惯影响精力、脑力\",\"content\":\"经常吃得过饱 有压力时，吃撑是一种放松，但是吃撑会令血糖快速变化，刺激胰岛素的分泌，久之易患糖尿病。吃撑时，胃肠过度扩张，久之有损肠胃健康。如果年轻时经常吃得过饱，年老后患骨质疏松和老年痴呆的机会就会明显增加。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，影响精力、脑力\",\"content\":\"爱吃油炸 香脆的油炸食物有短暂的缓解压力的效果，但吃得过多，则有害，增加患心脑血管疾病风险，令人精力不足，还要额外消耗体内的维生素和矿物质，同时增加胃肠负担。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，影响精力、脑力\",\"content\":\"常吃包装或加工食品 对精力脑力状态要求较高时，不应吃太多包装零食和加工食物，这会导致必要营养素不足，甚至消耗体内已有的能对抗压力的维生素和矿物质，还会影响对正餐的食欲，令营养不均衡。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，影响精力、脑力\",\"content\":\"常喝甜饮料 含糖饮料会增加体内维生素B族的消耗，维生素B群是维持正常情绪不可缺乏的营养物质。同时甜饮料也会令血糖不稳，影响情绪。碳酸饮料和糖份高的饮料相同，会促进钙质流失，令免疫力降低。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，影响精力、脑力\",\"content\":\"爱吃重口味食物 喜欢重口味食物说明你需要休息，因为身体在疲劳的状态下才更需要刺激味蕾来增加食欲。西北有“吃盐力气大”的说法，而辣可以刺激口水分泌，令胃口大开，压力越大越想吃辣，尤其是感到郁闷的时候。压力大会令血糖不稳，导致对甜味食物的渴望。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，影响精力、脑力\",\"content\":\"肉食过多 如果“无荤不饭”，过度摄入肉食，会额外消耗体内的维生素和矿物质，从而产生四肢无力、全身酸痛、精神不振等亚健康症状，更重要的是这将削弱您的免疫力，让身体成为慢性病的温床。\"},{\"section\":\"生活习惯提醒\",\"title\":\"健康食物吃得过少，无法保持最佳的精力、脑力\",\"content\":\"豆、奶制品吃得少 很少吃豆、奶制品可能导致你缺乏钙质，奶制品和豆制品被认为是钙的主要来源，钙是维持骨骼、牙齿、神经健康的主要矿物质，缺乏则容易造成失眠、紧张、疼痛、骨质疏松、龋齿等问题。\"},{\"section\":\"生活习惯提醒\",\"title\":\"健康食物吃得过少，无法保持最佳的精力、脑力\",\"content\":\"粗粮吃得少 很少吃粗粮会令纤维素和维生素B族以及矿物质缺乏。纤维素能减少胆固醇和脂肪的吸收，还能维持血糖的平稳，也能帮助粪便成型，排出毒素。维生素B族能协助体内能量代谢，维持稳定情绪，保持精力充沛。各种矿物质也是维护身体健康所必需的。\"},{\"section\":\"生活习惯提醒\",\"title\":\"健康食物吃得过少，无法保持最佳的精力、脑力\",\"content\":\"蔬果吃得少 蔬菜水果吃得太少，你可能缺乏足够的抗氧化剂应对高压力的生活，加速身体衰老。日常生活的污染，辐射，煎炸食物，阳光照射甚至呼吸都会产生令身体老化的自由基。缺乏那些抗氧化物会令身体更快衰老，更加易患心脑血管疾病等慢性病。蔬菜水果也提供了大量的有减少脂肪吸收、平稳血糖、协助排便和清除肠道功能的纤维素。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳有损精力、脑力\",\"content\":\"经常熬夜 经常熬夜，可以被称为慢性睡眠不足，已经有大量的科学研究表明睡眠不足与心脏疾病、高血压、中风、糖尿病有直接关系。\\n如果你倾向于“晚睡晚起”的睡眠形态，一旦失眠，将会表现出比一般人更加严重的认知缺损，如健忘、不能集中精力等。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳有损精力、脑力\",\"content\":\"睡眠不足 每个人需要的睡眠时间长短并不一样，如果每天醒来你不需要闹钟，不需要冷水，不需要咖啡就能让自己充满能量地迎接新的一天，这说明你的睡眠效率很高，总之自己的感觉是最直接、最重要的。\\n但如果因为睡不够觉得困倦则说明睡眠效率较低。睡眠不足不但产生健康问题，还会影响注意力、处事能力、记忆力和心情。如果只睡很短时间，就会提高血压和心率的平均水平，由此可能会增大心血管系统的压力。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳有损精力、脑力\",\"content\":\"多梦 梦多是在快速动眼睡眠中出现的，这段时间人的体温上升，心跳增加，血流速快，区别于入睡时的生理状态（体温下降），这类似于人们醒着时候的生理状态，人们也更容易在这段时间内醒过来。这种状况出现的原因很多，压力大、精神紧张、焦虑都有可能成为多梦易醒的原因。而长期的睡眠质量不佳也会导致不良情绪，影响精力脑力。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳有损精力、脑力\",\"content\":\"起床困难或睡不够 喜欢赖床或睡后精力不能补足。可能是繁重的脑力或体力劳动造成极度疲劳，使得需要更长时间的睡眠来恢复。也可能不规律的生活习惯造成了生物钟紊乱，无法控制睡眠和觉醒的正常切换。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳有损精力、脑力\",\"content\":\"失眠 入睡困难或者失眠的人往往表现为：很难睡着、晚上总是醒、睡得不踏实，很难再入睡。原因很多，压力、咖啡因、酒精、环境变化、时差、慢性疾病、焦虑等等。失眠者的压力激素含量比一般人高，因此身体处在高度活跃状态，不容易轻松入睡。失眠导致更大压力，甚至形成严重后果。解决问题首先要找到原因，是不是有些不良的生活习惯，导致这个问题，不过，有时候，这可能是自己无法控制的，但是要明确一点，这并不是不可以解决的问题，所以不用担心，给自己一点儿耐心。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不规律的睡眠扰乱循环系统生物钟，身体隐形“生病”\",\"content\":\"睡眠不规律 其实除了睡眠，我们心率、呼吸、血压、体温、蛋白质合成等体内一切生命活动，往往都呈现昼夜节律的倾向，反映在血液循环、内分泌和各种器官功能中。不规律的睡眠就好比在不停地倒时差，各器官的生物钟调整所需的时间不同，睡眠两三天就可以调整过来，但是体内的器官及循环系统的生物钟却需要两三周的时间调整。\"},{\"section\":\"生活习惯提醒\",\"title\":\"用运动释放您的压力\",\"content\":\"多做运动，这样不仅能消耗过多会带来压力感的肾上腺素，同时还能够有效地培养我们身体和精神上的毅力。深呼吸能够给大脑提供更多的氧气，而氧气是大脑最好的补品。\"},{\"section\":\"生活习惯提醒\",\"title\":\"烟草会强化您的压力和焦虑\",\"content\":\"烟草中的尼古丁很快就可以到达大脑，令人有一种轻松愉快的感觉。可是，由于尼古丁是成瘾性物质，在逐渐的重复使用中，机体对尼古丁产生了耐受性，即必须吸入更多的尼古丁以达到和以前同样的舒适感，形成烟瘾。\\n最初的压力和不适也许来自生活和情绪，吸烟后似乎缓解了。但形成烟瘾之后，不适和焦虑感则来自尼古丁水平的下降，不吸烟会更加难受。这个时候，烟草的作用，变成了负性强化，不得不用了。\"},{\"section\":\"生活习惯提醒\",\"title\":\"过量饮酒影响精力、脑力\",\"content\":\"也许饮酒是释放压力的渠道，但连续5年以上平均每天摄入酒精超过40克者，48％的人会患有不同程度的酒精性肝病。过量的酒精会使肝脏形成瘢痕组织，称为肝硬化。还会对大脑和神经系统造成多种损伤，严重损伤肝脏、脾脏和心血管系统。\"},{\"section\":\"生活习惯提醒\",\"title\":\"辐射产生的自由基加速人体细胞氧化\",\"content\":\"手机和电脑产生的低频电磁辐射是人体外源性自由基产生的原因之一。随着辐射剂量累积增加，可能会引起神经系统、循环系统、免疫系统、生殖系统以及皮肤和眼睛的功能、代谢或形态的改变，这是一个自由基生成加速身体细胞氧化的过程。\"}]','精力脑力评估'),
	(34,'精力脑力','[{\"section\":\"饮食习惯\",\"title\":\"压力状态下，多选择蒸煮炖食物\",\"content\":\"蒸、煮、炖的烹饪方式相对于煎炸能保留更多的营养，当体内营养均衡时，精力和体力能够保持最佳的自然状态，很少对刺激提神的咖啡，烟酒产生依赖。\"},{\"section\":\"饮食习惯\",\"title\":\"压力状态下，多选择蒸煮炖食物\",\"content\":\"这种烹饪方式令食物口感更细软，容易被消化吸收，减少压力下的肠胃消化负担。\"},{\"section\":\"饮食习惯\",\"title\":\"容易消化的早餐令你上午精力充沛，情绪稳定\",\"content\":\"越是繁忙，早餐越重要。选择容易消化的碳水化合物和水果为主的早餐。全麦面包，坚果，豆浆，或一个水果，这种组合会令能量平稳的释放，而且对消化系统压力最小。早起30分钟，给早餐预留时间，匆忙不利于消化。\"},{\"section\":\"饮食习惯\",\"title\":\"餐前吃些水果，有助于情绪稳定，精力持久\",\"content\":\"草莓、樱桃、桑椹、蓝莓、苹果、梨，这类水果可以缓释能量，保持血糖平稳。保持血糖平稳是你保持精力持久、情绪稳定和维持正常食欲最重要的因素。餐前少吃些这类水果可以增加饱腹感，调节血糖，避免正餐过饱。\"},{\"section\":\"饮食习惯\",\"title\":\"天然零食，缓解压力的方便法门\",\"content\":\"压力下吃些零食有助于舒缓情绪，挑选健康零食一举两得。时令水果、酸奶、原味坚果、天然果干，这些都可以作为零食，既能满足吃零食的习惯，也能给身体提供必要营养。\"},{\"section\":\"饮食习惯\",\"title\":\"天然零食，缓解压力的方便法门\",\"content\":\"当食欲大增时，不要和大脑作对，但是要选对食物。对甜品的渴望可以由水果或葡萄干、菠萝干、黑巧克力等代替。\\n食欲不佳时，可以用味道清新的芹菜、柠檬、薄荷、橙子等制成饮料，提神醒脑，补充维生素。\"},{\"section\":\"饮食习惯\",\"title\":\"口味清淡，头脑清爽\",\"content\":\"常喝白水或矿泉水。人体的70%是水分，确保体内有足够洁净的水分，才能将代谢产生的废物及时排出体外。尤其是体型偏瘦的人，更需要有效水份来滋润你的细胞。白水、矿泉水、鲜榨的蔬果汁、口味清淡的菜汤都可以是补充水分的来源。\"},{\"section\":\"饮食习惯\",\"title\":\"口味清淡，头脑清爽\",\"content\":\"逐渐让口味变淡，不仅有益于健康，低油、盐糖的饮食在避免增加血液粘稠的同时也能令头脑更清爽。你可以用水洗涮菜肴的酱汁。少吃红烧，糖醋，水煮，回锅等浓油赤酱的菜式。当然，及时让自己减压，才能令粗茶淡饭也香甜。\"},{\"section\":\"饮食习惯\",\"title\":\"多吃天然食物，尽量少吃包装加工食物\",\"content\":\"尽量选择新鲜的当地、当季产新鲜食物，它们除了能提供必需的营养素还能帮助代谢掉人体中产生有的有害物质，如促使老化的自由基。\\n加工食品中存在多种人工合成的化学物质，已有许多国家研究表明加工食品中的各种添加剂摄入过多，会存在致癌风险。\"},{\"section\":\"饮食习惯\",\"title\":\"细嚼慢咽，七分饱\",\"content\":\"研究表明，吃饭过快会给精神带来较大负担。多咀嚼可加快心脏运动，使脑部荷尔蒙分泌增多，从而思维能力和记忆力也随之提高。当然最重要的是能够避免吃撑。日本学者统计约有30%--40%的老年性痴呆病人，青壮年时期有饱食的习惯。因摄入的总热量远远超过机体的需要，致使体内脂肪过剩、血脂增高，导致脑动脉粥样硬化，有一种被称为“纤维芽细胞生长因子”的物质在大脑中明显增多，这种物质被认为是引起脑动脉硬化的重要因素。\"},{\"section\":\"饮食结构\",\"title\":\"充足钙质，稳定情绪\",\"content\":\"牛奶和豆类食物是钙的优质来源，您还可以选择发菜、紫菜、荠菜、木耳等，或者适量芝麻、花生、榛子等坚果，虾米也是很不错的选择，它们都是很好的钙质来源。充足钙质是健康的神经系统必需的。\"},{\"section\":\"饮食结构\",\"title\":\"粗杂粮稳定你的情绪，提高你的精力脑力\",\"content\":\"最新的美国营养学研究建议，主食中的一半应该是全谷物和粗杂粮，包括玉米、全麦制品、糙米、燕麦等。粗粮富含水溶性纤维素，可以改善肠道健康，维持血糖平稳释放，有利于精力体力的保持。\"},{\"section\":\"饮食结构\",\"title\":\"富含抗氧化剂的食物提供充沛精力脑力\",\"content\":\"颜色丰富，时令新鲜的果蔬，未经深加工的粗粮谷豆，坚果和种子会提供身体必需的全面营养。研究表明血液中营养素水平越低，智力表现就越差。摄入最佳水平的维生素和矿物质，可以思考的更快，精神集中的时间可以更长。\"},{\"section\":\"饮食结构\",\"title\":\"富含抗氧化剂的食物提供充沛精力脑力\",\"content\":\"需要更多的精力？你可以将多种色彩的时令蔬果搭配组合，其中含有的抗氧化剂可以协同作用，清除体内自由基，改善疲劳，精力不佳。比如：草莓、桑椹、樱桃、杏子、葡萄、番茄、玉米、胡萝卜、西兰花、红薯、豌豆等。\"},{\"section\":\"饮食结构\",\"title\":\"白肉多于红肉\",\"content\":\"压力越大对脂肪含量高的食物越渴望，但富含饱和脂肪的红肉往往更难消化，在满足口腹之欲后，就可能面对消化不良。而贝类，鱼类，去皮的鸡鸭肉相对胆固醇含量低，更容易被消化。\"},{\"section\":\"饮食结构\",\"title\":\"压力下，最好避免刺激物\",\"content\":\"咖啡、可乐、糖和精制食物，这些都是消耗身体中营养素的食物，只能一时提神。血液中营养素水平越低，智力表现就越差。保持体内有最佳水平的维生素和矿物质，可以思考的更快，精神集中的时间可以更长。\"},{\"section\":\"饮食结构\",\"title\":\"压力下，最好避免刺激物\",\"content\":\"咖啡因确有提神效果，不过这种提神效果因人而异，而且也只能是暂时性的，大量饮用咖啡会造成代谢速度加快，消耗体内与神经、肌肉协调有关的维生素B群，缺乏维生素B群人本来就容易累，更可能形成恶性循环，使人饮用成瘾，越饮越多。\"},{\"section\":\"饮食结构\",\"title\":\"你需要有益头脑的好脂肪\",\"content\":\"食用优质鱼油，或种子油，如：南瓜籽，芝麻，葵花籽，亚麻籽，红花，玉米，核桃，大豆和麦胚油等。这些可以提供必需的不饱和脂肪酸，满足灵活头脑和旺盛精力的需要。\"},{\"section\":\"饮食结构\",\"title\":\"你需要有益头脑的好脂肪\",\"content\":\"“许多研究表明，摄入最佳的必需脂肪，特别是Omega-3脂肪，可以改善情绪，提高智力”（出自《营养圣经》）。因为Omega-3脂肪能促进有情绪稳定作用的五羟色胺的“分泌”。亚麻籽、南瓜籽、三文鱼、鲭鱼、鲱鱼、金枪鱼或者它们榨取的油都可以提供Omega-3脂肪。\"}]','合理营养建议'),
	(35,'精力脑力','[{\"section\":\"运动缓解压力问题\",\"title\":\"压力时头痛或胸闷 ?  舒缓运动和按摩改善紧张性头痛\",\"content\":\"过量的运动会提高紧张激素皮质醇的水平，所以不要在过度紧张的时候运动太多。心理体操，瑜伽，太极，按摩、散步半个小时或是静坐，都有助于恢复紧张激素的水平。\"},{\"section\":\"运动缓解压力问题\",\"title\":\"情绪低落 ?  有氧运动，让心情HIGH起来\",\"content\":\"经常做些有氧运动可以提高情绪，在心血管耐力型运动中，如：跑步、散步、游泳，能够由大脑和其他部位释放β-内啡肽（天然镇定剂）使人感到心情愉快。\"},{\"section\":\"运动缓解压力问题\",\"title\":\"焦虑、易发火 ?  运动可以降低对压力和紧张的敏感度\",\"content\":\"适度的运动可以对心理产生影响，包括能够降低沮丧和焦虑的感觉。那些让你觉得活力充沛但又不太疲劳的运动最为适宜，瑜伽、慢跑、快走、游泳等。\"},{\"section\":\"运动缓解压力问题\",\"title\":\"懒惰、杂乱 ?  适度运动，提神醒脑\",\"content\":\"“实际上，使自己更有精神的最好的方式是更积极更有活力的生活方式”，一项对于70多个有关运动效应和精力水平的不同研究的回归性研究发现，90%的研究揭示同一个道理：久坐的人开始锻炼后疲劳感减少，而且更有精神。当然要避免运动过度，那会令你更疲劳。\"}]','有效运动建议'),
	(36,'精力脑力','[{\"section\":\"\",\"title\":\"早起30分钟，给生物钟一个起点\",\"content\":\"比起控制睡眠时间，控制起床时间更为容易，早起至少30分钟，是令你充满活力的开始，这可以让你的早晨更从容，顺手清理一下居室，整洁的环境也会令心情更好。让你的起床时间有规律是改善睡眠不规律的方法，当然这取决于个人的意愿。\"},{\"section\":\"\",\"title\":\"正视焦虑\",\"content\":\"正视焦虑将减少恐惧和不安，焦虑往往源自对未知的消极看法，理性分析有助于认清形势，减轻压力。\"},{\"section\":\"\",\"title\":\"深呼吸，扩展安稳的边界\",\"content\":\"胆小和敏感也是身体的情绪。每天早起，练习深呼吸，深呼吸不仅能促进人体与外界的氧气交换，还能使人心跳减缓，血压降低。它能转移人在压抑环境中的注意力，并提高自我意识。可以通过深呼吸保持镇静，缓解焦虑情绪。\"},{\"section\":\"\",\"title\":\"深呼吸，扩展安稳的边界\",\"content\":\"常常练习深呼吸，放松身体的每一块肌肉，就能节省很多营养补充剂。保守估计，处于压力状态下，你需要的维生素是平时的两倍。\"},{\"section\":\"\",\"title\":\"压力下保持健康，从一个微笑开始\",\"content\":\"美国《今日心理学》杂志结合多项科学研究指出，看喜剧、听笑话时最自然、最舒畅的“傻笑”，对健康最有益。而且令人惊讶的是，在绽放笑容之前，人们对欢笑的期待也会收到奇迹般的效果。喜剧开幕前，研究人员抽取了观众的血样，结果发现其内啡肽的含量已经增加了27%，生长激素的含量增加了87%。内啡肽有助于增强免疫系统的功能，生长激素对肌肉、骨骼和内脏有益。\"},{\"section\":\"\",\"title\":\"提高睡眠质量\",\"content\":\"研究表明，睡眠可以降低体内压力激素的分泌。每当感到压力大的时候，即使打个小盹，也能让你迅速释放压力，提高工作效率。\"},{\"section\":\"\",\"title\":\"提高睡眠质量\",\"content\":\"睡得好，能让你更聪明。在睡眠状态下，脑细胞能量得到贮存，大脑耗氧量开始减少。醒后人的大脑思路开阔，思维敏捷，记忆力增强。\"},{\"section\":\"\",\"title\":\"提高睡眠质量\",\"content\":\"固定起床时间可以帮助生物钟正常工作，也就是说，无论什么时候开始睡觉一定要在每天的同一时间起床，这样可以在一定程度上保证生物钟的正常运作，也是拥有高质量睡眠的前提条件，也可以通过午睡或者安全状态下的Flash睡眠(瞬时闪睡)来进行一定的睡眠补充，这是最现实的办法。但是决不能根据前一天的睡觉时间决定起床的时间，否则很难改善生物钟紊乱的症状。\"},{\"section\":\"\",\"title\":\"提高睡眠质量\",\"content\":\"除了正常的睡眠（23点--7点），可以增加30分钟的午睡，经常性的闭目养神，3分钟左右的打盹等。利用好短时间的休息，同样可以补充精力。\"},{\"section\":\"\",\"title\":\"吸烟，要停下来很容易\",\"content\":\"你可能尝试戒烟，并体会过戒烟带来的各种不适反应。但是应该记得的是，这些不适反应只不过是些可以改善的症状，远比吸烟对内脏、心脑血管的直接损伤来的微小的多。\"},{\"section\":\"\",\"title\":\"长期有规律的适量饮酒帮助提升机体健康\",\"content\":\"人体可自然分解消化酒精，每天适当饮酒可使动脉血管扩张、血压下降，有利于促进血液循环，更可提升对人体有利的高密度脂蛋白及降低有害的低密度脂蛋白，从而防止心脏病发作。而适量葡萄酒（含有抗氧化物质白藜芦醇）可能降低机体老化速度。\\n适量的饮酒相当于每天一杯150ml红葡萄酒或360ml啤酒。\"},{\"section\":\"\",\"title\":\"常常受到辐射侵扰，需要抗氧化剂抵抗辐射侵袭\",\"content\":\"1.需要有意识地通过饮食或膳食补充剂摄入花青素、维生素C、维生素E、维生素A等抗氧化剂，及时清除代谢产生的自由基。 2.避免长时间连续操作电脑，眼睛与屏幕的距离应在40~50厘米。  3.室内光线要适宜，不可过亮或过暗。  4.安装防护装置，削弱电磁辐射的强度。\"},{\"section\":\"\",\"title\":\"一定要在23点也就是子时到来前睡觉\",\"content\":\"人的一天有两个自然的睡眠周期，这是由人的生物节律决定的，一个是22点左右，这时候人的体温下降，进入能量低潮期，这时候如果关闭灯光、游戏、电视、互联网，便可以很快进入睡眠状态。另外一个自然的睡眠周期是下午1点~4点，同样的也是进入能量低潮期，这时候也会犯困，需要睡眠。\\n所以，正确的睡眠应该遵循身体的生物节律，22~23点入睡，早上6-7点醒来。可以把你工作的时间放在早上，这样不仅有适量的睡眠，也睡在合适的时间。\"},{\"section\":\"\",\"title\":\"循序渐进延长睡眠时间\",\"content\":\"如果你经常看着电视、看着书进入梦乡，这意味着你缺乏足够的睡眠，补充睡眠并不需要比往常提前1小时上床，这只会令你睡不着觉，每天晚上只要提前10~15分钟，在下午13点~16点之间小睡一下，半个小时为宜，多了则会影响晚间的睡眠，在调整睡眠规律期间清淡饮食。\"},{\"section\":\"\",\"title\":\"梦是你熟睡时心中产生的愿望，你可以学习控制你的梦\",\"content\":\"通过一些训练，学习控制你的梦，享受美好的梦境，缓解压力，帮助你更好的睡眠。\\n根据英国睡眠指导专家研究，有42%的人通过睡前的想象控制自己的梦，多试几次这个比例可以达到更高。其实很简单，在睡前安静的坐上10分钟，放慢呼吸，暗示自己可以在做梦的时候保持清醒，想象一个快乐美好的主题，多试几次。\"},{\"section\":\"\",\"title\":\"给自己一些时间调整你的睡眠\",\"content\":\"上床以后不要继续思考烦心事，如果睡不着，宁可起来做点事，也不要躺在床上发愁，真正折磨你的不是睡眠不足，而是你的忧愁。无论晚上几点入睡，早上都要在同一时间起床。可以尝试这个睡前的意念调节，平躺，全身放松，集中精神调节呼吸。闭上眼睛，浮想一些简单的图形；然后控制呼吸的节奏，缓慢吸气，保持一秒钟静止状态，缓慢呼气，反复几次。这样可以排除杂念，达到睡眠前的最佳状态！\"},{\"section\":\"\",\"title\":\"分解任务，立即行动\",\"content\":\"把事情分解成小任务，先行动起来。心理学研究发现，行动引发动机，动机又激发更多的行动。因此不要等到你有动力了准备好了才开始做事儿，当你去做了，积极性就会上来，愿意做的事情也更多了。\"},{\"section\":\"\",\"title\":\"张弛有度，劳逸结合更能保持激情\",\"content\":\"长期的亢奋，有激情的生活虽然令人振奋，有成就感，但长期如此，对身体的消耗过度，容易导致疲劳过度引起各种虚弱不适：气短、心慌、失眠等植物性神经紊乱问题。\\n保证有充足的优质睡眠、适度的放松运动，常常让自己慢下来，全身心的放松，才能保证更持久的高效率。\"},{\"section\":\"\",\"title\":\"成为真正的自己\",\"content\":\"减轻压力的捷径，首先最重要的是要有一种良好的自我感觉，要去做自己真正想做的事，要为自己的理想而生活。在寻找自己真正想做的事的过程中，反省自己是非常重要的。为此，每天一定要抽出一点时间让自己的心安静下来，这种放松是很必要的。\"},{\"section\":\"\",\"title\":\"冥想或禅定有助于改善免疫力\",\"content\":\"医学研究指出，当人禅坐进入入定状态时所出现的生理效应，如呼吸减缓、交感神经系统功能下降、副交感神经系统功能相对增强、消化系统功能亢进、耗能减缓而储能增大等，有利体内能量的充盈。禅定可以调节免疫系统，同时为神经、内分泌系统带来正面影响。\"}]','生活方式建议'),
	(37,'免疫力方面','[{\"section\":\"生活习惯影响免疫力\",\"title\":\"不健康食物吃得多，削弱免疫力\",\"content\":\"常喝甜饮料 体内糖过多会抑制免疫系统。研究表明在摄入75-100g糖之后的几个小时内，白细胞的抗菌功能会持续地受到抑制（来自WebMD）。每100ml可乐含11g糖，相当于只要喝2罐350ml的可乐就足以降低免疫力了，茶或果汁类饮料含糖量也并不低于可乐。\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"不健康食物吃得多，削弱免疫力\",\"content\":\"爱吃油炸 常吃油炸食物对免疫系统不利，食物在油炸的过程中会产生致癌物（丙烯酰胺 ）和自由基，这增加了免疫系统负担，清除这类致癌物是免疫细胞的基本工作之一，过量的自由基会抑制免疫功能。油炸过程会破坏食物中部分的必需脂肪酸、维生素等免疫系统必需的营养素， 这也会导致免疫力受到影响。\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"不健康食物吃得多，削弱免疫力\",\"content\":\"爱吃重口味食物 偏爱重口味食物会增加消化系统发生炎症的风险，高盐或辣味食物会刺激胃粘膜，日久则容易形成溃疡等问题，影响消化吸收，会因营养不足而降低免疫系统功能。\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"不健康食物吃得多，削弱免疫力\",\"content\":\"常吃包装或加工食品 经过加工的包装食物吃得过多会降低免疫力。这些食物中的添加剂不仅会阻止营养素的吸收和利用，还会加速营养素的流失，并增加肝脏的代谢负担。肝脏是一个重要的免疫器官，负责吞噬和清除血液中和肠道吸收来的毒物、细菌、残留农药的解毒等。\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"不健康食物吃得多，削弱免疫力\",\"content\":\"肉食过多 过度摄入肉食会削弱免疫力。 研究表明，若饮食中饱和脂肪含量高，会阻塞淋巴管而抑制免疫系统（来自WebMD）。\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"不良的就餐习惯削弱免疫力\",\"content\":\"常在外就餐 经常外食可能会影响免疫功能。餐馆食物多油盐调料、少新鲜果蔬，很难保证营养均衡充足，而健康的免疫系统需要维生素A\\\\B\\\\C\\\\E，叶酸、铁、锌、镁、钾和充足的抗氧化剂，若长期在外就餐就需要额外补充复合维生素同时尽量多选择营养损失较小的烹饪方法（凉拌、蒸、少油炒）。\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"不良的就餐习惯削弱免疫力\",\"content\":\"不吃早餐 经常不吃早餐有可能令免疫力下降。因为没有供能早餐，身体会分解蛋白质作为能量来源，久之容易引起蛋白质不足，令免疫力降低。长期不吃早餐也是引发胃肠消化问题的原因，可能导致身体缺乏免疫系统必需的营养而降低免疫力。\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"不良的就餐习惯削弱免疫力\",\"content\":\"经常吃得过饱 吃撑会影响免疫力。因为过剩的食物残渣，特别是高脂肪食物在肠内利于大量的厌氧菌生长及产生其它毒物，这会增加肝脏解毒负担，增加患胃肠炎症风险，也会破坏肠道菌群平衡，胃肠中存在的益生菌群会因此受到破坏，益生菌能帮助改善免疫系统健康，减少过敏、炎症。\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"健康食物吃得过少，无法保持免疫系统的最佳状态\",\"content\":\"蔬果吃得少 蔬菜水果吃的少会影响免疫系统健康水平。为了抵抗感染，身体需要大量的抗氧化物质，特别是维生素C。而天然的抗氧化物，如维生素C的主要来源就是新鲜的多彩蔬果。\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"健康食物吃得过少，无法保持免疫系统的最佳状态\",\"content\":\"豆、奶制品吃得少 很少吃豆、奶制品可能导致你缺乏钙质，奶制品和豆制品被认为是钙的主要来源，钙是维持骨骼、牙齿、神经健康的主要矿物质，缺乏则容易造成失眠、紧张、骨质疏松、龋齿等问题。虽然钙并未被证明和免疫力有直接的联系，但是优质睡眠和轻松的情绪对提高免疫力是有帮助的。\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"质量不佳的睡眠削弱免疫功能\",\"content\":\"起床困难或睡不够 早晨睡不醒或醒后精力不能补足的疲劳状态下，免疫力会降低。可能是繁重的脑力或体力劳动造成极度疲劳，使得需要更长时间的睡眠来恢复。也可能是不规律的生活习惯造成了生物钟紊乱，无法控制睡眠和觉醒的正常切换。\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"质量不佳的睡眠削弱免疫功能\",\"content\":\"经常熬夜 经常熬夜、超过24点不睡，可以被称为慢性睡眠不足，长时间熬夜会引起神经和内分泌调节紊乱，从而导致机体免疫力下降。日久还会引起各类健康问题，已经有大量的科学研究表明睡眠不足与心脏疾病、高血压、中风、糖尿病有直接关系。\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"质量不佳的睡眠削弱免疫功能\",\"content\":\"睡眠不足 美国《内科学文献》提到，每天睡眠不足７小时会降低人体免疫能力。芝加哥大学的研究人员发现注射流感疫苗后，相对于每天睡7-8小时的人，每天只睡4小时的人，血液里的流感抗体只有前者的50%。这反映了睡眠不足时，免疫系统产生抗体的能力大大降低。（来自WebMD）\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"质量不佳的睡眠削弱免疫功能\",\"content\":\"失眠 经常失眠的人更要注重保护免疫力，失眠引起的免疫力下降会导致身体整体健康水平下降。导致失眠的原因可能有：压力、咖啡因、酒精、环境变化、时差、慢性疾病、焦虑等等。调整自身的生活习惯为第一位。这可能是自己无法控制的，但是要明确一点，这并不是不可以解决的问题，所以不用担心，给自己一点儿耐心。\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"质量不佳的睡眠削弱免疫功能\",\"content\":\"多梦 多梦和易醒出现的原因很多，压力大、精神紧张、焦虑都有可能成为多梦易醒的原因。 多梦和易醒会降低您的睡眠质量，这会影响免疫系统的功能，虽然还没有明确的研究指出高质量的睡眠是如何提升免疫系统的，但是保证7-9小时的优质睡眠对健康的免疫力是必要的。\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"不规律的睡眠扰乱生物钟，影响免疫功能\",\"content\":\"睡眠不规律 免疫系统和血液循环、内分泌系统一样受到昼夜节律的影响，不规律的睡眠（上夜班、跨时区旅行）会令身体的各系统功能降低。美国弗吉尼亚大学教授吉恩·布洛克在一项实验中意外发现，生理时钟错乱的老鼠比较早死，研究人员据此认为，工作需要轮班导致日夜颠倒，或者经常搭飞机跨越不同时区，可能会对身体健康带来永久性的负面影响。该研究在《现代生物学》期刊发表。\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"缺乏运动会降低免疫力\",\"content\":\"和有适度运动量的人相比，缺乏运动会令你无精打采，甚至免疫力降低。缺乏运动会直接影响免疫系统健康，你会更容易感冒或感染。缺乏运动还会影响睡眠,令你更容易患糖尿病，这些都增加了疾病风险。（来自WebMD）\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"辐射产生的自由基加速人体细胞氧化\",\"content\":\"长时间的手机和电脑会对人体产生低频电磁辐射影响。辐射对人体的危害，最主要就是产生大量自由基，自由基会攻击和破坏细胞内各种执行正常功能的生物分子，从而造成包括免疫系统在内的人体组织损伤。\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"吸烟破坏肺组织，降低免疫力\",\"content\":\"吸烟会破坏肺组织，影响肺部对病菌的防御；同时烟雾会削弱鼻毛阻挡病菌的能力。（美国疾病预防控制中心）这会增加感冒、哮喘和肺部罹患癌症的风险。 虽然日常补充果蔬或绿茶等抗氧化物质含量高的食物有助于减少吸烟带来的危害，但戒烟才是解决问题的根本之道。\"},{\"section\":\"生活习惯影响免疫力\",\"title\":\"过量饮酒影响机体的抗菌能力\",\"content\":\"英国《BMC免疫学》杂志最新的研究表明，一场大醉后，身体的免疫力至少在24小时内难以恢复至正常水平。此外，酒精促使身体脱水，影响鼻子和喉咙排出细菌的能力。\"},{\"section\":\"免疫力相关疾病/问题\",\"title\":\"冠心病增加猝死风险\",\"content\":\"冠心病若养护不当，猝死率很高（统计表明50-60%猝死是冠心病）。吸烟、高血压、高血糖、高血脂、肥胖、久坐等问题都是冠心病发作的危险因素，通过合理的饮食和有规律的运动，可以大大减少患病的风险，甚至可以免除药物的使用。可以配合每天服用抗氧化维生素，如维生素E、维生素C、叶酸等，阻止低密度脂蛋白在血管的积累，预防发病。\"},{\"section\":\"免疫力相关疾病/问题\",\"title\":\"骨质疏松可能和伴随衰老发生的免疫力降低有关\",\"content\":\"骨骼密度在30岁左右到达高峰后开始流失，这个过程同时受到先天因素、钙摄入不足、缺乏运动、吸烟、过量饮酒、使用激素类药物、女性过早绝经等因素的影响。同时最新的科学研究发现，正常衰老引起的骨质疏松是免疫细胞和骨髓共同作用所保持的骨钙平衡被破坏的结果，通常这也意味着免疫系统也随之变弱。不过相对于恢复已降低的骨密度，保持骨密度不变要来的更容易些。预防措施包括摄入足够的钙和维生素D和从事负重锻炼。\"},{\"section\":\"免疫力相关疾病/问题\",\"title\":\"脂肪肝影响免疫器官肝脏的健康\",\"content\":\"脂肪肝是肝细胞内过量的脂肪（甘油三酯）积累，脂肪肝最常见的原因是酗酒、肝炎、肥胖、糖尿病、血清甘油三酯水平过高，因为起初没有任何症状，但可引起肝脏的反复损害，最终可从脂肪肝发展到肝硬化。肝脏是体内强大的免疫器官，若功能受损，则会影响免疫功能。\"},{\"section\":\"免疫力相关疾病/问题\",\"title\":\"乳腺增生会引起免疫力下降\",\"content\":\"乳腺增生患者的正常生活规律可能因为乳房疼痛及情绪波动等的原因而被扰乱，若长期如此机体的免疫功能会被削弱。\\n乳腺增生多发生在25-35岁之间，发生率高达70%~85%，通过触按可以发现，如果没有疼痛的感觉或仅仅是经前期胀痛，往往不会引起重视，导致进一步恶化。婚育、膳食、压力和遗传因素是乳腺发病的主要原因。\\n将体重控制在健康的水平，特别注意心情和压力的调整，因为精神因素引发的内分泌失调、植物神经紊乱，睡不好觉、脾气暴躁，这些都会对乳腺产生不良影响。放松，静坐，舒压按摩都是很好的方法，保证足量有氧运动，每周至少3次。\"},{\"section\":\"免疫力相关疾病/问题\",\"title\":\"免疫系统反应过激和失调导致的疾病\",\"content\":\"鼻炎 鼻炎由特定因素引起的免疫应激，如冷空气、花粉、异味等，改善鼻炎除了避免诱发因素外，重点是改善呼吸系统的健康，增加免疫力。刺激的物质，如汽车废气、香烟、有烟雾的环境、灰尘和花粉等会引起鼻炎的发作，同时过多的酒精、油炸食物、或压力，缺乏足够的睡眠，吃得太多会削弱免疫系统，加重鼻炎症状。\"},{\"section\":\"免疫力相关疾病/问题\",\"title\":\"免疫系统反应过激和失调导致的疾病\",\"content\":\"哮喘 哮喘是呼吸系统的炎症反应，和免疫系统反应过激有关。潜在的过敏原、过大的压力或者环境条件的改变如天气变化都可能引起疾病的发作。通过增加饮食中的抗氧化营养和必需脂肪酸有助于改善哮喘的问题。（具体参见营养建议部分）\"},{\"section\":\"免疫力相关疾病/问题\",\"title\":\"免疫系统反应过激和失调导致的疾病\",\"content\":\"过敏 过敏是免疫系统敌友不分导致的，也是免疫系统不健康的表现。除了遵医嘱和避免接触过敏源外，补充必需脂肪酸，保证消化系统健康，还有增加体内的益生菌都有助于改善过敏。(具体参考饮食建议部分)\"},{\"section\":\"免疫力相关疾病/问题\",\"title\":\"免疫系统反应过激和失调导致的疾病\",\"content\":\"荨麻疹 免疫的防御功能紊乱时，可以导致机体出现“变态反应”，即通常所说的“过敏”，荨麻疹是其中之一。可能受到温度变化，饮食添加剂，特定食物，药物等因素的影响而发生。除了避开过敏源之外，调整生活习惯和饮食结构可以改善症状。\"},{\"section\":\"免疫力相关疾病/问题\",\"title\":\"免疫系统反应过激和失调导致的疾病\",\"content\":\"类风湿关节炎 类风湿关节炎属于难以根治的自身免疫疾病，防治重点在于减少痛苦并减缓关节破坏，同时要降低药物对身体的损害。减少甚至断绝烟酒、饱和脂肪、高糖；适度的运动；保持健康体重；补充改善炎症反应的食物，如富含抗氧化剂的彩色果蔬和深海鱼油；适当的钙质对保持骨关节健康也是必要的。（具体参见营养建议部分）\"},{\"section\":\"免疫力相关疾病/问题\",\"title\":\"慢性妇科炎症提示免疫力下降\",\"content\":\"妇科炎症通常由感染或正常阴道细菌的改变引起，而状态较好的免疫系统会更快的消除炎症。任何引起阴道酸度下降（PH上升）的因素都会导致阴道菌群失衡而增加患阴道感染的可能性。月经前、月经期及妊娠期的阴道酸度都会随着体内雌激素水平的变化而下降。\\n所以除了遵医嘱用药外，保证阴部环境干爽、减少久坐、穿宽松、透气的裤子，清洁性交、常常运动是预防感染发生的方法，不主张频繁冲洗阴道，这会增加患盆腔炎的风险。\"},{\"section\":\"免疫力相关疾病/问题\",\"title\":\"超重会降低免疫力，增加慢病风险\",\"content\":\"超重不仅增加了你患糖尿病、癌症、心脏病的几率，还会增加免疫系统的健康风险。因为脂肪细胞会在体内引起慢性的炎症反应，使得健康组织逐渐被损害。 实验表明肥胖的老鼠注射疫苗后的免疫应答能力降低，产生的抗体低于平均数量。（来自WebMD）\"},{\"section\":\"免疫力相关疾病/问题\",\"title\":\"造成胆囊炎的危险因素也会降低免疫力\",\"content\":\"免疫力低下会造成胆道感染，而饮食油腻、暴饮暴食、压力造成的焦虑或易怒都会诱使或加重胆囊问题，同时这些情况也会导致免疫力降低。提高免疫力、少吃多餐、避免油腻高脂肪的食物、腹部保暖、保持情绪平稳都有助于避免胆囊问题的加重。\"},{\"section\":\"免疫力相关疾病/问题\",\"title\":\"功能较弱的免疫系统导致炎症或感冒\",\"content\":\"感冒 容易感冒而迁延日久提示需要加强免疫力。免疫力较高的人，感冒症状在24小时之内就会烟消云散。在保证营养充足的基础上补充提高免疫力的植物精华有助于减少感冒次数和症状困扰。（具体参见营养建议部分）\"},{\"section\":\"免疫力相关疾病/问题\",\"title\":\"功能较弱的免疫系统导致炎症或感冒\",\"content\":\"炎症 机体的抗炎能力和免疫力有关。当炎症发生时，免疫系统的反应是产生用于攻击特异入侵微生物的各种物质和因子。当免疫力下降时炎症会难以痊愈，同时局部血液供应减少，营养的缺乏（蛋白质、维生素C、锌）对炎症的消除影响也较大。（具体参见营养建议部分）\"},{\"section\":\"免疫力相关疾病/问题\",\"title\":\"腰围超标增加免疫系统负担，更易患慢性疾病\",\"content\":\"腰围的重要性仅次于体重指数。腰围的大小可以表明脂肪堆积的部位。脂肪聚集在腰上的人更容易患高血压、糖尿病、冠状动脉疾病,研究表明腹部脂肪过多可能释放某种蛋白质或激素，导致炎症，增加免疫系统负担。男性腰围超过102厘米或女性超过88厘米，患相关疾病的危险就会增大，尤其是体重指数为25或以上的人群。\"},{\"section\":\"免疫力相关疾病/问题\",\"title\":\"高血压和免疫功能息息相关\",\"content\":\"美国埃默里大学科研人员的研究发现，如果增强对免疫系统的调节，那么对血压的平稳也有帮助，因为从调节免疫入手，会改善受损器官和血管修复 。\"},{\"section\":\"免疫力相关疾病/问题\",\"title\":\"血脂高诱发体内的自由基反应， 影响免疫功能\",\"content\":\"血脂高会诱发体内一系列自由基反应，损伤各类细胞，尤其是作为免疫器官和脂肪代谢的肝脏健康会受到影响，从而降低免疫功能。 因此必须在生活方式上加以注意，并在医生指导下借助药物改善状况。\"},{\"section\":\"免疫力相关疾病/问题\",\"title\":\"血糖高会增加体内炎症反应\",\"content\":\"血糖高的人易发炎症，增加免疫系统负担。一旦被病菌感染，因为血中糖含量高，很容易称为细菌的培养基，发展成各类炎症，本身的伤口也因此而易感染而难愈合。所以降低血糖的同时要改善免疫功能，减少患病风险。\"},{\"section\":\"影响免疫力的其他因素\",\"title\":\"长期服药或滥用药物会削弱您的免疫系统\",\"content\":\"长期服药 长期服药可能削弱免疫力。因为患有慢性病而需要长期用药，这不仅会增加肝脏解毒负担，还会消耗对免疫系统有益的维生素和矿物质，从而令机体免疫力下降。\"},{\"section\":\"影响免疫力的其他因素\",\"title\":\"长期服药或滥用药物会削弱您的免疫系统\",\"content\":\"惯用抗生素 不科学的使用抗生素会增加健康风险。因为抗生素会杀死部分菌群而导致人体菌群失调，同时也增加了细菌的耐药性，所以加重了继发感染的危险。世卫组织警告说抗生素的滥用可能导致这一最强效的药物失去作用。\"},{\"section\":\"影响免疫力的其他因素\",\"title\":\"节食导致的营养不均衡影响免疫力\",\"content\":\"你的免疫能力和维生素、矿物质的摄入量是否适宜有关。缺乏维生素A、维生素B族、维生素C、维生素E、铁、锌、镁、钾都会影响免疫系统的功能，适当的蛋白质是免疫系统的物质基础。（《营养圣经》）只有科学的搭配饮食才能保证好形体又不损害免疫力，塑造健康之美。\"},{\"section\":\"影响免疫力的其他因素\",\"title\":\"不佳的心理状态会降低机体免疫力\",\"content\":\"害怕生病 恐惧削弱免疫力。美国医生可瑞斯珶纳-瑙斯汝浦认为，仅仅对疾病的恐惧就足以让人染病。她发现，潜意识压力的出现，能够导致皮质醇和肾上腺激素的释放。如果这些激素在较长时间里保持很高水平的话，免疫力被削弱，病原体就表现活跃。卡内基梅隆大学的心理学家谢尔登-科恩的研究也得出了类似的结果。\"},{\"section\":\"影响免疫力的其他因素\",\"title\":\"不佳的心理状态会降低机体免疫力\",\"content\":\"遇事担忧 消极情绪削弱免疫力。如果一个人经常设想不好的情景，他得病的机率就越高。这是美国威斯康星大学的科学家们最近研究得出的结论，该结论刊登在美国国家科学院公报上。这些科学家们研究证明，大脑的“消极”活动与免疫力的削弱有着直接的关系。\"},{\"section\":\"影响免疫力的其他因素\",\"title\":\"不佳的心理状态会降低机体免疫力\",\"content\":\"孤独感 孤独感会削弱你的免疫系统。研究表明长期处于孤独状态不仅会影响身心健康，甚至身体会在基因层面影响对免疫系统的调控。\"},{\"section\":\"影响免疫力的其他因素\",\"title\":\"不佳的心理状态会降低机体免疫力\",\"content\":\"压力和紧张 短暂、适度压力可以调节免疫系统，因为身体在压力状态下会释放令免疫系统更活跃的皮质醇，但长期的心理状态低迷，如紧张、沮丧、悲痛，会抑制免疫系统。\"},{\"section\":\"影响免疫力的其他因素\",\"title\":\"影响免疫功能的身体状态\",\"content\":\"怕冷 通常免疫力低的人都会有怕冷的表现，而研究也表明当体温每降低1度，人体中抗菌的白血球所提供的免疫力便会减少37%。免疫系统在温暖的环境中工作效果最佳，这也解释了身体用发烧来产热升温的原因。\"},{\"section\":\"影响免疫力的其他因素\",\"title\":\"影响免疫功能的身体状态\",\"content\":\"从小身体弱 无论是遗传因素还是营养因素，从小就较弱的身体提示您更需关注您的免疫系统。免疫系统的功能是可以被改善的，避免有损免疫力的食物，减少自由基的伤害，补充增强免疫力的营养素，适度平缓的运动，保持轻松乐观的精神状态，可以增强免疫力，从而改善体质。\"},{\"section\":\"影响免疫力的其他因素\",\"title\":\"影响免疫功能的身体状态\",\"content\":\"疲劳 疲劳状态下免疫力会受到负面影响。因为处于疲劳状态下免疫系统活力较差，这也是为什么在劳累时更容易感冒的原因。及时休息，疲劳时避免感受风寒都会减少患病的可能。\"},{\"section\":\"影响免疫力的其他因素\",\"title\":\"影响免疫功能的身体状态\",\"content\":\"压力大 压力和疲劳会降低免疫力，在此状态下，身体的营养和能量处于匮乏状态，这令身体用于常规修复和保养的能量不足，包括对免疫系统的维护。通过放松和休息以及补充营养可以改善整体状态，从而加强免疫系统功能。\"},{\"section\":\"影响免疫力的其他因素\",\"title\":\"污染的空气削弱免疫力，增加患病风险\",\"content\":\"空气污染 污染的大气长期反复作用于机体，对人体健康构成现实危害和潜在危害 。生活在重污染区的人体对空气污染物的反应更敏感，患支气管哮喘的潜在危险性增加。俄亥俄州立大学的一项研究表明，呼吸被污染的空气，会引起身体炎症。因为空气中的细颗粒物（PM）会激发肺部炎症，炎症的产物溢出进入身体的循环系统，到达脂肪组织引起炎症、血管功能障碍，从而引起心脏，呼吸道疾病。\"},{\"section\":\"影响免疫力的其他因素\",\"title\":\"污染的空气削弱免疫力，增加患病风险\",\"content\":\"二手烟或油烟 二手烟或油烟都含有对健康有害自由基。而过多的自由基会作用于免疫系统，引起淋巴细胞损害，造成人体免疫功能下降，对疾病的抵抗能力下降，还能导致自身免疫性疾病\"},{\"section\":\"影响免疫力的其他因素\",\"title\":\"性爱可以增强免疫力\",\"content\":\"研究表明每周做爱一次或两次可以拥有较高水平的抗体免疫球蛋白A（IgA），IgA可以加强机体避免感冒和其他感染的能力。（来自WebMD）\"}]','免疫力评估'),
	(38,'免疫力方面','[{\"section\":\"饮食习惯\",\"title\":\"口味清淡，令免疫系统维持最佳状态\",\"content\":\"多选择蒸 、煮、生吃的烹饪方式， 相对于煎炸保留食物中更多的营养，尤其是维生素和抗氧化物质，产生较少的自由基，这对维护健康的免疫系统有益。\"},{\"section\":\"饮食习惯\",\"title\":\"正确喝水，也能提高免疫力\",\"content\":\"口渴要及时喝水。人体的70%是水分，确保体内有足够洁净的水分，才能将代谢产生的废物及时排出体外。多喝水可使口腔和鼻腔黏膜保持湿润，从而发挥捕捉细菌的功能。矿泉水、淡茶、鲜榨的蔬果汁、口味清淡的菜汤都是优质水分的来源。若没有胃寒的问题，可以常喝绿茶，因其富含强抗氧化物质茶多酚，对感冒和癌症都有预防效果。\"},{\"section\":\"饮食习惯\",\"title\":\"细嚼慢咽，七分饱\",\"content\":\"保持细嚼慢咽的习惯，有助于消化吸收，也能自然达到七分饱。这对免疫系统的门户－胃肠的健康有益。 实验表明，长期适度减少热量摄入的动物，体重更轻，患有心脑血管疾病的风险更低，更健康，更长寿。\"},{\"section\":\"饮食习惯\",\"title\":\"多吃天然食物， 给免疫系统加油\",\"content\":\"有效提高免疫力的途径之一就是吃品种丰富的天然食物，和加工包装食物相比，以素食为主的新鲜的食材组合能提供免疫系统需要的全面维生素、矿物质以及抗氧化物质。\"},{\"section\":\"饮食结构\",\"title\":\"增加抗氧化食物的种类，有效提升免疫力\",\"content\":\"抗氧化剂丰富的食物能够协助清除体内的自由基，增强免疫系统的力量，从而抑制炎症反应。对免疫系统而言，食物的协同作用要优于单独补充营养素，协同抗氧化能力最佳的三种维生素是胡萝卜素、维生素C和维生素E 。抗氧化营养素在紫、蓝、红、桔、黄色果蔬中含量较高。如，杏子、芦笋、甜菜、西兰花、哈密瓜、 胡萝卜、玉米、 卷心菜、芒果、芜菁（香大头）、羽衣甘蓝、油桃、粉红西柚、南瓜、倭瓜、红薯、紫薯、蜜橘、西红柿、西瓜、蓝莓、树莓、覆盆子  、球芽甘蓝、菜花、柚子、甜瓜 、奇异果、油桃、橙子、木瓜、彩椒、荷兰豆、 草莓、 樱桃、芥末、菠菜等。也要多吃些富含维生素E的食物，如坚果和葵花籽等。\"},{\"section\":\"饮食结构\",\"title\":\"好油脂帮助改善过敏和炎症\",\"content\":\"油脂占总热量的30％以下，选择浅色肉食，如鱼类、水生贝壳、鸡鸭等，因为它们中的饱和脂肪及胆固醇含量明显低于红肉。过多的饱和脂肪会和高糖分一样影响白细胞的杀菌能力，增加机体炎症发生几率。\"},{\"section\":\"饮食结构\",\"title\":\"好油脂帮助改善过敏和炎症\",\"content\":\"必需脂肪酸在体内可以转化为具有抗炎作用的前列腺素，从而帮助改善过敏和炎症反应。种子（芝麻、南瓜籽、松籽、葵花籽、亚麻籽）和深海鱼（金枪鱼、三文鱼、沙丁鱼、鲭鱼、鲱鱼）、冷榨种子油或必需脂肪酸营养补充剂都是良好来源。\"},{\"section\":\"饮食结构\",\"title\":\"补充更多钙质食物\",\"content\":\"牛奶和豆类食物是钙的优质来源，若对牛奶过敏，您还可以选  择发菜、紫菜、荠菜、木耳等，或者适量芝麻、花生、榛子等坚果，虾米也是很不错的选择，它们都是很好的钙质来源。\"},{\"section\":\"饮食结构\",\"title\":\"多吃些公认的免疫力援兵食物\",\"content\":\"能够提高免疫力的食物还包括， 槲皮素含量较高的红酒、苹果、洋葱等可以帮助改善过敏和炎症。接骨木富含抗氧化剂，抵御感冒和发炎。蘑菇在抗感冒和抗菌以及预防癌症都有着公认的卓越表现。鸡汤可以加速感冒的康复。\"},{\"section\":\"饮食结构\",\"title\":\"补充益生菌能改善炎症和过敏\",\"content\":\"益生菌对减轻过敏时的炎症反应有帮助。益生菌不仅令致病菌生存艰难，它们还会积极地激活你的免疫系统，这意味着益生菌对于治疗癌症和过敏症同样重要。（出自《营养圣经》）常吃酸奶、干酪、泡菜、味增汤、豆腐、葡萄酒、酸面包（小麦或黑麦制品）等发酵食物，可以补充体内的益生菌。\"}]','合理营养建议'),
	(39,'免疫力方面','[{\"section\":\"运动对抗各种免疫力问题\",\"title\":\"类风湿关节炎 ?  传统慢运动可以改善关节疼痛\",\"content\":\"如果你尝试了很多治疗都不能减弱关节疼痛，可以尝试冥想、太极、腹式呼吸，它们被证明有改善疼痛，减少压力荷尔蒙的效果。尤其是太极，可以扩展关节的活动，并增加关节的力量。\"},{\"section\":\"运动对抗各种免疫力问题\",\"title\":\"怕冷 ?  适度的运动让身体温暖\",\"content\":\"除了身体虚弱的原因，适度的运动可以改善怕冷的问题。运动会加速血液循环，通过增加新鲜血液的输送将热量分布到肢体末端和体表，同时也加速了身体废物的代谢，提高到正常的体温有助于免疫系统正常运作。运动到身体发热或微微出汗为好，避免出汗过多。\"}]','有效运动建议'),
	(40,'免疫力方面','[{\"section\":\"\",\"title\":\"大笑能提高免疫力\",\"content\":\"加利福尼亚洛马琳达大学医学院的研究发现，大笑能减少压力荷尔蒙的分泌，增加免疫细胞的数量。午休时间看段搞笑视频、分享朋友转发的笑话或漫画都是获得快乐的方法。\"},{\"section\":\"\",\"title\":\"冥想或禅定有助于改善免疫力\",\"content\":\"学会如何应付紧张、处理心理问题和放松身心，是激发免疫系统的关键。实验表明在注射感冒疫苗时，经过持续8周冥想训练的人会比不冥想的人产生更多的抗体，而且这个免疫系统优势会持续4个月之久。\"},{\"section\":\"\",\"title\":\"冥想或禅定有助于改善免疫力\",\"content\":\"医学研究指出，当人禅坐进入入定状态时所出现的生理效应，如呼吸减缓、交感神经系统功能下降、副交感神经系统功能相对增强、消化系统功能亢进、耗能减缓而储能增大等，有利体内能量的充盈。禅定可以调节免疫系统，同时为神经、内分泌系统带来正面影响。\"},{\"section\":\"\",\"title\":\"为免疫系统的健康而减重\",\"content\":\"超重对免疫系统的健康不利，保持稳定的健康体重需要要改变生活方式：饮食和运动水平合理化；下定决心减肥，给自己时间和耐心，逐渐改变饮食和运动习惯；只有长期的改变才算成功。\"},{\"section\":\"\",\"title\":\"您需要高质量睡眠以维持免疫系统健康\",\"content\":\"虽然研究人员并不知道充足的睡眠如何提高免疫力，但是很明显，成人保持7-9小时的优质睡眠对免疫系统健康是有帮助的（信息来自WebMD）。繁忙时，在下午13点~16点之间小睡15分钟也可以帮助缓解缺乏睡眠带来的疲劳感。\"},{\"section\":\"\",\"title\":\"您需要高质量睡眠以维持免疫系统健康\",\"content\":\"睡眠质量低可能影响细胞激素或组胺，从而影响人体免疫功能。而固定起床时间可以帮助生物钟正常工作，也是拥有高质量睡眠的前提条件，也可以通过午睡或者安全状态下的Flash睡眠(瞬时闪睡)来进行一定的睡眠补充，这是最现实的办法。但是决不能根据前一天的睡觉时间决定起床的时间，否则很难改善生物钟紊乱的症状。\"},{\"section\":\"\",\"title\":\"您需要高质量睡眠以维持免疫系统健康\",\"content\":\"机体免疫力可以通过良好的睡眠得以恢复。美国哈佛大学医院的学者研究者发现，人在睡眠时体内的微生物可制造一种叫“胞壁酸”的物质，这种“胞壁酸”既可促进睡眠，也有增强免疫力的作用。可促使白细胞增多，巨噬细胞活跃，肝脏解毒功能增强。\"},{\"section\":\"\",\"title\":\"戒烟，是最佳的选择\",\"content\":\"你可能尝试戒烟，并体会过戒烟带来的各种不适反应。但是应该记得的是，这些不适反应只不过是些可以改善的症状，远比吸烟对内脏、心脑血管的直接损伤来的微小得多。更有许多实践经验表明，戒烟会令体重增加，增加的体重可以通过深呼吸练习、运动、均衡营养的方式减下来。\"},{\"section\":\"\",\"title\":\"长期有规律的适量饮酒帮助提升机体健康\",\"content\":\"人体可自然分解消化酒精，每天适当饮酒可使动脉血管扩张、血压下降，有利于促进血液循环，更可提升对人体有利的高密度脂蛋白及降低有害的低密度脂蛋白，从而防止心脏病发作。而适量葡萄酒（含有抗氧化物质白藜芦醇）可能降低机体老化速度。\\n适量的饮酒相当于每天一杯150ml红葡萄酒或360ml啤酒。\"},{\"section\":\"\",\"title\":\"常常受到辐射侵扰，需要抗氧化剂抵抗辐射侵袭\",\"content\":\"1.需要有意识地通过饮食或膳食补充剂摄入花青素、维生素C、维生素E、维生素A等抗氧化剂，及时清除代谢产生的自由基。 2.避免长时间连续操作电脑，眼睛与屏幕的距离应在40~50厘米。  3.室内光线要适宜，不可过亮或过暗。  4.安装防护装置，削弱电磁辐射的强度。\"},{\"section\":\"\",\"title\":\"亲密关系有助于提高免疫力\",\"content\":\"研究表明，良好的性爱或者亲密关系可以通过提高IGA水平而增加免疫力。IGA是免疫系统中一种关键的蛋白质，大脑分泌的内啡肽水平可以影响其数量。在愉悦的身体接触时，大脑会分泌更多的内啡肽。（来自WebMD）\"}]','生活方式建议'),
	(41,'抗衰老方面','[{\"section\":\"衰老信号盘点\",\"title\":\"韧带、骨骼和肌肉的退化使人体活力下降加速衰老\",\"content\":\"肌肉松软 通常过了40岁，人的肌肉开始以平均每年0.5%到2%的速度减少。从肌肉力量减少到松软下垂，多是30岁以后缺乏运动加速衰老的表现。30岁之后，肌肉蛋白质降解速度渐渐加快，但其合成速度不变，从而使肌肉渐渐衰老，不过此时肌肉仍然处于生长、衰退的循环之中，可以通过优质蛋白的摄入和日常锻炼得以恢复。\"},{\"section\":\"衰老信号盘点\",\"title\":\"胃肠功能减弱提醒您改善生活习惯，防止衰老\",\"content\":\"不耐受甜食和油腻 吃太多甜食或油腻食物有不适感说明消化能力下降，尤其是协助脂肪和糖类代谢的肝胆功能下降。随着年龄的增加，肝血流量减少，肝的转运、代谢、排泄、解毒功能都有下降。胆囊弹性降低，胆汁淤积浓缩。但正常情况60岁才会有明显的功能减退。而不良的生活习惯，如饮酒、熬夜、吃得过油腻等会令肝胆功能退化加速。\"},{\"section\":\"衰老信号盘点\",\"title\":\"胃肠功能减弱提醒您改善生活习惯，防止衰老\",\"content\":\"消化不良 正常的消化功能会随着年龄的增长而减弱。一般40岁以后消化功能会逐步减弱，减弱的过程分为机械损伤和化学损伤两部分。胃黏膜及腺细胞萎缩、退化，胃液分泌减少，易造成胃黏膜的机械损伤；胃粘膜的退化萎缩，致使胃黏膜易被胃酸和胃蛋白酶破坏，造成化学性损伤。加之不良的饮食习惯、长期不放松的情绪等因素都会加速这种老化进程。尝试七分饱的饮食方式给肠胃减负。\"},{\"section\":\"衰老信号盘点\",\"title\":\"胃肠功能减弱提醒您改善生活习惯，防止衰老\",\"content\":\"便秘 通常人在50岁左右会因肠道平滑肌支配神经系统的退化令肠道蠕动减慢，这是增龄引起便秘的重要因素。在30、40岁出现长期便秘的状况可能是肠道提早老化，也可能是其他因素，但无论何种原因的长期便秘都会影响肠道的消化、吸收和蠕动动力，从而加速肠道的老化，而适当的营养、运动、饮水可以延缓这一退化过程。\"},{\"section\":\"衰老信号盘点\",\"title\":\"胃肠功能减弱提醒您改善生活习惯，防止衰老\",\"content\":\"饭量减少 随着年龄的增长，胃的容量、动力下降（由于弹性下降），导致饭量下降，但这一般是4、50岁发生的衰老转变。若是3、40岁出现这种状况多半是缺乏运动、喜食油腻等引起新陈代谢缓慢或是因饮食不规律、压力等原因导致慢性胃肠炎的先兆，这些都是加速胃衰老的原因。改善不良饮食习惯，保护你的胃。\"},{\"section\":\"衰老信号盘点\",\"title\":\"胃肠功能减弱提醒您改善生活习惯，防止衰老\",\"content\":\"怕吃凉 《默克家庭医学手册》提到，年龄导致胃酸分泌减少的一些疾病，如萎缩性胃炎是很常见的，50岁以上发病率高达53%。怕吃凉的是萎缩性胃炎发生的先兆和典型症状。胃是一个对外界气候和温度很敏感的器官，人体受到冷空气刺激后，胃部容易发生痉挛性收缩，从而引发胃痛、消化不良、呕吐、腹泻等症状，平日需要注意胃部保暖并尽量食用温热的食物。\"},{\"section\":\"衰老信号盘点\",\"title\":\"容颜和五官变化透露衰老信息\",\"content\":\"牙龈萎缩、齿缝变宽 年轻时，牙与牙之间的缝隙很小，随着年龄增长，牙周组织萎缩，牙缝变宽，咀嚼效率也随之下降。出现明显的牙龈萎缩现象一般是在40岁左右，可以通过细嚼慢咽的方法利用食物对牙龈产生按摩促进牙龈、牙槽骨血液循环，防止齿根外露，过敏，牙齿松动等进一步老化现象发生。\"},{\"section\":\"衰老信号盘点\",\"title\":\"容颜和五官变化透露衰老信息\",\"content\":\"视物模糊或飞蚊症 通常40岁以后视觉细胞衰老的征象开始出现，晶状体纤维的弹性减弱，透明度降低，眼睛不再有以前明亮，出现浑浊，视物模糊，这是一种自然老化的现象。但是往往我们因为用眼过度，使得眼睛提前出现退化变性的现象，需要特别注意眼睛的休息和养护。\"},{\"section\":\"衰老信号盘点\",\"title\":\"内分泌水平紊乱引发衰老问题\",\"content\":\"压力引发头疼 压力作用于交感神经，引发肾上腺素、去甲肾上腺素分泌增加，引起脑缺血及血管的“无菌性炎症”，产生头痛感。《营养圣经》提出由压力引发的头痛出现频繁则说明您长期处于过度压力中，而长期压力与老化过程加速密切相关。\"},{\"section\":\"衰老信号盘点\",\"title\":\"内分泌水平紊乱引发衰老问题\",\"content\":\"经前期综合症 对女性而言，雌激素和黄体酮的平衡至关重要。如雌激素相对过量，则称雌激素优势，雌激素优势的早期症状包括经前综合征、抑郁、性欲减弱、嗜好甜食、经量增多、体重增加，乳房肿胀和水分潴留等。雌激素优势的原因可能是摄入雌激素过多或者体内缺乏黄体酮，或者两者兼有。这与平时喜欢肉食，久坐缺乏运动，压力过大等都有关系，雌激素同样也存在于大多数避孕药和激素替代疗法中。\"},{\"section\":\"衰老信号盘点\",\"title\":\"内分泌水平紊乱引发衰老问题\",\"content\":\"脾气暴躁 通常无论男女，在更年期年龄附近（40-55岁）容易发生激素水平紊乱的情况。三十几岁经常有难以控制的坏脾气，可能是压力过大导致的，长期如此会加速机体衰老。当压力过大时体内的激素水平会出现较大的波动，从而需要调用本应用来修复或维护机体日常生理功能的营养素，甚至影响了正常的生理功能，增加了心脏病、高血压、糖尿病等慢性疾病的患病风险。\"},{\"section\":\"衰老信号盘点\",\"title\":\"内分泌水平紊乱引发衰老问题\",\"content\":\"性趣降低 《抗衰老学》提出：性激素的分泌，男性在40岁时达高峰，以后就逐渐减弱，女性35~40岁雌激素急剧减少。不但会使得性趣降低，同时女性雌激素的减少还影响骨质成分，骨胶原，钙盐等减少，出现骨质疏松。可以通过补充钙质和大豆异黄酮对雌激素起到调节作用。\"},{\"section\":\"衰老信号盘点\",\"title\":\"内分泌水平紊乱引发衰老问题\",\"content\":\"腰腹发胖 调查显示：腰部肥胖更容易引起胰岛素反应迟钝，因此，摄入同等量糖类食物时会释放更多胰岛素，过多的胰岛素将增加脂肪的合成，使体内有更多的脂肪堆积在腰腹及内脏，让你更容易患上与衰老密切相关的各种慢性疾病。\"},{\"section\":\"衰老信号盘点\",\"title\":\"内分泌水平紊乱引发衰老问题\",\"content\":\"痛经 无论是原发性还是继发性痛经，月经分泌物中都含大量前列腺素，前列腺素可能引起子宫收缩，造成子宫缺血和疼痛。严重的痛经常伴有头痛、恶心、眩晕、腹泻等，和前列腺素全身中毒症状相同。痛经时体内前列腺素水平升高，这使得体内免疫系统失去调节的作用，人体免疫力降低，并增加患乳腺增生/子宫炎症等疾病可能，从而加速了人体衰老。\"},{\"section\":\"衰老信号盘点\",\"title\":\"心肺功能的降低，意味着衰老的到来\",\"content\":\"爬楼气喘 爬5层楼梯是衰老与心肺活力的基础测试， 30 岁左右的人，一步迈两层台阶，能快速登上 5 层楼，仍觉得轻松，说明健康状况良好； 不论哪个年龄阶段的人，如果气喘吁吁，心跳加速，则说明心肺功能降低。心肺功能关系着一呼一吸的大事，体内供氧量的大小及新陈代谢的快慢和正常与否，与人体的衰老紧密相连。需要加强日常运动习惯养成，提高心肺功能，减缓衰老进程。\"},{\"section\":\"衰老信号盘点\",\"title\":\"泌尿系统的衰老变化\",\"content\":\"尿频、尿少或夜尿增多 一般来说人到50岁以后，肾功能逐渐减退，膀胱容量减少，尿道肌肉松弛使得排尿出现问题。如果是3、40岁出现尿频、夜尿增多、小便量少的状况需要引起关注，养成良好的运动习惯（详情见运动建议部分），改变久坐不动、憋尿等生活中的不良习惯延缓其衰老速度，必要时就医调养。\"},{\"section\":\"衰老信号盘点\",\"title\":\"脑力会随着年龄增长而出现退化现象\",\"content\":\"记忆力减退 美国有研究表明，人的记忆力到了37岁开始明显转差，大脑的其他一些能力也会在42岁左右开始走下坡路。过早出现的记忆力减退可能由于压力或睡眠问题引发，一方面注意休息，另一方面大脑的衰退，完全是受到“用进废退”规律的支配，常常阅读、学习，进行常识性知识的积累（人的这方面能力直到60~70岁也不会减退），能够延缓大脑其他功能的衰老速度，培养多用脑的习惯，在整个生命阶段中都不要停顿，这样才能常保脑力青春。\"},{\"section\":\"衰老信号盘点\",\"title\":\"固执或对新鲜事物缺少兴趣更多的是心理上的衰老现象\",\"content\":\"固执、兴趣缺乏 现代心理学研究指出，人到一定年龄（30岁以后），神经系统的反射就形成一定的固定模式，如果不进行积极的调节和控制就趋向于自我内向性认同，导致固执己见或对新鲜事物缺少兴趣，这是一种心理衰老的表现。如果因此而很少接触新鲜事物，使大脑缺少应有的刺激，就会加速生理衰老，导致敏感性和反应力明显减退，而形成思维能力下降、记忆力锐减及老年痴呆等一系列问题。积极培养自己各种爱好和兴趣，养成锻炼身体的习惯，有助于恢复心理活力。\"},{\"section\":\"衰老信号盘点\",\"title\":\"免疫力下降与衰老相互影响，形成恶性循环\",\"content\":\"炎症、感染恢复时间长 当发现炎症及感冒恢复明显变慢时，可能是机体衰老的信号。随着年龄增长，人体内的补体蛋白不断流失，使得机体自我修复功能受到影响，因此会有免疫力下降感觉。《抗衰老学》中提到，下降的免疫力同时会加速衰老的进程，使得人们更易患感染、自身免疫性疾病和恶性肿瘤。注意日常优质蛋白的补充（如乳清蛋白，大豆蛋白）、提升免疫力，树立抗衰老屏障。\"},{\"section\":\"衰老信号盘点\",\"title\":\"免疫力下降与衰老相互影响，形成恶性循环\",\"content\":\"过敏、感冒等频发 《抗衰老学》提到：随着年龄增长，免疫功能减退，对自身免疫细胞的识别能力减弱，除了攻击外来病原体外，还攻击自身免疫组织，引发机体衰老，导致流感、炎症、过敏等免疫疾病更容易发生。可通过调整饮食结构，合理运动提升自身免疫力延缓衰老。\"},{\"section\":\"衰老相关疾病\",\"title\":\"血糖高引起的糖基化作用会加速衰老\",\"content\":\"血糖升高会对人体细胞产生糖基化作用，胶原质是最早受到影响的蛋白质之一。胶原质因为糖基化而失去弹性，从而加速老化的发生，这意味着血管、肺组织和关节、皮肤会失去弹性，眼睛的晶状体变模糊，形成白内障。\"},{\"section\":\"衰老相关疾病\",\"title\":\"延缓衰老的生活方式有助于降低冠心病风险\",\"content\":\"冠心病若养护不当，猝死率很高（统计表明50-60%猝死是冠心病）。吸烟、高血压、高血糖、高血脂、肥胖、久坐等问题都是冠心病发作的危险因素，通过合理的饮食和有规律的运动，可以大大减少患病的风险，甚至可以免除药物的使用。可以配合每天服用抗氧化维生素，如维生素E、维生素C、叶酸等，阻止低密度脂蛋白在血管的积累，预防发病。\"},{\"section\":\"衰老相关疾病\",\"title\":\"过早出现骨质疏松症可能是提前衰老的表现\",\"content\":\"大多数人的骨密度在30岁后开始减低，特别是女性更年期后骨密度降低的速度加快。《默克家庭医学手册》提到，骨质疏松的发生不但与遗传和免疫功能异常有关，尤其与体内激素的分泌失调，营养状况及物理因素有关。不过相对于恢复已降低的骨密度，保持骨密度不变要来的更容易些。预防措施包括摄入足够的钙和维生素D和从事负重锻炼。\"},{\"section\":\"衰老相关疾病\",\"title\":\"脂肪肝是导致多种肝脏损害、衰老的诱因\",\"content\":\"运动少，高热量饮食，过量饮酒都是脂肪肝发生的原因，这本身就是一个不良生活习惯导致的自由基和脂质过氧化物增加导致肝损伤的过程。 因为起初没有明显症状，所以不太引起重视，但如果不改变生活习惯，则容易引起肝脏的反复损害，最终发展到肝硬化。即便在单纯的脂肪肝阶段，身体的新陈代谢也会因为肝功能受损而受到影响，有毒物质的排出和油脂、蛋白质、维生素的利用出现紊乱，最初的表现可能只在油光的皮肤，但最终的结果却是加速全身器官的老化。\"},{\"section\":\"衰老相关疾病\",\"title\":\"乳腺增生多发生在25-35岁之间，延缓衰老的生活方式有助于改善乳腺增生\",\"content\":\"发生率高达70%~85%，通过触按可以发现，如果没有疼痛的感觉或仅仅是经前期胀痛，往往不会引起重视，导致进一步恶化。婚育、膳食、压力和遗传因素是乳腺发病的主要原因。 将体重控制在健康的水平，特别注意心情和压力的调整，因为精神因素引发的内分泌失调、植物神经紊乱，睡不好觉、脾气暴躁，这些都会对乳腺产生不良影响。放松，静坐，舒压按摩都是很好的方法，保证足量有氧运动，每周至少3次。\"},{\"section\":\"衰老相关疾病\",\"title\":\"慢性妇科炎症会导致生殖系统衰老\",\"content\":\"对于女性来说，任何引起阴道酸度下降（PH上升）的因素都会增加患阴道感染的可能性。月经前、月经期及妊娠期的阴道酸度都会随着体内雌激素水平的变化而下降。\\n所以除了遵医嘱用药外，保证阴部环境干爽、减少久坐、穿宽松、透气的裤子，清洁性交、常常运动是预防感染发生的方法，不主张频繁冲洗阴道，这会增加患盆腔炎的风险。\"},{\"section\":\"衰老相关疾病\",\"title\":\"健康的胆囊，助您保持年轻态\",\"content\":\"慢性胆囊炎若不及时治愈，会给生活增加很多限制，常常因为消化不良，厌油腻食物、上腹部闷胀、嗳气、胃部灼热等问题而影响品味美食的心情，而且压力和情绪的变化也会对胆囊健康产生影响。\"},{\"section\":\"衰老相关疾病\",\"title\":\"体重增加令代谢功能降低，增加罹患多种慢性疾病风险\",\"content\":\"肥胖的原因很多，但总归导致体内脂质代谢紊乱，机体虽可能无明确疾病，但令整体活力和适应性不足，成为加速衰老的因素。 此外，脂肪在皮下和内脏器官周围堆积，这使得患冠心病、高血压、糖尿病等慢性疾病的风险增加。\"},{\"section\":\"衰老相关疾病\",\"title\":\"腰围超标意味越过慢病安全警戒线\",\"content\":\"腰围的重要性仅次于体重指数（BMI=体重KG/身高M²）。腰围的大小可以表明脂肪堆积的部位。脂肪聚集在腰上的人更容易患高血压、糖尿病、冠状动脉疾病，因为腹部的脂肪更容易分解并在动脉里聚集。男性腰围超过102厘米或女性超过88厘米，患相关疾病的危险就会增大，尤其是体重指数为25或以上的人群。\"},{\"section\":\"衰老相关疾病\",\"title\":\"高血压会加速机体衰老\",\"content\":\"高血压若不及时防治则会损害血管、心、脑、肾、视网膜等组织器官，从而加速机体衰老。血压高会令动脉粥样硬化发病率增高，影响重要器官组织供血甚至威胁生命；血压升高会导致心脏的结构和功能发生变化，日久会出现心力衰竭与心率失常；高血压引起的脑出血会导致身体致残，甚至死亡；高血压会影响肾中的小动脉，导致肾脏缺血、肾小球萎缩，最终发展为肾功能损害引起尿毒症。因此积极治疗，保持良好的生活习惯，则有助于稳定血压，延缓衰老。\"},{\"section\":\"衰老相关疾病\",\"title\":\"血脂高诱发体内的自由基反应，会加速衰老\",\"content\":\"有大量调查资料表明，血脂中血清总胆固醇和甘油三酯会从青年时期就随年龄而上升，当然这与生活习惯和脂类物质代谢功能退化有很大关系。 大量基础研究表明，高血脂时体内自由基的产生和清除平衡被破坏，许多自由基清除剂（如SOD、CAT）活性降低，产生大量的脂质过氧化物（LPO），继而损伤各类细胞，加速肝、脑等重要器官的衰老；同时造成动脉狭窄或阻塞，导致主要器官供血不足，加速衰老。因此必须及时调整生活方式，并在医生指导下借助药物改善状况。\"},{\"section\":\"衰老危险因素\",\"title\":\"不健康食物吃得多，会令衰老提前发生\",\"content\":\"爱吃油炸 常吃油炸食物增加早衰风险。油炸会破坏食物中的必需脂肪酸、蛋白质和维生素，食物经油炸产生的致癌物（丙烯酰胺 ）会威胁细胞DNA的安全，这会增加心脏病，癌症的风险。\"},{\"section\":\"衰老危险因素\",\"title\":\"不健康食物吃得多，会令衰老提前发生\",\"content\":\"常吃包装或加工食品 包装或加工食物对想保持年轻活力的您无益，这些食物中的添加剂不仅会阻止营养素的吸收和利用，还会加速营养素的流失，并增加肝脏的代谢负担，常吃这类“反营养食物”会加速身体衰老。（出自《营养圣经》）\"},{\"section\":\"衰老危险因素\",\"title\":\"不健康食物吃得多，会令衰老提前发生\",\"content\":\"爱吃重口味食物 爱吃重口味（如过咸）食物可能会加速衰老。盐摄入过度不仅容易导致高血压，而且能令皮肤细胞缺水萎缩，导致皱纹增多（此信息来自法国国家医学院），甚至影响肾代谢而导致蛋白质合成受损，引起脱发。长期吃辣对消化道刺激大，引起慢性炎症。\"},{\"section\":\"衰老危险因素\",\"title\":\"不健康食物吃得多，会令衰老提前发生\",\"content\":\"常喝甜饮料 “甜味垃圾食品吃得太多的人更容易衰老” 发表于以色列科学技术研究所《营养学杂志》。一是糖分会使皮肤和骨质中的胶原蛋白丧失，结果是皮肤、骨骼缺乏弹性。二是加工食物中的糖会令血糖快速升高，增加糖尿病和心脑血管疾病的风险。三是人工合成的甜味剂，如阿斯巴甜也能刺激大脑对甜食产生更多渴望。\"},{\"section\":\"衰老危险因素\",\"title\":\"不健康食物吃得多，会令衰老提前发生\",\"content\":\"肉食过多 “无荤不饭”会引起各种老化性疾病提早发生。肉类中的饱和脂肪和胆固醇会增加动脉硬化的风险，进而影响到听力、视力、心脏、大脑、肝脏等各个器官健康，加速机体衰老。芝加哥大学的一项研究表明持续六周的高蛋白膳食就会给肾脏带来明显的酸代谢负担，增加形成结石的风险，降低钙平衡水平，加速骨质疏松症的发生。\"},{\"section\":\"衰老危险因素\",\"title\":\"不良的就餐习惯会令你提前衰老\",\"content\":\"常在外就餐 常在外就餐可能会加速机体老化。餐馆食物多油盐调料、少新鲜果蔬，很难保证营养均衡充足，尤其是缺少清除自由基的抗氧化物质，这种饮食不足以抵御现代城市生活带来的衰老风险。你需要适当补充富含维生素和高抗氧化物质的食物。（具体膳食建议见营养补充部分）\"},{\"section\":\"衰老危险因素\",\"title\":\"不良的就餐习惯会令你提前衰老\",\"content\":\"不吃早餐 养成每天一顿认真早餐的习惯能够帮你延缓衰老。伦敦大学帝国学院研究发现，不吃早餐，会令大脑自觉对高热量食物产生好感，引起肥胖，胆固醇增高，增加患心脏病的风险；另外，因空腹时间过长，使胆固醇在胆囊中沉积形成结晶形成胆石症；此外长期不吃早餐的人需要动用体内储存的糖原和蛋白质来提供能量，产生过度消耗也会加速身体老化。\"},{\"section\":\"衰老危险因素\",\"title\":\"不良的就餐习惯会令你提前衰老\",\"content\":\"经常吃得过饱 经常吃得过饱，会损害人体的各种机能甚至导致疾病。常常吃撑会令血糖快速变化，刺激胰岛素的分泌，久之易患糖尿病。吃撑时，胃肠过度扩张，易患胃肠黏膜炎症，有实验证明，长期吃撑，年老后体内甲状旁腺素含量会明显增加，加速了骨骼脱钙，从而增加骨质疏松风险。日本有关专家还发现，大约有30%-40%的老年痴呆病人，在青壮年时期都有长期饱食的习惯。\"},{\"section\":\"衰老危险因素\",\"title\":\"健康食物吃得过少，不利机体保持年轻\",\"content\":\"蔬果吃得少 蔬果吃得少对延缓衰老无益，蔬果提供丰富的抗氧化物质，可以延缓细胞衰老并对抗感染，提高免疫力。你吃的蔬果种类越丰富，抗氧化作用就越理想，因为抗氧剂需要“团队作业”。良好的营养，尤其是维生素C和维生素E可以极大地延长寿命。（出自《营养圣经》）\"},{\"section\":\"衰老危险因素\",\"title\":\"健康食物吃得过少，不利机体保持年轻\",\"content\":\"豆、奶制品吃得少 很少吃豆、奶制品可能导致因缺乏钙质而产生机体功能衰退，奶制品和豆制品被认为是钙的主要来源，钙是维持骨骼、牙齿、神经健康的主要矿物质，缺乏则容易造成骨质疏松、龋齿、失眠、紧张等问题。\"},{\"section\":\"衰老危险因素\",\"title\":\"睡眠质量不佳影响荷尔蒙功能及新陈代谢，开启衰老加速器\",\"content\":\"起床困难或睡不够 喜欢赖床或睡后精力不能补足。可能是繁重的脑力或体力劳动造成极度疲劳，使得需要更长时间的睡眠来恢复。也可能不规律的生活习惯造成了生物钟紊乱，无法控制睡眠和觉醒的正常切换。\"},{\"section\":\"衰老危险因素\",\"title\":\"睡眠质量不佳影响荷尔蒙功能及新陈代谢，开启衰老加速器\",\"content\":\"失眠 失眠者的衰老速度是正常人的2.5-3倍（出自《抗衰老学》）。失眠会影响人的精力和脑力，尤其是创造性思维能力，很多慢性疾病如心脏病、高血压等也被证明和睡眠质量不佳有关，这些都能加速机体老化。 思考生活习惯的问题：如压力、咖啡因、酒精、环境变化、时差、慢性疾病、焦虑等等，调整自身的生活习惯视为第一位。这可能是自己无法控制的，但是要明确一点，这并不是不可以解决的问题，所以不用担心，给自己一点儿耐心。\"},{\"section\":\"衰老危险因素\",\"title\":\"睡眠质量不佳影响荷尔蒙功能及新陈代谢，开启衰老加速器\",\"content\":\"经常熬夜 超过24点不睡会影响新陈代谢效率，从而加速机体老化。睡眠时流经肝脏的血液是平时的七倍，有利于增强肝细胞功能，提高解毒能力。尤其是1点－3点是皮肤代谢的高峰期，此时不睡很容易导致皮肤干燥、暗沉。\"},{\"section\":\"衰老危险因素\",\"title\":\"睡眠质量不佳影响荷尔蒙功能及新陈代谢，开启衰老加速器\",\"content\":\"睡眠不足 美国芝加哥大学研究发现，长期处于睡眠不足的状态，使得体内紧张激素“皮质醇”水平长期偏高，其负面效应显现为：血糖升高、影响情绪和记忆、抑制肌肉及骨骼中蛋白质合成等，将身体推向衰老状态。\"},{\"section\":\"衰老危险因素\",\"title\":\"睡眠质量不佳影响荷尔蒙功能及新陈代谢，开启衰老加速器\",\"content\":\"多梦 梦多是在快速动眼睡眠中出现的，这段时间人的体温上升，心跳增加，血流速快，这种状况出现的原因很多，也会对睡眠质量造成影响，压力大、精神紧张、焦虑都有可能成为多梦易醒的原因。 但是如果并不能构成对睡眠质量的影响，且没有其他睡眠问题则不妨将其看做一次美妙的夜的旅行。\"},{\"section\":\"衰老危险因素\",\"title\":\"不规律的睡眠扰乱循环系统生物钟，身体隐形“生病”\",\"content\":\"睡眠不规律 其实除了睡眠，我们心率、呼吸、血压、体温、蛋白质合成等体内一切生命活动，往往都呈现昼夜节律的倾向，反映在血液循环、内分泌和各种器官功能中。不规律的睡眠就好比在不停地倒时差，各器官的生物钟调整所需的时间不同，睡眠两三天就可以调整过来，但是体内的器官及循环系统的生物钟却需要两三周的时间调整。经常如此，会加速机体衰老。\"},{\"section\":\"衰老危险因素\",\"title\":\"运动量不够，依然健康长寿的可能性不大\",\"content\":\"运动量不够而依然健康长寿，并非完全不可能，但这种可能性不大。《抗衰老计划——阿特金斯医生的建议》中提到“调查显示，在有可能导致心脏病的各种因素中，缺乏运动是首要因素。锻炼身体能够预防、减缓或逆转随着年老而减弱的葡萄糖耐受和胰岛素敏感性。”因此，重要的是知道自己的身体需要多大的运动量，此外还应该知道什么样的运动对你的健康最有利。（建议详情请见有效运动部分）\"},{\"section\":\"衰老危险因素\",\"title\":\"每吸一根烟会使人折寿11分钟\",\"content\":\"据英国一项研究表明，吸一根烟会使人折寿11分钟，如果一天吸15支烟，则会减寿2小时45分钟，若从16岁开始吸烟，71岁的平均年龄死亡，一生将吸烟31万余支，这样吸烟者平均比不吸烟的人早死亡6.5年。\\n吸烟除了人所共知的尼古丁危害外，在吸烟过程中产生的气相烟雾和焦油中还存在着大量对人体有害的自由基，引发各种过氧化反应，损伤细胞膜成分，进而引发衰老甚至恶性疾病，如癌症或心脏病。\\n日常需要适当补充天然抗氧化剂，如：绿茶、新鲜果蔬等，起到清除活性氧自由基的作用。\"},{\"section\":\"衰老危险因素\",\"title\":\"过量饮酒可导致提前衰老，并遭到与衰老有关的疾病侵袭\",\"content\":\"过量饮酒的人看上去往往更加憔悴。人们普遍认为过量饮酒可导致提前衰老并遭到与衰老有关的疾病侵袭。\\n意大利米兰大学进行的一项研究指出，过量酒精可使细胞染色体失去完整性和稳定性的保护，从而加速衰老，增加癌症风险。\\n世卫组织认为每日安全饮酒量的上限是25g酒精，相当于1两42度白酒，或200ml红酒，一瓶啤酒（640ml）。\"},{\"section\":\"衰老危险因素\",\"title\":\"辐射产生的自由基加速人体细胞氧化\",\"content\":\"手机和电脑产生的低频电磁辐射是人体外源性自由基产生的原因之一。随着辐射剂量累积增加，可能会引起神经系统、循环系统、免疫系统、生殖系统以及皮肤和眼睛的功能、代谢或形态的改变，这是一个自由基加速身体细胞氧化的过程。\"}]','抗衰老评估'),
	(42,'抗衰老方面','[{\"section\":\"饮食习惯\",\"title\":\"口味清淡，衰老慢些\",\"content\":\"尽量选择蒸、炖、煮的烹饪方式，这些烹饪方式相对于煎炸能保留更多的营养，产生更少的自由基，减少体内的自由基对延缓细胞衰老有帮助。\"},{\"section\":\"饮食习惯\",\"title\":\"天然食物有效抗击衰老\",\"content\":\"健康零食都是天然食物，时令水果、酸奶、原味坚果、天然果干，这些都可以作为零食，既能满足吃零食的习惯，也能给身体提供必要营养。\"},{\"section\":\"饮食习惯\",\"title\":\"天然食物有效抗击衰老\",\"content\":\"尽量选择新鲜的当地、当季产新鲜食物，它们除了能提供必需的营养素外，还能帮助清除体内有害物质，如导致衰老的自由基。加工食品中存在多种人工合成的化学物质，已有许多国家研究表明加工食品中的各种添加剂摄入过多，会存在致癌风险。\"},{\"section\":\"饮食习惯\",\"title\":\"聪明饮水，保持机体年轻态\",\"content\":\"常喝白水或矿泉水。人体的70%是水分，确保体内有足够洁净的水分，才能将代谢产生的废物及时排出体外，保持机体年轻态。尤其是体型偏瘦的人，更需要有效水份来滋润你的细胞。白水、矿泉水、鲜榨的蔬果汁、口味清淡的菜汤都可以是补充水分的来源。\"},{\"section\":\"饮食习惯\",\"title\":\"聪明饮水，保持机体年轻态\",\"content\":\"无论红茶、绿茶还是乌龙茶、普洱茶，都可以根据自己的喜好每天喝一杯，茶中含有促进消化酶，能帮助分解脂肪，减轻消化系统负担。喝茶宜在饭后1小时。\"},{\"section\":\"饮食习惯\",\"title\":\"聪明饮水，保持机体年轻态\",\"content\":\"养成每天晨起空腹喝一杯温开水的习惯。可刺激胃肠蠕动，促进排便，且能增加肠中水分，防止粪便干燥。\"},{\"section\":\"饮食习惯\",\"title\":\"细嚼慢咽、七分饱，轻松保持年轻态\",\"content\":\"保持细嚼慢咽的习惯，每口饭至少咀嚼20次，不仅有助于消化吸收，也能自然达到七分饱。日本学者统计约有30%--40%的老年性痴呆病人在青壮年时期有饱食的习惯。因摄入的总热量远远超过机体的需要，致使体内脂肪过剩、血脂增高，导致脑动脉粥样硬化，有一种被称为“纤维芽细胞生长因子”的物质在大脑中明显增多，这种物质被认为是引起脑动脉硬化的重要因素，而脑动脉硬化是引发老年痴呆的重要原因。实验表明，长期适度减少热量摄入的人更长寿，体重更轻，患心脑血管疾病的风险更低。\"},{\"section\":\"饮食结构\",\"title\":\"全谷物食物助您保持年轻活力\",\"content\":\"一顿对自己负责任的早餐最好是全谷物配合优质蛋白；如杂豆粥、薯类、全麦面包等，加一份蛋白质（坚果、鸡蛋、瘦肉、牛奶或豆浆等）。繁忙时，最简单的早餐也可以是一份冲饮的藕粉、芝麻糊、豆奶、五谷粥、麦片等。全谷物搭配优质蛋白可以提供令你保持精力充沛的全面营养。\"},{\"section\":\"饮食结构\",\"title\":\"优质蛋白抗衰老\",\"content\":\"吃肉的时候尽量选择浅色肉食，如鱼类、水生贝壳、鸡鸭等，因为它们中的饱和脂肪及胆固醇含量明显低于红肉，提供丰富蛋白质和细嫩口感的同时，也降低了令心脑血管衰老的慢性疾病。\"},{\"section\":\"饮食结构\",\"title\":\"优质蛋白抗衰老\",\"content\":\"我们的皮肤、肌肉、骨骼和韧带都是由大量的胶原蛋白构成并维持良好的特性，平日可以有意多吃些富含胶原蛋白的猪皮、猪蹄、牛蹄筋、牛腱。如果吃素也可以从丰富的杂粮、种子、豆类中获得足够的蛋白质。\"},{\"section\":\"饮食结构\",\"title\":\"色彩鲜艳的果蔬是良好的抗氧化、抗衰老食物\",\"content\":\"衰老就像“生锈”一样，体内产生大量自由基，污染和腐蚀人体细胞、组织、器官，比如常见的心脑血管疾病，眼睛功能老化等都和自由基有关 。研究表明，血液中抗氧化剂的含量或日常饮食摄入抗氧化剂量高的人死亡率低 ，会降低老年痴呆、癌症、心血管疾病、白内障、糖尿病、高血压、关节炎、视力功能退化等问题的发生率。\"},{\"section\":\"饮食结构\",\"title\":\"色彩鲜艳的果蔬是良好的抗氧化、抗衰老食物\",\"content\":\"保持皮肤年轻态可以多吃些黄色、橙色和红色的蔬果，它们都富含β-胡萝卜素，在体内能转换成维生素A，是维持皮肤健康必需的营养素，且对眼睛极有帮助。当维生素A缺乏时皮肤抗感染能力下降，肤质粗糙，眼睛失去光泽。\"},{\"section\":\"饮食结构\",\"title\":\"色彩鲜艳的果蔬是良好的抗氧化、抗衰老食物\",\"content\":\"维生素C是抗氧化能力较强的物质。生活中，维生素C广泛存在于草莓、樱桃、蓝莓、葡萄、番茄、西兰花等水果蔬菜中。\"},{\"section\":\"饮食结构\",\"title\":\"色彩鲜艳的果蔬是良好的抗氧化、抗衰老食物\",\"content\":\"维生素E作为主要的天然抗氧化剂之一， 具有抗皮肤老化的作用，帮助保持皮肤弹性，有助延缓肌肤松弛和细纹形成，保持肌肤年轻状态。维生素E则广泛存在于坚果、粗粮中。\"},{\"section\":\"饮食结构\",\"title\":\"必需脂肪酸有助于保持年轻态的精力脑力\",\"content\":\"必需脂肪酸对保持年轻态有重要作用，尤其是OMEGA-3不饱和脂肪酸对大脑的视觉、学习能力、协调能力和情绪等功能的维持是必不可少的。平日要多吃些：南瓜籽、胡桃、三文鱼、青鱼、沙丁鱼、金枪鱼和鸡蛋、葵花籽、芝麻、玉米、大豆和小麦胚芽。\"},{\"section\":\"饮食结构\",\"title\":\"补充益生菌改善肠道健康\",\"content\":\"并非由于缺乏纤维素引起的便秘，可以由补充增加肠道益生菌食物而得以缓解。常吃酸奶、干酪、泡菜、味增汤、豆腐、葡萄酒、酸面包（小麦或黑麦制品）。常吃香蕉、洋葱、大豆等也有帮助。因为益生菌在肠道中能促进矿物质的吸收，并能制造维生素，主要是维生素K、B12、叶酸，这些维生素能够帮助缓解便秘。同时益生菌也能保护肠道的健康。\"},{\"section\":\"饮食结构\",\"title\":\"补充含钙质丰富的食物，预防钙丢失引发衰老\",\"content\":\"中国营养学会建议，30岁以后需要每天补充800mg的钙质，以维持机体健康，并预防衰老性疾病发生。牛奶和豆类食物是钙的优质来源，此外还可以选择发菜、紫菜、荠菜、木耳、海米、软骨、骨汤等，或者适量芝麻、花生、榛子等坚果，都是很好的钙质来源。\"}]','合理营养建议'),
	(43,'抗衰老方面','[{\"section\":\"运动对抗各种衰老问题\",\"title\":\"记忆力下降 ?  有氧运动和冥想能帮助调节情绪\",\"content\":\"有氧运动也叫心血管耐力型运动，能够由大脑和其他部位释放β-内啡肽（天然镇定剂）使人感到心情愉快，而且这种重复的、有规律性的运动，提供了一种冥想形式，将大脑左半球的优势转移到右半球。它可以影响人的情绪、提高记忆力水平。\\n结合心理体操，瑜伽，或是静坐，把你的脑袋调至放空的频率，渐渐让自己安静下来。\"},{\"section\":\"运动对抗各种衰老问题\",\"title\":\"免疫力下降 ?  适度运动提升人体免疫力\",\"content\":\"研究表明，常规适度的运动，如每天步行30分钟，就可以增加白细胞的水平，白细胞是免疫系统中抗感染的主力军（信息来自WebMD）。平缓而非紧张的运动形式，对提高免疫力也有利。\"},{\"section\":\"运动对抗各种衰老问题\",\"title\":\"用眼过度 ?  经常参加室外运动是对眼睛很好的放松\",\"content\":\"运动不仅可以锻炼身体，在运动时眼睛还可以得到休息。人在运动时，往往习惯向远处看，如果近处有活动的物体，又会调节眼肌，追随着活动的物体；此外，在室外散步、跑步、爬山还可以让眼睛进行远近视野的交换，这对眼调节肌的放松以及视疲劳的预防有很好的作用。\"},{\"section\":\"运动对抗各种衰老问题\",\"title\":\"肌肉松软 ?  力量训练保护骨骼、关节和肌肉\",\"content\":\"力量训练是身体的必须活动，目的在于增加和保持骨骼重量（这一点对女性更为重要，女性骨骼重量下降的风险比男性早得多），拥有足够的肌肉量以保护和稳定关节。\\n安排一周两三天进行身体力量训练，每次半小时，有指导的器械、哑铃或普拉提运动。\"}]','有效运动建议'),
	(44,'抗衰老方面','[{\"section\":\"\",\"title\":\"吸烟，要停下来也容易\",\"content\":\"你可能尝试戒烟，并体会过戒烟带来的各种不适反应。但是应该记得的是，这些不适反应只不过是些可以改善的症状，远比吸烟产生的直接损伤微小的多。更有许多实践经验表明，虽然戒烟会令体重增加，但增加的体重完全可以通过深呼吸练习、运动、均衡营养的方式减下来。\"},{\"section\":\"\",\"title\":\"长期有规律的适量饮酒帮助提升机体健康\",\"content\":\"人体可自然分解消化酒精，每天适当饮酒可使动脉血管扩张、血压下降，有利于促进血液循环，更可提升对人体有利的高密度脂蛋白及降低有害的低密度脂蛋白，从而防止心脏病发作。而适量葡萄酒（含有抗氧化物质白藜芦醇）可能降低机体老化速度。\\n适量的饮酒相当于每天一杯150ml红葡萄酒或360ml啤酒。\"},{\"section\":\"\",\"title\":\"常常受到辐射侵扰，需要抗氧化剂抵抗辐射侵袭\",\"content\":\"1.需要有意识地通过饮食或膳食补充剂摄入花青素、维生素C、维生素E、维生素A等抗氧化剂，及时清除代谢产生的自由基。 2.避免长时间连续操作电脑，眼睛与屏幕的距离应在40~50厘米。  3.室内光线要适宜，不可过亮或过暗。  4.安装防护装置，削弱电磁辐射的强度。\"},{\"section\":\"\",\"title\":\"睡眠周期符合自然规律，有助于延缓衰老\",\"content\":\"虽然不能够像长寿老人那样终生保持日出而作、日落而息的作息习惯，至少应该遵循身体的生物节律，尝试22点关闭灯光、游戏、电视、互联网，便可以很快进入睡眠状态。可以把你工作的时间放在早上，这样不仅有适量的睡眠，也睡在合适的时间。\"},{\"section\":\"\",\"title\":\"睡眠周期符合自然规律，有助于延缓衰老\",\"content\":\"不论晚上睡眠如何糟糕，你的生物钟需要一个起点。比起控制睡眠时间，控制起床时间更为容易，保证在7：30分左右起床（或者是自己既定的时间，但是不要超过8：00），尽量将其保持在30分钟的浮动范围内，让你的起床时间有规律是改善睡眠不规律的方法，当然这取决于个人的意愿。\"},{\"section\":\"\",\"title\":\"午间补充短暂睡眠，缓解疲劳事半功倍\",\"content\":\"在11点－13点小憩可以缓解晚间睡眠不足带来的疲劳感。但是午睡时间不要超过30分钟，否则反而会影响夜间睡眠。\"},{\"section\":\"\",\"title\":\"认真的休息，良好的睡眠，不要对身体产生过度消耗\",\"content\":\"通过一些训练，学习控制你的梦，享受美好的梦境，缓解压力，帮助你更好的睡眠。 根据英国睡眠指导专家研究，有42%的人通过睡前的想象控制自己的梦，多试几次这个比例可以达到更高。其实很简单，在睡前安静地坐上10分钟，放慢呼吸，暗示自己可以在做梦的时候保持清醒，想象一个快乐美好的主题，多试几次。\"},{\"section\":\"\",\"title\":\"避免那些可能引发失眠的习惯\",\"content\":\"避免下午食用这些食物：咖啡因（浓茶、苏打饮料、咖啡），油腻或辛辣食物。睡前4小时不要吃太饱。卧室里不要有蓝光光源，不能关闭的设备可以覆盖。睡前做剧烈运动也会令您因为兴奋而失眠。（信息来自WebMD）\"},{\"section\":\"\",\"title\":\"多动脑，让生活丰富起来，保持年轻心态\",\"content\":\"虽然人自出生后，神经细胞便不会再分裂，但使神经细胞批次联系而传递信息的“树突”却依然生长，即使是40~50岁还有生长的能力，勤于思考、学习是必要的事情。\"},{\"section\":\"\",\"title\":\"多动脑，让生活丰富起来，保持年轻心态\",\"content\":\"改变固执心态，扩大生活的兴趣点有助于抗衰老。新加坡国立大学研究指出，进行购物、烹饪、做家务等活动，比打麻将、跑步、看电影、出游等达到的防脑细胞退化效果明显。\\n将“收藏”作为一种爱好可以延缓衰老，因为“收藏”被证实具有辅助治疗高血压、胃病、神经衰弱的疾病的功效，已被20多个国家列入“心理疗法”的正式科目。\"},{\"section\":\"\",\"title\":\"多动脑，让生活丰富起来，保持年轻心态\",\"content\":\"接触儿童，激活童心和脑细胞。孩子总有问不完的为什么，他们的思路会让你从新的角度看待以为熟悉的事物，或者需要重新学习寻找答案。\"},{\"section\":\"\",\"title\":\"经常休息眼睛有助于保护视力\",\"content\":\"眼睛时刻处在光线的刺激中，时间越久，眼睛受到的伤害越大，当眼睛出现发痒、发紧、干涩时可连续眨眼10~20次，这动作有助于泪液分泌，滋养角膜、结膜，使眼球处于湿润环境中。长时间用眼后，应停下来向远处望一会儿。\"},{\"section\":\"\",\"title\":\"养成饭后漱口的习惯\",\"content\":\"养成饭后漱口的习惯，及时清除部分牙齿间隙中食物残渣，这样既减少了口腔中细菌数量，也断绝了细菌产酸的“粮草”。饭后漱口最好选择茶水，茶叶为碱性，可中和食物中的酸性物质，能使口腔短时间内保持弱碱性环境，有利于牙齿的保护。\"},{\"section\":\"\",\"title\":\"养成最佳的排便习惯\",\"content\":\"对于有便秘的人来说，养成定时大便的习惯十分重要，排便动作本身是一种反射性活动，是可以建立条件反射的。早晨是清理肠道排除宿便的最佳时间，一杯250ml的温水，一顿高纤维的营养早餐，食物和水进入胃内能引起“胃和结肠反射”，促进胃肠蠕动，出现大蠕动波，更易于排便反射的产生。因此早饭后是建立排便反射的好时机，开始蹲厕所的时候，可能并没有便意，也没有粪便排出，但却是结肠道重新调整规律的机会。只要坚持定时排便一段时间，形成习惯后就能定时顺利快捷地 排出大便了。\"}]','生活方式建议'),
	(45,'没有关注','[{\"section\":\"体检问题\",\"title\":\"恰当的饮食起居可以改善胆囊健康\",\"content\":\"除了家族遗传的因素外，饮食油腻、暴饮暴食、压力造成的焦虑或易怒都会诱使或加重胆囊问题。少吃多餐、避免油腻高脂肪的食物、腹部保暖、保持情绪平稳都有助于避免胆囊问题的加重。\"},{\"section\":\"体检问题\",\"title\":\"体重增加令代谢功能降低，增加罹患多种慢性疾病风险\",\"content\":\"肥胖的原因很多，但总归导致体内脂质代谢紊乱，造成脂肪在皮下和内脏器官周围堆积，这使得人们患冠心病、高血压、糖尿病等慢性疾病的风险增加，要改变生活方式；饮食和运动水平合理化；下定决心减肥，给自己时间和耐心，逐渐改变饮食和运动习惯；只有长期的改变才算成功。\"},{\"section\":\"体检问题\",\"title\":\"腰围超标意味越过慢病安全警戒线\",\"content\":\"腰围的重要性仅次于体重指数。腰围的大小可以表明脂肪堆积的部位。脂肪聚集在腰上的人更容易患高血压、糖尿病、冠状动脉疾病，因为腹部的脂肪更容易分解并在动脉里聚集。男性腰围超过102厘米或女性超过88厘米，患相关疾病的危险就会增大，尤其是体重指数为25或以上的人群。\"},{\"section\":\"体检问题\",\"title\":\"有高血压问题，生活方式是不容忽视的\",\"content\":\"最好的方法是改变生活方式，包括饮食习惯以及运动等。世界高血压组织研究表明，如果只是1期的高血压，是可以通过生活方式的改善控制血压的。即便是通过用药，生活方式也不容忽视，限制食盐的摄入，减重，运动，戒烟，限制酒精的摄入：每日饮用超过30毫升酒精,相当于600毫升啤酒,200毫升葡萄酒或75毫升标准威士忌,会使血压升高并使冠心病、中风的发病和死亡率上升。\"},{\"section\":\"体检问题\",\"title\":\"降低血脂，降低心脑血管患病风险\",\"content\":\"目前无论在美国还是中国，心脑血管疾病仍然是头号杀手。血脂在动脉血管壁的沉积是毫无声息的，如果体检已经确认血脂超标，就必须在生活方式上加以注意，加强运动，进行低脂肪饮食等，无效的情况下，需要在医生指导下借助药物改善状况。\"},{\"section\":\"体检问题\",\"title\":\"糖尿病意味着您需要更平衡的生活\",\"content\":\"血糖水平往往受到饮食、运动、差旅、压力等生活习惯的影响。糖尿病意味着需要比其他人更注意你的生活，摄入具有丰富膳食纤维的蔬果，减少高脂肪食物和零食，控制体重，控制吸烟和饮酒。有规律的运动可以帮助保持身体素质，对心脏和血管都有益处，促进血液循环，并定期监测自己的血糖水平，防止糖尿病并发症发生的风险。\"},{\"section\":\"体检问题\",\"title\":\"冠心病增加猝死风险\",\"content\":\"冠心病若养护不当，猝死率很高（统计表明50-60%猝死是冠心病）。吸烟、高血压、高血糖、高血脂、肥胖、久坐等问题都是冠心病发作的危险因素，通过合理的饮食和有规律的运动，可以大大减少患病的风险，甚至可以免除药物的使用。可以配合每天服用抗氧化维生素，如维生素E、维生素C、叶酸等，阻止低密度脂蛋白在血管的积累，预防发病。\"},{\"section\":\"体检问题\",\"title\":\"对于骨质疏松日常生活的调理比治疗更有效果\",\"content\":\"先天因素，饮食中钙摄入不足，不常运动，体格瘦弱，吸烟、过量饮酒、女性过早绝经，都是直接导致骨质疏松的危险因素，不过相对于恢复已降低的骨密度，保持骨密度不变要来的更容易些。预防措施包括摄入足够的钙和维生素D和从事负重锻炼。\"},{\"section\":\"体检问题\",\"title\":\"即便没有不舒服的症状也要重视脂肪肝\",\"content\":\"脂肪肝是肝细胞内过量的脂肪（甘油三酯）积累，脂肪肝最常见的原因是酗酒、肝炎、肥胖、糖尿病、血清甘油三酯水平过高，因为起初没有任何症状，但可引起肝脏的反复损害，最终可从脂肪肝发展到肝硬化。早期如果在生活方式上加以改善也很快就会看到改善。\"},{\"section\":\"体检问题\",\"title\":\"乳腺增生是可以消失的\",\"content\":\"乳腺增生多发生在25-35岁之间，发生率高达70%~85%，通过触按可以发现，如果没有疼痛的感觉或仅仅是经前期胀痛，往往不会引起重视，导致进一步恶化。婚育、膳食、压力和遗传因素是乳腺发病的主要原因。\\n将体重控制在健康的水平，特别注意心情和压力的调整，放松，静坐，舒压按摩都是很好的方法，保证足量有氧运动，每周至少3次。\"},{\"section\":\"体检问题\",\"title\":\"改变习惯，加速炎症康复\",\"content\":\"对于女性来说，任何引起阴道酸度下降（PH上升）的因素都会增加患阴道感染的可能性。月经前、月经期及妊娠期的阴道酸度都会随着体内雌激素水平的变化而下降。\\n所以除了遵医嘱用药外，保证阴部环境干爽、减少久坐、穿宽松、透气的裤子，清洁性交、常常运动是预防感染发生的方法，不主张频繁冲洗阴道，这会增加患盆腔炎的风险。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不良的就餐习惯增加健康风险\",\"content\":\"常在外就餐 常在外吃，油脂、盐可能摄入过量了。这对血脂、血压、血糖的控制都不利，吃的又油又咸，是造成动脉粥样硬化的原因之一，过多的盐会影响血压健康，造成肾脏负担。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不良的就餐习惯增加健康风险\",\"content\":\"不吃早餐 不吃早餐是胆囊疾患和超量午餐的隐患，不吃早餐很容易午餐吃撑，这会引起身体肥胖，血糖不稳，血压升高，甚至加重中风隐患。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不良的就餐习惯增加健康风险\",\"content\":\"经常吃得过饱 常常吃撑，这会影响血糖平稳，令脂肪合成增加，同时增加肝脏和消化系统负担，最终影响到血压和血脂。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，不利于机体保持最佳状态\",\"content\":\"爱吃油炸 油炸食物就是三高人群的定时炸弹，会加重高血压，糖尿病，甚至中风、致癌的风险。油炸也令食物不容易被消化吸收，加之高温对营养的破坏，常吃油炸食物会导致营养不均衡。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，不利于机体保持最佳状态\",\"content\":\"常吃包装或加工食品 长期食用加工食物会令身体缺乏必要的营养，尤其是水溶性维生素和蛋白质。这些食物中的添加剂不仅会阻止营养素的吸收和利用，还会加速营养素的流失，并增加肝脏的代谢负担，影响气色和精力。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，不利于机体保持最佳状态\",\"content\":\"常喝甜饮料 甜饮料和加重健康风险。甜饮料尤其是碳酸类饮料会增加身体钙质流失，同时升高血糖，促进脂肪合成，久了会令血管壁受损，血糖紊乱，增加心脑血管疾病风险。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，不利于机体保持最佳状态\",\"content\":\"爱吃重口味食物 口味诱人的菜肴多数采用了多油多酱多糖的烹饪方式，油脂和糖分的过量都会增加体内脂肪含量，盐会增加肾脏负担，这会令三高的风险增加。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不健康食物吃得多，不利于机体保持最佳状态\",\"content\":\"肉食过多 看似美味的肉食，却隐藏着高脂肪、高胆固醇，长期如此会加速血栓的形成。大量的人群调查观察到：多食入动物性脂肪（主要含饱和脂肪酸），可使血胆固醇和低密度脂蛋白含量增高，高密度脂蛋白含量则降低，进而增加动脉硬化的危险，对高血压、高血脂、糖尿病、肥胖等慢性病患者不利。\"},{\"section\":\"生活习惯提醒\",\"title\":\"健康食物吃得过少，令机体缺乏必需营养，影响健康状态\",\"content\":\"豆、奶制品吃得少 很少吃豆、奶制品可能导致你缺乏钙质，奶制品和豆制品被认为是钙的主要来源，钙是维持骨骼、牙齿、神经健康的主要矿物质，缺乏则容易造成失眠、紧张、骨质疏松、龋齿等问题。同时钙也被认为和血压健康关系密切，缺乏时会导致血压升高。\"},{\"section\":\"生活习惯提醒\",\"title\":\"健康食物吃得过少，令机体缺乏必需营养，影响健康状态\",\"content\":\"蔬果吃得少 果蔬吃得过少对你无益。水果蔬菜能提供丰富的水溶性纤维素可以降低身体对脂肪和胆固醇的吸收，还有平稳血糖的效果。果蔬还能提供清除自由基的抗氧化物，日常生活的污染，辐射，煎炸食物，阳光照射甚至呼吸都会产生令身体老化的自由基。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳影响健康状态\",\"content\":\"经常熬夜 经常熬夜，可以被称为慢性睡眠不足，已经有大量的科学研究表明睡眠不足与心脏疾病、高血压、中风、糖尿病有直接关系。\\n当然在这些问题发生之前，你可能正在经历着熬夜带来的皮肤问题，只需要几个晚上，皮肤就马上会呈现出蜡黄，眼睛浮肿，长期则导致皮肤黯淡，细纹，眼睛下方的黑眼圈。 没有在适当的时候睡觉，生物节律被打乱，身体会释放更多的应激激素皮质醇，这些皮质醇会分解皮肤中的胶原蛋白，令你的皮肤失去光滑和弹性。\\n睡眠不足与食欲增加有直接关系，从而导致肥胖，据2008年的一项研究，熬夜人群发胖的可能性比正常人高出30%，由于刺激了对高脂肪，高碳水化合物的食物的渴望。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳影响健康状态\",\"content\":\"睡眠不足 每个人需要的睡眠时间长短并不一样，如果每天醒来你不需要闹钟，不需要冷水，不需要咖啡就能让自己充满能量地迎接新的一天，这说明你的睡眠效率很高，总之自己的感觉是最直接、最重要的。\\n但如果因为睡不够觉得困倦则说明睡眠效率较低。长期睡眠不足的话，会使得记忆力减退、情绪波动厉害。如果只睡很短时间，就会提高血压和心率的平均水平，由此可能会增大心血管系统的压力。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳影响健康状态\",\"content\":\"多梦 梦多是在快速动眼睡眠中出现的，这段时间人的体温上升，心跳增加，血流速快，区别于入睡时的生理状态（体温下降），这类似于人们醒着时候的生理状态，人们也更容易在这段时间内醒过来。这种状况出现的原因很多，压力大、精神紧张、焦虑都有可能成为多梦易醒的原因。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳影响健康状态\",\"content\":\"起床困难或睡不够 喜欢赖床或睡后精力不能补足。可能是繁重的脑力或体力劳动造成极度疲劳，使得需要更长时间的睡眠来恢复。也可能不规律的生活习惯造成了生物钟紊乱，无法控制睡眠和觉醒的正常切换。\"},{\"section\":\"生活习惯提醒\",\"title\":\"睡眠质量不佳影响健康状态\",\"content\":\"失眠 入睡困难或者失眠的人往往表现为：很难睡着、晚上总是醒、睡得不踏实，很难再入睡。原因很多，包括压力、咖啡因、酒精、环境变化、时差、慢性疾病、焦虑等等，解决问题首先要找到原因，是不是有些不良的生活习惯，导致这个问题，不过，有时候，这可能是自己无法控制的，但是要明确一点，这并不是不可以解决的问题，所以不用担心，给自己一点儿耐心。\"},{\"section\":\"生活习惯提醒\",\"title\":\"不规律的睡眠扰乱循环系统生物钟，身体隐形“生病”\",\"content\":\"睡眠不规律 其实除了睡眠，我们心率、呼吸、血压、体温、蛋白质合成等体内一切生命活动，往往都呈现昼夜节律的倾向，反映在血液循环、内分泌和各种器官功能中。不规律的睡眠就好比在不停地倒时差，各器官的生物钟调整所需的时间不同，睡眠两三天就可以调整过来，但是体内的器官及循环系统的生物钟却需要两三周的时间调整。\"},{\"section\":\"生活习惯提醒\",\"title\":\"目前的运动状况起不到改善健康的作用\",\"content\":\"每天或者隔天花30~60分钟，做一些有氧运动，这是保持年轻活力的很好方法，有氧运动会使肌肉消耗大量的氧气和营养物质，从而使得胃肠、心肺、肾脏等功能提高。在一定条件下，能量消耗得越多，这种补偿作用越明显。\"},{\"section\":\"生活习惯提醒\",\"title\":\"吸烟几乎损害身体的每个器官\",\"content\":\"吸烟几乎损害身体的每个器官，造成许多疾病和减少一般吸烟者的健康。由吸烟引起的疾病的名单已经扩大到包括腹主动脉瘤，急性髓系白血病，白内障，宫颈癌，肾癌，胰腺癌，肺炎，牙周炎，和胃癌。这些都是除了先前已知的由吸烟引起的的疾病，包括膀胱癌，食道癌，喉癌，肺癌，口腔和咽喉癌症，慢性肺部疾病，冠心病，心血管疾病，以及影响生殖系统和婴儿猝死综合症。\"},{\"section\":\"生活习惯提醒\",\"title\":\"过量饮酒损伤健康\",\"content\":\"过量饮酒的人看上去往往更加憔悴。人们普遍认为过量饮酒可导致提前衰老并遭到与衰老有关的疾病侵袭。\\n意大利米兰大学进行的一项研究指出，过量酒精可使细胞染色体失去完整性和稳定性的保护，从而加速衰老增加癌症风险。\\n世卫组织认为每日安全饮酒量的上限是25g酒精，相当于1两42度白酒，或200ml红酒，一瓶啤酒。\"},{\"section\":\"生活习惯提醒\",\"title\":\"辐射产生的自由基加速人体细胞氧化\",\"content\":\"手机和电脑产生的低频电磁辐射是人体外源性自由基产生的原因之一。随着辐射剂量累积增加，可能会引起神经系统、循环系统、免疫系统、生殖系统以及皮肤和眼睛的功能、代谢或形态的改变，这是一个自由基生成加速身体细胞氧化的过程。\"}]','基本健康评估'),
	(46,'没有关注','[{\"section\":\"饮食习惯\",\"title\":\"口味清淡些\",\"content\":\"过量摄入脂肪会降低身体内胰岛素的活性，使血糖升高，且血中脂肪量过高易沉积在血管内壁形成斑块，造成动脉硬化；吃盐多能引起血压、血糖升高，三高人群应采取低脂肪、低盐、低糖、高纤维、高维生素饮食。\"},{\"section\":\"饮食习惯\",\"title\":\"口味清淡些\",\"content\":\"少用油、酱、糖、辣，你才能体会食物的天然味道，而且也减少对胃肠的刺激。清淡的食物减少代谢负担，对提高免疫力、精力、改善疲劳都有帮助。\"},{\"section\":\"饮食习惯\",\"title\":\"你适合高碳水化合物早餐\",\"content\":\"《美国临床营养学杂志》报道，经常不吃早餐，能增加患心脏病的风险。而且经常不吃早餐的人，更容易肥胖、胆固醇增高、腹部脂肪堆积。\\n你更适合谷物和水果组成的高碳水化合物早餐，而不是油腻的高蛋白早餐。\"},{\"section\":\"饮食习惯\",\"title\":\"最好不吃煎炸食物\",\"content\":\"煎炸食物热量高，高温油炸过程中产生对血管有害的自由基，以及致癌物。研究表明常吃油炸食物人群的冠心病，心绞痛，心梗，癌症发病率远远高于正常人群。\"},{\"section\":\"饮食习惯\",\"title\":\"坚果作为零食\",\"content\":\"原味的开心果、松籽、花生、杏仁、富含对心脑血管有益的不饱和脂肪酸和蛋白质，可以作为零食搭配适量的水果，令血糖平稳释放。\"},{\"section\":\"饮食习惯\",\"title\":\"选择有效饮水\",\"content\":\"常喝白水或矿泉水。人体的70%是水分，确保体内有足够洁净的水分，才能将代谢产生的废物及时排出体外。白水、矿泉水、鲜榨的蔬果汁、口味清淡的菜汤都可以是补充水分的来源。\"},{\"section\":\"饮食习惯\",\"title\":\"天然食物有益健康\",\"content\":\"尽量选择新鲜的当地、当季产新鲜食物，它们除了能提供必需的营养素还能帮助代谢掉人体中产生有的有害物质，如促使老化的自由基。\\n加工食品中存在多种人工合成的化学物质，已有许多国家研究表明加工食品中的各种添加剂摄入过多，会存在致癌风险。\"},{\"section\":\"饮食习惯\",\"title\":\"天然食物有益健康\",\"content\":\"慢慢习惯清淡饮食，让这些食物常常出现在你的餐桌上，这可以帮助你降低胆固醇：豆类及豆制品，含有丰富的优质蛋白质，可以提高身体对蛋白利用； 香菇、黑木耳、洋葱、大蒜能使血中有害胆固醇转化成有益心脏的胆固醇；高纤维饮食可以平衡血糖：燕麦、粗杂粮、干豆类、海带、新鲜的蔬菜。\"},{\"section\":\"饮食习惯\",\"title\":\"细嚼慢咽，七分饱\",\"content\":\"日本学者统计约有30%--40%的老年性痴呆病人，青壮年时期有饱食的习惯。因摄入的总热量远远超过机体的需要 ,致使体内脂肪过剩 ,血脂增高 ,导致脑动脉粥样硬化 ,有一种被称为“纤维芽细胞生长因子”的物质在大脑中明显增多 ,这种物质被认为是引起脑动脉硬化的重要因素。实验表明，长期适度减少热量摄入的人更长寿，体重更轻，患心脑血管疾病的风险更低。\"},{\"section\":\"饮食结构\",\"title\":\"增加矿物质来源\",\"content\":\"骨矿研究测试表明，人到35岁以后，平均骨矿含量以每年0.5-1%的速率下降。钙在人体中处于不断丢失和补损的动态平衡过程中，人体中钙的存量关系到骨骼、牙齿的韧性，血液凝结，激活酶系统，维持肌肉伸缩性和心跳规律。\\n通过膳食来调节体内钙平衡是人类最佳补钙法，牛奶和豆类食物是钙的优质来源，此外还可以选择发菜、紫菜、荠菜、木耳、海米、软骨、骨汤等，或者适量芝麻、花生、榛子等坚果，都是很好的钙质来源。\"},{\"section\":\"饮食结构\",\"title\":\"增加果蔬食物，为健康添保障\",\"content\":\"研究表明，血液中抗氧化剂的含量或日常饮食摄入抗氧化剂量高的人死亡率低。同时也会降低老年痴呆、癌症、心血管疾病、白内障、糖尿病、高血压、关节炎、视力功能退化等问题的发生率。富含抗氧化剂的蔬果，主要有草莓、樱桃、蓝莓、葡萄、番茄、西兰花、胡萝卜、豌豆、玉米等。\"},{\"section\":\"饮食结构\",\"title\":\"选择健康肉类\",\"content\":\"吃肉的时候尽量选择浅色肉食，如鱼类、水生贝壳、去皮鸡鸭肉等，因为它们中的饱和脂肪及胆固醇含量明显低于红肉。但要控制好量，平均每天不要超过半幅扑克牌大小的量。\"}]','合理营养建议'),
	(47,'没有关注','[{\"section\":\"\",\"title\":\"你需要戒烟\",\"content\":\"这里并不是在劝你少吸一点儿，事实上，少吸一支半支对你的现状不会有任何改变，不吸烟的人一定比吸烟的人更加轻松享受，并且不会失去良好的健康，充沛的精力，心灵的安宁，勇气，自信，自尊和自由。这是最关键的，因为你没有必要向任何人证明吸烟给你带来了多大的好处。别人能看到的，只是你被这种东西奴役了，这么容易就被征服了，一支接着一支，失去自由，并且没有勇气克服。\"},{\"section\":\"\",\"title\":\"长期有规律的适量饮酒帮助提升机体健康\",\"content\":\"人体可自然分解消化酒精，每天适当饮酒可使动脉血管扩张、血压下降，有利于促进血液循环，更可提升对人体有利的高密度脂蛋白及降低有害的低密度脂蛋白，从而防止心脏病发作。而适量葡萄酒（含有抗氧化物质白藜芦醇）可能降低机体老化速度。\\n适量的饮酒相当于每天一杯150ml红葡萄酒或360ml啤酒。\"},{\"section\":\"\",\"title\":\"常常受到辐射侵扰，需要抗氧化剂抵抗辐射侵袭\",\"content\":\"1.需要有意识地通过饮食或膳食补充剂摄入花青素、维生素C、维生素E、维生素A等抗氧化剂，及时清除代谢产生的自由基。 2.避免长时间连续操作电脑，眼睛与屏幕的距离应在40~50厘米。  3.室内光线要适宜，不可过亮或过暗。  4.安装防护装置，削弱电磁辐射的强度。\"},{\"section\":\"\",\"title\":\"一定要在23点也就是子时到来前睡觉\",\"content\":\"人的一天有两个自然的睡眠周期，这是由人的生物节律决定的，一个是22点左右，这时候人的体温下降，进入能量低潮期，这时候如果关闭灯光、游戏、电视、互联网，便可以很快进入睡眠状态。另外一个自然的睡眠周期是下午1点~4点，同样的也是进入能量低潮期，这时候也会犯困，需要睡眠。\\n所以，正确的睡眠应该遵循身体的生物节律，22~23点入睡，早上6-7点醒来。可以把你工作的时间放在早上，这样不仅有适量的睡眠，也睡在合适的时间。\"},{\"section\":\"\",\"title\":\"不论晚上睡眠如何糟糕，你的生物钟需要一个起点\",\"content\":\"比起控制睡眠时间，控制起床时间更为容易，保证在7：30分左右起床（或者是自己既定的时间，但是不要超过8：00），尽量将其保持在30分钟的浮动范围内，让你的起床时间有规律是改善睡眠不规律的方法，当然这取决于个人的意愿。\"},{\"section\":\"\",\"title\":\"循序渐进延长睡眠时间\",\"content\":\"如果你经常看着电视、看着书进入梦乡，这意味着你缺乏足够的睡眠，补充睡眠并不需要比往常提前1小时上床，这只会令你睡不着觉，每天晚上只要提前10~15分钟，在下午13点~16点之间小睡一下，半个小时为宜，多了则会影响晚间的睡眠，在调整睡眠规律期间清淡饮食。\"},{\"section\":\"\",\"title\":\"梦是你熟睡时心中产生的愿望，你可以学习控制你的梦\",\"content\":\"通过一些训练，学习控制你的梦，享受美好的梦境，缓解压力，帮助你更好的睡眠。\\n根据英国睡眠指导专家研究，有42%的人通过睡前的想象控制自己的梦，多试几次这个比例可以达到更高。其实很简单，在睡前安静的坐上10分钟，放慢呼吸，暗示自己可以在做梦的时候保持清醒，想象一个快乐美好的主题，多试几次。\"},{\"section\":\"\",\"title\":\"提高睡眠质量\",\"content\":\"固定起床时间可以帮助生物钟正常工作，也就是说，无论什么时候开始睡觉一定要在每天的同一时间起床，这样可以在一定程度上保证生物钟的正常运作，也是拥有高质量睡眠的前提条件，也可以通过午睡或者安全状态下的Flash睡眠(瞬时闪睡)来进行一定的睡眠补充，这是最现实的办法。但是决不能根据前一天的睡觉时间决定起床的时间，否则很难改善生物钟紊乱的症状。\"},{\"section\":\"\",\"title\":\"给自己一些时间调整你的睡眠\",\"content\":\"上床以后不要继续思考烦心事，如果睡不着，宁可起来做点事，也不要躺在床上发愁，真正折磨你的不是睡眠不足，而是你的忧愁。无论晚上几点入睡，早上都要在同一时间起床。可以尝试这个睡前的意念调节，平躺，全身放松，集中精神调节呼吸。闭上眼睛，浮想一些简单的图形；然后控制呼吸的节奏，缓慢吸气，保持一秒钟静止状态，缓慢呼气，反复几次。这样可以排除杂念，达到睡眠前的最佳状态！\"}]','生活方式建议');

