INSERT INTO `tbl_gnc_sports` (`id`, `title`, `sub`, `content`, `img`, `branch`, `sex`)
VALUES
	(1,'瑜伽','平衡身心','消耗能量：4.3千卡/千克.小时\n有氧等级：5~6\n试着把瑜伽融入你的生活，通过呼吸调息及那优美、独特的姿势给予身体适度的刺激，以除去身体的不安定因素，保存并增加体内生命能量使之不浪费不虚耗，从而令身心健康、从容自在。不过，贵在坚持！','http://www.gnc.com.cn/reviews/images/sport/yujianan.jpg','容颜美好/免疫力方面',NULL),
	(2,'爬楼梯','简单有氧，方便坚持','消耗能量：5.9千卡/千克.小时\n有氧等级：7~8\n在写字楼都可以进行的锻炼方法，能使肌肉获得比平常高出10倍的氧气，促进细胞新陈代谢，锻炼心肺功能。','http://www.gnc.com.cn/reviews/images/sport/pashannan.jpg','容颜美好/抗衰老方面','male'),
	(3,'跳舞','开心的全身运动','消耗能量：4千卡/千克.小时\n有氧等级：5~6\n关乎心情的运动，或奔放、或优雅、或舒展、或跃动，跟随者音乐用肢体诉说心情，或者一个人，无关舞步，无关节拍，只是悠扬地摆动都会令人开心。虽然热量消耗未必够高，但因能同时愉悦身心，可以成为培养运动习惯的入门项目。','http://www.gnc.com.cn/reviews/images/sport/tiaowunan.jpg','容颜美好/没有关注','male'),
	(4,'游泳','对关节无损的全身运动','消耗能量：5.5千卡/千克.小时\n有氧等级：7\n禁忌：女子生理期，肾病，传染疾病，肝病，心脏病，活动性肺结核\n人和水天生就是亲近的，游泳能消耗能量、塑造体型。游泳是全身运动，要做好准备活动。','http://www.gnc.com.cn/reviews/images/sport/youyongnan.jpg','容颜美好/免疫力方面/没有关注','male'),
	(5,'有氧操','让身心融入音乐的韵律运动','消耗能量：3.18千卡/千克.小时\n有氧等级：5~6\n禁忌：膝关节损伤，腰椎疾病\n在音乐的伴奏下，锻炼着全身的肌肉力量和心肺功能，而且还可以放松身心。不一定非要在健身房哦，在家下载一段有氧操视频跟着做也不错。','http://www.gnc.com.cn/reviews/images/sport/youyangcaonv.jpg','容颜美好',NULL),
	(6,'按揉腹部','减少腹部脂肪堆积，有助于加强胃肠排毒','入睡前或起床前进行,取仰卧位,双膝屈曲,全身放松,双手相叠，以肚脐为中心。先按顺时针方向，绕脐按揉腹部50次，再逆时针方向按揉50次。按揉时，稍微用力，有明显的按压感，精力要集中，呼吸要自然。持之以恒，才会收到明显的健身效果，促进排便也能帮助身体排毒。','http://www.gnc.com.cn/reviews/images/sport/anroufubunv.jpg','免疫力方面','female'),
	(7,'按揉腹部','减少腹部脂肪堆积，有助于加强胃肠排毒','入睡前或起床前进行,取仰卧位,双膝屈曲,全身放松,双手相叠，以肚脐为中心。先按顺时针方向，绕脐按揉腹部50次，再逆时针方向按揉50次。按揉时，稍微用力，有明显的按压感，精力要集中，呼吸要自然。持之以恒，才会收到明显的健身效果，促进排便也能帮助身体排毒。','http://www.gnc.com.cn/reviews/images/sport/anroufubunan.jpg','免疫力方面','male'),
	(8,'跳舞','开心的全身运动','消耗能量：4千卡/千克.小时\n有氧等级：5~6\n关乎心情的运动，或奔放、或优雅、或舒展、或跃动，跟随者音乐用肢体诉说心情，或者一个人，无关舞步，无关节拍，只是悠扬地摆动都会令人开心。虽然热量消耗未必够高，但因能同时愉悦身心，可以成为培养运动习惯的入门项目。','http://www.gnc.com.cn/reviews/images/sport/tiaowunv.jpg','免疫力方面/没有关注','female'),
	(9,'爬楼梯','简单有氧，方便坚持','消耗能量：5.9千卡/千克.小时\n有氧等级：7~8\n在写字楼都可以进行的锻炼方法，能使肌肉获得比平常高出10倍的氧气，促进细胞新陈代谢，锻炼心肺功能。','http://www.gnc.com.cn/reviews/images/sport/pashannv.jpg','免疫力方面','female'),
	(10,'跑步(16公里/小时)','聪明人的耐力挑战','消耗能量：6.9千卡/千克.小时\n有氧等级：7~8\n禁忌：膝关节损伤\n希腊山的岩石上刻着这样的文字：你想变得健康吗？那就跑步吧；你想变得聪明吗？那就跑步吧；你想变得美丽吗？那就跑步吧！唯一要提醒的是，不要在运动后喝冰水，不要吹冷风，要及时换下汗湿的衣物。','http://www.gnc.com.cn/reviews/images/sport/paobunan.jpg','抗衰老方面','male'),
	(11,'跳绳','高耗能的简单运动','消耗能量：10千卡/千克.小时\n有氧等级：5~6\n禁忌：膝关节损伤，腰椎疾病\n跳绳是非常简单有效的有氧运动，每半小时消耗热量400千卡，对心肺系统等各种脏器、协调性、姿态、减肥等都有相当大的帮助，通过促进血液循环而改善气色和健康。','http://www.gnc.com.cn/reviews/images/sport/tiaoshengnan.jpg','抗衰老方面','male'),
	(12,'爬山','有氧和无氧锻炼兼备','消耗能量：6.9千卡/千克.小时\n有氧等级：7~8\n离开喧嚣城市，呼吸充足的新鲜氧气才是爬山的真正目的，能使肌肉获得比平常高出10倍的氧气，促进细胞新陈代谢，加快脂肪消耗。','http://www.gnc.com.cn/reviews/images/sport/pashannan.jpg','没有关注','male'),
	(13,'游泳','对关节无损的全身运动','消耗能量：5.5千卡/千克.小时\n有氧等级：7\n禁忌：女子生理期，肾病，传染疾病，肝病，心脏病，活动性肺结核\n人和水天生就是亲近的，游泳能消耗能量、塑造体型。游泳是全身运动，要做好准备活动。','http://www.gnc.com.cn/reviews/images/sport/youyongnv.jpg','没有关注','female'),
	(14,'爬山','有氧和无氧锻炼兼备','消耗能量：6.9千卡/千克.小时\n有氧等级：7~8\n离开喧嚣城市，呼吸充足的新鲜氧气才是爬山的真正目的，能使肌肉获得比平常高出10倍的氧气，促进细胞新陈代谢，加快脂肪消耗。','http://www.gnc.com.cn/reviews/images/sport/pashannv.jpg','没有关注','female');

