DROP TABLE IF EXISTS `tbl_gnc_tabs`;

CREATE TABLE `tbl_gnc_tabs` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) DEFAULT NULL,
  `title` varchar(255) DEFAULT NULL,
  `description1` text,
  `description2` text,
  `branch` varchar(255) DEFAULT NULL,
  `description3` text,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

LOCK TABLES `tbl_gnc_tabs` WRITE;
/*!40000 ALTER TABLE `tbl_gnc_tabs` DISABLE KEYS */;

INSERT INTO `tbl_gnc_tabs` (`id`, `name`, `title`, `description1`, `description2`, `branch`, `description3`)
VALUES
	(1,'合理营养建议','建议您的健康饮食','[\'吃什么和怎么吃对改善免疫系统的功能同样重要，尤其是当免疫系统正处威胁之中。选择对免疫力有帮助的食物，如多彩果蔬、必需脂肪酸含量丰富的种子和深海鱼、富含益生菌的发酵食物。同时细嚼慢咽和清淡的饮食能减轻胃肠中免疫系统的负担，也有助于减少免疫系统问题的发生。\']',NULL,'免疫力方面',NULL),
	(2,'免疫力评估','根据您对免疫力部分问题的回答，\n评估您目前免疫力状况处于：','{\"1\":{\"title\":\"免疫系统有问题\",\"img\":\"mianyili1.jpg\",\"description\":\"你的免疫系统健康已经出现问题，需要从营养、起居、情绪、运动各方面，并配合能够有效提升免疫系统健康水平的营养补充剂来进行全方位的调整。必要时，寻求医疗辅助以降低你的健康风险。\"},\"2\":{\"title\":\"免疫系统有风险\",\"img\":\"mianyili2.jpg\",\"description\":\"你的免疫系统状态稍有不佳，导致你有一些健康困扰，你可以通过调整营养、起居、情绪、运动等来改善免疫系统功能，提升整体健康状态。\"},\"3\":{\"title\":\"免疫系统健康\",\"img\":\"mianyili3.jpg\",\"description\":\"你的免疫系统状态目前良好，你可以加强对免疫系统有帮助的营养摄入，帮助你的免疫系统保持最佳状态。\"}}',NULL,'免疫力方面',NULL),
	(3,'有效运动建议','基本健康运动习惯养成','{\"title\":\"当免疫系统面临挑战时，正确的运动是必不可少的。研究证实过于疲劳或激烈的运动会抑制免疫系统，而平缓非紧张的运动形式对提高免疫力最为有利。每日30分钟的慢运动，包括瑜伽、冥想、太极等，有助于激发你的免疫系统。\",\"items\":[\"中等强度避免运动损伤\",\"有氧运动提升心血管耐力\",\"每周3、4次，每次20~60分钟避免过度消耗\"]}','缓而非紧张的运动形式，对提高免疫力最为有利。研究证实，中国的太极拳可以使T细胞（身体免疫细胞的一种）的数量增加40%之多。（出自《营养圣经》） 美国运动医学会（ACSM）推荐20~60分钟的有氧活动，每周3~4次即可，对提高最大吸氧量而言，每周锻炼3次与5次的效果基本相同。 注意：运动过程中请把您的心率保持在112 - 140次/每分钟范围内。','免疫力方面','※消耗能量：不同生活活动和运动每小时每公斤体重所消耗的能量。数值越高代表单位时间内从事该项运动所消耗的能量越大，对减重越有帮助。\n※有氧等级：针对有氧运动而言，是对从事该项运动时应该达到的运动强度的描述。有氧等级越高，对有氧酵素、心血管及新陈代谢的影响就越大，最终会影响到体内的脂肪比例。\n有氧等级5：呼吸略有加快，会出汗，稍有疲惫感，但是很舒服，类似热身完成的感觉。\n有氧等级6：小幅度运动，或运动一段时间后的感觉，有疲惫感，但是知道自己可以坚持下来。\n有氧等级7：这是运动进入状态时的感觉，呼吸沉重，虽然可以说话，但是宁愿不说。\n有氧等级8：这是到达运动极限时的感觉。有明显的疲惫感，呼吸沉重急促，突破这个极限，保持平稳的运动，还会恢复到第7级状态。'),
	(4,'坏习惯弥补','坏习惯弥补建议','{\"bad\":\"改掉坏习惯对于改善身体状况永远是第一位的。对于戒除有困难者，建议采取以下方法以减少坏习惯带来的健康损害。\",\"good\":\"恭喜！您的生活习惯很好，继续保持那些好的生活习惯，对维护稳定的健康状态大有裨益。\"}',NULL,'免疫力方面',NULL),
	(5,'生活方式建议','你的健康生活作息时间表','[[\"7:00~8:00\",\" 随着阳光起床，振奋的精神启动最佳身体状态\"],[\"起床后的第一件事\",\" 晨练，早晨的太阳和空气更有助于增强免疫功能\"],[\"起床后的第二件事\",\" 容易消化吸收的营养早餐（蛋类、全谷物粥、一个水果），饭后服用螺旋藻胶囊和紫锥菊精华胶囊，循序渐进改善免疫力\"],[\"起床后的第三件事\",\" 排除宿便，涤清肠道，畅通排毒机能\"],[\"10:10~10:20\",\" 十分钟小运动，劳逸结合\"],[\"12:00~13:00\",\" 午餐7~8分饱，营养合理，打造健康基础（主食超过肉类、充足绿叶蔬菜），饭后散步20分钟\"],[\"13:00~13:30\",\" 午睡20~30分钟，恢复精气神\"],[\"18:30~19:30\",\" 晚餐尽量清淡，以谷物和蔬菜为主，7~8分饱 ，补充维生素C和浓缩鱼油软胶囊(ω-3)等免疫加强营养素\"],[\"21:00~21:30\",\" 做些瑜伽、太极等舒缓运动，可提高免疫力\"],[\"22:00~23:00\",\" 按时进入睡眠状态，保证享受8小时充足的睡眠\"]]',NULL,'免疫力方面',NULL),
	(6,'抗衰老评估','根据您对抗衰老部分问题的回答，\n评估您目前衰老状况处于：','{\"1\":{\"title\":\"高速老化\",\"img\":\"shuailao1.jpg\",\"description\":\"是什么加速了你的老化？人从出生就开始经历逐步老化的过程，而各种不良的生活习惯、压力、环境、疾病会使一些衰老问题提前发生，导致“30岁拥有40岁的身体”。你需要趁年轻，优化生活习惯，增强自身的防衰机制，延缓衰老的过程。也可以明智地服用营养补充剂，支持身体的防御系统和自愈力而达到抵抗衰老的目的。以下报告，根据你所描述的生活/健康状态，为你定制远离衰老的评估和建议。\"},\"2\":{\"title\":\"正常老化\",\"img\":\"shuailao2.jpg\",\"description\":\"\"},\"3\":{\"title\":\"低速老化\",\"img\":\"shuailao3.jpg\",\"description\":\"你比实际年龄年轻的状态令人羡慕！关注衰老问题更是很聪明的表现。衰老并不足惧，人从出生就开始经历逐步老化的过程。保护自身已有的防御系统和修复功能，不但可以避免因衰老给身体带来的伤害，也可以享受低速衰老带来的健康生活。以下报告，根据你所描述的生活/健康状态，为你定制延缓衰老的评估和建议。\"}}',NULL,'抗衰老方面',NULL),
	(7,'合理营养建议','建议您的健康饮食','[\'尝试重新思考你的饮食目的，一种高抗氧化、低发病且一生可为的饮食计划。良好的饮食习惯帮助你的身体保持正常的新陈代谢，避免体内产生过多的自由基。均衡的饮食结构可以帮助你摄取足量的高抗氧化物食物。明智地服用饮食补充剂，以支持身体的防御系统和自愈力。\']',NULL,'抗衰老方面',NULL),
	(8,'有效运动建议','基本健康运动习惯养成','{\"title\":\"养成一个基本运动习惯，把运动看成和吃饭、睡觉一样平常和必须的事情，帮助你的代谢维持在正常水平从而对抗衰老、减少慢性疾病发生。\",\"items\":[\"中等强度避免运动损伤\",\"有氧运动提升心血管耐力\",\"每周3、4次，每次20~60分钟避免过度消耗\"]}','随着年龄增长，体内的激素水平和代谢都起了变化，有一定紧张程度，而又不过量的有氧运动，能锻炼心肌，改善血管弹性，调节体内代谢水平，也减少关节损伤，避免过量运动产生过多自由基的可能性。 运动过程中请把您的心率保持在112 - 140次/每分钟范围内，这是适合你的运动强度。 美国运动医学会（ACSM）推荐20~60分钟的有氧活动，每周3~4次即可，正常的新陈代谢意味着正常的氧化防御系统。','抗衰老方面','※消耗能量：不同生活活动和运动每小时每公斤体重所消耗的能量。数值越高代表单位时间内从事该项运动所消耗的能量越大，对减重越有帮助。\n※有氧等级：针对有氧运动而言，是对从事该项运动时应该达到的运动强度的描述。有氧等级越高，对有氧酵素、心血管及新陈代谢的影响就越大，最终会影响到体内的脂肪比例。\n有氧等级5：呼吸略有加快，会出汗，稍有疲惫感，但是很舒服，类似热身完成的感觉。\n有氧等级6：小幅度运动，或运动一段时间后的感觉，有疲惫感，但是知道自己可以坚持下来。\n有氧等级7：这是运动进入状态时的感觉，呼吸沉重，虽然可以说话，但是宁愿不说。\n有氧等级8：这是到达运动极限时的感觉。有明显的疲惫感，呼吸沉重急促，突破这个极限，保持平稳的运动，还会恢复到第7级状态。'),
	(9,'基础健康评估','基础健康评估','{\"normal\":\"人从20到35岁属于成年期，这是人身生长发育相对稳定的时期，人体所吸收的营养素主要用于体内细胞、组织、器官等维护、更新和修复。很多影响生活质量的慢性疾病都源于这个时期，如糖尿病、高血压、肿瘤等。这些慢性疾病是否发生，60%取决于你的生活习惯和生活方式，需要特别注意。\",\"sangao\":\"三高症引起的心脑血管疾病已经成为人类致命的“头号杀手”，研究表明人体的血脂、血压、血糖三者指标异常同时出现并非偶然，通常都和长时间的生活方式不佳有关。配合用药的同时，也要减少高脂肪和零食，控制体重，控制吸烟饮酒。在医生的指导下进行有规律的运动，可以帮助保持身体素质，对心脏和血管都有益处，尽量减少对心脑血管、肝肾的伤害，改善生活质量。\"}',NULL,'没有关注',NULL),
	(10,'合理营养建议','建议您的健康饮食','{\'normal\':\'食物是能量和营养的来源。对于多数人来说，吃也是一种愉快的享受。吃到足够的食物几乎不成问题，但是应该知道食物是构筑身体的基础材料，只有均衡饮食、合理营养才能保证长期具有良好的精神状态、远离疾病并以最佳的状态工作、生活。\',\'sangao\':\'三高对身体的危害是巨大的，因为这是多发动脉硬化的症结。在饮食上建议采用“3-5-7饮食原则” 3高：高纤维、高新鲜度、高植物蛋白；5低：低脂肪、低胆固醇、低盐、低糖及低酒精量； 7分饱：不要暴饮暴食，饭菜只吃七分饱。\'}\n',NULL,'没有关注',NULL),
	(11,'有效运动建议','基本健康运动习惯养成','{\"title\":\"机体本身存在着调节心血管活动的自动机制。其中，分布在心血管各部位的感受器可以经常性地发挥调节作用，这些感应器可以通过运动的的方式来训练，帮助改善偏高的血脂、血压和血糖，令指标恢复正常，同时运动还能帮助维持比较理想的体重。\",\"items\":[\"中等强度避免运动损伤\",\"有氧运动提升心血管耐力\",\"每周3、4次，每次20~60分钟避免过度消耗\"]}','全身性、有节奏、放松的项目，最适合三高人群（高血脂、高血压、糖尿病)，避免静力性力量练习或闭气练习。 运动时间保证在30~60分钟，保证时间比强度更为重要，在运动过程中养成用力呼气的习惯，以有效降低胸腔内压。 注意出现心律失常、心动过速、头晕、恶心、呕吐等现象时停止运动。','没有关注','※消耗能量：不同生活活动和运动每小时每公斤体重所消耗的能量。数值越高代表单位时间内从事该项运动所消耗的能量越大，对减重越有帮助。\n※有氧等级：针对有氧运动而言，是对从事该项运动时应该达到的运动强度的描述。有氧等级越高，对有氧酵素、心血管及新陈代谢的影响就越大，最终会影响到体内的脂肪比例。\n有氧等级5：呼吸略有加快，会出汗，稍有疲惫感，但是很舒服，类似热身完成的感觉。\n有氧等级6：小幅度运动，或运动一段时间后的感觉，有疲惫感，但是知道自己可以坚持下来。\n有氧等级7：这是运动进入状态时的感觉，呼吸沉重，虽然可以说话，但是宁愿不说。\n有氧等级8：这是到达运动极限时的感觉。有明显的疲惫感，呼吸沉重急促，突破这个极限，保持平稳的运动，还会恢复到第7级状态。'),
	(12,'容颜健康评估','根据您对美好容颜部分问题的回答，\n评估您目前容颜状况处于：','{\"1\":{\"title\":\"面临皮肤问题困扰\",\"img\":\"rongyan1.jpg\",\"description\":\"皮肤是一面镜子，各种皮肤问题都反映着身体内在的问题，了解其原因所在，才能真正解决肌肤问题，所以说，美丽是由内而外的。先从生活习惯开始，改善饮食，起居，运动的状态。\"},\"2\":{\"title\":\"容颜衰老预警\",\"img\":\"rongyan2.jpg\",\"description\":\"羡慕别人完美的肌肤，又对自己问题层出不穷的皮肤好像无能为力。没关系，年轻的好处是新陈代谢旺盛，代谢速度比较快，找到问题的关键点，谁说皮肤不可以宛若新生。年轻肌肤保养的秘密：第一是纠正不良生活习惯，保证皮肤代谢水油平衡；第二是抵御外界不良因素干扰。\"},\"3\":{\"title\":\"容颜年轻态\",\"img\":\"rongyan3.jpg\",\"description\":\"\"}}','','容颜美好',NULL),
	(13,'合理营养建议','建议您的健康饮食','[\"清淡饮食能避免体内产生过多的垃圾，简单烹饪的天然食物能提供对皮肤有益的维生素和抗氧化物质。七分饱和细嚼慢咽的优雅态度也会有助于改善皮肤健康。\"]',NULL,'容颜美好',NULL),
	(14,'有效运动建议','基本健康运动习惯养成','{\"title\":\"运动是生活的乐趣和保持活力的方法，适度的有氧运动会令你精神饱满，面色红润。\",\"items\":[\"中等强度避免运动损伤\",\"有氧运动提升心血管耐力\",\"每周3、4次，每次20~60分钟避免过度消耗\"]}','尽量采用长时间、中等强度的有氧运动方式，这会帮助你提高心血管的耐力，也减少关节损伤的可能性。 美国运动医学会（ACSM）推荐20~60分钟的有氧活动，每周3~4次即可，对提高最大吸氧量而言，每周锻炼3次与5次的效果基本相同。 注意：运动过程中请把您的心率保持在112 - 140次/每分钟范围内。','容颜美好','※消耗能量：不同生活活动和运动每小时每公斤体重所消耗的能量。数值越高代表单位时间内从事该项运动所消耗的能量越大，对减重越有帮助。\n※有氧等级：针对有氧运动而言，是对从事该项运动时应该达到的运动强度的描述。有氧等级越高，对有氧酵素、心血管及新陈代谢的影响就越大，最终会影响到体内的脂肪比例。\n有氧等级5：呼吸略有加快，会出汗，稍有疲惫感，但是很舒服，类似热身完成的感觉。\n有氧等级6：小幅度运动，或运动一段时间后的感觉，有疲惫感，但是知道自己可以坚持下来。\n有氧等级7：这是运动进入状态时的感觉，呼吸沉重，虽然可以说话，但是宁愿不说。\n有氧等级8：这是到达运动极限时的感觉。有明显的疲惫感，呼吸沉重急促，突破这个极限，保持平稳的运动，还会恢复到第7级状态。'),
	(15,'生活方式建议','你的健康生活作息时间表——良好的生活方式使容颜更加美好','[[\"6:00~7:00\",\" 早晨的事情对于美肤很重要，另外早起才能早睡\"],[\"起床后的第一件事\",\" 500cc的温开水，补充一夜水分缺失，增加胃肠蠕动\"],[\"起床后的第二件事\",\" 按时排便，涤清肠道\"],[\"起床后的第三件事\",\" 谷物早餐， 减轻肠胃负担，随餐服用石榴精华胶囊或绿茶精华胶囊\"],[\"9:00~10:00\",\" 用淡茶或白水作为饮料，补充一日所需水分\"],[\"课间10分钟\",\" 10分钟面部按摩\"],[\"11:00~11:30\",\" 一个橙色或红色水果，补充体内所需铁和维生素C\"],[\"12:00~13:00\",\" 午餐7/8分饱，控制热量摄入，饭后散步20分钟\"],[\"18:30~19:30\",\" 晚餐尽量清淡、7/8分饱，随餐补充复合维生素片\"],[\"20:00~21:00\",\" 30~45分钟：有氧运动促进皮肤新陈代谢\"],[\"21:00~21:30\",\" 洗个热水澡，做个面膜放松精神，给皮肤足够的水分和营养\"],[\"22:00~23:00\",\" 按时进入睡眠状态，保证享受8小时充足的睡眠\"]]',NULL,'容颜美好',NULL),
	(16,'坏习惯弥补','坏习惯弥补建议','{\"bad\":\"改掉坏习惯对于改善身体状况永远是第一位的。对于戒除有困难者，建议采取以下方法以减少坏习惯带来的健康损害。\",\"good\":\"恭喜！您的生活习惯很好，继续保持那些好的生活习惯，对维护稳定的健康状态大有裨益。\"}',NULL,'容颜美好',NULL),
	(17,'形体健康评估',NULL,NULL,NULL,'优美体形',NULL),
	(18,'合理营养建议','建议您的健康饮食','[\"忍饥挨饿是减重的误区，不要强力对抗你的食欲，培养节制的习惯，可以循序渐进，从不吃撑到七分饱，再到不吃晚餐。让清淡的天然食物成为餐单的主角，变化会自然发生。每一口食物都是构建身体的材料。懂得如何“组合食物”还能帮助你提高燃脂效率，做到真正的减脂不减营养。\"]',NULL,'优美体形',NULL),
	(19,'有效运动建议','基本健康运动习惯养成','{\"title\":\"运动不仅仅是热量的消耗，更多的也是生活的乐趣和保持活力的方法。保证心率在适当范围内，多做些对关节无损的运动，有氧和无氧相结合更适合你。\",\"items\":[\"中等强度避免运动损伤\",\"有氧运动帮助全身脂肪燃烧\",\"结合无氧运动紧实肌肉\",\"每周3、4次，每次20~60分钟避免过度消耗\"]}','从能量消耗的角度出发，中等强度运动可以持续较长时间，总热量消耗就多，有氧运动会加强你的心肺功能，令体内产生更多的有氧酵素，从而消耗掉更多的脂肪，结合增加肌肉的无氧运动，这可以帮助你储存更多的有氧酵素。 有氧运动和无氧运动最好交替进行，这可以帮助你重塑肌肉结构，更好的燃烧掉多余脂肪，令你的身形变得紧实、纤长。 美国运动医学会（ACSM）推荐20~60分钟的有氧活动。每周3~4次即可，对提高最大吸氧量而言，每周锻炼3次与5次的效果基本相同。在有氧运动中保持你的心跳112 - 140次/每分钟，持续30分钟，这会帮助你包括腹部在内的全身脂肪得到很好的燃烧。','优美体形','※消耗能量：不同生活活动和运动每小时每公斤体重所消耗的能量。数值越高代表单位时间内从事该项运动所消耗的能量越大，对减重越有帮助。\n※有氧等级：针对有氧运动而言，是对从事该项运动时应该达到的运动强度的描述。有氧等级越高，对有氧酵素、心血管及新陈代谢的影响就越大，最终会影响到体内的脂肪比例。\n有氧等级5：呼吸略有加快，会出汗，稍有疲惫感，但是很舒服，类似热身完成的感觉。\n有氧等级6：小幅度运动，或运动一段时间后的感觉，有疲惫感，但是知道自己可以坚持下来。\n有氧等级7：这是运动进入状态时的感觉，呼吸沉重，虽然可以说话，但是宁愿不说。\n有氧等级8：这是到达运动极限时的感觉。有明显的疲惫感，呼吸沉重急促，突破这个极限，保持平稳的运动，还会恢复到第7级状态。'),
	(20,'生活方式建议','你的健康生活作息时间表——良好的生活方式使塑型事半功倍','[[\"6:00~7:00\",\"早晨的事情对于减重很重要，另外早起才能早睡\"],[\"起床后的第一件事\",\"500cc的温开水，增加胃肠蠕动，促进排便\"],[\"起床后的第二件事\",\"按时排便是涤清肠道，减小腹围的重要事情\"],[\"起床后的第三件事\",\"认真早餐，冲调一杯多种维生素复合营养粉，摄入足够的蛋白质和碳水化合物可以抑制一天对零食和甜食的渴望\"],[\"出发去上班\",\"收腹提臀，小步急走，尽量不乘电梯\"],[\"9:00~10:00\",\"用淡茶或白水作为饮料，控制隐形热量摄入\"],[\"课间10分钟\",\"10分钟办公室有氧操，放松僵硬疲劳的肌肉和脊椎。\"],[\"午餐前1个半小时，服用2片甲壳素片\",\"\"],[\"12:00~13:00\",\"午餐7/8分饱，控制热量摄入，饭后散步20分钟\"],[\"课间10分钟\",\"10分钟办公室有氧操，放松僵硬疲劳的肌肉和脊椎\"],[\"17:00\",\"晚餐前1个半小时，服用2片甲壳素片\"],[\"18:30~19:30\",\"晚餐尽量清淡、7/8分饱，之后停止进食。餐后服用左旋肉碱，为1小时后运动做好准备\"],[\"20:00~21:00\",\"30~45分钟：15分钟有氧运动结合器械训练或专项无氧训练\"],[\"21:00~21:30\",\"洗个热水澡，有助于放松和睡眠\"],[\"21:30~22:30\",\"进入睡眠准备状态，睡前腹部按揉，促进排便，减小腹围\"],[\"22:00~23:00\",\"按时进入睡眠，保证正常新陈代谢\"]]',NULL,'优美体形',NULL),
	(21,'坏习惯弥补','坏习惯弥补建议','{\"bad\":\"改掉坏习惯对于改善身体状况永远是第一位的。对于戒除有困难者，建议采取以下方法以减少坏习惯带来的健康损害。\",\"good\":\"恭喜！您的生活习惯很好，继续保持那些好的生活习惯，对维护稳定的健康状态大有裨益。\"}',NULL,'优美体形',NULL),
	(22,'精力脑力评估','根据您对精力脑力部分问题的回答，\n评估您目前精力脑力状况处于：','{\"1\":{\"img\":\"yali1.jpg\",\"title\":\"压力水平极高\",\"description\":\"压力荷尔蒙的长期刺激将损害身体健康，降低人体免疫功能，使人们更容易受到疾病感染，处在压力下的每一分钟还会加速身体的衰老。你现在需要一段时间整顿心情并配合良好的生活方式提升精力脑力，淡化压力给身体带来的各种不适，还可配合使用一些营养补充品来持续提升你的整体健康水平和精神状态。\"},\"2\":{\"title\":\"压力水平较高\",\"img\":\"yali2.jpg\",\"description\":\"人长期处于压力状态使得机体常规修复和保养工作的能量，如消化吸收、清除废物和更新再生等所需的能量，被挪用来应对压力。包括头痛、胃口不佳、脾气暴躁、脱发、肌肉疼痛在内的所有症状都可能与情绪压力有关。 发生不适症状的同时，处在压力下的每一分钟都会加速身体的衰老。可以通过调节情绪并配合良好的生活方式提升精力、淡化压力为身体带来的各种不适，配合使用一些营养补充品来提升你的整体健康水平和精神状态。\"},\"3\":{\"title\":\"压力水平始终\",\"img\":\"yali3.jpg\",\"description\":\"当各方面压力积累到一定程度时，也会对你的身心健康造成严重影响， 产生各种不适症状，处在压力下的每一分钟还会加速身体的衰老。可以通过放松情绪并养成良好的生活习惯，把压力保持在一个可控制的范围内，让你的生活更加从容。若配合使用一些营养补充品来提升整体健康水平和精神状态，会使机体恢复高要求生活所需的能量和活力。\"},\"4\":{\"title\":\"压力水平\",\"img\":\"yali4.jpg\",\"description\":\"压力常常在事物失衡的时候出现，寻找和实践达到心理动态平衡的途径，要注意你的情感，当情感变得强烈时，要了解它的状况。学会表达情感，不要压抑它，照顾好你的身体，有规律地进行锻炼。保证良好的营养以及合适的睡眠时间，不要等到被大量的压力源袭击时才开始创建你的平静生活。把负性压力转变为动力也会让你富有生机活力。保证良好的生活方式，配合使用一些营养补充品来提升整体健康水平和精神状态，改善身体能量的代谢，减少压力反应造成身体所需营养的流失。\"}}',NULL,'精力脑力',NULL),
	(23,'合理营养建议','建议您的健康饮食','[\'高压力下，最容易消化不良，怎么吃更加重要？没有胃口时更要饮食清淡、容易消化，半饱即可。食欲旺盛时，稍有节制，细嚼慢咽，增加满足感。避免大量的无营养加工食物，尤其是高糖的饮料和甜点，会耗费身体对抗压力的营养素，而且会影响脑力水平。常处于压力下，多吃些坚果和水果，或糙米加鱼肉。这种优质蛋白和脂肪配合复杂碳水化合物的组合能提供抗压力的丰富营养素，而且保证能量均匀释放，令血糖平稳，精力充沛，头脑敏锐。\']',NULL,'精力脑力',NULL),
	(24,'有效运动建议','基本健康运动习惯养成','{\"title\":\"一定要放松——放松方法有助你减缓呼吸、专注于此时此地，树立活在当下的理念，而不是担心未来可能发生的事情。常用放松方法包括冥想、正念、太极和瑜伽。更有效的方法是到户外散步，或者参加轻松的体育活动。\",\"items\":[\"静下心来，放空脑袋\",\"心血管耐力型有氧运动缓解压力\",\"自我的、非竞争性运动愉悦情绪\",\"每周3~4次，20~60分钟的运动时长\"]}','希望改变压力水平，应该采用科学的运动方式，长时间、中等强度的运动方式，帮助你提高心血管的耐力，也减少关节损伤的可能性。 心血管耐力型运动，能够由大脑和其他部位释放β-内啡肽（天然镇定剂）使人感到心情愉快，而且这种重复的、有规律性的运动，提供了一种冥想形式，将大脑左半球的优势转移到右半球。 美国运动医学会（ACSM）推荐每周3~4次，20~60分钟的有氧活动。 结合心理体操，瑜伽，或是静坐，把你的脑袋调至放空的频率，渐渐让自己安静下来。','精力脑力','※消耗能量：不同生活活动和运动每小时每公斤体重所消耗的能量。数值越高代表单位时间内从事该项运动所消耗的能量越大，对减重越有帮助。\n※有氧等级：针对有氧运动而言，是对从事该项运动时应该达到的运动强度的描述。有氧等级越高，对有氧酵素、心血管及新陈代谢的影响就越大，最终会影响到体内的脂肪比例。\n有氧等级5：呼吸略有加快，会出汗，稍有疲惫感，但是很舒服，类似热身完成的感觉。\n有氧等级6：小幅度运动，或运动一段时间后的感觉，有疲惫感，但是知道自己可以坚持下来。\n有氧等级7：这是运动进入状态时的感觉，呼吸沉重，虽然可以说话，但是宁愿不说。\n有氧等级8：这是到达运动极限时的感觉。有明显的疲惫感，呼吸沉重急促，突破这个极限，保持平稳的运动，还会恢复到第7级状态。'),
	(25,'生活方式建议','你的健康生活作息时间表——良好的生活方式提升精力脑力','[[\"6:00~7:00 \",\" 和阳光一同起床，是令一天充满活力的基础，在床上做些放松伸展活动\"],[\"起床后的第一件事\",\" 对着镜子，记下自己的微笑，经常微笑的人是最擅长释放压力的人\"],[\"起床后的第二件事\",\" 足量维他命B族早餐，全谷类、牛奶、坚果等，随餐服用啤酒酵母片\"],[\"9:00~10:00\",\" 淡茶为饮，吃块ω-3不饱和脂肪酸软糖，清心静气、提神醒脑，\"],[\"工作时\",\" 一次只做一件事，尝试活在当下\"],[\"课间10分钟\",\" 10分钟闭目养神或腹式呼吸\"],[\"12:00~13:00 \",\"午餐尽量多吃深绿色蔬菜、牡蛎、海产，适量镁、锌可安定神经，帮助放松\"],[\"15:00~15:10\",\" 压力损耗维生素Ｃ使免疫力降低：柳橙、奇异果、葡萄柚等可轻松补充\"],[\"课间10分钟 \",\"10分钟闭目养神或腹式呼吸\"],[\"18:30~19:30\",\" 清淡晚餐避免肠胃负担或者找积极乐观的朋友聊天\"],[\"20:00~21:00\",\" 30~45分钟运动：这是和自己独处的时间，不容打扰。有氧运动或瑜伽、冥想\"],[\"21:00~21:30\",\" 洗个热水澡，有助于放松和睡眠\"],[\"22:00~23:00\",\" 睡前补充西番莲复合胶囊，然后按时进入睡眠，保证充足睡眠\"]]',NULL,'精力脑力',NULL),
	(26,'坏习惯弥补','坏习惯弥补建议','{\"bad\":\"改掉坏习惯对于改善身体状况永远是第一位的。对于戒除有困难者，建议采取以下方法以减少坏习惯带来的健康损害。\",\"good\":\"恭喜！您的生活习惯很好，继续保持那些好的生活习惯，对维护稳定的健康状态大有裨益。\"}',NULL,'精力脑力',NULL),
	(27,'生活方式建议','你的健康生活作息时间表','[[\"6:00~7:00\",\" 早起，为了确保在正确的时间睡眠\"],[\"起床后的第一件事\",\" 一杯250ml的温开水，补充花青素（葡萄籽）、鞣花酸（石榴精华）、白藜芦醇（红酒精华）等抗氧化元素\"],[\"起床后的第二件事\",\" 抗老排毒全谷物早餐（薯类、豆类、谷类、玉米等）\"],[\"9:00~10:00\",\" 坚果时间，富含抗氧化物质（维生素E）高的山核桃、榛子、开心果、杏仁、花生等\"],[\"12:00~13:00\",\" 午餐多样化，协同抗氧化。摄入充足的优质蛋白（鱼、蛋、豆类），饭后散步20分钟，帮助消化\"],[\"15:00~16:30\",\" 富含维生素C的水果猕猴桃、樱桃番茄、橙子等帮助清除自由基\"],[\"18:30~19:30\",\" 晚餐清淡、足量抗氧化蔬菜（西兰花、菠菜、芦笋等）为主，7～8分饱\"],[\"21:00~21:30\",\" 一小杯葡萄酒，保护心脑血管\"],[\"22:00~23:00\",\" 睡前补充钙镁软胶囊，帮助入睡，获得充足睡眠\"]]',NULL,'抗衰老方面',NULL),
	(28,'坏习惯弥补','坏习惯弥补建议','{\"bad\":\"改掉坏习惯对于改善身体状况永远是第一位的。对于戒除有困难者，建议采取以下方法以减少坏习惯带来的健康损害。\",\"good\":\"恭喜！您的生活习惯很好，继续保持那些好的生活习惯，对维护稳定的健康状态大有裨益。\"}',NULL,'没有关注',NULL),
	(31,'坏习惯弥补','坏习惯弥补建议','{\"bad\":\"改掉坏习惯对于改善身体状况永远是第一位的。对于戒除有困难者，建议采取以下方法以减少坏习惯带来的健康损害。\",\"good\":\"恭喜！您的生活习惯很好，继续保持那些好的生活习惯，对维护稳定的健康状态大有裨益。\"}',NULL,'抗衰老方面',NULL),
	(30,'生活方式建议','你的健康生活作息时间表','[[\"6:00~7:00 \",\"早晨的事情对于健康很重要，另外早起才能早睡\"],[\"起床后的第一件事\",\" 500cc的温开水，促进血液循环，增加胃肠蠕动\"],[\"起床后的第二件事\",\" 按时排便，涤清肠道\"],[\"起床后的第三件事\",\" 谷物早餐，有利于胆固醇排出\"],[\"9:00~10:00\",\" 用淡茶或白水作为饮料，补充一日所需水分\"],[\"12:00~13:00\",\" 午餐7/8分饱，豆类和鱼为主，控制热量摄入，饭后散步20分钟\"],[\"13:00~14:00\",\" 每天中午午休30分钟，心脏病死亡的几率会下降30%\"],[\"18:30~19:30\",\" 晚餐尽量清淡、基本素食、7/8分饱\"],[\"20:00~21:00 \",\"30~45分钟：步行或休闲活动\"],[\"22:00~23:00\",\" 按时进入睡眠状态，保证享受8小时充足的睡眠\"]]',NULL,'没有关注',NULL);

